hebsuperobject.biblemap.put("b23_57_11", new hebsuperobject(23,57,11,"ואת מי דאגת ותיראי כי תכזבי ואותי לא זכרת לא שמת על לבך הלא אני מחשה ומעלם ואותי לא תיראי׃"));
hebsuperobject.biblemap.put("b23_57_12", new hebsuperobject(23,57,12,"אני אגיד צדקתך ואת מעשיך ולא יועילוך׃"));
hebsuperobject.biblemap.put("b23_57_13", new hebsuperobject(23,57,13,"בזעקך יצילך קבוציך ואת כלם ישא רוח יקח הבל והחוסה בי ינחל ארץ ויירש הר קדשי׃"));
hebsuperobject.biblemap.put("b23_57_14", new hebsuperobject(23,57,14,"ואמר סלו סלו פנו דרך הרימו מכשול מדרך עמי׃"));
hebsuperobject.biblemap.put("b23_57_15", new hebsuperobject(23,57,15,"כי כה אמר רם ונשא שכן עד וקדוש שמו מרום וקדוש אשכון ואת דכא ושפל רוח להחיות רוח שפלים ולהחיות לב נדכאים׃"));
hebsuperobject.biblemap.put("b23_57_16", new hebsuperobject(23,57,16,"כי לא לעולם אריב ולא לנצח אקצוף כי רוח מלפני יעטוף ונשמות אני עשיתי׃"));
hebsuperobject.biblemap.put("b23_57_17", new hebsuperobject(23,57,17,"בעון בצעו קצפתי ואכהו הסתר ואקצף וילך שובב בדרך לבו׃"));
hebsuperobject.biblemap.put("b23_57_18", new hebsuperobject(23,57,18,"דרכיו ראיתי וארפאהו ואנחהו ואשלם נחמים לו ולאבליו׃"));
hebsuperobject.biblemap.put("b23_57_19", new hebsuperobject(23,57,19,"בורא נוב שפתים שלום שלום לרחוק ולקרוב אמר יהוה ורפאתיו׃"));
hebsuperobject.biblemap.put("b23_57_20", new hebsuperobject(23,57,20,"והרשעים כים נגרש כי השקט לא יוכל ויגרשו מימיו רפש וטיט׃"));
hebsuperobject.biblemap.put("b23_57_21", new hebsuperobject(23,57,21,"אין שלום אמר אלהי לרשעים׃"));
hebsuperobject.biblemap.put("b23_58_1", new hebsuperobject(23,58,1,"קרא בגרון אל תחשך כשופר הרם קולך והגד לעמי פשעם ולבית יעקב חטאתם׃"));
hebsuperobject.biblemap.put("b23_58_2", new hebsuperobject(23,58,2,"ואותי יום יום ידרשון ודעת דרכי יחפצון כגוי אשר צדקה עשה ומשפט אלהיו לא עזב ישאלוני משפטי צדק קרבת אלהים יחפצון׃"));
hebsuperobject.biblemap.put("b23_58_3", new hebsuperobject(23,58,3,"למה צמנו ולא ראית ענינו נפשנו ולא תדע הן ביום צמכם תמצאו חפץ וכל עצביכם תנגשו׃"));
hebsuperobject.biblemap.put("b23_58_4", new hebsuperobject(23,58,4,"הן לריב ומצה תצומו ולהכות באגרף רשע לא תצומו כיום להשמיע במרום קולכם׃"));
hebsuperobject.biblemap.put("b23_58_5", new hebsuperobject(23,58,5,"הכזה יהיה צום אבחרהו יום ענות אדם נפשו הלכף כאגמן ראשו ושק ואפר יציע הלזה תקרא צום ויום רצון ליהוה׃"));
hebsuperobject.biblemap.put("b23_58_6", new hebsuperobject(23,58,6,"הלוא זה צום אבחרהו פתח חרצבות רשע התר אגדות מוטה ושלח רצוצים חפשים וכל מוטה תנתקו׃"));
hebsuperobject.biblemap.put("b23_58_7", new hebsuperobject(23,58,7,"הלוא פרס לרעב לחמך ועניים מרודים תביא בית כי תראה ערם וכסיתו ומבשרך לא תתעלם׃"));
hebsuperobject.biblemap.put("b23_58_8", new hebsuperobject(23,58,8,"אז יבקע כשחר אורך וארכתך מהרה תצמח והלך לפניך צדקך כבוד יהוה יאספך׃"));
hebsuperobject.biblemap.put("b23_58_9", new hebsuperobject(23,58,9,"אז תקרא ויהוה יענה תשוע ויאמר הנני אם תסיר מתוכך מוטה שלח אצבע ודבר און׃"));
hebsuperobject.biblemap.put("b23_58_10", new hebsuperobject(23,58,10,"ותפק לרעב נפשך ונפש נענה תשביע וזרח בחשך אורך ואפלתך כצהרים׃"));
hebsuperobject.biblemap.put("b23_58_11", new hebsuperobject(23,58,11,"ונחך יהוה תמיד והשביע בצחצחות נפשך ועצמתיך יחליץ והיית כגן רוה וכמוצא מים אשר לא יכזבו מימיו׃"));
hebsuperobject.biblemap.put("b23_58_12", new hebsuperobject(23,58,12,"ובנו ממך חרבות עולם מוסדי דור ודור תקומם וקרא לך גדר פרץ משבב נתיבות לשבת׃"));
hebsuperobject.biblemap.put("b23_58_13", new hebsuperobject(23,58,13,"אם תשיב משבת רגלך עשות חפציך ביום קדשי וקראת לשבת ענג לקדוש יהוה מכבד וכבדתו מעשות דרכיך ממצוא חפצך ודבר דבר׃"));
hebsuperobject.biblemap.put("b23_58_14", new hebsuperobject(23,58,14,"אז תתענג על יהוה והרכבתיך על במותי ארץ והאכלתיך נחלת יעקב אביך כי פי יהוה דבר׃"));
hebsuperobject.biblemap.put("b23_59_1", new hebsuperobject(23,59,1,"הן לא קצרה יד יהוה מהושיע ולא כבדה אזנו משמוע׃"));
hebsuperobject.biblemap.put("b23_59_2", new hebsuperobject(23,59,2,"כי אם עונתיכם היו מבדלים בינכם לבין אלהיכם וחטאותיכם הסתירו פנים מכם משמוע׃"));
hebsuperobject.biblemap.put("b23_59_3", new hebsuperobject(23,59,3,"כי כפיכם נגאלו בדם ואצבעותיכם בעון שפתותיכם דברו שקר לשונכם עולה תהגה׃"));
hebsuperobject.biblemap.put("b23_59_4", new hebsuperobject(23,59,4,"אין קרא בצדק ואין נשפט באמונה בטוח על תהו ודבר שוא הרו עמל והוליד און׃"));
hebsuperobject.biblemap.put("b23_59_5", new hebsuperobject(23,59,5,"ביצי צפעוני בקעו וקורי עכביש יארגו האכל מביציהם ימות והזורה תבקע אפעה׃"));
hebsuperobject.biblemap.put("b23_59_6", new hebsuperobject(23,59,6,"קוריהם לא יהיו לבגד ולא יתכסו במעשיהם מעשיהם מעשי און ופעל חמס בכפיהם׃"));
hebsuperobject.biblemap.put("b23_59_7", new hebsuperobject(23,59,7,"רגליהם לרע ירצו וימהרו לשפך דם נקי מחשבותיהם מחשבות און שד ושבר במסלותם׃"));
hebsuperobject.biblemap.put("b23_59_8", new hebsuperobject(23,59,8,"דרך שלום לא ידעו ואין משפט במעגלותם נתיבותיהם עקשו להם כל דרך בה לא ידע שלום׃"));
hebsuperobject.biblemap.put("b23_59_9", new hebsuperobject(23,59,9,"על כן רחק משפט ממנו ולא תשיגנו צדקה נקוה לאור והנה חשך לנגהות באפלות נהלך׃"));
hebsuperobject.biblemap.put("b23_59_10", new hebsuperobject(23,59,10,"נגששה כעורים קיר וכאין עינים נגששה כשלנו בצהרים כנשף באשמנים כמתים׃"));
hebsuperobject.biblemap.put("b23_59_11", new hebsuperobject(23,59,11,"נהמה כדבים כלנו וכיונים הגה נהגה נקוה למשפט ואין לישועה רחקה ממנו׃"));
hebsuperobject.biblemap.put("b23_59_12", new hebsuperobject(23,59,12,"כי רבו פשעינו נגדך וחטאותינו ענתה בנו כי פשעינו אתנו ועונתינו ידענום׃"));
hebsuperobject.biblemap.put("b23_59_13", new hebsuperobject(23,59,13,"פשע וכחש ביהוה ונסוג מאחר אלהינו דבר עשק וסרה הרו והגו מלב דברי שקר׃"));
hebsuperobject.biblemap.put("b23_59_14", new hebsuperobject(23,59,14,"והסג אחור משפט וצדקה מרחוק תעמד כי כשלה ברחוב אמת ונכחה לא תוכל לבוא׃"));
hebsuperobject.biblemap.put("b23_59_15", new hebsuperobject(23,59,15,"ותהי האמת נעדרת וסר מרע משתולל וירא יהוה וירע בעיניו כי אין משפט׃"));
hebsuperobject.biblemap.put("b23_59_16", new hebsuperobject(23,59,16,"וירא כי אין איש וישתומם כי אין מפגיע ותושע לו זרעו וצדקתו היא סמכתהו׃"));
hebsuperobject.biblemap.put("b23_59_17", new hebsuperobject(23,59,17,"וילבש צדקה כשרין וכובע ישועה בראשו וילבש בגדי נקם תלבשת ויעט כמעיל קנאה׃"));
hebsuperobject.biblemap.put("b23_59_18", new hebsuperobject(23,59,18,"כעל גמלות כעל ישלם חמה לצריו גמול לאיביו לאיים גמול ישלם׃"));
hebsuperobject.biblemap.put("b23_59_19", new hebsuperobject(23,59,19,"וייראו ממערב את שם יהוה וממזרח שמש את כבודו כי יבוא כנהר צר רוח יהוה נססה בו׃"));
hebsuperobject.biblemap.put("b23_59_20", new hebsuperobject(23,59,20,"ובא לציון גואל ולשבי פשע ביעקב נאם יהוה׃"));
hebsuperobject.biblemap.put("b23_59_21", new hebsuperobject(23,59,21,"ואני זאת בריתי אותם אמר יהוה רוחי אשר עליך ודברי אשר שמתי בפיך לא ימושו מפיך ומפי זרעך ומפי זרע זרעך אמר יהוה מעתה ועד עולם׃"));
hebsuperobject.biblemap.put("b23_60_1", new hebsuperobject(23,60,1,"קומי אורי כי בא אורך וכבוד יהוה עליך זרח׃"));
hebsuperobject.biblemap.put("b23_60_2", new hebsuperobject(23,60,2,"כי הנה החשך יכסה ארץ וערפל לאמים ועליך יזרח יהוה וכבודו עליך יראה׃"));
hebsuperobject.biblemap.put("b23_60_3", new hebsuperobject(23,60,3,"והלכו גוים לאורך ומלכים לנגה זרחך׃"));
hebsuperobject.biblemap.put("b23_60_4", new hebsuperobject(23,60,4,"שאי סביב עיניך וראי כלם נקבצו באו לך בניך מרחוק יבאו ובנתיך על צד תאמנה׃"));
hebsuperobject.biblemap.put("b23_60_5", new hebsuperobject(23,60,5,"אז תראי ונהרת ופחד ורחב לבבך כי יהפך עליך המון ים חיל גוים יבאו לך׃"));
hebsuperobject.biblemap.put("b23_60_6", new hebsuperobject(23,60,6,"שפעת גמלים תכסך בכרי מדין ועיפה כלם משבא יבאו זהב ולבונה ישאו ותהלת יהוה יבשרו׃"));
hebsuperobject.biblemap.put("b23_60_7", new hebsuperobject(23,60,7,"כל צאן קדר יקבצו לך אילי נביות ישרתונך יעלו על רצון מזבחי ובית תפארתי אפאר׃"));
hebsuperobject.biblemap.put("b23_60_8", new hebsuperobject(23,60,8,"מי אלה כעב תעופינה וכיונים אל ארבתיהם׃"));
hebsuperobject.biblemap.put("b23_60_9", new hebsuperobject(23,60,9,"כי לי איים יקוו ואניות תרשיש בראשנה להביא בניך מרחוק כספם וזהבם אתם לשם יהוה אלהיך ולקדוש ישראל כי פארך׃"));
hebsuperobject.biblemap.put("b23_60_10", new hebsuperobject(23,60,10,"ובנו בני נכר חמתיך ומלכיהם ישרתונך כי בקצפי הכיתיך וברצוני רחמתיך׃"));
hebsuperobject.biblemap.put("b23_60_11", new hebsuperobject(23,60,11,"ופתחו שעריך תמיד יומם ולילה לא יסגרו להביא אליך חיל גוים ומלכיהם נהוגים׃"));
hebsuperobject.biblemap.put("b23_60_12", new hebsuperobject(23,60,12,"כי הגוי והממלכה אשר לא יעבדוך יאבדו והגוים חרב יחרבו׃"));
hebsuperobject.biblemap.put("b23_60_13", new hebsuperobject(23,60,13,"כבוד הלבנון אליך יבוא ברוש תדהר ותאשור יחדו לפאר מקום מקדשי ומקום רגלי אכבד׃"));
hebsuperobject.biblemap.put("b23_60_14", new hebsuperobject(23,60,14,"והלכו אליך שחוח בני מעניך והשתחוו על כפות רגליך כל מנאציך וקראו לך עיר יהוה ציון קדוש ישראל׃"));
hebsuperobject.biblemap.put("b23_60_15", new hebsuperobject(23,60,15,"תחת היותך עזובה ושנואה ואין עובר ושמתיך לגאון עולם משוש דור ודור׃"));
hebsuperobject.biblemap.put("b23_60_16", new hebsuperobject(23,60,16,"וינקת חלב גוים ושד מלכים תינקי וידעת כי אני יהוה מושיעך וגאלך אביר יעקב׃"));
hebsuperobject.biblemap.put("b23_60_17", new hebsuperobject(23,60,17,"תחת הנחשת אביא זהב ותחת הברזל אביא כסף ותחת העצים נחשת ותחת האבנים ברזל ושמתי פקדתך שלום ונגשיך צדקה׃"));
hebsuperobject.biblemap.put("b23_60_18", new hebsuperobject(23,60,18,"לא ישמע עוד חמס בארצך שד ושבר בגבוליך וקראת ישועה חומתיך ושעריך תהלה׃"));
hebsuperobject.biblemap.put("b23_60_19", new hebsuperobject(23,60,19,"לא יהיה לך עוד השמש לאור יומם ולנגה הירח לא יאיר לך והיה לך יהוה לאור עולם ואלהיך לתפארתך׃"));
hebsuperobject.biblemap.put("b23_60_20", new hebsuperobject(23,60,20,"לא יבוא עוד שמשך וירחך לא יאסף כי יהוה יהיה לך לאור עולם ושלמו ימי אבלך׃"));
hebsuperobject.biblemap.put("b23_60_21", new hebsuperobject(23,60,21,"ועמך כלם צדיקים לעולם יירשו ארץ נצר מטעו מעשה ידי להתפאר׃"));
hebsuperobject.biblemap.put("b23_60_22", new hebsuperobject(23,60,22,"הקטן יהיה לאלף והצעיר לגוי עצום אני יהוה בעתה אחישנה׃"));
hebsuperobject.biblemap.put("b23_61_1", new hebsuperobject(23,61,1,"רוח אדני יהוה עלי יען משח יהוה אתי לבשר ענוים שלחני לחבש לנשברי לב לקרא לשבוים דרור ולאסורים פקח קוח׃"));
hebsuperobject.biblemap.put("b23_61_2", new hebsuperobject(23,61,2,"לקרא שנת רצון ליהוה ויום נקם לאלהינו לנחם כל אבלים׃"));
hebsuperobject.biblemap.put("b23_61_3", new hebsuperobject(23,61,3,"לשום לאבלי ציון לתת להם פאר תחת אפר שמן ששון תחת אבל מעטה תהלה תחת רוח כהה וקרא להם אילי הצדק מטע יהוה להתפאר׃"));
hebsuperobject.biblemap.put("b23_61_4", new hebsuperobject(23,61,4,"ובנו חרבות עולם שממות ראשנים יקוממו וחדשו ערי חרב שממות דור ודור׃"));
hebsuperobject.biblemap.put("b23_61_5", new hebsuperobject(23,61,5,"ועמדו זרים ורעו צאנכם ובני נכר אכריכם וכרמיכם׃"));
hebsuperobject.biblemap.put("b23_61_6", new hebsuperobject(23,61,6,"ואתם כהני יהוה תקראו משרתי אלהינו יאמר לכם חיל גוים תאכלו ובכבודם תתימרו׃"));
hebsuperobject.biblemap.put("b23_61_7", new hebsuperobject(23,61,7,"תחת בשתכם משנה וכלמה ירנו חלקם לכן בארצם משנה יירשו שמחת עולם תהיה להם׃"));
hebsuperobject.biblemap.put("b23_61_8", new hebsuperobject(23,61,8,"כי אני יהוה אהב משפט שנא גזל בעולה ונתתי פעלתם באמת וברית עולם אכרות להם׃"));
hebsuperobject.biblemap.put("b23_61_9", new hebsuperobject(23,61,9,"ונודע בגוים זרעם וצאצאיהם בתוך העמים כל ראיהם יכירום כי הם זרע ברך יהוה׃"));
hebsuperobject.biblemap.put("b23_61_10", new hebsuperobject(23,61,10,"שוש אשיש ביהוה תגל נפשי באלהי כי הלבישני בגדי ישע מעיל צדקה יעטני כחתן יכהן פאר וככלה תעדה כליה׃"));
hebsuperobject.biblemap.put("b23_61_11", new hebsuperobject(23,61,11,"כי כארץ תוציא צמחה וכגנה זרועיה תצמיח כן אדני יהוה יצמיח צדקה ותהלה נגד כל הגוים׃"));
hebsuperobject.biblemap.put("b23_62_1", new hebsuperobject(23,62,1,"למען ציון לא אחשה ולמען ירושלם לא אשקוט עד יצא כנגה צדקה וישועתה כלפיד יבער׃"));
hebsuperobject.biblemap.put("b23_62_2", new hebsuperobject(23,62,2,"וראו גוים צדקך וכל מלכים כבודך וקרא לך שם חדש אשר פי יהוה יקבנו׃"));
hebsuperobject.biblemap.put("b23_62_3", new hebsuperobject(23,62,3,"והיית עטרת תפארת ביד יהוה וצנוף מלוכה בכף אלהיך׃"));
hebsuperobject.biblemap.put("b23_62_4", new hebsuperobject(23,62,4,"לא יאמר לך עוד עזובה ולארצך לא יאמר עוד שממה כי לך יקרא חפצי בה ולארצך בעולה כי חפץ יהוה בך וארצך תבעל׃"));
hebsuperobject.biblemap.put("b23_62_5", new hebsuperobject(23,62,5,"כי יבעל בחור בתולה יבעלוך בניך ומשוש חתן על כלה ישיש עליך אלהיך׃"));
hebsuperobject.biblemap.put("b23_62_6", new hebsuperobject(23,62,6,"על חומתיך ירושלם הפקדתי שמרים כל היום וכל הלילה תמיד לא יחשו המזכרים את יהוה אל דמי לכם׃"));
hebsuperobject.biblemap.put("b23_62_7", new hebsuperobject(23,62,7,"ואל תתנו דמי לו עד יכונן ועד ישים את ירושלם תהלה בארץ׃"));
hebsuperobject.biblemap.put("b23_62_8", new hebsuperobject(23,62,8,"נשבע יהוה בימינו ובזרוע עזו אם אתן את דגנך עוד מאכל לאיביך ואם ישתו בני נכר תירושך אשר יגעת בו׃"));
hebsuperobject.biblemap.put("b23_62_9", new hebsuperobject(23,62,9,"כי מאספיו יאכלהו והללו את יהוה ומקבציו ישתהו בחצרות קדשי׃"));
hebsuperobject.biblemap.put("b23_62_10", new hebsuperobject(23,62,10,"עברו עברו בשערים פנו דרך העם סלו סלו המסלה סקלו מאבן הרימו נס על העמים׃"));
hebsuperobject.biblemap.put("b23_62_11", new hebsuperobject(23,62,11,"הנה יהוה השמיע אל קצה הארץ אמרו לבת ציון הנה ישעך בא הנה שכרו אתו ופעלתו לפניו׃"));
hebsuperobject.biblemap.put("b23_62_12", new hebsuperobject(23,62,12,"וקראו להם עם הקדש גאולי יהוה ולך יקרא דרושה עיר לא נעזבה׃"));
hebsuperobject.biblemap.put("b23_63_1", new hebsuperobject(23,63,1,"מי זה בא מאדום חמוץ בגדים מבצרה זה הדור בלבושו צעה ברב כחו אני מדבר בצדקה רב להושיע׃"));
hebsuperobject.biblemap.put("b23_63_2", new hebsuperobject(23,63,2,"מדוע אדם ללבושך ובגדיך כדרך בגת׃"));
hebsuperobject.biblemap.put("b23_63_3", new hebsuperobject(23,63,3,"פורה דרכתי לבדי ומעמים אין איש אתי ואדרכם באפי וארמסם בחמתי ויז נצחם על בגדי וכל מלבושי אגאלתי׃"));
hebsuperobject.biblemap.put("b23_63_4", new hebsuperobject(23,63,4,"כי יום נקם בלבי ושנת גאולי באה׃"));
hebsuperobject.biblemap.put("b23_63_5", new hebsuperobject(23,63,5,"ואביט ואין עזר ואשתומם ואין סומך ותושע לי זרעי וחמתי היא סמכתני׃"));
hebsuperobject.biblemap.put("b23_63_6", new hebsuperobject(23,63,6,"ואבוס עמים באפי ואשכרם בחמתי ואוריד לארץ נצחם׃"));
hebsuperobject.biblemap.put("b23_63_7", new hebsuperobject(23,63,7,"חסדי יהוה אזכיר תהלת יהוה כעל כל אשר גמלנו יהוה ורב טוב לבית ישראל אשר גמלם כרחמיו וכרב חסדיו׃"));
hebsuperobject.biblemap.put("b23_63_8", new hebsuperobject(23,63,8,"ויאמר אך עמי המה בנים לא ישקרו ויהי להם למושיע׃"));
hebsuperobject.biblemap.put("b23_63_9", new hebsuperobject(23,63,9,"בכל צרתם לא צר ומלאך פניו הושיעם באהבתו ובחמלתו הוא גאלם וינטלם וינשאם כל ימי עולם׃"));
hebsuperobject.biblemap.put("b23_63_10", new hebsuperobject(23,63,10,"והמה מרו ועצבו את רוח קדשו ויהפך להם לאויב הוא נלחם בם׃"));
hebsuperobject.biblemap.put("b23_63_11", new hebsuperobject(23,63,11,"ויזכר ימי עולם משה עמו איה המעלם מים את רעי צאנו איה השם בקרבו את רוח קדשו׃"));
hebsuperobject.biblemap.put("b23_63_12", new hebsuperobject(23,63,12,"מוליך לימין משה זרוע תפארתו בוקע מים מפניהם לעשות לו שם עולם׃"));
hebsuperobject.biblemap.put("b23_63_13", new hebsuperobject(23,63,13,"מוליכם בתהמות כסוס במדבר לא יכשלו׃"));
hebsuperobject.biblemap.put("b23_63_14", new hebsuperobject(23,63,14,"כבהמה בבקעה תרד רוח יהוה תניחנו כן נהגת עמך לעשות לך שם תפארת׃"));
hebsuperobject.biblemap.put("b23_63_15", new hebsuperobject(23,63,15,"הבט משמים וראה מזבל קדשך ותפארתך איה קנאתך וגבורתך המון מעיך ורחמיך אלי התאפקו׃"));
hebsuperobject.biblemap.put("b23_63_16", new hebsuperobject(23,63,16,"כי אתה אבינו כי אברהם לא ידענו וישראל לא יכירנו אתה יהוה אבינו גאלנו מעולם שמך׃"));
hebsuperobject.biblemap.put("b23_63_17", new hebsuperobject(23,63,17,"למה תתענו יהוה מדרכיך תקשיח לבנו מיראתך שוב למען עבדיך שבטי נחלתך׃"));
hebsuperobject.biblemap.put("b23_63_18", new hebsuperobject(23,63,18,"למצער ירשו עם קדשך צרינו בוססו מקדשך׃"));
hebsuperobject.biblemap.put("b23_63_19", new hebsuperobject(23,63,19,"היינו מעולם לא משלת בם לא נקרא שמך עליהם׃"));
