hebsuperobject.biblemap.put("b23_64_3", new hebsuperobject(23,64,3,"בעשותך נוראות לא נקוה ירדת מפניך הרים נזלו׃"));
hebsuperobject.biblemap.put("b23_64_4", new hebsuperobject(23,64,4,"ומעולם לא שמעו לא האזינו עין לא ראתה אלהים זולתך יעשה למחכה לו׃"));
hebsuperobject.biblemap.put("b23_64_5", new hebsuperobject(23,64,5,"פגעת את שש ועשה צדק בדרכיך יזכרוך הן אתה קצפת ונחטא בהם עולם ונושע׃"));
hebsuperobject.biblemap.put("b23_64_6", new hebsuperobject(23,64,6,"ונהי כטמא כלנו וכבגד עדים כל צדקתינו ונבל כעלה כלנו ועוננו כרוח ישאנו׃"));
hebsuperobject.biblemap.put("b23_64_7", new hebsuperobject(23,64,7,"ואין קורא בשמך מתעורר להחזיק בך כי הסתרת פניך ממנו ותמוגנו ביד עוננו׃"));
hebsuperobject.biblemap.put("b23_64_8", new hebsuperobject(23,64,8,"ועתה יהוה אבינו אתה אנחנו החמר ואתה יצרנו ומעשה ידך כלנו׃"));
hebsuperobject.biblemap.put("b23_64_9", new hebsuperobject(23,64,9,"אל תקצף יהוה עד מאד ואל לעד תזכר עון הן הבט נא עמך כלנו׃"));
hebsuperobject.biblemap.put("b23_64_10", new hebsuperobject(23,64,10,"ערי קדשך היו מדבר ציון מדבר היתה ירושלם שממה׃"));
hebsuperobject.biblemap.put("b23_64_11", new hebsuperobject(23,64,11,"בית קדשנו ותפארתנו אשר הללוך אבתינו היה לשרפת אש וכל מחמדינו היה לחרבה׃"));
hebsuperobject.biblemap.put("b23_64_12", new hebsuperobject(23,64,12,"העל אלה תתאפק יהוה תחשה ותעננו עד מאד׃"));
