hebsuperobject.biblemap.put("b23_65_2", new hebsuperobject(23,65,2,"פרשתי ידי כל היום אל עם סורר ההלכים הדרך לא טוב אחר מחשבתיהם׃"));
hebsuperobject.biblemap.put("b23_65_3", new hebsuperobject(23,65,3,"העם המכעיסים אותי על פני תמיד זבחים בגנות ומקטרים על הלבנים׃"));
