hebsuperobject.biblemap.put("b23_65_8", new hebsuperobject(23,65,8,"כה אמר יהוה כאשר ימצא התירוש באשכול ואמר אל תשחיתהו כי ברכה בו כן אעשה למען עבדי לבלתי השחית הכל׃"));
hebsuperobject.biblemap.put("b23_65_9", new hebsuperobject(23,65,9,"והוצאתי מיעקב זרע ומיהודה יורש הרי וירשוה בחירי ועבדי ישכנו שמה׃"));
hebsuperobject.biblemap.put("b23_65_10", new hebsuperobject(23,65,10,"והיה השרון לנוה צאן ועמק עכור לרבץ בקר לעמי אשר דרשוני׃"));
hebsuperobject.biblemap.put("b23_65_11", new hebsuperobject(23,65,11,"ואתם עזבי יהוה השכחים את הר קדשי הערכים לגד שלחן והממלאים למני ממסך׃"));
hebsuperobject.biblemap.put("b23_65_12", new hebsuperobject(23,65,12,"ומניתי אתכם לחרב וכלכם לטבח תכרעו יען קראתי ולא עניתם דברתי ולא שמעתם ותעשו הרע בעיני ובאשר לא חפצתי בחרתם׃"));
hebsuperobject.biblemap.put("b23_65_13", new hebsuperobject(23,65,13,"לכן כה אמר אדני יהוה הנה עבדי יאכלו ואתם תרעבו הנה עבדי ישתו ואתם תצמאו הנה עבדי ישמחו ואתם תבשו׃"));
hebsuperobject.biblemap.put("b23_65_14", new hebsuperobject(23,65,14,"הנה עבדי ירנו מטוב לב ואתם תצעקו מכאב לב ומשבר רוח תילילו׃"));
