hebsuperobject.biblemap.put("b23_65_16", new hebsuperobject(23,65,16,"אשר המתברך בארץ יתברך באלהי אמן והנשבע בארץ ישבע באלהי אמן כי נשכחו הצרות הראשנות וכי נסתרו מעיני׃"));
hebsuperobject.biblemap.put("b23_65_17", new hebsuperobject(23,65,17,"כי הנני בורא שמים חדשים וארץ חדשה ולא תזכרנה הראשנות ולא תעלינה על לב׃"));
hebsuperobject.biblemap.put("b23_65_18", new hebsuperobject(23,65,18,"כי אם שישו וגילו עדי עד אשר אני בורא כי הנני בורא את ירושלם גילה ועמה משוש׃"));
hebsuperobject.biblemap.put("b23_65_19", new hebsuperobject(23,65,19,"וגלתי בירושלם וששתי בעמי ולא ישמע בה עוד קול בכי וקול זעקה׃"));
hebsuperobject.biblemap.put("b23_65_20", new hebsuperobject(23,65,20,"לא יהיה משם עוד עול ימים וזקן אשר לא ימלא את ימיו כי הנער בן מאה שנה ימות והחוטא בן מאה שנה יקלל׃"));
hebsuperobject.biblemap.put("b23_65_21", new hebsuperobject(23,65,21,"ובנו בתים וישבו ונטעו כרמים ואכלו פרים׃"));
hebsuperobject.biblemap.put("b23_65_22", new hebsuperobject(23,65,22,"לא יבנו ואחר ישב לא יטעו ואחר יאכל כי כימי העץ ימי עמי ומעשה ידיהם יבלו בחירי׃"));
hebsuperobject.biblemap.put("b23_65_23", new hebsuperobject(23,65,23,"לא ייגעו לריק ולא ילדו לבהלה כי זרע ברוכי יהוה המה וצאצאיהם אתם׃"));
hebsuperobject.biblemap.put("b23_65_24", new hebsuperobject(23,65,24,"והיה טרם יקראו ואני אענה עוד הם מדברים ואני אשמע׃"));
hebsuperobject.biblemap.put("b23_65_25", new hebsuperobject(23,65,25,"זאב וטלה ירעו כאחד ואריה כבקר יאכל תבן ונחש עפר לחמו לא ירעו ולא ישחיתו בכל הר קדשי אמר יהוה׃"));
hebsuperobject.biblemap.put("b23_66_1", new hebsuperobject(23,66,1,"כה אמר יהוה השמים כסאי והארץ הדם רגלי אי זה בית אשר תבנו לי ואי זה מקום מנוחתי׃"));
hebsuperobject.biblemap.put("b23_66_2", new hebsuperobject(23,66,2,"ואת כל אלה ידי עשתה ויהיו כל אלה נאם יהוה ואל זה אביט אל עני ונכה רוח וחרד על דברי׃"));
hebsuperobject.biblemap.put("b23_66_3", new hebsuperobject(23,66,3,"שוחט השור מכה איש זובח השה ערף כלב מעלה מנחה דם חזיר מזכיר לבנה מברך און גם המה בחרו בדרכיהם ובשקוציהם נפשם חפצה׃"));
hebsuperobject.biblemap.put("b23_66_4", new hebsuperobject(23,66,4,"גם אני אבחר בתעלליהם ומגורתם אביא להם יען קראתי ואין עונה דברתי ולא שמעו ויעשו הרע בעיני ובאשר לא חפצתי בחרו׃"));
hebsuperobject.biblemap.put("b23_66_5", new hebsuperobject(23,66,5,"שמעו דבר יהוה החרדים אל דברו אמרו אחיכם שנאיכם מנדיכם למען שמי יכבד יהוה ונראה בשמחתכם והם יבשו׃"));
hebsuperobject.biblemap.put("b23_66_6", new hebsuperobject(23,66,6,"קול שאון מעיר קול מהיכל קול יהוה משלם גמול לאיביו׃"));
hebsuperobject.biblemap.put("b23_66_7", new hebsuperobject(23,66,7,"בטרם תחיל ילדה בטרם יבוא חבל לה והמליטה זכר׃"));
hebsuperobject.biblemap.put("b23_66_8", new hebsuperobject(23,66,8,"מי שמע כזאת מי ראה כאלה היוחל ארץ ביום אחד אם יולד גוי פעם אחת כי חלה גם ילדה ציון את בניה׃"));
hebsuperobject.biblemap.put("b23_66_9", new hebsuperobject(23,66,9,"האני אשביר ולא אוליד יאמר יהוה אם אני המוליד ועצרתי אמר אלהיך׃"));
