hebsuperobject.biblemap.put("b23_66_11", new hebsuperobject(23,66,11,"למען תינקו ושבעתם משד תנחמיה למען תמצו והתענגתם מזיז כבודה׃"));
hebsuperobject.biblemap.put("b23_66_12", new hebsuperobject(23,66,12,"כי כה אמר יהוה הנני נטה אליה כנהר שלום וכנחל שוטף כבוד גוים וינקתם על צד תנשאו ועל ברכים תשעשעו׃"));
hebsuperobject.biblemap.put("b23_66_13", new hebsuperobject(23,66,13,"כאיש אשר אמו תנחמנו כן אנכי אנחמכם ובירושלם תנחמו׃"));
hebsuperobject.biblemap.put("b23_66_14", new hebsuperobject(23,66,14,"וראיתם ושש לבכם ועצמותיכם כדשא תפרחנה ונודעה יד יהוה את עבדיו וזעם את איביו׃"));
hebsuperobject.biblemap.put("b23_66_15", new hebsuperobject(23,66,15,"כי הנה יהוה באש יבוא וכסופה מרכבתיו להשיב בחמה אפו וגערתו בלהבי אש׃"));
hebsuperobject.biblemap.put("b23_66_16", new hebsuperobject(23,66,16,"כי באש יהוה נשפט ובחרבו את כל בשר ורבו חללי יהוה׃"));
