hebsuperobject.biblemap.put("b24_13_13", new hebsuperobject(24,13,13,"ואמרת אליהם כה אמר יהוה הנני ממלא את כל ישבי הארץ הזאת ואת המלכים הישבים לדוד על כסאו ואת הכהנים ואת הנביאים ואת כל ישבי ירושלם שכרון׃"));
hebsuperobject.biblemap.put("b24_13_14", new hebsuperobject(24,13,14,"ונפצתים איש אל אחיו והאבות והבנים יחדו נאם יהוה לא אחמול ולא אחוס ולא ארחם מהשחיתם׃"));
hebsuperobject.biblemap.put("b24_13_15", new hebsuperobject(24,13,15,"שמעו והאזינו אל תגבהו כי יהוה דבר׃"));
