hebsuperobject.biblemap.put("b24_14_1", new hebsuperobject(24,14,1,"אשר היה דבר יהוה אל ירמיהו על דברי הבצרות׃"));
hebsuperobject.biblemap.put("b24_14_2", new hebsuperobject(24,14,2,"אבלה יהודה ושעריה אמללו קדרו לארץ וצוחת ירושלם עלתה׃"));
