hebsuperobject.biblemap.put("b24_14_4", new hebsuperobject(24,14,4,"בעבור האדמה חתה כי לא היה גשם בארץ בשו אכרים חפו ראשם׃"));
hebsuperobject.biblemap.put("b24_14_5", new hebsuperobject(24,14,5,"כי גם אילת בשדה ילדה ועזוב כי לא היה דשא׃"));
hebsuperobject.biblemap.put("b24_14_6", new hebsuperobject(24,14,6,"ופראים עמדו על שפים שאפו רוח כתנים כלו עיניהם כי אין עשב׃"));
hebsuperobject.biblemap.put("b24_14_7", new hebsuperobject(24,14,7,"אם עונינו ענו בנו יהוה עשה למען שמך כי רבו משובתינו לך חטאנו׃"));
