hebsuperobject.biblemap.put("b24_14_10", new hebsuperobject(24,14,10,"כה אמר יהוה לעם הזה כן אהבו לנוע רגליהם לא חשכו ויהוה לא רצם עתה יזכר עונם ויפקד חטאתם׃"));
hebsuperobject.biblemap.put("b24_14_11", new hebsuperobject(24,14,11,"ויאמר יהוה אלי אל תתפלל בעד העם הזה לטובה׃"));
