hebsuperobject.biblemap.put("b24_15_4", new hebsuperobject(24,15,4,"ונתתים לזועה לכל ממלכות הארץ בגלל מנשה בן יחזקיהו מלך יהודה על אשר עשה בירושלם׃"));
hebsuperobject.biblemap.put("b24_15_5", new hebsuperobject(24,15,5,"כי מי יחמל עליך ירושלם ומי ינוד לך ומי יסור לשאל לשלם לך׃"));
