hebsuperobject.biblemap.put("b24_15_16", new hebsuperobject(24,15,16,"נמצאו דבריך ואכלם ויהי דבריך לי לששון ולשמחת לבבי כי נקרא שמך עלי יהוה אלהי צבאות׃"));
hebsuperobject.biblemap.put("b24_15_17", new hebsuperobject(24,15,17,"לא ישבתי בסוד משחקים ואעלז מפני ידך בדד ישבתי כי זעם מלאתני׃"));
