hebsuperobject.biblemap.put("b24_15_19", new hebsuperobject(24,15,19,"לכן כה אמר יהוה אם תשוב ואשיבך לפני תעמד ואם תוציא יקר מזולל כפי תהיה ישבו המה אליך ואתה לא תשוב אליהם׃"));
hebsuperobject.biblemap.put("b24_15_20", new hebsuperobject(24,15,20,"ונתתיך לעם הזה לחומת נחשת בצורה ונלחמו אליך ולא יוכלו לך כי אתך אני להושיעך ולהצילך נאם יהוה׃"));
hebsuperobject.biblemap.put("b24_15_21", new hebsuperobject(24,15,21,"והצלתיך מיד רעים ופדתיך מכף ערצים׃"));
