hebsuperobject.biblemap.put("b24_16_3", new hebsuperobject(24,16,3,"כי כה אמר יהוה על הבנים ועל הבנות הילודים במקום הזה ועל אמתם הילדות אותם ועל אבותם המולדים אותם בארץ הזאת׃"));
hebsuperobject.biblemap.put("b24_16_4", new hebsuperobject(24,16,4,"ממותי תחלאים ימתו לא יספדו ולא יקברו לדמן על פני האדמה יהיו ובחרב וברעב יכלו והיתה נבלתם למאכל לעוף השמים ולבהמת הארץ׃"));
hebsuperobject.biblemap.put("b24_16_5", new hebsuperobject(24,16,5,"כי כה אמר יהוה אל תבוא בית מרזח ואל תלך לספוד ואל תנד להם כי אספתי את שלומי מאת העם הזה נאם יהוה את החסד ואת הרחמים׃"));
hebsuperobject.biblemap.put("b24_16_6", new hebsuperobject(24,16,6,"ומתו גדלים וקטנים בארץ הזאת לא יקברו ולא יספדו להם ולא יתגדד ולא יקרח להם׃"));
