hebsuperobject.biblemap.put("b24_16_10", new hebsuperobject(24,16,10,"והיה כי תגיד לעם הזה את כל הדברים האלה ואמרו אליך על מה דבר יהוה עלינו את כל הרעה הגדולה הזאת ומה עוננו ומה חטאתנו אשר חטאנו ליהוה אלהינו׃"));
hebsuperobject.biblemap.put("b24_16_11", new hebsuperobject(24,16,11,"ואמרת אליהם על אשר עזבו אבותיכם אותי נאם יהוה וילכו אחרי אלהים אחרים ויעבדום וישתחוו להם ואתי עזבו ואת תורתי לא שמרו׃"));
hebsuperobject.biblemap.put("b24_16_12", new hebsuperobject(24,16,12,"ואתם הרעתם לעשות מאבותיכם והנכם הלכים איש אחרי שררות לבו הרע לבלתי שמע אלי׃"));
hebsuperobject.biblemap.put("b24_16_13", new hebsuperobject(24,16,13,"והטלתי אתכם מעל הארץ הזאת על הארץ אשר לא ידעתם אתם ואבותיכם ועבדתם שם את אלהים אחרים יומם ולילה אשר לא אתן לכם חנינה׃"));
hebsuperobject.biblemap.put("b24_16_14", new hebsuperobject(24,16,14,"לכן הנה ימים באים נאם יהוה ולא יאמר עוד חי יהוה אשר העלה את בני ישראל מארץ מצרים׃"));
