hebsuperobject.biblemap.put("b24_17_1", new hebsuperobject(24,17,1,"חטאת יהודה כתובה בעט ברזל בצפרן שמיר חרושה על לוח לבם ולקרנות מזבחותיכם׃"));
hebsuperobject.biblemap.put("b24_17_2", new hebsuperobject(24,17,2,"כזכר בניהם מזבחותם ואשריהם על עץ רענן על גבעות הגבהות׃"));
