hebsuperobject.biblemap.put("b24_20_5", new hebsuperobject(24,20,5,"ונתתי את כל חסן העיר הזאת ואת כל יגיעה ואת כל יקרה ואת כל אוצרות מלכי יהודה אתן ביד איביהם ובזזום ולקחום והביאום בבלה׃"));
hebsuperobject.biblemap.put("b24_20_6", new hebsuperobject(24,20,6,"ואתה פשחור וכל ישבי ביתך תלכו בשבי ובבל תבוא ושם תמות ושם תקבר אתה וכל אהביך אשר נבאת להם בשקר׃"));
hebsuperobject.biblemap.put("b24_20_7", new hebsuperobject(24,20,7,"פתיתני יהוה ואפת חזקתני ותוכל הייתי לשחוק כל היום כלה לעג לי׃"));
hebsuperobject.biblemap.put("b24_20_8", new hebsuperobject(24,20,8,"כי מדי אדבר אזעק חמס ושד אקרא כי היה דבר יהוה לי לחרפה ולקלס כל היום׃"));
hebsuperobject.biblemap.put("b24_20_9", new hebsuperobject(24,20,9,"ואמרתי לא אזכרנו ולא אדבר עוד בשמו והיה בלבי כאש בערת עצר בעצמתי ונלאיתי כלכל ולא אוכל׃"));
hebsuperobject.biblemap.put("b24_20_10", new hebsuperobject(24,20,10,"כי שמעתי דבת רבים מגור מסביב הגידו ונגידנו כל אנוש שלומי שמרי צלעי אולי יפתה ונוכלה לו ונקחה נקמתנו ממנו׃"));
hebsuperobject.biblemap.put("b24_20_11", new hebsuperobject(24,20,11,"ויהוה אותי כגבור עריץ על כן רדפי יכשלו ולא יכלו בשו מאד כי לא השכילו כלמת עולם לא תשכח׃"));
hebsuperobject.biblemap.put("b24_20_12", new hebsuperobject(24,20,12,"ויהוה צבאות בחן צדיק ראה כליות ולב אראה נקמתך מהם כי אליך גליתי את ריבי׃"));
hebsuperobject.biblemap.put("b24_20_13", new hebsuperobject(24,20,13,"שירו ליהוה הללו את יהוה כי הציל את נפש אביון מיד מרעים׃"));
hebsuperobject.biblemap.put("b24_20_14", new hebsuperobject(24,20,14,"ארור היום אשר ילדתי בו יום אשר ילדתני אמי אל יהי ברוך׃"));
hebsuperobject.biblemap.put("b24_20_15", new hebsuperobject(24,20,15,"ארור האיש אשר בשר את אבי לאמר ילד לך בן זכר שמח שמחהו׃"));
hebsuperobject.biblemap.put("b24_20_16", new hebsuperobject(24,20,16,"והיה האיש ההוא כערים אשר הפך יהוה ולא נחם ושמע זעקה בבקר ותרועה בעת צהרים׃"));
hebsuperobject.biblemap.put("b24_20_17", new hebsuperobject(24,20,17,"אשר לא מותתני מרחם ותהי לי אמי קברי ורחמה הרת עולם׃"));
hebsuperobject.biblemap.put("b24_20_18", new hebsuperobject(24,20,18,"למה זה מרחם יצאתי לראות עמל ויגון ויכלו בבשת ימי׃"));
hebsuperobject.biblemap.put("b24_21_1", new hebsuperobject(24,21,1,"הדבר אשר היה אל ירמיהו מאת יהוה בשלח אליו המלך צדקיהו את פשחור בן מלכיה ואת צפניה בן מעשיה הכהן לאמר׃"));
hebsuperobject.biblemap.put("b24_21_2", new hebsuperobject(24,21,2,"דרש נא בעדנו את יהוה כי נבוכדראצר מלך בבל נלחם עלינו אולי יעשה יהוה אותנו ככל נפלאתיו ויעלה מעלינו׃"));
hebsuperobject.biblemap.put("b24_21_3", new hebsuperobject(24,21,3,"ויאמר ירמיהו אליהם כה תאמרן אל צדקיהו׃"));
hebsuperobject.biblemap.put("b24_21_4", new hebsuperobject(24,21,4,"כה אמר יהוה אלהי ישראל הנני מסב את כלי המלחמה אשר בידכם אשר אתם נלחמים בם את מלך בבל ואת הכשדים הצרים עליכם מחוץ לחומה ואספתי אותם אל תוך העיר הזאת׃"));
hebsuperobject.biblemap.put("b24_21_5", new hebsuperobject(24,21,5,"ונלחמתי אני אתכם ביד נטויה ובזרוע חזקה ובאף ובחמה ובקצף גדול׃"));
hebsuperobject.biblemap.put("b24_21_6", new hebsuperobject(24,21,6,"והכיתי את יושבי העיר הזאת ואת האדם ואת הבהמה בדבר גדול ימתו׃"));
hebsuperobject.biblemap.put("b24_21_7", new hebsuperobject(24,21,7,"ואחרי כן נאם יהוה אתן את צדקיהו מלך יהודה ואת עבדיו ואת העם ואת הנשארים בעיר הזאת מן הדבר מן החרב ומן הרעב ביד נבוכדראצר מלך בבל וביד איביהם וביד מבקשי נפשם והכם לפי חרב לא יחוס עליהם ולא יחמל ולא ירחם׃"));
hebsuperobject.biblemap.put("b24_21_8", new hebsuperobject(24,21,8,"ואל העם הזה תאמר כה אמר יהוה הנני נתן לפניכם את דרך החיים ואת דרך המות׃"));
hebsuperobject.biblemap.put("b24_21_9", new hebsuperobject(24,21,9,"הישב בעיר הזאת ימות בחרב וברעב ובדבר והיוצא ונפל על הכשדים הצרים עליכם יחיה והיתה לו נפשו לשלל׃"));
hebsuperobject.biblemap.put("b24_21_10", new hebsuperobject(24,21,10,"כי שמתי פני בעיר הזאת לרעה ולא לטובה נאם יהוה ביד מלך בבל תנתן ושרפה באש׃"));
hebsuperobject.biblemap.put("b24_21_11", new hebsuperobject(24,21,11,"ולבית מלך יהודה שמעו דבר יהוה׃"));
hebsuperobject.biblemap.put("b24_21_12", new hebsuperobject(24,21,12,"בית דוד כה אמר יהוה דינו לבקר משפט והצילו גזול מיד עושק פן תצא כאש חמתי ובערה ואין מכבה מפני רע מעלליהם׃"));
hebsuperobject.biblemap.put("b24_21_13", new hebsuperobject(24,21,13,"הנני אליך ישבת העמק צור המישר נאם יהוה האמרים מי יחת עלינו ומי יבוא במעונותינו׃"));
hebsuperobject.biblemap.put("b24_21_14", new hebsuperobject(24,21,14,"ופקדתי עליכם כפרי מעלליכם נאם יהוה והצתי אש ביערה ואכלה כל סביביה׃"));
hebsuperobject.biblemap.put("b24_22_1", new hebsuperobject(24,22,1,"כה אמר יהוה רד בית מלך יהודה ודברת שם את הדבר הזה׃"));
hebsuperobject.biblemap.put("b24_22_2", new hebsuperobject(24,22,2,"ואמרת שמע דבר יהוה מלך יהודה הישב על כסא דוד אתה ועבדיך ועמך הבאים בשערים האלה׃"));
hebsuperobject.biblemap.put("b24_22_3", new hebsuperobject(24,22,3,"כה אמר יהוה עשו משפט וצדקה והצילו גזול מיד עשוק וגר יתום ואלמנה אל תנו אל תחמסו ודם נקי אל תשפכו במקום הזה׃"));
hebsuperobject.biblemap.put("b24_22_4", new hebsuperobject(24,22,4,"כי אם עשו תעשו את הדבר הזה ובאו בשערי הבית הזה מלכים ישבים לדוד על כסאו רכבים ברכב ובסוסים הוא ועבדו ועמו׃"));
hebsuperobject.biblemap.put("b24_22_5", new hebsuperobject(24,22,5,"ואם לא תשמעו את הדברים האלה בי נשבעתי נאם יהוה כי לחרבה יהיה הבית הזה׃"));
hebsuperobject.biblemap.put("b24_22_6", new hebsuperobject(24,22,6,"כי כה אמר יהוה על בית מלך יהודה גלעד אתה לי ראש הלבנון אם לא אשיתך מדבר ערים לא נושבה׃"));
hebsuperobject.biblemap.put("b24_22_7", new hebsuperobject(24,22,7,"וקדשתי עליך משחתים איש וכליו וכרתו מבחר ארזיך והפילו על האש׃"));
hebsuperobject.biblemap.put("b24_22_8", new hebsuperobject(24,22,8,"ועברו גוים רבים על העיר הזאת ואמרו איש אל רעהו על מה עשה יהוה ככה לעיר הגדולה הזאת׃"));
hebsuperobject.biblemap.put("b24_22_9", new hebsuperobject(24,22,9,"ואמרו על אשר עזבו את ברית יהוה אלהיהם וישתחוו לאלהים אחרים ויעבדום׃"));
hebsuperobject.biblemap.put("b24_22_10", new hebsuperobject(24,22,10,"אל תבכו למת ואל תנדו לו בכו בכו להלך כי לא ישוב עוד וראה את ארץ מולדתו׃"));
hebsuperobject.biblemap.put("b24_22_11", new hebsuperobject(24,22,11,"כי כה אמר יהוה אל שלם בן יאשיהו מלך יהודה המלך תחת יאשיהו אביו אשר יצא מן המקום הזה לא ישוב שם עוד׃"));
hebsuperobject.biblemap.put("b24_22_12", new hebsuperobject(24,22,12,"כי במקום אשר הגלו אתו שם ימות ואת הארץ הזאת לא יראה עוד׃"));
hebsuperobject.biblemap.put("b24_22_13", new hebsuperobject(24,22,13,"הוי בנה ביתו בלא צדק ועליותיו בלא משפט ברעהו יעבד חנם ופעלו לא יתן לו׃"));
hebsuperobject.biblemap.put("b24_22_14", new hebsuperobject(24,22,14,"האמר אבנה לי בית מדות ועליות מרוחים וקרע לו חלוני וספון בארז ומשוח בששר׃"));
hebsuperobject.biblemap.put("b24_22_15", new hebsuperobject(24,22,15,"התמלך כי אתה מתחרה בארז אביך הלוא אכל ושתה ועשה משפט וצדקה אז טוב לו׃"));
hebsuperobject.biblemap.put("b24_22_16", new hebsuperobject(24,22,16,"דן דין עני ואביון אז טוב הלוא היא הדעת אתי נאם יהוה׃"));
hebsuperobject.biblemap.put("b24_22_17", new hebsuperobject(24,22,17,"כי אין עיניך ולבך כי אם על בצעך ועל דם הנקי לשפוך ועל העשק ועל המרוצה לעשות׃"));
hebsuperobject.biblemap.put("b24_22_18", new hebsuperobject(24,22,18,"לכן כה אמר יהוה אל יהויקים בן יאשיהו מלך יהודה לא יספדו לו הוי אחי והוי אחות לא יספדו לו הוי אדון והוי הדה׃"));
hebsuperobject.biblemap.put("b24_22_19", new hebsuperobject(24,22,19,"קבורת חמור יקבר סחוב והשלך מהלאה לשערי ירושלם׃"));
hebsuperobject.biblemap.put("b24_22_20", new hebsuperobject(24,22,20,"עלי הלבנון וצעקי ובבשן תני קולך וצעקי מעברים כי נשברו כל מאהביך׃"));
hebsuperobject.biblemap.put("b24_22_21", new hebsuperobject(24,22,21,"דברתי אליך בשלותיך אמרת לא אשמע זה דרכך מנעוריך כי לא שמעת בקולי׃"));
hebsuperobject.biblemap.put("b24_22_22", new hebsuperobject(24,22,22,"כל רעיך תרעה רוח ומאהביך בשבי ילכו כי אז תבשי ונכלמת מכל רעתך׃"));
hebsuperobject.biblemap.put("b24_22_23", new hebsuperobject(24,22,23,"ישבתי בלבנון מקננתי בארזים מה נחנת בבא לך חבלים חיל כילדה׃"));
hebsuperobject.biblemap.put("b24_22_24", new hebsuperobject(24,22,24,"חי אני נאם יהוה כי אם יהיה כניהו בן יהויקים מלך יהודה חותם על יד ימיני כי משם אתקנך׃"));
hebsuperobject.biblemap.put("b24_22_25", new hebsuperobject(24,22,25,"ונתתיך ביד מבקשי נפשך וביד אשר אתה יגור מפניהם וביד נבוכדראצר מלך בבל וביד הכשדים׃"));
hebsuperobject.biblemap.put("b24_22_26", new hebsuperobject(24,22,26,"והטלתי אתך ואת אמך אשר ילדתך על הארץ אחרת אשר לא ילדתם שם ושם תמותו׃"));
hebsuperobject.biblemap.put("b24_22_27", new hebsuperobject(24,22,27,"ועל הארץ אשר הם מנשאים את נפשם לשוב שם שמה לא ישובו׃"));
hebsuperobject.biblemap.put("b24_22_28", new hebsuperobject(24,22,28,"העצב נבזה נפוץ האיש הזה כניהו אם כלי אין חפץ בו מדוע הוטלו הוא וזרעו והשלכו על הארץ אשר לא ידעו׃"));
hebsuperobject.biblemap.put("b24_22_29", new hebsuperobject(24,22,29,"ארץ ארץ ארץ שמעי דבר יהוה׃"));
hebsuperobject.biblemap.put("b24_22_30", new hebsuperobject(24,22,30,"כה אמר יהוה כתבו את האיש הזה ערירי גבר לא יצלח בימיו כי לא יצלח מזרעו איש ישב על כסא דוד ומשל עוד ביהודה׃"));
hebsuperobject.biblemap.put("b24_23_1", new hebsuperobject(24,23,1,"הוי רעים מאבדים ומפצים את צאן מרעיתי נאם יהוה׃"));
hebsuperobject.biblemap.put("b24_23_2", new hebsuperobject(24,23,2,"לכן כה אמר יהוה אלהי ישראל על הרעים הרעים את עמי אתם הפצתם את צאני ותדחום ולא פקדתם אתם הנני פקד עליכם את רע מעלליכם נאם יהוה׃"));
hebsuperobject.biblemap.put("b24_23_3", new hebsuperobject(24,23,3,"ואני אקבץ את שארית צאני מכל הארצות אשר הדחתי אתם שם והשבתי אתהן על נוהן ופרו ורבו׃"));
hebsuperobject.biblemap.put("b24_23_4", new hebsuperobject(24,23,4,"והקמתי עליהם רעים ורעום ולא ייראו עוד ולא יחתו ולא יפקדו נאם יהוה׃"));
hebsuperobject.biblemap.put("b24_23_5", new hebsuperobject(24,23,5,"הנה ימים באים נאם יהוה והקמתי לדוד צמח צדיק ומלך מלך והשכיל ועשה משפט וצדקה בארץ׃"));
hebsuperobject.biblemap.put("b24_23_6", new hebsuperobject(24,23,6,"בימיו תושע יהודה וישראל ישכן לבטח וזה שמו אשר יקראו יהוה צדקנו׃"));
hebsuperobject.biblemap.put("b24_23_7", new hebsuperobject(24,23,7,"לכן הנה ימים באים נאם יהוה ולא יאמרו עוד חי יהוה אשר העלה את בני ישראל מארץ מצרים׃"));
hebsuperobject.biblemap.put("b24_23_8", new hebsuperobject(24,23,8,"כי אם חי יהוה אשר העלה ואשר הביא את זרע בית ישראל מארץ צפונה ומכל הארצות אשר הדחתים שם וישבו על אדמתם׃"));
hebsuperobject.biblemap.put("b24_23_9", new hebsuperobject(24,23,9,"לנבאים נשבר לבי בקרבי רחפו כל עצמותי הייתי כאיש שכור וכגבר עברו יין מפני יהוה ומפני דברי קדשו׃"));
hebsuperobject.biblemap.put("b24_23_10", new hebsuperobject(24,23,10,"כי מנאפים מלאה הארץ כי מפני אלה אבלה הארץ יבשו נאות מדבר ותהי מרוצתם רעה וגבורתם לא כן׃"));
hebsuperobject.biblemap.put("b24_23_11", new hebsuperobject(24,23,11,"כי גם נביא גם כהן חנפו גם בביתי מצאתי רעתם נאם יהוה׃"));
hebsuperobject.biblemap.put("b24_23_12", new hebsuperobject(24,23,12,"לכן יהיה דרכם להם כחלקלקות באפלה ידחו ונפלו בה כי אביא עליהם רעה שנת פקדתם נאם יהוה׃"));
hebsuperobject.biblemap.put("b24_23_13", new hebsuperobject(24,23,13,"ובנביאי שמרון ראיתי תפלה הנבאו בבעל ויתעו את עמי את ישראל׃"));
hebsuperobject.biblemap.put("b24_23_14", new hebsuperobject(24,23,14,"ובנבאי ירושלם ראיתי שערורה נאוף והלך בשקר וחזקו ידי מרעים לבלתי שבו איש מרעתו היו לי כלם כסדם וישביה כעמרה׃"));
hebsuperobject.biblemap.put("b24_23_15", new hebsuperobject(24,23,15,"לכן כה אמר יהוה צבאות על הנבאים הנני מאכיל אותם לענה והשקתים מי ראש כי מאת נביאי ירושלם יצאה חנפה לכל הארץ׃"));
hebsuperobject.biblemap.put("b24_23_16", new hebsuperobject(24,23,16,"כה אמר יהוה צבאות אל תשמעו על דברי הנבאים הנבאים לכם מהבלים המה אתכם חזון לבם ידברו לא מפי יהוה׃"));
hebsuperobject.biblemap.put("b24_23_17", new hebsuperobject(24,23,17,"אמרים אמור למנאצי דבר יהוה שלום יהיה לכם וכל הלך בשררות לבו אמרו לא תבוא עליכם רעה׃"));
hebsuperobject.biblemap.put("b24_23_18", new hebsuperobject(24,23,18,"כי מי עמד בסוד יהוה וירא וישמע את דברו מי הקשיב דברי וישמע׃"));
hebsuperobject.biblemap.put("b24_23_19", new hebsuperobject(24,23,19,"הנה סערת יהוה חמה יצאה וסער מתחולל על ראש רשעים יחול׃"));
