hebsuperobject.biblemap.put("b28_7_7", new hebsuperobject(28,7,7,"כלם יחמו כתנור ואכלו את שפטיהם כל מלכיהם נפלו אין קרא בהם אלי׃"));
hebsuperobject.biblemap.put("b28_7_8", new hebsuperobject(28,7,8,"אפרים בעמים הוא יתבולל אפרים היה עגה בלי הפוכה׃"));
hebsuperobject.biblemap.put("b28_7_9", new hebsuperobject(28,7,9,"אכלו זרים כחו והוא לא ידע גם שיבה זרקה בו והוא לא ידע׃"));
hebsuperobject.biblemap.put("b28_7_10", new hebsuperobject(28,7,10,"וענה גאון ישראל בפניו ולא שבו אל יהוה אלהיהם ולא בקשהו בכל זאת׃"));
hebsuperobject.biblemap.put("b28_7_11", new hebsuperobject(28,7,11,"ויהי אפרים כיונה פותה אין לב מצרים קראו אשור הלכו׃"));
hebsuperobject.biblemap.put("b28_7_12", new hebsuperobject(28,7,12,"כאשר ילכו אפרוש עליהם רשתי כעוף השמים אורידם איסרם כשמע לעדתם׃"));
hebsuperobject.biblemap.put("b28_7_13", new hebsuperobject(28,7,13,"אוי להם כי נדדו ממני שד להם כי פשעו בי ואנכי אפדם והמה דברו עלי כזבים׃"));
hebsuperobject.biblemap.put("b28_7_14", new hebsuperobject(28,7,14,"ולא זעקו אלי בלבם כי יילילו על משכבותם על דגן ותירוש יתגוררו יסורו בי׃"));
hebsuperobject.biblemap.put("b28_7_15", new hebsuperobject(28,7,15,"ואני יסרתי חזקתי זרועתם ואלי יחשבו רע׃"));
hebsuperobject.biblemap.put("b28_7_16", new hebsuperobject(28,7,16,"ישובו לא על היו כקשת רמיה יפלו בחרב שריהם מזעם לשונם זו לעגם בארץ מצרים׃"));
hebsuperobject.biblemap.put("b28_8_1", new hebsuperobject(28,8,1,"אל חכך שפר כנשר על בית יהוה יען עברו בריתי ועל תורתי פשעו׃"));
hebsuperobject.biblemap.put("b28_8_2", new hebsuperobject(28,8,2,"לי יזעקו אלהי ידענוך ישראל׃"));
hebsuperobject.biblemap.put("b28_8_3", new hebsuperobject(28,8,3,"זנח ישראל טוב אויב ירדפו׃"));
hebsuperobject.biblemap.put("b28_8_4", new hebsuperobject(28,8,4,"הם המליכו ולא ממני השירו ולא ידעתי כספם וזהבם עשו להם עצבים למען יכרת׃"));
hebsuperobject.biblemap.put("b28_8_5", new hebsuperobject(28,8,5,"זנח עגלך שמרון חרה אפי בם עד מתי לא יוכלו נקין׃"));
hebsuperobject.biblemap.put("b28_8_6", new hebsuperobject(28,8,6,"כי מישראל והוא חרש עשהו ולא אלהים הוא כי שבבים יהיה עגל שמרון׃"));
hebsuperobject.biblemap.put("b28_8_7", new hebsuperobject(28,8,7,"כי רוח יזרעו וסופתה יקצרו קמה אין לו צמח בלי יעשה קמח אולי יעשה זרים יבלעהו׃"));
hebsuperobject.biblemap.put("b28_8_8", new hebsuperobject(28,8,8,"נבלע ישראל עתה היו בגוים ככלי אין חפץ בו׃"));
hebsuperobject.biblemap.put("b28_8_9", new hebsuperobject(28,8,9,"כי המה עלו אשור פרא בודד לו אפרים התנו אהבים׃"));
hebsuperobject.biblemap.put("b28_8_10", new hebsuperobject(28,8,10,"גם כי יתנו בגוים עתה אקבצם ויחלו מעט ממשא מלך שרים׃"));
hebsuperobject.biblemap.put("b28_8_11", new hebsuperobject(28,8,11,"כי הרבה אפרים מזבחת לחטא היו לו מזבחות לחטא׃"));
hebsuperobject.biblemap.put("b28_8_12", new hebsuperobject(28,8,12,"אכתוב לו רבו תורתי כמו זר נחשבו׃"));
hebsuperobject.biblemap.put("b28_8_13", new hebsuperobject(28,8,13,"זבחי הבהבי יזבחו בשר ויאכלו יהוה לא רצם עתה יזכר עונם ויפקד חטאותם המה מצרים ישובו׃"));
hebsuperobject.biblemap.put("b28_8_14", new hebsuperobject(28,8,14,"וישכח ישראל את עשהו ויבן היכלות ויהודה הרבה ערים בצרות ושלחתי אש בעריו ואכלה ארמנתיה׃"));
hebsuperobject.biblemap.put("b28_9_1", new hebsuperobject(28,9,1,"אל תשמח ישראל אל גיל כעמים כי זנית מעל אלהיך אהבת אתנן על כל גרנות דגן׃"));
hebsuperobject.biblemap.put("b28_9_2", new hebsuperobject(28,9,2,"גרן ויקב לא ירעם ותירוש יכחש בה׃"));
hebsuperobject.biblemap.put("b28_9_3", new hebsuperobject(28,9,3,"לא ישבו בארץ יהוה ושב אפרים מצרים ובאשור טמא יאכלו׃"));
hebsuperobject.biblemap.put("b28_9_4", new hebsuperobject(28,9,4,"לא יסכו ליהוה יין ולא יערבו לו זבחיהם כלחם אונים להם כל אכליו יטמאו כי לחמם לנפשם לא יבוא בית יהוה׃"));
hebsuperobject.biblemap.put("b28_9_5", new hebsuperobject(28,9,5,"מה תעשו ליום מועד וליום חג יהוה׃"));
hebsuperobject.biblemap.put("b28_9_6", new hebsuperobject(28,9,6,"כי הנה הלכו משד מצרים תקבצם מף תקברם מחמד לכספם קמוש יירשם חוח באהליהם׃"));
hebsuperobject.biblemap.put("b28_9_7", new hebsuperobject(28,9,7,"באו ימי הפקדה באו ימי השלם ידעו ישראל אויל הנביא משגע איש הרוח על רב עונך ורבה משטמה׃"));
hebsuperobject.biblemap.put("b28_9_8", new hebsuperobject(28,9,8,"צפה אפרים עם אלהי נביא פח יקוש על כל דרכיו משטמה בבית אלהיו׃"));
hebsuperobject.biblemap.put("b28_9_9", new hebsuperobject(28,9,9,"העמיקו שחתו כימי הגבעה יזכור עונם יפקוד חטאתם׃"));
hebsuperobject.biblemap.put("b28_9_10", new hebsuperobject(28,9,10,"כענבים במדבר מצאתי ישראל כבכורה בתאנה בראשיתה ראיתי אבותיכם המה באו בעל פעור וינזרו לבשת ויהיו שקוצים כאהבם׃"));
hebsuperobject.biblemap.put("b28_9_11", new hebsuperobject(28,9,11,"אפרים כעוף יתעופף כבודם מלדה ומבטן ומהריון׃"));
hebsuperobject.biblemap.put("b28_9_12", new hebsuperobject(28,9,12,"כי אם יגדלו את בניהם ושכלתים מאדם כי גם אוי להם בשורי מהם׃"));
hebsuperobject.biblemap.put("b28_9_13", new hebsuperobject(28,9,13,"אפרים כאשר ראיתי לצור שתולה בנוה ואפרים להוציא אל הורג בניו׃"));
hebsuperobject.biblemap.put("b28_9_14", new hebsuperobject(28,9,14,"תן להם יהוה מה תתן תן להם רחם משכיל ושדים צמקים׃"));
hebsuperobject.biblemap.put("b28_9_15", new hebsuperobject(28,9,15,"כל רעתם בגלגל כי שם שנאתים על רע מעלליהם מביתי אגרשם לא אוסף אהבתם כל שריהם סררים׃"));
hebsuperobject.biblemap.put("b28_9_16", new hebsuperobject(28,9,16,"הכה אפרים שרשם יבש פרי בלי יעשון גם כי ילדון והמתי מחמדי בטנם׃"));
hebsuperobject.biblemap.put("b28_9_17", new hebsuperobject(28,9,17,"ימאסם אלהי כי לא שמעו לו ויהיו נדדים בגוים׃"));
hebsuperobject.biblemap.put("b28_10_1", new hebsuperobject(28,10,1,"גפן בוקק ישראל פרי ישוה לו כרב לפריו הרבה למזבחות כטוב לארצו היטיבו מצבות׃"));
hebsuperobject.biblemap.put("b28_10_2", new hebsuperobject(28,10,2,"חלק לבם עתה יאשמו הוא יערף מזבחותם ישדד מצבותם׃"));
hebsuperobject.biblemap.put("b28_10_3", new hebsuperobject(28,10,3,"כי עתה יאמרו אין מלך לנו כי לא יראנו את יהוה והמלך מה יעשה לנו׃"));
hebsuperobject.biblemap.put("b28_10_4", new hebsuperobject(28,10,4,"דברו דברים אלות שוא כרת ברית ופרח כראש משפט על תלמי שדי׃"));
hebsuperobject.biblemap.put("b28_10_5", new hebsuperobject(28,10,5,"לעגלות בית און יגורו שכן שמרון כי אבל עליו עמו וכמריו עליו יגילו על כבודו כי גלה ממנו׃"));
hebsuperobject.biblemap.put("b28_10_6", new hebsuperobject(28,10,6,"גם אותו לאשור יובל מנחה למלך ירב בשנה אפרים יקח ויבוש ישראל מעצתו׃"));
hebsuperobject.biblemap.put("b28_10_7", new hebsuperobject(28,10,7,"נדמה שמרון מלכה כקצף על פני מים׃"));
hebsuperobject.biblemap.put("b28_10_8", new hebsuperobject(28,10,8,"ונשמדו במות און חטאת ישראל קוץ ודרדר יעלה על מזבחותם ואמרו להרים כסונו ולגבעות נפלו עלינו׃"));
hebsuperobject.biblemap.put("b28_10_9", new hebsuperobject(28,10,9,"מימי הגבעה חטאת ישראל שם עמדו לא תשיגם בגבעה מלחמה על בני עלוה׃"));
hebsuperobject.biblemap.put("b28_10_10", new hebsuperobject(28,10,10,"באותי ואסרם ואספו עליהם עמים באסרם לשתי עינתם׃"));
hebsuperobject.biblemap.put("b28_10_11", new hebsuperobject(28,10,11,"ואפרים עגלה מלמדה אהבתי לדוש ואני עברתי על טוב צוארה ארכיב אפרים יחרוש יהודה ישדד לו יעקב׃"));
hebsuperobject.biblemap.put("b28_10_12", new hebsuperobject(28,10,12,"זרעו לכם לצדקה קצרו לפי חסד נירו לכם ניר ועת לדרוש את יהוה עד יבוא וירה צדק לכם׃"));
hebsuperobject.biblemap.put("b28_10_13", new hebsuperobject(28,10,13,"חרשתם רשע עולתה קצרתם אכלתם פרי כחש כי בטחת בדרכך ברב גבוריך׃"));
hebsuperobject.biblemap.put("b28_10_14", new hebsuperobject(28,10,14,"וקאם שאון בעמך וכל מבצריך יושד כשד שלמן בית ארבאל ביום מלחמה אם על בנים רטשה׃"));
hebsuperobject.biblemap.put("b28_10_15", new hebsuperobject(28,10,15,"ככה עשה לכם בית אל מפני רעת רעתכם בשחר נדמה נדמה מלך ישראל׃"));
hebsuperobject.biblemap.put("b28_11_1", new hebsuperobject(28,11,1,"כי נער ישראל ואהבהו וממצרים קראתי לבני׃"));
hebsuperobject.biblemap.put("b28_11_2", new hebsuperobject(28,11,2,"קראו להם כן הלכו מפניהם לבעלים יזבחו ולפסלים יקטרון׃"));
hebsuperobject.biblemap.put("b28_11_3", new hebsuperobject(28,11,3,"ואנכי תרגלתי לאפרים קחם על זרועתיו ולא ידעו כי רפאתים׃"));
hebsuperobject.biblemap.put("b28_11_4", new hebsuperobject(28,11,4,"בחבלי אדם אמשכם בעבתות אהבה ואהיה להם כמרימי על על לחיהם ואט אליו אוכיל׃"));
hebsuperobject.biblemap.put("b28_11_5", new hebsuperobject(28,11,5,"לא ישוב אל ארץ מצרים ואשור הוא מלכו כי מאנו לשוב׃"));
hebsuperobject.biblemap.put("b28_11_6", new hebsuperobject(28,11,6,"וחלה חרב בעריו וכלתה בדיו ואכלה ממעצותיהם׃"));
hebsuperobject.biblemap.put("b28_11_7", new hebsuperobject(28,11,7,"ועמי תלואים למשובתי ואל על יקראהו יחד לא ירומם׃"));
hebsuperobject.biblemap.put("b28_11_8", new hebsuperobject(28,11,8,"איך אתנך אפרים אמגנך ישראל איך אתנך כאדמה אשימך כצבאים נהפך עלי לבי יחד נכמרו נחומי׃"));
hebsuperobject.biblemap.put("b28_11_9", new hebsuperobject(28,11,9,"לא אעשה חרון אפי לא אשוב לשחת אפרים כי אל אנכי ולא איש בקרבך קדוש ולא אבוא בעיר׃"));
hebsuperobject.biblemap.put("b28_11_10", new hebsuperobject(28,11,10,"אחרי יהוה ילכו כאריה ישאג כי הוא ישאג ויחרדו בנים מים׃"));
hebsuperobject.biblemap.put("b28_11_11", new hebsuperobject(28,11,11,"יחרדו כצפור ממצרים וכיונה מארץ אשור והושבתים על בתיהם נאם יהוה׃"));
hebsuperobject.biblemap.put("b28_11_12", new hebsuperobject(28,11,12,"סבבני בכחש אפרים ובמרמה בית ישראל ויהודה עד רד עם אל ועם קדושים נאמן׃"));
hebsuperobject.biblemap.put("b28_12_1", new hebsuperobject(28,12,1,"אפרים רעה רוח ורדף קדים כל היום כזב ושד ירבה וברית עם אשור יכרתו ושמן למצרים יובל׃"));
hebsuperobject.biblemap.put("b28_12_2", new hebsuperobject(28,12,2,"וריב ליהוה עם יהודה ולפקד על יעקב כדרכיו כמעלליו ישיב לו׃"));
hebsuperobject.biblemap.put("b28_12_3", new hebsuperobject(28,12,3,"בבטן עקב את אחיו ובאונו שרה את אלהים׃"));
hebsuperobject.biblemap.put("b28_12_4", new hebsuperobject(28,12,4,"וישר אל מלאך ויכל בכה ויתחנן לו בית אל ימצאנו ושם ידבר עמנו׃"));
hebsuperobject.biblemap.put("b28_12_5", new hebsuperobject(28,12,5,"ויהוה אלהי הצבאות יהוה זכרו׃"));
hebsuperobject.biblemap.put("b28_12_6", new hebsuperobject(28,12,6,"ואתה באלהיך תשוב חסד ומשפט שמר וקוה אל אלהיך תמיד׃"));
hebsuperobject.biblemap.put("b28_12_7", new hebsuperobject(28,12,7,"כנען בידו מאזני מרמה לעשק אהב׃"));
hebsuperobject.biblemap.put("b28_12_8", new hebsuperobject(28,12,8,"ויאמר אפרים אך עשרתי מצאתי און לי כל יגיעי לא ימצאו לי עון אשר חטא׃"));
hebsuperobject.biblemap.put("b28_12_9", new hebsuperobject(28,12,9,"ואנכי יהוה אלהיך מארץ מצרים עד אושיבך באהלים כימי מועד׃"));
hebsuperobject.biblemap.put("b28_12_10", new hebsuperobject(28,12,10,"ודברתי על הנביאים ואנכי חזון הרביתי וביד הנביאים אדמה׃"));
hebsuperobject.biblemap.put("b28_12_11", new hebsuperobject(28,12,11,"אם גלעד און אך שוא היו בגלגל שורים זבחו גם מזבחותם כגלים על תלמי שדי׃"));
hebsuperobject.biblemap.put("b28_12_12", new hebsuperobject(28,12,12,"ויברח יעקב שדה ארם ויעבד ישראל באשה ובאשה שמר׃"));
hebsuperobject.biblemap.put("b28_12_13", new hebsuperobject(28,12,13,"ובנביא העלה יהוה את ישראל ממצרים ובנביא נשמר׃"));
hebsuperobject.biblemap.put("b28_12_14", new hebsuperobject(28,12,14,"הכעיס אפרים תמרורים ודמיו עליו יטוש וחרפתו ישיב לו אדניו׃"));
hebsuperobject.biblemap.put("b28_13_1", new hebsuperobject(28,13,1,"כדבר אפרים רתת נשא הוא בישראל ויאשם בבעל וימת׃"));
hebsuperobject.biblemap.put("b28_13_2", new hebsuperobject(28,13,2,"ועתה יוספו לחטא ויעשו להם מסכה מכספם כתבונם עצבים מעשה חרשים כלה להם הם אמרים זבחי אדם עגלים ישקון׃"));
hebsuperobject.biblemap.put("b28_13_3", new hebsuperobject(28,13,3,"לכן יהיו כענן בקר וכטל משכים הלך כמץ יסער מגרן וכעשן מארבה׃"));
hebsuperobject.biblemap.put("b28_13_4", new hebsuperobject(28,13,4,"ואנכי יהוה אלהיך מארץ מצרים ואלהים זולתי לא תדע ומושיע אין בלתי׃"));
hebsuperobject.biblemap.put("b28_13_5", new hebsuperobject(28,13,5,"אני ידעתיך במדבר בארץ תלאבות׃"));
hebsuperobject.biblemap.put("b28_13_6", new hebsuperobject(28,13,6,"כמרעיתם וישבעו שבעו וירם לבם על כן שכחוני׃"));
hebsuperobject.biblemap.put("b28_13_7", new hebsuperobject(28,13,7,"ואהי להם כמו שחל כנמר על דרך אשור׃"));
hebsuperobject.biblemap.put("b28_13_8", new hebsuperobject(28,13,8,"אפגשם כדב שכול ואקרע סגור לבם ואכלם שם כלביא חית השדה תבקעם׃"));
hebsuperobject.biblemap.put("b28_13_9", new hebsuperobject(28,13,9,"שחתך ישראל כי בי בעזרך׃"));
hebsuperobject.biblemap.put("b28_13_10", new hebsuperobject(28,13,10,"אהי מלכך אפוא ויושיעך בכל עריך ושפטיך אשר אמרת תנה לי מלך ושרים׃"));
hebsuperobject.biblemap.put("b28_13_11", new hebsuperobject(28,13,11,"אתן לך מלך באפי ואקח בעברתי׃"));
