hebsuperobject.biblemap.put("b28_13_15", new hebsuperobject(28,13,15,"כי הוא בין אחים יפריא יבוא קדים רוח יהוה ממדבר עלה ויבוש מקורו ויחרב מעינו הוא ישסה אוצר כל כלי חמדה׃"));
hebsuperobject.biblemap.put("b28_13_16", new hebsuperobject(28,13,16,"תאשם שמרון כי מרתה באלהיה בחרב יפלו עלליהם ירטשו והריותיו יבקעו׃"));
hebsuperobject.biblemap.put("b28_14_1", new hebsuperobject(28,14,1,"שובה ישראל עד יהוה אלהיך כי כשלת בעונך׃"));
