hebsuperobject.biblemap.put("b28_14_3", new hebsuperobject(28,14,3,"אשור לא יושיענו על סוס לא נרכב ולא נאמר עוד אלהינו למעשה ידינו אשר בך ירחם יתום׃"));
hebsuperobject.biblemap.put("b28_14_4", new hebsuperobject(28,14,4,"ארפא משובתם אהבם נדבה כי שב אפי ממנו׃"));
