hebsuperobject.biblemap.put("b3_25_42", new hebsuperobject(3,25,42,"כי עבדי הם אשר הוצאתי אתם מארץ מצרים לא ימכרו ממכרת עבד׃"));
hebsuperobject.biblemap.put("b3_25_43", new hebsuperobject(3,25,43,"לא תרדה בו בפרך ויראת מאלהיך׃"));
hebsuperobject.biblemap.put("b3_25_44", new hebsuperobject(3,25,44,"ועבדך ואמתך אשר יהיו לך מאת הגוים אשר סביבתיכם מהם תקנו עבד ואמה׃"));
hebsuperobject.biblemap.put("b3_25_45", new hebsuperobject(3,25,45,"וגם מבני התושבים הגרים עמכם מהם תקנו וממשפחתם אשר עמכם אשר הולידו בארצכם והיו לכם לאחזה׃"));
hebsuperobject.biblemap.put("b3_25_46", new hebsuperobject(3,25,46,"והתנחלתם אתם לבניכם אחריכם לרשת אחזה לעלם בהם תעבדו ובאחיכם בני ישראל איש באחיו לא תרדה בו בפרך׃"));
hebsuperobject.biblemap.put("b3_25_47", new hebsuperobject(3,25,47,"וכי תשיג יד גר ותושב עמך ומך אחיך עמו ונמכר לגר תושב עמך או לעקר משפחת גר׃"));
hebsuperobject.biblemap.put("b3_25_48", new hebsuperobject(3,25,48,"אחרי נמכר גאלה תהיה לו אחד מאחיו יגאלנו׃"));
hebsuperobject.biblemap.put("b3_25_49", new hebsuperobject(3,25,49,"או דדו או בן דדו יגאלנו או משאר בשרו ממשפחתו יגאלנו או השיגה ידו ונגאל׃"));
hebsuperobject.biblemap.put("b3_25_50", new hebsuperobject(3,25,50,"וחשב עם קנהו משנת המכרו לו עד שנת היבל והיה כסף ממכרו במספר שנים כימי שכיר יהיה עמו׃"));
hebsuperobject.biblemap.put("b3_25_51", new hebsuperobject(3,25,51,"אם עוד רבות בשנים לפיהן ישיב גאלתו מכסף מקנתו׃"));
hebsuperobject.biblemap.put("b3_25_52", new hebsuperobject(3,25,52,"ואם מעט נשאר בשנים עד שנת היבל וחשב לו כפי שניו ישיב את גאלתו׃"));
hebsuperobject.biblemap.put("b3_25_53", new hebsuperobject(3,25,53,"כשכיר שנה בשנה יהיה עמו לא ירדנו בפרך לעיניך׃"));
hebsuperobject.biblemap.put("b3_25_54", new hebsuperobject(3,25,54,"ואם לא יגאל באלה ויצא בשנת היבל הוא ובניו עמו׃"));
hebsuperobject.biblemap.put("b3_25_55", new hebsuperobject(3,25,55,"כי לי בני ישראל עבדים עבדי הם אשר הוצאתי אותם מארץ מצרים אני יהוה אלהיכם׃"));
hebsuperobject.biblemap.put("b3_26_1", new hebsuperobject(3,26,1,"לא תעשו לכם אלילם ופסל ומצבה לא תקימו לכם ואבן משכית לא תתנו בארצכם להשתחות עליה כי אני יהוה אלהיכם׃"));
hebsuperobject.biblemap.put("b3_26_2", new hebsuperobject(3,26,2,"את שבתתי תשמרו ומקדשי תיראו אני יהוה׃"));
hebsuperobject.biblemap.put("b3_26_3", new hebsuperobject(3,26,3,"אם בחקתי תלכו ואת מצותי תשמרו ועשיתם אתם׃"));
hebsuperobject.biblemap.put("b3_26_4", new hebsuperobject(3,26,4,"ונתתי גשמיכם בעתם ונתנה הארץ יבולה ועץ השדה יתן פריו׃"));
hebsuperobject.biblemap.put("b3_26_5", new hebsuperobject(3,26,5,"והשיג לכם דיש את בציר ובציר ישיג את זרע ואכלתם לחמכם לשבע וישבתם לבטח בארצכם׃"));
hebsuperobject.biblemap.put("b3_26_6", new hebsuperobject(3,26,6,"ונתתי שלום בארץ ושכבתם ואין מחריד והשבתי חיה רעה מן הארץ וחרב לא תעבר בארצכם׃"));
hebsuperobject.biblemap.put("b3_26_7", new hebsuperobject(3,26,7,"ורדפתם את איביכם ונפלו לפניכם לחרב׃"));
hebsuperobject.biblemap.put("b3_26_8", new hebsuperobject(3,26,8,"ורדפו מכם חמשה מאה ומאה מכם רבבה ירדפו ונפלו איביכם לפניכם לחרב׃"));
hebsuperobject.biblemap.put("b3_26_9", new hebsuperobject(3,26,9,"ופניתי אליכם והפריתי אתכם והרביתי אתכם והקימתי את בריתי אתכם׃"));
hebsuperobject.biblemap.put("b3_26_10", new hebsuperobject(3,26,10,"ואכלתם ישן נושן וישן מפני חדש תוציאו׃"));
hebsuperobject.biblemap.put("b3_26_11", new hebsuperobject(3,26,11,"ונתתי משכני בתוככם ולא תגעל נפשי אתכם׃"));
hebsuperobject.biblemap.put("b3_26_12", new hebsuperobject(3,26,12,"והתהלכתי בתוככם והייתי לכם לאלהים ואתם תהיו לי לעם׃"));
hebsuperobject.biblemap.put("b3_26_13", new hebsuperobject(3,26,13,"אני יהוה אלהיכם אשר הוצאתי אתכם מארץ מצרים מהית להם עבדים ואשבר מטת עלכם ואולך אתכם קוממיות׃"));
hebsuperobject.biblemap.put("b3_26_14", new hebsuperobject(3,26,14,"ואם לא תשמעו לי ולא תעשו את כל המצות האלה׃"));
hebsuperobject.biblemap.put("b3_26_15", new hebsuperobject(3,26,15,"ואם בחקתי תמאסו ואם את משפטי תגעל נפשכם לבלתי עשות את כל מצותי להפרכם את בריתי׃"));
hebsuperobject.biblemap.put("b3_26_16", new hebsuperobject(3,26,16,"אף אני אעשה זאת לכם והפקדתי עליכם בהלה את השחפת ואת הקדחת מכלות עינים ומדיבת נפש וזרעתם לריק זרעכם ואכלהו איביכם׃"));
hebsuperobject.biblemap.put("b3_26_17", new hebsuperobject(3,26,17,"ונתתי פני בכם ונגפתם לפני איביכם ורדו בכם שנאיכם ונסתם ואין רדף אתכם׃"));
hebsuperobject.biblemap.put("b3_26_18", new hebsuperobject(3,26,18,"ואם עד אלה לא תשמעו לי ויספתי ליסרה אתכם שבע על חטאתיכם׃"));
hebsuperobject.biblemap.put("b3_26_19", new hebsuperobject(3,26,19,"ושברתי את גאון עזכם ונתתי את שמיכם כברזל ואת ארצכם כנחשה׃"));
hebsuperobject.biblemap.put("b3_26_20", new hebsuperobject(3,26,20,"ותם לריק כחכם ולא תתן ארצכם את יבולה ועץ הארץ לא יתן פריו׃"));
hebsuperobject.biblemap.put("b3_26_21", new hebsuperobject(3,26,21,"ואם תלכו עמי קרי ולא תאבו לשמע לי ויספתי עליכם מכה שבע כחטאתיכם׃"));
hebsuperobject.biblemap.put("b3_26_22", new hebsuperobject(3,26,22,"והשלחתי בכם את חית השדה ושכלה אתכם והכריתה את בהמתכם והמעיטה אתכם ונשמו דרכיכם׃"));
hebsuperobject.biblemap.put("b3_26_23", new hebsuperobject(3,26,23,"ואם באלה לא תוסרו לי והלכתם עמי קרי׃"));
hebsuperobject.biblemap.put("b3_26_24", new hebsuperobject(3,26,24,"והלכתי אף אני עמכם בקרי והכיתי אתכם גם אני שבע על חטאתיכם׃"));
hebsuperobject.biblemap.put("b3_26_25", new hebsuperobject(3,26,25,"והבאתי עליכם חרב נקמת נקם ברית ונאספתם אל עריכם ושלחתי דבר בתוככם ונתתם ביד אויב׃"));
hebsuperobject.biblemap.put("b3_26_26", new hebsuperobject(3,26,26,"בשברי לכם מטה לחם ואפו עשר נשים לחמכם בתנור אחד והשיבו לחמכם במשקל ואכלתם ולא תשבעו׃"));
hebsuperobject.biblemap.put("b3_26_27", new hebsuperobject(3,26,27,"ואם בזאת לא תשמעו לי והלכתם עמי בקרי׃"));
hebsuperobject.biblemap.put("b3_26_28", new hebsuperobject(3,26,28,"והלכתי עמכם בחמת קרי ויסרתי אתכם אף אני שבע על חטאתיכם׃"));
hebsuperobject.biblemap.put("b3_26_29", new hebsuperobject(3,26,29,"ואכלתם בשר בניכם ובשר בנתיכם תאכלו׃"));
hebsuperobject.biblemap.put("b3_26_30", new hebsuperobject(3,26,30,"והשמדתי את במתיכם והכרתי את חמניכם ונתתי את פגריכם על פגרי גלוליכם וגעלה נפשי אתכם׃"));
hebsuperobject.biblemap.put("b3_26_31", new hebsuperobject(3,26,31,"ונתתי את עריכם חרבה והשמותי את מקדשיכם ולא אריח בריח ניחחכם׃"));
hebsuperobject.biblemap.put("b3_26_32", new hebsuperobject(3,26,32,"והשמתי אני את הארץ ושממו עליה איביכם הישבים בה׃"));
hebsuperobject.biblemap.put("b3_26_33", new hebsuperobject(3,26,33,"ואתכם אזרה בגוים והריקתי אחריכם חרב והיתה ארצכם שממה ועריכם יהיו חרבה׃"));
hebsuperobject.biblemap.put("b3_26_34", new hebsuperobject(3,26,34,"אז תרצה הארץ את שבתתיה כל ימי השמה ואתם בארץ איביכם אז תשבת הארץ והרצת את שבתתיה׃"));
hebsuperobject.biblemap.put("b3_26_35", new hebsuperobject(3,26,35,"כל ימי השמה תשבת את אשר לא שבתה בשבתתיכם בשבתכם עליה׃"));
hebsuperobject.biblemap.put("b3_26_36", new hebsuperobject(3,26,36,"והנשארים בכם והבאתי מרך בלבבם בארצת איביהם ורדף אתם קול עלה נדף ונסו מנסת חרב ונפלו ואין רדף׃"));
hebsuperobject.biblemap.put("b3_26_37", new hebsuperobject(3,26,37,"וכשלו איש באחיו כמפני חרב ורדף אין ולא תהיה לכם תקומה לפני איביכם׃"));
hebsuperobject.biblemap.put("b3_26_38", new hebsuperobject(3,26,38,"ואבדתם בגוים ואכלה אתכם ארץ איביכם׃"));
hebsuperobject.biblemap.put("b3_26_39", new hebsuperobject(3,26,39,"והנשארים בכם ימקו בעונם בארצת איביכם ואף בעונת אבתם אתם ימקו׃"));
hebsuperobject.biblemap.put("b3_26_40", new hebsuperobject(3,26,40,"והתודו את עונם ואת עון אבתם במעלם אשר מעלו בי ואף אשר הלכו עמי בקרי׃"));
hebsuperobject.biblemap.put("b3_26_41", new hebsuperobject(3,26,41,"אף אני אלך עמם בקרי והבאתי אתם בארץ איביהם או אז יכנע לבבם הערל ואז ירצו את עונם׃"));
hebsuperobject.biblemap.put("b3_26_42", new hebsuperobject(3,26,42,"וזכרתי את בריתי יעקוב ואף את בריתי יצחק ואף את בריתי אברהם אזכר והארץ אזכר׃"));
hebsuperobject.biblemap.put("b3_26_43", new hebsuperobject(3,26,43,"והארץ תעזב מהם ותרץ את שבתתיה בהשמה מהם והם ירצו את עונם יען וביען במשפטי מאסו ואת חקתי געלה נפשם׃"));
hebsuperobject.biblemap.put("b3_26_44", new hebsuperobject(3,26,44,"ואף גם זאת בהיותם בארץ איביהם לא מאסתים ולא געלתים לכלתם להפר בריתי אתם כי אני יהוה אלהיהם׃"));
hebsuperobject.biblemap.put("b3_26_45", new hebsuperobject(3,26,45,"וזכרתי להם ברית ראשנים אשר הוצאתי אתם מארץ מצרים לעיני הגוים להית להם לאלהים אני יהוה׃"));
hebsuperobject.biblemap.put("b3_26_46", new hebsuperobject(3,26,46,"אלה החקים והמשפטים והתורת אשר נתן יהוה בינו ובין בני ישראל בהר סיני ביד משה׃"));
hebsuperobject.biblemap.put("b3_27_1", new hebsuperobject(3,27,1,"וידבר יהוה אל משה לאמר׃"));
hebsuperobject.biblemap.put("b3_27_2", new hebsuperobject(3,27,2,"דבר אל בני ישראל ואמרת אלהם איש כי יפלא נדר בערכך נפשת ליהוה׃"));
hebsuperobject.biblemap.put("b3_27_3", new hebsuperobject(3,27,3,"והיה ערכך הזכר מבן עשרים שנה ועד בן ששים שנה והיה ערכך חמשים שקל כסף בשקל הקדש׃"));
hebsuperobject.biblemap.put("b3_27_4", new hebsuperobject(3,27,4,"ואם נקבה הוא והיה ערכך שלשים שקל׃"));
hebsuperobject.biblemap.put("b3_27_5", new hebsuperobject(3,27,5,"ואם מבן חמש שנים ועד בן עשרים שנה והיה ערכך הזכר עשרים שקלים ולנקבה עשרת שקלים׃"));
hebsuperobject.biblemap.put("b3_27_6", new hebsuperobject(3,27,6,"ואם מבן חדש ועד בן חמש שנים והיה ערכך הזכר חמשה שקלים כסף ולנקבה ערכך שלשת שקלים כסף׃"));
hebsuperobject.biblemap.put("b3_27_7", new hebsuperobject(3,27,7,"ואם מבן ששים שנה ומעלה אם זכר והיה ערכך חמשה עשר שקל ולנקבה עשרה שקלים׃"));
hebsuperobject.biblemap.put("b3_27_8", new hebsuperobject(3,27,8,"ואם מך הוא מערכך והעמידו לפני הכהן והעריך אתו הכהן על פי אשר תשיג יד הנדר יעריכנו הכהן׃"));
hebsuperobject.biblemap.put("b3_27_9", new hebsuperobject(3,27,9,"ואם בהמה אשר יקריבו ממנה קרבן ליהוה כל אשר יתן ממנו ליהוה יהיה קדש׃"));
hebsuperobject.biblemap.put("b3_27_10", new hebsuperobject(3,27,10,"לא יחליפנו ולא ימיר אתו טוב ברע או רע בטוב ואם המר ימיר בהמה בבהמה והיה הוא ותמורתו יהיה קדש׃"));
hebsuperobject.biblemap.put("b3_27_11", new hebsuperobject(3,27,11,"ואם כל בהמה טמאה אשר לא יקריבו ממנה קרבן ליהוה והעמיד את הבהמה לפני הכהן׃"));
hebsuperobject.biblemap.put("b3_27_12", new hebsuperobject(3,27,12,"והעריך הכהן אתה בין טוב ובין רע כערכך הכהן כן יהיה׃"));
hebsuperobject.biblemap.put("b3_27_13", new hebsuperobject(3,27,13,"ואם גאל יגאלנה ויסף חמישתו על ערכך׃"));
hebsuperobject.biblemap.put("b3_27_14", new hebsuperobject(3,27,14,"ואיש כי יקדש את ביתו קדש ליהוה והעריכו הכהן בין טוב ובין רע כאשר יעריך אתו הכהן כן יקום׃"));
hebsuperobject.biblemap.put("b3_27_15", new hebsuperobject(3,27,15,"ואם המקדיש יגאל את ביתו ויסף חמישית כסף ערכך עליו והיה לו׃"));
hebsuperobject.biblemap.put("b3_27_16", new hebsuperobject(3,27,16,"ואם משדה אחזתו יקדיש איש ליהוה והיה ערכך לפי זרעו זרע חמר שערים בחמשים שקל כסף׃"));
hebsuperobject.biblemap.put("b3_27_17", new hebsuperobject(3,27,17,"אם משנת היבל יקדיש שדהו כערכך יקום׃"));
hebsuperobject.biblemap.put("b3_27_18", new hebsuperobject(3,27,18,"ואם אחר היבל יקדיש שדהו וחשב לו הכהן את הכסף על פי השנים הנותרת עד שנת היבל ונגרע מערכך׃"));
hebsuperobject.biblemap.put("b3_27_19", new hebsuperobject(3,27,19,"ואם גאל יגאל את השדה המקדיש אתו ויסף חמשית כסף ערכך עליו וקם לו׃"));
hebsuperobject.biblemap.put("b3_27_20", new hebsuperobject(3,27,20,"ואם לא יגאל את השדה ואם מכר את השדה לאיש אחר לא יגאל עוד׃"));
hebsuperobject.biblemap.put("b3_27_21", new hebsuperobject(3,27,21,"והיה השדה בצאתו ביבל קדש ליהוה כשדה החרם לכהן תהיה אחזתו׃"));
hebsuperobject.biblemap.put("b3_27_22", new hebsuperobject(3,27,22,"ואם את שדה מקנתו אשר לא משדה אחזתו יקדיש ליהוה׃"));
hebsuperobject.biblemap.put("b3_27_23", new hebsuperobject(3,27,23,"וחשב לו הכהן את מכסת הערכך עד שנת היבל ונתן את הערכך ביום ההוא קדש ליהוה׃"));
hebsuperobject.biblemap.put("b3_27_24", new hebsuperobject(3,27,24,"בשנת היובל ישוב השדה לאשר קנהו מאתו לאשר לו אחזת הארץ׃"));
hebsuperobject.biblemap.put("b3_27_25", new hebsuperobject(3,27,25,"וכל ערכך יהיה בשקל הקדש עשרים גרה יהיה השקל׃"));
hebsuperobject.biblemap.put("b3_27_26", new hebsuperobject(3,27,26,"אך בכור אשר יבכר ליהוה בבהמה לא יקדיש איש אתו אם שור אם שה ליהוה הוא׃"));
hebsuperobject.biblemap.put("b3_27_27", new hebsuperobject(3,27,27,"ואם בבהמה הטמאה ופדה בערכך ויסף חמשתו עליו ואם לא יגאל ונמכר בערכך׃"));
