hebsuperobject.biblemap.put("b36_3_11", new hebsuperobject(36,3,11,"ביום ההוא לא תבושי מכל עלילתיך אשר פשעת בי כי אז אסיר מקרבך עליזי גאותך ולא תוספי לגבהה עוד בהר קדשי׃"));
hebsuperobject.biblemap.put("b36_3_12", new hebsuperobject(36,3,12,"והשארתי בקרבך עם עני ודל וחסו בשם יהוה׃"));
