hebsuperobject.biblemap.put("b36_3_14", new hebsuperobject(36,3,14,"רני בת ציון הריעו ישראל שמחי ועלזי בכל לב בת ירושלם׃"));
hebsuperobject.biblemap.put("b36_3_15", new hebsuperobject(36,3,15,"הסיר יהוה משפטיך פנה איבך מלך ישראל יהוה בקרבך לא תיראי רע עוד׃"));
