hebsuperobject.biblemap.put("b41_3_30", new hebsuperobject(41,3,30,"כי המה אמרו רוח טמאה בו׃"));
hebsuperobject.biblemap.put("b41_3_31", new hebsuperobject(41,3,31,"ויבאו אמו ואחיו ויעמדו מחוץ לבית וישלחו אליו לקרא לו׃"));
hebsuperobject.biblemap.put("b41_3_32", new hebsuperobject(41,3,32,"והמון העם ישבו סביביו ויאמרו אליו הנה אמך ואחיך בחוץ מבקשים אותך׃"));
