hebsuperobject.biblemap.put("b41_11_20", new hebsuperobject(41,11,20,"ויהי הם עברים בבקר ויראו את התאנה כי יבשה עם שרשיה׃"));
hebsuperobject.biblemap.put("b41_11_21", new hebsuperobject(41,11,21,"ויזכר פטרוס ויאמר אליו רבי הנה התאנה אשר אררתה יבשה׃"));
hebsuperobject.biblemap.put("b41_11_22", new hebsuperobject(41,11,22,"ויען ישוע ויאמר אליהם תהי נא בכם אמונת אלהים׃"));
hebsuperobject.biblemap.put("b41_11_23", new hebsuperobject(41,11,23,"כי אמן אמר אני לכם כל אשר יאמר אל ההר הזה הנשא והעתק אל תוך הים ואין ספק בלבבו כי אם יאמין כי יהיה כאשר אמר כן גם יהיה לו׃"));
hebsuperobject.biblemap.put("b41_11_24", new hebsuperobject(41,11,24,"על כן אני אמר לכם כל אשר תשאלו בהתפללכם האמינו כי תקחו ויהי לכם׃"));
hebsuperobject.biblemap.put("b41_11_25", new hebsuperobject(41,11,25,"וכי תעמדו להתפלל תסלחו לכל איש את אשר בלבבכם עליו למען יסלח לכם אביכם שבשמים אף הוא את פשעיכם׃"));
hebsuperobject.biblemap.put("b41_11_26", new hebsuperobject(41,11,26,"ואתם אם לא תסלחו אף אביכם שבשמים לא יסלח לכם את פשעיכם׃"));
hebsuperobject.biblemap.put("b41_11_27", new hebsuperobject(41,11,27,"וישובו ויבאו ירושלים ויהי הוא מתהלך במקדש ויבאו אליו הכהנים הגדולים והסופרים והזקנים׃"));
hebsuperobject.biblemap.put("b41_11_28", new hebsuperobject(41,11,28,"ויאמרו אליו באי זו רשות אתה עשה אלה ומי נתן לך את הרשות הזאת לעשות את אלה׃"));
hebsuperobject.biblemap.put("b41_11_29", new hebsuperobject(41,11,29,"ויאמר ישוע אליהם גם אני אשאלה אתכם דבר אחד ואתם השיבוני ואמר לכם באי זו רשות אני עשה אלה׃"));
hebsuperobject.biblemap.put("b41_11_30", new hebsuperobject(41,11,30,"טבילת יוחנן המן השמים היתה אם מבני אדם השיבוני׃"));
hebsuperobject.biblemap.put("b41_11_31", new hebsuperobject(41,11,31,"ויחשבו כה וכה בקרבם לאמר אם נאמר מן השמים יאמר מדוע אפוא לא האמנתם לו׃"));
hebsuperobject.biblemap.put("b41_11_32", new hebsuperobject(41,11,32,"או הנאמר מבני אדם וייראו את העם כי כלם חשבו את יוחנן לנביא באמת׃"));
hebsuperobject.biblemap.put("b41_11_33", new hebsuperobject(41,11,33,"ויענו ויאמרו אל ישוע לא ידענו ויען ישוע ויאמר אליהם אם כן גם אני לא אמר לכם באי זו רשות אני עשה אלה׃"));
hebsuperobject.biblemap.put("b41_12_1", new hebsuperobject(41,12,1,"ויחל לדבר אליהם במשלים לאמר איש נטע כרם ויעש גדר סביבותיו ויחצב יקב ויבן מגדל ויתנהו אל כרמים וילך למרחקים׃"));
hebsuperobject.biblemap.put("b41_12_2", new hebsuperobject(41,12,2,"ולמועד שלח אל הכרמים עבד לקחת מאת הכרמים מפרי הכרם׃"));
hebsuperobject.biblemap.put("b41_12_3", new hebsuperobject(41,12,3,"ויחזיקו בו ויכהו וישלחהו ריקם׃"));
hebsuperobject.biblemap.put("b41_12_4", new hebsuperobject(41,12,4,"ויסף לשלח אליהם עבד אחר ואתו סקלו באבנים ומחקו ראשו וישלחהו נכלם׃"));
hebsuperobject.biblemap.put("b41_12_5", new hebsuperobject(41,12,5,"ויסף וישלח אחר וגם אתו הרגו וכן עשו ברבים אחרים מהם הכו ומהם הרגו׃"));
hebsuperobject.biblemap.put("b41_12_6", new hebsuperobject(41,12,6,"ויהי לו עוד בן יחיד אשר אהבו וישלח גם אתו אליהם באחרנה לאמר מפני בני יגורו׃"));
hebsuperobject.biblemap.put("b41_12_7", new hebsuperobject(41,12,7,"והכרמים ההם אמרו איש אל רעהו הנה זה הוא היורש לכו ונהרגהו ולנו תהיה הירשה׃"));
hebsuperobject.biblemap.put("b41_12_8", new hebsuperobject(41,12,8,"ויחזיקו בו ויהרגו אותו וישליכהו אל מחוץ לכרם׃"));
hebsuperobject.biblemap.put("b41_12_9", new hebsuperobject(41,12,9,"ועתה מה יעשה בעל הכרם יבוא ויאבד את הכרמים ההם ויתן את הכרם לאחרים׃"));
hebsuperobject.biblemap.put("b41_12_10", new hebsuperobject(41,12,10,"הלא קראתם את הכתוב הזה אבן מאסו הבונים היתה לראש פנה׃"));
hebsuperobject.biblemap.put("b41_12_11", new hebsuperobject(41,12,11,"מאת יהוה היתה זאת היא נפלאת בעינינו׃"));
hebsuperobject.biblemap.put("b41_12_12", new hebsuperobject(41,12,12,"ויבקשו לתפשו וייראו מפני העם יען אשר הבינו כי עליהם דבר את המשל הזה ויעזבהו וילכו להם׃"));
hebsuperobject.biblemap.put("b41_12_13", new hebsuperobject(41,12,13,"וישלחו אליו אנשים מן הפרושים ומן ההורדוסים ללכד אתו בדברו׃"));
hebsuperobject.biblemap.put("b41_12_14", new hebsuperobject(41,12,14,"ויבאו ויאמרו אליו רבי ידענו כי איש אמת אתה ולא תגור מפני איש כי לא תכיר פנים כי אם באמת מורה אתה את דרך האלהים הנכון לתת מס אל הקיסר אם לא נתן׃"));
hebsuperobject.biblemap.put("b41_12_15", new hebsuperobject(41,12,15,"והוא ידע את חנפתם ויאמר אליהם מה תנסוני הביאו אלי דינר ואראה׃"));
hebsuperobject.biblemap.put("b41_12_16", new hebsuperobject(41,12,16,"ויביאו ויאמר אליהם של מי הצורה הזאת והמכתב אשר עליו ויאמרו אליו של הקיסר׃"));
hebsuperobject.biblemap.put("b41_12_17", new hebsuperobject(41,12,17,"ויאמר ישוע אליהם את אשר לקיסר תנו לקיסר ואת אשר לאלהים תנו לאלהים ויתמהו עליו׃"));
hebsuperobject.biblemap.put("b41_12_18", new hebsuperobject(41,12,18,"ויבאו אליו מן הצדוקים האמרים כי אין תחיה למתים וישאלהו לאמר׃"));
hebsuperobject.biblemap.put("b41_12_19", new hebsuperobject(41,12,19,"רבי כתב לנו משה כי ימות אחי איש ועזב אשה ובנים אין לו ייבם אחיו את אשתו ויקם זרע לאחיו׃"));
hebsuperobject.biblemap.put("b41_12_20", new hebsuperobject(41,12,20,"והנה שבעה אחים והראשון לקח אשה ובמותו לא השאיר אחריו זרע׃"));
hebsuperobject.biblemap.put("b41_12_21", new hebsuperobject(41,12,21,"ויקח אתה השני וימת ולא הניח זרע וכן גם השלישי׃"));
hebsuperobject.biblemap.put("b41_12_22", new hebsuperobject(41,12,22,"ויקחוה כל השבעה ולא השאירו אחריהם זרע ואחרי מות כלם מתה גם האשה׃"));
hebsuperobject.biblemap.put("b41_12_23", new hebsuperobject(41,12,23,"ועתה בתחית המתים כשיקומו למי מהם תהיה לאשה כי לשבעה היתה לאשה׃"));
hebsuperobject.biblemap.put("b41_12_24", new hebsuperobject(41,12,24,"ויאמר ישוע אליהם הלא בזאת אתם תעים באשר אינכם יודעים גם את הכתובים גם את גבורת האלהים׃"));
hebsuperobject.biblemap.put("b41_12_25", new hebsuperobject(41,12,25,"כי בעת קומם מן המתים האנשים לא ישאו נשים ולא תנשאנה אך יהיו כמלאכי השמים׃"));
hebsuperobject.biblemap.put("b41_12_26", new hebsuperobject(41,12,26,"ועל דבר המתים כי קום יקומו הלא קראתם בספר משה בסנה את אשר דבר אליו האלהים לאמר אנכי אלהי אברהם ואלהי יצחק ואלהי יעקב׃"));
hebsuperobject.biblemap.put("b41_12_27", new hebsuperobject(41,12,27,"האלהים איננו אלהי המתים כי אם אלהי החיים לכן הרביתם לתעות׃"));
hebsuperobject.biblemap.put("b41_12_28", new hebsuperobject(41,12,28,"ואחד מן הסופרים שמע אתם מתוכחים ויקרב אליהם וירא כי היטב השיבם וישאלהו מה היא הראשנה לכל המצות׃"));
hebsuperobject.biblemap.put("b41_12_29", new hebsuperobject(41,12,29,"ויען ישוע וידבר אליו הראשנה לכל המצות היא שמע ישראל יהוה אלהינו יהוה אחד׃"));
hebsuperobject.biblemap.put("b41_12_30", new hebsuperobject(41,12,30,"ואהבת את יהוה אלהיך בכל לבבך ובכל נפשך ובכל מדעך ובכל מאדך זאת היא המצוה הראשנה׃"));
hebsuperobject.biblemap.put("b41_12_31", new hebsuperobject(41,12,31,"והשנית הדמה לה היא ואהבת לרעך כמוך ואין מצוה גדולה מאלה׃"));
hebsuperobject.biblemap.put("b41_12_32", new hebsuperobject(41,12,32,"ויאמר אליו הסופר אמנם רבי יפה דברת כי אלהים אחד הוא ואין עוד מלבדו׃"));
hebsuperobject.biblemap.put("b41_12_33", new hebsuperobject(41,12,33,"ולאהבה אתו בכל לבב ובכל מדע ובכל נפש ובכל מאד ולאהבה את הרע כנפשו גדולה היא מכל עלות וזבחים׃"));
hebsuperobject.biblemap.put("b41_12_34", new hebsuperobject(41,12,34,"וירא ישוע כי ענה בדעת ויאמר אליו לא רחוק אתה ממלכות האלהים ואיש לא ערב עוד את לבו לשאל אותו שאלה׃"));
hebsuperobject.biblemap.put("b41_12_35", new hebsuperobject(41,12,35,"ויען ישוע בלמדו במקדש ויאמר איך יאמרו הסופרים כי המשיח הוא בן דוד׃"));
hebsuperobject.biblemap.put("b41_12_36", new hebsuperobject(41,12,36,"והוא דוד אמר ברוח הקדש נאם יהוה לאדני שב לימיני עד אשית איביך הדם לרגליך׃"));
hebsuperobject.biblemap.put("b41_12_37", new hebsuperobject(41,12,37,"הנה דוד בעצמו קרא לו אדון ואיך הוא בנו ויאהב רב העם לשמע אתו׃"));
hebsuperobject.biblemap.put("b41_12_38", new hebsuperobject(41,12,38,"ויאמר אליהם בלמד אתם השמרו מן הסופרים האהבים להתהלך עטופי טלית ואת שאלות שלומם בשוקים׃"));
hebsuperobject.biblemap.put("b41_12_39", new hebsuperobject(41,12,39,"ואת מושבי הראש בבתי כנסיות ואת מסבות הראש בסעודות׃"));
hebsuperobject.biblemap.put("b41_12_40", new hebsuperobject(41,12,40,"הבלעים את בתי האלמנות ומאריכים בתפלה למראה עינים המה משפט על יתר יקחו׃"));
hebsuperobject.biblemap.put("b41_12_41", new hebsuperobject(41,12,41,"וישוע ישב ממול ארון האוצר והוא ראה את העם משליכים מעות לארון האוצר ועשירים רבים נתנו הרבה׃"));
hebsuperobject.biblemap.put("b41_12_42", new hebsuperobject(41,12,42,"ותבא אלמנה עניה ותשלך שתי פרוטות אשר הן רבע אסר׃"));
hebsuperobject.biblemap.put("b41_12_43", new hebsuperobject(41,12,43,"ויקרא אל תלמידיו ויאמר אליהם אמן אמר אני לכם כי האלמנה העניה הזאת נתנה יותר מכל הנתנים אל ארון האוצר׃"));
hebsuperobject.biblemap.put("b41_12_44", new hebsuperobject(41,12,44,"כי כלם נתנו מן העדף שלהם והיא ממחסרה נתנה כל אשר לה את כל רכושה׃"));
hebsuperobject.biblemap.put("b41_13_1", new hebsuperobject(41,13,1,"ויהי בצאתו מן המקדש ויאמר אליו אחד מתלמידיו רבי ראה מה יפו האבנים והבנינים האלה׃"));
hebsuperobject.biblemap.put("b41_13_2", new hebsuperobject(41,13,2,"ויען ישוע אתו ויאמר הראית את כל הבנינים הגדולים האלה לא תשאר אבן על אבן אשר לא תתפרק׃"));
hebsuperobject.biblemap.put("b41_13_3", new hebsuperobject(41,13,3,"וישב על הר הזיתים ממול המקדש וישאלהו פטרוס ויעקב ויוחנן ואנדרי והם אתו לבדם׃"));
hebsuperobject.biblemap.put("b41_13_4", new hebsuperobject(41,13,4,"אמר נא לנו מתי תהיה זאת ומה הוא האות בבא העת אשר תעשה בה כל זאת׃"));
hebsuperobject.biblemap.put("b41_13_5", new hebsuperobject(41,13,5,"ויען אתם ישוע ויחל לדבר ראו פן יתעה אתכם איש׃"));
hebsuperobject.biblemap.put("b41_13_6", new hebsuperobject(41,13,6,"כי רבים יבאו בשמי לאמר אני הוא ויתעו רבים׃"));
hebsuperobject.biblemap.put("b41_13_7", new hebsuperobject(41,13,7,"ובשמעכם מלחמות ושמעות מלחמה אל תבהלו כי היו תהיה זאת אך לא זאת היא הקץ׃"));
hebsuperobject.biblemap.put("b41_13_8", new hebsuperobject(41,13,8,"כי יקום גוי על גוי וממלכה על ממלכה והיה רעש כה וכה והיה רעב ומהומה׃"));
hebsuperobject.biblemap.put("b41_13_9", new hebsuperobject(41,13,9,"אלה ראשית החבלים ואתם השמרו בנפשתיכם כי ימסרו אתכם לסנהדריות והכיתם בבתי כנסיות ולפני משלים ומלכים תובאו למעני לעדות להם׃"));
hebsuperobject.biblemap.put("b41_13_10", new hebsuperobject(41,13,10,"והבשורה צריכה להקרא בראשנה לכל הגוים׃"));
hebsuperobject.biblemap.put("b41_13_11", new hebsuperobject(41,13,11,"וכאשר יוליכו ומסרו אתכם אל תדאגו ואל תחשבו מה תדברו כי הדבר אשר ינתן לכם בשעה ההיא אותו דברו יען לא אתם הם המדברים כי אם רוח הקדש׃"));
hebsuperobject.biblemap.put("b41_13_12", new hebsuperobject(41,13,12,"ואח ימסר את אחיו למות ואב את בנו וקמו בנים באבותם והמיתו אותם׃"));
hebsuperobject.biblemap.put("b41_13_13", new hebsuperobject(41,13,13,"והייתם שנואים לכל אדם למען שמי והמחכה עד עת קץ הוא יושע׃"));
hebsuperobject.biblemap.put("b41_13_14", new hebsuperobject(41,13,14,"וכי תראו את שקוץ משמם הנאמר ביד דניאל הנביא עמד במקום אשר לא לו הקורא יבין אז נוס ינוסו אנשי יהודה אל ההרים׃"));
hebsuperobject.biblemap.put("b41_13_15", new hebsuperobject(41,13,15,"ואשר על הגג אל ירד הביתה ואל יבא פנימה לשאת דבר מביתו׃"));
hebsuperobject.biblemap.put("b41_13_16", new hebsuperobject(41,13,16,"ואשר בשדה אל ישב הביתה לשאת מלבושו׃"));
hebsuperobject.biblemap.put("b41_13_17", new hebsuperobject(41,13,17,"ואוי להרות ולמיניקות בימים ההמה׃"));
hebsuperobject.biblemap.put("b41_13_18", new hebsuperobject(41,13,18,"אך התפללו אשר לא תהיה מנוסתכם בחרף׃"));
hebsuperobject.biblemap.put("b41_13_19", new hebsuperobject(41,13,19,"כי הימים ההם יהיו עת צרה אשר לא נהיתה כמוה מראשית הבריאה אשר ברא אלהים עד עתה וכמוה לא תהיה עוד׃"));
hebsuperobject.biblemap.put("b41_13_20", new hebsuperobject(41,13,20,"ולולי קצר יהוה את הימים ההם לא יושע כל בשר אך למען הבחירים אשר בחר בם קצר את הימים׃"));
hebsuperobject.biblemap.put("b41_13_21", new hebsuperobject(41,13,21,"ואז אם יאמר איש אליכם הנה פה המשיח או הנהו שם אל תאמינו׃"));
hebsuperobject.biblemap.put("b41_13_22", new hebsuperobject(41,13,22,"כי יקומו משיחי שקר ונביאי שקר ונתנו אתות ומופתים להתעות אף את הבחירים אם יוכלו׃"));
hebsuperobject.biblemap.put("b41_13_23", new hebsuperobject(41,13,23,"ואתם ראו הנה מראש הגדתי לכם את כל׃"));
hebsuperobject.biblemap.put("b41_13_24", new hebsuperobject(41,13,24,"והיה בימים ההם אחרי הצרה ההיא תחשך השמש והירח לא יגיה אורו׃"));
hebsuperobject.biblemap.put("b41_13_25", new hebsuperobject(41,13,25,"והכוכבים יפלו מן השמים וכחות השמים יתמוטטו׃"));
hebsuperobject.biblemap.put("b41_13_26", new hebsuperobject(41,13,26,"ואז יראו את בן האדם בא בעננים בגבורה רבה ובכבוד׃"));
hebsuperobject.biblemap.put("b41_13_27", new hebsuperobject(41,13,27,"ואז ישלח את מלאכיו ויקבץ את בחיריו מארבע הרוחות מקצה הארץ עד קצה השמים׃"));
hebsuperobject.biblemap.put("b41_13_28", new hebsuperobject(41,13,28,"למדו נא את משל התאנה כשירטב ענפה ופרח עלה ידעתם כי קרוב הקיץ׃"));
