hebsuperobject.biblemap.put("b42_11_17", new hebsuperobject(42,11,17,"והוא ידע את מחשבותם ויאמר אליהם כל ממלכה הנחלקה על עצמה תחרב ובית יפל על בית׃"));
hebsuperobject.biblemap.put("b42_11_18", new hebsuperobject(42,11,18,"וגם השטן אם נחלק על עצמו איככה תכון ממלכתו כי אמרתם שבבעל זבוב מגרש אני את השדים׃"));
hebsuperobject.biblemap.put("b42_11_19", new hebsuperobject(42,11,19,"ואם אני מגרש את השדים בבעל זבוב בניכם במי הם מגרשים אתם על כן המה יהיו שפטיכם׃"));
hebsuperobject.biblemap.put("b42_11_20", new hebsuperobject(42,11,20,"ואם באצבע אלהים מגרש אני את השדים הנה הגיעה אליכם מלכות האלהים׃"));
hebsuperobject.biblemap.put("b42_11_21", new hebsuperobject(42,11,21,"בהיות הגבור שמר את חצרו והוא מזין והיה רכושו שלום׃"));
hebsuperobject.biblemap.put("b42_11_22", new hebsuperobject(42,11,22,"ואם יבוא עליו חזק ממנו ותקפו ישא ממנו את נשקו אשר בטח בו ואת מלקחו יחלק׃"));
hebsuperobject.biblemap.put("b42_11_23", new hebsuperobject(42,11,23,"כל אשר איננו אתי הוא לנגדי ואשר איננו מאסף אתי הוא מפזר׃"));
hebsuperobject.biblemap.put("b42_11_24", new hebsuperobject(42,11,24,"הרוח הטמאה אחרי צאתה מן האדם תשוטט במקמות ציה לבקש לה מנוחה ולא תמצא אז תאמר אשובה נא אל ביתי אשר יצאתי משם׃"));
hebsuperobject.biblemap.put("b42_11_25", new hebsuperobject(42,11,25,"ובבואה תמצא אתו מטאטא ומהדר׃"));
hebsuperobject.biblemap.put("b42_11_26", new hebsuperobject(42,11,26,"ואחר תלך ולקחה שבע רוחות אחרות רעות ממנה ובאו ושכנו שם והיתה אחרית האדם ההוא רעה מראשיתו׃"));
hebsuperobject.biblemap.put("b42_11_27", new hebsuperobject(42,11,27,"ויהי כדברו את הדברים האלה ואשה אחת מן העם נשאה את קולה ותאמר אליו אשרי הבטן אשר נשאתך והשדים אשר ינקת׃"));
hebsuperobject.biblemap.put("b42_11_28", new hebsuperobject(42,11,28,"והוא אמר ואף כי אשרי השמעים והשמרים את דבר האלהים׃"));
hebsuperobject.biblemap.put("b42_11_29", new hebsuperobject(42,11,29,"ויקבצו עם רב ויחל לדבר הדור הזה דור רע הוא אות הוא מבקש ואות לא ינתן לו בלתי אם אות יונה הנביא׃"));
hebsuperobject.biblemap.put("b42_11_30", new hebsuperobject(42,11,30,"כי כאשר היה יונה לאנשי נינוה לאות כן יהיה גם בן האדם לדור הזה׃"));
hebsuperobject.biblemap.put("b42_11_31", new hebsuperobject(42,11,31,"מלכת תימן תקום במשפט עם אנשי הדור הזה והרשיעה אותם כי באה מקצות הארץ לשמע את חכמת שלמה והנה יש פה גדול משלמה׃"));
hebsuperobject.biblemap.put("b42_11_32", new hebsuperobject(42,11,32,"אנשי נינוה יקומו במשפט עם הדור הזה והרשיעהו כי הם שבו בקריאת יונה והנה יש פה גדול מיונה׃"));
hebsuperobject.biblemap.put("b42_11_33", new hebsuperobject(42,11,33,"אין מדליק נר אשר ישימהו בסתר או תחת האיפה כי אם על המנורה למען יראו באי הבית את אורו׃"));
hebsuperobject.biblemap.put("b42_11_34", new hebsuperobject(42,11,34,"נר הגוף הוא העין לכן בהיות עינך תמימה גם כל גופך יאור ובהיותה רעה וחשך גם גופך׃"));
hebsuperobject.biblemap.put("b42_11_35", new hebsuperobject(42,11,35,"על כן השמר לך פן יחשך האור אשר בקרבך׃"));
hebsuperobject.biblemap.put("b42_11_36", new hebsuperobject(42,11,36,"והנה אם גופך כלו אור ואין בו כל דבר חשך אז יאור כלו והיה כהאיר לך הנר בברק נגהו׃"));
hebsuperobject.biblemap.put("b42_11_37", new hebsuperobject(42,11,37,"ויהי בדברו ויבקש ממנו פרוש אחד לאכל אתו לחם ויבא הביתה ויסב׃"));
hebsuperobject.biblemap.put("b42_11_38", new hebsuperobject(42,11,38,"ויתמה הפרוש בראתו אשר לא נטל ידיו בראשונה לפני הסעודה׃"));
hebsuperobject.biblemap.put("b42_11_39", new hebsuperobject(42,11,39,"ויאמר אליו האדון כעת אתם הפרושים מטהרים את הכום והקערה מחוץ והפנימי אשר בכם הוא מלא גזל ורשע׃"));
hebsuperobject.biblemap.put("b42_11_40", new hebsuperobject(42,11,40,"הכסילים הלא עשה החיצון גם עשה את הפנימי׃"));
hebsuperobject.biblemap.put("b42_11_41", new hebsuperobject(42,11,41,"אבל תנו לצדקה את אשר בם והנה הכל טהור לכם׃"));
hebsuperobject.biblemap.put("b42_11_42", new hebsuperobject(42,11,42,"אך אוי לכם הפרושים כי תעשרו את המנתא ואת הפיגם ואת כל הירק ותעברו את המשפט ואת אהבת אלהים והיה לכם לעשות את אלה ולא להניח גם את אלה׃"));
hebsuperobject.biblemap.put("b42_11_43", new hebsuperobject(42,11,43,"אוי לכם הפרושים כי תאהבו את מושב הראש בבתי הכנסיות ואת שאלות שלומכם בשוקים׃"));
hebsuperobject.biblemap.put("b42_11_44", new hebsuperobject(42,11,44,"אוי לכם הסופרים והפרושים החנפים כי אתם כקברים הנסתרים ובני האדם מתהלכים עליהם ולא ידעו׃"));
hebsuperobject.biblemap.put("b42_11_45", new hebsuperobject(42,11,45,"ויען אחד מבעלי התורה ויאמר אליו רבי בדבריך אלה תחרף גם אתנו׃"));
hebsuperobject.biblemap.put("b42_11_46", new hebsuperobject(42,11,46,"ויאמר אוי גם לכם בעלי התורה כי עמסים אתם על בני האדם משאות כבדים מסבל ואתם בעצמכם אינכם נגעים במשאות גם באחת מאצבעותיכם׃"));
hebsuperobject.biblemap.put("b42_11_47", new hebsuperobject(42,11,47,"אוי לכם כי בונים אתם את קברות הנביאים ואבותיכם הרגו אותם׃"));
hebsuperobject.biblemap.put("b42_11_48", new hebsuperobject(42,11,48,"וכן אתם מעידים ורוצים במעשי אבותיכם כי המה הרגו אותם ואתם בונים את קבריהם׃"));
hebsuperobject.biblemap.put("b42_11_49", new hebsuperobject(42,11,49,"בעבור זאת גם אמרה חכמת האלהים אשלח אליהם נביאים ושליחים ומהם יהרגו וירדפו׃"));
hebsuperobject.biblemap.put("b42_11_50", new hebsuperobject(42,11,50,"למען ידרש מן הדור הזה דם כל הנביאים אשר נשפך למן הוסד הארץ׃"));
hebsuperobject.biblemap.put("b42_11_51", new hebsuperobject(42,11,51,"מדם הבל עד דם זכריהו אשר נהרג בין המזבח ולבית הן אמר אני לכם דרוש ידרש מן הדור הזה׃"));
hebsuperobject.biblemap.put("b42_11_52", new hebsuperobject(42,11,52,"אוי לכם בעלי התורה כי הסירתם את מפתח הדעת אתם לא באתם ואת הבאים מנעתם׃"));
hebsuperobject.biblemap.put("b42_11_53", new hebsuperobject(42,11,53,"ויהי כדברו להם את אלה ויחלו הסופרים והפרושים לשטם אותו מאד ולהקשות לו בדברים רבים׃"));
hebsuperobject.biblemap.put("b42_11_54", new hebsuperobject(42,11,54,"ויארבו לו ויבקשו לצודד דבר מפיהו למען ימצאו עליו שטנה׃"));
hebsuperobject.biblemap.put("b42_12_1", new hebsuperobject(42,12,1,"ויהי עד כה ועד כה בהתאסף רבבות עם עד כי לחצו איש את רעהו ויחל לדבר אל תלמידיו בראשונה השמרו לנפשתיכם משאור הפרושים שהוא החנפה׃"));
hebsuperobject.biblemap.put("b42_12_2", new hebsuperobject(42,12,2,"ואין דבר מכסה אשר לא יגלה ואין נעלם אשר לא יודע׃"));
hebsuperobject.biblemap.put("b42_12_3", new hebsuperobject(42,12,3,"לכן כל אשר דברתם בחשך באור ישמע ואת אשר לחשתם לאזן בחדרים קרא יקרא על הגגות׃"));
hebsuperobject.biblemap.put("b42_12_4", new hebsuperobject(42,12,4,"ואני אמר לכם ידידי אל תיראו מן הממיתים את הגוף ואחרי זאת אין לאל ידם לעשות עוד דבר׃"));
hebsuperobject.biblemap.put("b42_12_5", new hebsuperobject(42,12,5,"אבל אורה אתכם את אשר תיראו יראו את אשר יש לו שלטן אחרי המיתו להשליך אל גיהנם הן אני אמר לכם אותו תיראון׃"));
hebsuperobject.biblemap.put("b42_12_6", new hebsuperobject(42,12,6,"הלא חמש צפרים תמכרנה בשני אסרים ואין אחת מהן נשכחת לפני האלהים׃"));
hebsuperobject.biblemap.put("b42_12_7", new hebsuperobject(42,12,7,"ואתם גם שערות ראשכם נמנות כלן לכן אל תיראו יקרתם מצפרים רבות׃"));
hebsuperobject.biblemap.put("b42_12_8", new hebsuperobject(42,12,8,"ואני אמר לכם כל אשר יודה בי לפני האדם גם בן האדם יודה בו לפני מלאכי אלהים׃"));
hebsuperobject.biblemap.put("b42_12_9", new hebsuperobject(42,12,9,"ואשר יכחש בי לפני האדם הוא יכחש לפני מלאכי אלהים׃"));
hebsuperobject.biblemap.put("b42_12_10", new hebsuperobject(42,12,10,"וכל אשר ידבר דבר חרפה על בן האדם יסלח לו והמגדף את רוח הקדש לא יסלח לו׃"));
hebsuperobject.biblemap.put("b42_12_11", new hebsuperobject(42,12,11,"וכאשר יביאו אתכם אל בתי הכנסיות ולפני הרשיות והשלטונים אל תדאגו איך או במה תצטדקו ומה תדברו׃"));
hebsuperobject.biblemap.put("b42_12_12", new hebsuperobject(42,12,12,"כי רוח הקדש הוא יורה אתכם בשעה ההיא את הנכון לדבר׃"));
hebsuperobject.biblemap.put("b42_12_13", new hebsuperobject(42,12,13,"ויאמר אליו אחד מן העם רבי אמר נא אל אחי ויחלק אתי את הירשה׃"));
hebsuperobject.biblemap.put("b42_12_14", new hebsuperobject(42,12,14,"ויאמר אליו בן אדם מי שמני עליכם לשפט ולמחלק׃"));
hebsuperobject.biblemap.put("b42_12_15", new hebsuperobject(42,12,15,"ויאמר אליהם ראו והשמרו לכם מבצע בצע כי חיי האדם אינם תלוים בהרבות נכסיו׃"));
hebsuperobject.biblemap.put("b42_12_16", new hebsuperobject(42,12,16,"וישא משלו ויאמר אליהם לאמר שדה איש עשיר אחד עשה תבואה הרבה׃"));
hebsuperobject.biblemap.put("b42_12_17", new hebsuperobject(42,12,17,"ויחשב בלבו לאמר מה אעשה כי אין לי מקום לכנוס בו את תבואתי׃"));
hebsuperobject.biblemap.put("b42_12_18", new hebsuperobject(42,12,18,"ויאמר את זאת אעשה הרס את אסמי ובנה גדולים מהם ואכנסה שמה את כל יבולי וטובי׃"));
hebsuperobject.biblemap.put("b42_12_19", new hebsuperobject(42,12,19,"ואמר לנפשי נפשי יש לך עתודות הרבה לשנים רבות הנפשי אכלי שתי ושישי׃"));
hebsuperobject.biblemap.put("b42_12_20", new hebsuperobject(42,12,20,"והאלהים אמר לו אתה הכסיל בעצם הלילה הזה ידרשו ממך את נפשך ואשר הכינות לך למי יהיה׃"));
hebsuperobject.biblemap.put("b42_12_21", new hebsuperobject(42,12,21,"זה חלק האצר לו אצרות ולא יעשיר באלהים׃"));
hebsuperobject.biblemap.put("b42_12_22", new hebsuperobject(42,12,22,"ויאמר אל תלמידיו לכן אני אמר לכם אל תדאגו לנפשכם מה תאכלו ולגופכם מה תלבשו׃"));
hebsuperobject.biblemap.put("b42_12_23", new hebsuperobject(42,12,23,"הנפש יקרה היא מן המזון והגוף יקר מן המלבוש׃"));
hebsuperobject.biblemap.put("b42_12_24", new hebsuperobject(42,12,24,"התבוננו אל הערבים אשר אינם זרעים ואינם קצרים וגם אין להם מגורה ואוצר והאלהים מכלכל אותם ומה מעלים אתם מן העוף׃"));
hebsuperobject.biblemap.put("b42_12_25", new hebsuperobject(42,12,25,"ומי זה מכם אשר בדאגתו יוכל להוסיף אמה אחת על קומתו׃"));
hebsuperobject.biblemap.put("b42_12_26", new hebsuperobject(42,12,26,"ועתה הן מעט מזער אין ביכלתכם וליותר מה תדאגו׃"));
hebsuperobject.biblemap.put("b42_12_27", new hebsuperobject(42,12,27,"התבוננו אל השושנים הצמחות ואינן טות ואינן ארגות ואני אמר לכם כי גם שלמה בכל הדרו לא היה לבוש כאחת מהנה׃"));
hebsuperobject.biblemap.put("b42_12_28", new hebsuperobject(42,12,28,"ואם ככה ילביש אלהים את חציר השדה אשר היום ישנו ומחר ישלך לתוך התנור אף כי אתכם קטני האמונה׃"));
hebsuperobject.biblemap.put("b42_12_29", new hebsuperobject(42,12,29,"גם אתם אל תדרשו מה תאכלו ומה תשתו ואל תהלכו בגדלות׃"));
hebsuperobject.biblemap.put("b42_12_30", new hebsuperobject(42,12,30,"כי את כל אלה מבקשים גויי הארץ ואביכם הוא יודע כי צריכים אתם לאלה׃"));
hebsuperobject.biblemap.put("b42_12_31", new hebsuperobject(42,12,31,"אך בקשו את מלכות האלהים ונוסף לכם כל אלה׃"));
hebsuperobject.biblemap.put("b42_12_32", new hebsuperobject(42,12,32,"אל תירא העדר הקטן כי רצה אביכם לתת לכם את המלכות׃"));
hebsuperobject.biblemap.put("b42_12_33", new hebsuperobject(42,12,33,"מכרו את רכושכם ותנו צדקה עשו לכם כיסים אשר לא יבלו ואוצר בשמים אשר לא יגרע לעולם אשר גנב לא יקרב אליו וסס לא יאכלהו׃"));
hebsuperobject.biblemap.put("b42_12_34", new hebsuperobject(42,12,34,"כי במקום אשר אוצרכם בו שם יהיה גם לבבכם׃"));
hebsuperobject.biblemap.put("b42_12_35", new hebsuperobject(42,12,35,"מתניכם יהיו חגורים והנרות דלקים׃"));
hebsuperobject.biblemap.put("b42_12_36", new hebsuperobject(42,12,36,"ואתם היו דמים לאנשים המחכים לאדניהם מתי ישוב מן החתנה וכאשר יבוא ודפק יפתחו לו כרגע׃"));
hebsuperobject.biblemap.put("b42_12_37", new hebsuperobject(42,12,37,"אשרי העבדים ההם אשר בבוא האדון ימצאם שקדים אמן אמר אני לכם כי יתאזר ויושיבם וילך לשרת אותם׃"));
hebsuperobject.biblemap.put("b42_12_38", new hebsuperobject(42,12,38,"ואם יבוא באשמרה השנית או באשמרה השלישית וכן ימצא אשרי העבדים ההם׃"));
hebsuperobject.biblemap.put("b42_12_39", new hebsuperobject(42,12,39,"וזאת דעו אשר אם ידע ידע בעל הבית באי זו שעה יבוא הגנב כי עתה שקד שקוד ולא יתן לחתר את ביתו׃"));
hebsuperobject.biblemap.put("b42_12_40", new hebsuperobject(42,12,40,"לכן גם אתם היו נכונים כי בשעה אשר לא פללתם יבוא בן האדם׃"));
hebsuperobject.biblemap.put("b42_12_41", new hebsuperobject(42,12,41,"ויאמר פטרוס אדנינו הלנו אתה אמר את המשל הזה אם גם לכל אדם׃"));
hebsuperobject.biblemap.put("b42_12_42", new hebsuperobject(42,12,42,"ויאמר האדון מי הוא אפוא הסכן הנאמן והנבון אשר יפקידהו האדון על עבדתו לתת את ארחתם בעתו׃"));
hebsuperobject.biblemap.put("b42_12_43", new hebsuperobject(42,12,43,"אשרי העבד ההוא אשר בבא אדניו ימצאהו עשה כן׃"));
hebsuperobject.biblemap.put("b42_12_44", new hebsuperobject(42,12,44,"אמת אמר אני לכם כי על כל אשר יש לו יפקידהו׃"));
hebsuperobject.biblemap.put("b42_12_45", new hebsuperobject(42,12,45,"והעבד ההוא אם יאמר בלבו בשש אדני לבוא והחל להכות את העבדים ואת השפחות ולאכל ולשתות ולשכר׃"));
hebsuperobject.biblemap.put("b42_12_46", new hebsuperobject(42,12,46,"בוא יבוא אדני העבד ההוא ביום לא יצפה ובשעה לא ידע וישסף אותו וישים את חלקו עם הסוררים׃"));
