hebsuperobject.biblemap.put("b43_3_17", new hebsuperobject(43,3,17,"כי האלהים לא שלח את בנו אל העולם לדין את העולם כי אם למען יושע בו העולם׃"));
hebsuperobject.biblemap.put("b43_3_18", new hebsuperobject(43,3,18,"המאמין בו לא ידון ואשר לא יאמין בו כבר נדון כי לא האמין בשם בן האלהים היחיד׃"));
hebsuperobject.biblemap.put("b43_3_19", new hebsuperobject(43,3,19,"וזה הוא הדין כי האור בא אל העולם ובני האדם אהבו החשך מן האור כי רעים מעשיהם׃"));
hebsuperobject.biblemap.put("b43_3_20", new hebsuperobject(43,3,20,"כי כל פעל עולה ישנא את האור ולא יבא לאור פן יוכחו מעשיו׃"));
hebsuperobject.biblemap.put("b43_3_21", new hebsuperobject(43,3,21,"אבל עשה האמת יבא לאור למען יגלו מעשיו כי נעשו באלהים׃"));
hebsuperobject.biblemap.put("b43_3_22", new hebsuperobject(43,3,22,"ויהי אחרי הדברים האלה ויבא ישוע ותלמידיו אל ארץ יהודה ויגר שם עמהם ויטבל׃"));
hebsuperobject.biblemap.put("b43_3_23", new hebsuperobject(43,3,23,"וגם יוחנן היה טבל בעינון קרוב לשלם כי שמה מים לרב ויבאו ויטבלו׃"));
hebsuperobject.biblemap.put("b43_3_24", new hebsuperobject(43,3,24,"כי עוד לא נתן יוחנן אל בית הסהר׃"));
hebsuperobject.biblemap.put("b43_3_25", new hebsuperobject(43,3,25,"ויהי ריב בין תלמידי יוחנן ובין היהודים על דבר הטהרה׃"));
hebsuperobject.biblemap.put("b43_3_26", new hebsuperobject(43,3,26,"ויבאו אל יוחנן ויאמרו אליו רבי האיש אשר היה עמך בעבר הירדן ואשר העידת לו הנו טבל וכלם באים אליו׃"));
hebsuperobject.biblemap.put("b43_3_27", new hebsuperobject(43,3,27,"ויען יוחנן לא יוכל איש לקחת דבר בלתי אם נתן לו מן השמים׃"));
hebsuperobject.biblemap.put("b43_3_28", new hebsuperobject(43,3,28,"ואתם עדי כי אמרתי אנכי אינני המשיח רק שלוח אני לפניו׃"));
hebsuperobject.biblemap.put("b43_3_29", new hebsuperobject(43,3,29,"אשר לו הכלה הוא החתן ורע החתן העמד ושמע אתו שמוח ישמח לקול החתן הנה שמחתי זאת עתה שלמה׃"));
hebsuperobject.biblemap.put("b43_3_30", new hebsuperobject(43,3,30,"הוא יגדל הלוך וגדל ואני אחסר הלוך וחסור׃"));
hebsuperobject.biblemap.put("b43_3_31", new hebsuperobject(43,3,31,"הבא ממעל נעלה על כל ואשר מארץ מארץ הוא ומארץ ידבר הבא משמים נעלה על כל׃"));
hebsuperobject.biblemap.put("b43_3_32", new hebsuperobject(43,3,32,"ואת אשר ראה ושמע את זאת יעיד ואין מקבל עדותו׃"));
hebsuperobject.biblemap.put("b43_3_33", new hebsuperobject(43,3,33,"ואשר קבל עדותו חתום חתם כי האלהים אמת׃"));
hebsuperobject.biblemap.put("b43_3_34", new hebsuperobject(43,3,34,"כי את אשר שלחו אלהים דברי אלהים ידבר כי לא במדה נתן אלהים את הרוח׃"));
hebsuperobject.biblemap.put("b43_3_35", new hebsuperobject(43,3,35,"האב אהב את בנו ואת כל נתן בידו׃"));
hebsuperobject.biblemap.put("b43_3_36", new hebsuperobject(43,3,36,"כל המאמין בבן יש לו חיי עולמים ואשר לא יאמין בבן לא יראה חיים כי אם חרון אלהים ישכן עליו׃"));
hebsuperobject.biblemap.put("b43_4_1", new hebsuperobject(43,4,1,"ויהי כאשר נודע לאדון כי שמעו הפרושים אשר ישוע העמיד והטביל תלמידים הרבה מיוחנן׃"));
hebsuperobject.biblemap.put("b43_4_2", new hebsuperobject(43,4,2,"ואולם ישוע הוא לא הטביל כי אם תלמידיו׃"));
hebsuperobject.biblemap.put("b43_4_3", new hebsuperobject(43,4,3,"ויעזב את ארץ יהודה וילך שנית הגלילה׃"));
hebsuperobject.biblemap.put("b43_4_4", new hebsuperobject(43,4,4,"ויהי לו לעבר בארץ שמרון׃"));
hebsuperobject.biblemap.put("b43_4_5", new hebsuperobject(43,4,5,"ויבא לעיר מערי שמרון ושמה סוכר היא ממול חלקת השדה אשר נתנה יעקב לבנו ליוסף׃"));
hebsuperobject.biblemap.put("b43_4_6", new hebsuperobject(43,4,6,"ושם באר יעקב וישוע היה עיף מן הדרך וישב לו על הבאר והעת כשעה הששית׃"));
hebsuperobject.biblemap.put("b43_4_7", new hebsuperobject(43,4,7,"ותבא אשה משמרון לשאב מים ויאמר אליה ישוע תני נא לי לשתות׃"));
hebsuperobject.biblemap.put("b43_4_8", new hebsuperobject(43,4,8,"כי תלמידיו הלכו העירה לקנות אכל׃"));
hebsuperobject.biblemap.put("b43_4_9", new hebsuperobject(43,4,9,"ותאמר אליו האשה השמרונית הן יהודי אתה ואיככה תשאל ממני לשתות ואנכי אשה שמרונית כי לא יתערבו היהודים עם השמרונים׃"));
hebsuperobject.biblemap.put("b43_4_10", new hebsuperobject(43,4,10,"ויען ישוע ויאמר אליה לו ידעת את מתת אלהים ומי הוא האמר אליך תני נא לי לשתות כי עתה שאלת ממנו ונתן לך מים חיים׃"));
hebsuperobject.biblemap.put("b43_4_11", new hebsuperobject(43,4,11,"ותאמר אליו האשה אדני הן אין לך כלי לשאב בו והבאר עמקה ומאין לך אפוא מים חיים׃"));
hebsuperobject.biblemap.put("b43_4_12", new hebsuperobject(43,4,12,"האתה גדול מיעקב אבינו אשר נתן לנו את הבאר הזאת וישת ממנה הוא ובניו ובעירו׃"));
hebsuperobject.biblemap.put("b43_4_13", new hebsuperobject(43,4,13,"ויען ישוע ויאמר אליה כל השתה מן המים האלה ישוב ויצמא׃"));
hebsuperobject.biblemap.put("b43_4_14", new hebsuperobject(43,4,14,"ואשר ישתה מן המים אשר אנכי נתן לו לא יצמא לעולם כי המים אשר אתן לו יהיו בקרבו למקור מים נבעים לחיי העולמים׃"));
hebsuperobject.biblemap.put("b43_4_15", new hebsuperobject(43,4,15,"ותאמר אליו האשה אדני תנה לי המים ההם למען לא אצמא עוד ולא אוסיף לבוא הנה לשאב׃"));
hebsuperobject.biblemap.put("b43_4_16", new hebsuperobject(43,4,16,"ויאמר אליה ישוע לכי וקראי לאישך ושובי הלום׃"));
hebsuperobject.biblemap.put("b43_4_17", new hebsuperobject(43,4,17,"ותען האשה ותאמר אין לי איש ויאמר אליה ישוע כן דברת אין לי איש׃"));
hebsuperobject.biblemap.put("b43_4_18", new hebsuperobject(43,4,18,"כי בעלים חמשה היו לך ואשר לך עתה איננו בעלך לכן אמת הדבר אשר דברת׃"));
hebsuperobject.biblemap.put("b43_4_19", new hebsuperobject(43,4,19,"ותאמר אליו האשה אדני ראה אנכי כי נביא אתה׃"));
hebsuperobject.biblemap.put("b43_4_20", new hebsuperobject(43,4,20,"אבותינו השתחוו בהר הזה ואתם אמרים כי ירושלים היא המקום הנבחר להשתחות שמה׃"));
hebsuperobject.biblemap.put("b43_4_21", new hebsuperobject(43,4,21,"ויאמר אליה ישוע אשה האמיני לי כי תבוא שעה אשר לא בהר הזה אף לא בירושלים תשתחוו לאב׃"));
hebsuperobject.biblemap.put("b43_4_22", new hebsuperobject(43,4,22,"אתם משתחוים אל אשר לא ידעתם ואנחנו משתחוים אל אשר ידענו כי הישועה מן היהודים היא׃"));
hebsuperobject.biblemap.put("b43_4_23", new hebsuperobject(43,4,23,"אולם תבוא שעה ועתה היא אשר עבדי אל האמתים ישתחוו לאב ברוח ובאמת כי במשתחוים כאלה חפץ האב׃"));
hebsuperobject.biblemap.put("b43_4_24", new hebsuperobject(43,4,24,"האלהים רוח הוא והמשתחוים לו צריכים להשתחות ברוח ובאמת׃"));
hebsuperobject.biblemap.put("b43_4_25", new hebsuperobject(43,4,25,"ותאמר אליו האשה ידעתי כי יבא המשיח הנקרא כריסטוס הוא בבאו יגיד לנו את כל׃"));
hebsuperobject.biblemap.put("b43_4_26", new hebsuperobject(43,4,26,"ויאמר אליה ישוע אני הוא המדבר אליך׃"));
hebsuperobject.biblemap.put("b43_4_27", new hebsuperobject(43,4,27,"ויהי הוא מדבר ככה ותלמידיו באו ויתמהו על דברו עם אשה אך לא אמר לו איש מה זה תשאל או מה תדבר עמה׃"));
hebsuperobject.biblemap.put("b43_4_28", new hebsuperobject(43,4,28,"ותעזב האשה את כדה ותלך העירה ותאמר אל האנשים׃"));
hebsuperobject.biblemap.put("b43_4_29", new hebsuperobject(43,4,29,"באו וראו איש אשר הגיד לי כל אשר עשיתי הכי זה הוא המשיח׃"));
hebsuperobject.biblemap.put("b43_4_30", new hebsuperobject(43,4,30,"ויצאו מן העיר ויבאו אליו׃"));
hebsuperobject.biblemap.put("b43_4_31", new hebsuperobject(43,4,31,"טרם יבאו ותלמידיו בקשו ממנו לאמר אכל נא אדני׃"));
hebsuperobject.biblemap.put("b43_4_32", new hebsuperobject(43,4,32,"ויאמר אליהם יש לי אכל לאכל אשר אתם לא ידעתם׃"));
hebsuperobject.biblemap.put("b43_4_33", new hebsuperobject(43,4,33,"ויאמרו התלמידים איש אל רעהו הכי הביא לו איש לאכל׃"));
hebsuperobject.biblemap.put("b43_4_34", new hebsuperobject(43,4,34,"ויאמר אליהם ישוע מאכלי הוא לעשות רצון שלחי ולהשלים מעשהו׃"));
hebsuperobject.biblemap.put("b43_4_35", new hebsuperobject(43,4,35,"הלא אתם תאמרו כי עוד ארבעה חדשים והקציר בא הנה אני אמר לכם שאו עיניכם וראו בשדות כי כבר הלבינו לקציר׃"));
hebsuperobject.biblemap.put("b43_4_36", new hebsuperobject(43,4,36,"והקוצר יקח שכרו ויאסף תבואה לחיי עולמים למען ישמחו יחדו גם הזרע גם הקוצר׃"));
hebsuperobject.biblemap.put("b43_4_37", new hebsuperobject(43,4,37,"כי בזאת אמת המשל כי זה זרע ואחר יקצר׃"));
hebsuperobject.biblemap.put("b43_4_38", new hebsuperobject(43,4,38,"אנכי שלחתי אתכם לקצר את אשר לא עמלתם בו ואחרים עמלו ואתם נכנסתם בעמלם׃"));
hebsuperobject.biblemap.put("b43_4_39", new hebsuperobject(43,4,39,"ויאמינו בו שמרנים רבים מן העיר ההיא על דבר האשה אשר העידה לאמר הוא הגיד לי את כל אשר עשיתי׃"));
hebsuperobject.biblemap.put("b43_4_40", new hebsuperobject(43,4,40,"ויהי כאשר באו אליו השמרנים וישאלו ממנו לשבת אתם וישב שם יומים׃"));
hebsuperobject.biblemap.put("b43_4_41", new hebsuperobject(43,4,41,"ועוד רבים מהמה האמינו בו בעבור דברו׃"));
hebsuperobject.biblemap.put("b43_4_42", new hebsuperobject(43,4,42,"ויאמרו אל האשה מעתה לא בעבור מאמרך נאמין כי באזנינו שמענו ונדע כי אמנם זה הוא המשיח מושיע העולם׃"));
hebsuperobject.biblemap.put("b43_4_43", new hebsuperobject(43,4,43,"ויהי מקץ שני הימים ויצא משם ללכת הגלילה׃"));
hebsuperobject.biblemap.put("b43_4_44", new hebsuperobject(43,4,44,"כי הוא ישוע העיד כי נביא בארץ מולדתו איננו נכבד׃"));
hebsuperobject.biblemap.put("b43_4_45", new hebsuperobject(43,4,45,"ויהי הוא בא ארץ הגליל ויאספהו אנשי הגליל כי ראו את כל אשר עשה בירושלים בימי החג כי גם הם עלו לחג את החג׃"));
hebsuperobject.biblemap.put("b43_4_46", new hebsuperobject(43,4,46,"ויבא ישוע עוד הפעם אל קנה אשר בגליל מקום שומו המים ליין ואיש היה מעבדי המלך ובנו חלה בכפר נחום׃"));
hebsuperobject.biblemap.put("b43_4_47", new hebsuperobject(43,4,47,"ויהי כשמעו כי בא ישוע מיהודה לארץ הגליל וילך אליו וישאל מאתו לרדת ולרפא את בנו כי קרב למות׃"));
hebsuperobject.biblemap.put("b43_4_48", new hebsuperobject(43,4,48,"ויאמר אליו ישוע אם לא תראו אתות ומופתים לא תאמינו׃"));
hebsuperobject.biblemap.put("b43_4_49", new hebsuperobject(43,4,49,"ויאמר אליו האיש אשר מעבדי המלך אדני רדה נא בטרם ימות בני׃"));
hebsuperobject.biblemap.put("b43_4_50", new hebsuperobject(43,4,50,"ויאמר אליו ישוע לך לך בנך חי והאיש האמין לדבר אשר דבר אליו ישוע וילך׃"));
hebsuperobject.biblemap.put("b43_4_51", new hebsuperobject(43,4,51,"ויהי ברדתו ויפגעו בו עבדיו ויבשרו אתו כי חי בנו׃"));
hebsuperobject.biblemap.put("b43_4_52", new hebsuperobject(43,4,52,"וידרש מאתם את השעה אשר בה רוח לו ויאמרו אליו תמול בשעה השביעית עזבתו הקדחת׃"));
hebsuperobject.biblemap.put("b43_4_53", new hebsuperobject(43,4,53,"וידע אביהו כי היתה השעה אשר דבר לו ישוע בנך חי ויאמן הוא וכל ביתו׃"));
hebsuperobject.biblemap.put("b43_4_54", new hebsuperobject(43,4,54,"זה הוא האות השני אשר עשה ישוע בבאו מיהודה לארץ הגליל׃"));
hebsuperobject.biblemap.put("b43_5_1", new hebsuperobject(43,5,1,"אחר הדברים האלה היה חג ליהודים ויעל ישוע ירושלים׃"));
hebsuperobject.biblemap.put("b43_5_2", new hebsuperobject(43,5,2,"ובירושלים ברכה קרובה לשער הצאן ושמה בלשון עברית בית חסדא ולה חמשה אלמים׃"));
hebsuperobject.biblemap.put("b43_5_3", new hebsuperobject(43,5,3,"שמה שכבו חולים ועורים ופסחים ויבשי כח לרב והמה מיחלים לתנועת המים׃"));
hebsuperobject.biblemap.put("b43_5_4", new hebsuperobject(43,5,4,"כי מלאך ירד במועדו אל הברכה וירעש את מימיה והיה הירד ראשון אל תוכה אחרי התגעשו המים הוא נרפא מכל מחלה אשר דבקה בו׃"));
hebsuperobject.biblemap.put("b43_5_5", new hebsuperobject(43,5,5,"ואיש היה שם אשר חלה חליו זה שלשים ושמנה שנה׃"));
hebsuperobject.biblemap.put("b43_5_6", new hebsuperobject(43,5,6,"וירא אתו ישוע שכב וידע כי ארכו לו ימי חליו ויאמר אליו התחפץ להרפא׃"));
hebsuperobject.biblemap.put("b43_5_7", new hebsuperobject(43,5,7,"ויען החולה אדני אין איש אתי אשר ישליכני בהרעש המים אל הברכה ובטרם אבא וירד אחר לפני׃"));
hebsuperobject.biblemap.put("b43_5_8", new hebsuperobject(43,5,8,"ויאמר אליו ישוע קום שא את משכבך והתהלך׃"));
hebsuperobject.biblemap.put("b43_5_9", new hebsuperobject(43,5,9,"וכרגע שב האיש לאיתנו וישא את משכבו ויתהלך והיום ההוא יום שבת היה׃"));
hebsuperobject.biblemap.put("b43_5_10", new hebsuperobject(43,5,10,"ויאמרו היהודים אל האיש הנרפא שבת היום אסור לך לשאת את משכבך׃"));
hebsuperobject.biblemap.put("b43_5_11", new hebsuperobject(43,5,11,"ויען אתם לאמר האיש אשר החלימני הוא אמר אלי שא את משכבך והתהלך׃"));
hebsuperobject.biblemap.put("b43_5_12", new hebsuperobject(43,5,12,"וישאלהו מי זה האיש אשר אמר לך שא את משכבך והתהלך׃"));
hebsuperobject.biblemap.put("b43_5_13", new hebsuperobject(43,5,13,"והנרפא לא ידע מי הוא כי סר ישוע וילך בהיות המון רב במקום ההוא׃"));
hebsuperobject.biblemap.put("b43_5_14", new hebsuperobject(43,5,14,"ויהי אחרי כן וימצאהו ישוע בבית המקדש ויאמר אליו הנה נרפא לך אל תוסיף לחטא פן תאנה אליך רעה גדולה מזאת׃"));
hebsuperobject.biblemap.put("b43_5_15", new hebsuperobject(43,5,15,"וילך האיש ויגד ליהודים כי ישוע הוא אשר רפאו׃"));
hebsuperobject.biblemap.put("b43_5_16", new hebsuperobject(43,5,16,"ועל כן רדפו היהודים את ישוע ויבקשו המיתו על כי עשה כזאת בשבת׃"));
hebsuperobject.biblemap.put("b43_5_17", new hebsuperobject(43,5,17,"ויען אתם ישוע אבי פעל עד עתה וגם אנכי פעל׃"));
hebsuperobject.biblemap.put("b43_5_18", new hebsuperobject(43,5,18,"אז יוסיפו היהודים לבקש את נפשו כי מלבד אשר חלל את השבת עוד אמר כי האלהים הוא אביו וידמה לאלהים׃"));
hebsuperobject.biblemap.put("b43_5_19", new hebsuperobject(43,5,19,"ויען ישוע ויאמר אליהם אמן אמן אני אמר לכם לא יוכל הבן לעשות דבר מנפשו בלתי את אשר יראה את אביו עשה כי את אשר עשה הוא גם הבן יעשה כמהו׃"));
hebsuperobject.biblemap.put("b43_5_20", new hebsuperobject(43,5,20,"כי האב אהב את הבן ומראה אתו כל אשר יעשה ועוד מעשים גדולים מאלה יראהו למען תתמהו׃"));
hebsuperobject.biblemap.put("b43_5_21", new hebsuperobject(43,5,21,"כי כאשר האב יעיר ויחיה את המתים כן גם הבן יחיה את אשר יחפץ׃"));
hebsuperobject.biblemap.put("b43_5_22", new hebsuperobject(43,5,22,"כי האב לא ידין איש כי אם כל המשפט נתן לבן למען יכבדו כלם את הבן כאשר יכבדו את האב׃"));
hebsuperobject.biblemap.put("b43_5_23", new hebsuperobject(43,5,23,"מי אשר לא יכבד את הבן גם את האב אשר שלחו איננו מכבד׃"));
hebsuperobject.biblemap.put("b43_5_24", new hebsuperobject(43,5,24,"אמן אמן אני אמר לכם השמע דברי ומאמין לשלחי יש לו חיי עולמים ולא יבא במשפט כי עבר ממות לחיים׃"));
hebsuperobject.biblemap.put("b43_5_25", new hebsuperobject(43,5,25,"אמן אמן אני אמר לכם כי תבוא שעה ועתה היא אשר ישמעו המתים את קול בן האלהים והשמעים חיה יחיו׃"));
hebsuperobject.biblemap.put("b43_5_26", new hebsuperobject(43,5,26,"כי כאשר לאב יש חיים בעצמו כן נתן גם לבן להיות לו חיים בעצמו׃"));
hebsuperobject.biblemap.put("b43_5_27", new hebsuperobject(43,5,27,"ואף שלטן נתן לו לעשות משפט כי בן אדם הוא׃"));
hebsuperobject.biblemap.put("b43_5_28", new hebsuperobject(43,5,28,"אל תתמהו על זאת כי הנה שעה באה אשר כל שכני קבר את קולו ישמעון׃"));
hebsuperobject.biblemap.put("b43_5_29", new hebsuperobject(43,5,29,"ויצאו עשי הטוב לתקומת החיים ועשי הרע לתקומת המשפט׃"));
hebsuperobject.biblemap.put("b43_5_30", new hebsuperobject(43,5,30,"לא אוכל לעשות דבר מנפשי כאשר אשמע כן אשפט ומשפטי משפט צדק כי לא אבקש רצוני כי אם רצון האב אשר שלחני׃"));
hebsuperobject.biblemap.put("b43_5_31", new hebsuperobject(43,5,31,"אם אנכי מעיד עלי עדותי איננה נאמנה׃"));
hebsuperobject.biblemap.put("b43_5_32", new hebsuperobject(43,5,32,"יש אחר המעיד עלי וידעתי כי עדותו אשר הוא מעיד עלי נאמנה היא׃"));
hebsuperobject.biblemap.put("b43_5_33", new hebsuperobject(43,5,33,"אתם שלחתם אל יוחנן והוא העיד על האמת׃"));
hebsuperobject.biblemap.put("b43_5_34", new hebsuperobject(43,5,34,"ואני אינני לקח עדות מאדם אך אמרתי זאת למען תושעון׃"));
hebsuperobject.biblemap.put("b43_5_35", new hebsuperobject(43,5,35,"הוא היה הנר הדלק והמאיר ואתם רציתם לשוש כשעה לאורו׃"));
hebsuperobject.biblemap.put("b43_5_36", new hebsuperobject(43,5,36,"ואני יש לי עדות גדולה מעדות יוחנן כי המעשים אשר נתן לי אבי להשלימם המעשים האלה אשר אני עשה מעידים עלי כי האב שלחני׃"));
hebsuperobject.biblemap.put("b43_5_37", new hebsuperobject(43,5,37,"והאב אשר שלחני הוא מעיד עלי ואתם את קולו לא שמעתם מעולם ותמונתו לא ראיתם׃"));
hebsuperobject.biblemap.put("b43_5_38", new hebsuperobject(43,5,38,"ודברו איננו שכן בקרבכם כי אינכם מאמינים לשלוחו׃"));
hebsuperobject.biblemap.put("b43_5_39", new hebsuperobject(43,5,39,"דרשו בכתובים אשר תחשבו שיש לכם חיי עולמים בהם והמה המעידים עלי׃"));
hebsuperobject.biblemap.put("b43_5_40", new hebsuperobject(43,5,40,"ואתם אינכם אבים לבוא אלי להיות לכם חיים׃"));
hebsuperobject.biblemap.put("b43_5_41", new hebsuperobject(43,5,41,"לא אקח כבוד מבני אדם׃"));
hebsuperobject.biblemap.put("b43_5_42", new hebsuperobject(43,5,42,"אכן ידעתי אתכם כי אין אהבת אלהים בקרבכם׃"));
hebsuperobject.biblemap.put("b43_5_43", new hebsuperobject(43,5,43,"אני הנה באתי בשם אבי ולא קבלתם אתו ואם יבא אחר בשם עצמו אתו תקבלו׃"));
hebsuperobject.biblemap.put("b43_5_44", new hebsuperobject(43,5,44,"איך תוכלו להאמין אתם הלקחים כבוד איש מרעהו ואת הכבוד אשר מאת האלהים היחיד לא תבקשו׃"));
hebsuperobject.biblemap.put("b43_5_45", new hebsuperobject(43,5,45,"אל תחשבו כי אנכי אטען עליכם לפני אבי משה הוא הטען עליכם אשר לו תיחלו׃"));
hebsuperobject.biblemap.put("b43_5_46", new hebsuperobject(43,5,46,"כי לו האמנתם למשה גם לי תאמינו כי הוא כתב עלי׃"));
hebsuperobject.biblemap.put("b43_5_47", new hebsuperobject(43,5,47,"ואם לכתביו אינכם מאמינים איכה לדברי תאמינו׃"));
hebsuperobject.biblemap.put("b43_6_1", new hebsuperobject(43,6,1,"ויהי אחרי כן ויצא ישוע אל עבר ים הגליל אשר לטיבריה׃"));
hebsuperobject.biblemap.put("b43_6_2", new hebsuperobject(43,6,2,"וילכו אחריו המון עם רב כי ראו אותתיו אשר עשה עם החולים׃"));
hebsuperobject.biblemap.put("b43_6_3", new hebsuperobject(43,6,3,"ויעל ישוע על ההר וישב שם הוא ותלמידיו׃"));
hebsuperobject.biblemap.put("b43_6_4", new hebsuperobject(43,6,4,"וימי הפסח חג היהודים קרבו לבוא׃"));
hebsuperobject.biblemap.put("b43_6_5", new hebsuperobject(43,6,5,"וישא ישוע את עיניו וירא עם רב בא אליו ויאמר אל פילפוס מאין נקנה להם לחם לאכל׃"));
hebsuperobject.biblemap.put("b43_6_6", new hebsuperobject(43,6,6,"ואך למען נסות אתו דבר זאת כי הוא ידע את אשר יעשה׃"));
hebsuperobject.biblemap.put("b43_6_7", new hebsuperobject(43,6,7,"ויען אתו פילפוס לחם מאתים דינר לא ימצא להם לקחת לו איש איש מעט׃"));
hebsuperobject.biblemap.put("b43_6_8", new hebsuperobject(43,6,8,"ויאמר אליו אחד מתלמידיו אנדרי אחי שמעון פטרוס׃"));
hebsuperobject.biblemap.put("b43_6_9", new hebsuperobject(43,6,9,"יש פה נער אשר לו חמש ככרות לחם שערים ושני דגים אך אלה מה המה לעם רב כזה׃"));
hebsuperobject.biblemap.put("b43_6_10", new hebsuperobject(43,6,10,"ויאמר ישוע צוו את העם לשבת ארצה וירק דשא לרב היה במקום ההוא וישבו לארץ כחמשת אלפי איש במספר׃"));
hebsuperobject.biblemap.put("b43_6_11", new hebsuperobject(43,6,11,"ויקח ישוע את ככרות הלחם ויברך ויתן לתלמידיו והתלמידים אל המסבים וככה גם מן הדגים כאות נפשם׃"));
hebsuperobject.biblemap.put("b43_6_12", new hebsuperobject(43,6,12,"ויהי כאשר שבעו ויאמר אל תלמידיו אספו את פתותי לחם הנותרים למען לא יאבד מאומה׃"));
hebsuperobject.biblemap.put("b43_6_13", new hebsuperobject(43,6,13,"ויאספו וימלאו שנים עשר סלים בפתותי חמש ככרות לחם השערים הנותרים לאכליהם׃"));
hebsuperobject.biblemap.put("b43_6_14", new hebsuperobject(43,6,14,"ויהי כראות האנשים את האות הזה אשר עשה ישוע ויאמרו הנה זה הוא באמת הנביא הבא לעולם׃"));
hebsuperobject.biblemap.put("b43_6_15", new hebsuperobject(43,6,15,"וידע ישוע כי יבאו ויתפשהו להמליך אותו וימלט עוד הפעם אל ההר הוא לבדו׃"));
hebsuperobject.biblemap.put("b43_6_16", new hebsuperobject(43,6,16,"ויהי בערב וירדו תלמידיו אל הים ויבאו באניה ויעברו אל עבר הים אל כפר נחום׃"));
hebsuperobject.biblemap.put("b43_6_17", new hebsuperobject(43,6,17,"ויכס אתם החשך וישוע לא בא אליהם׃"));
hebsuperobject.biblemap.put("b43_6_18", new hebsuperobject(43,6,18,"ויסער הים כי רוח גדולה נושבת׃"));
hebsuperobject.biblemap.put("b43_6_19", new hebsuperobject(43,6,19,"והם חתרו במשוטיהם כדרך עשרים וחמש או שלשים ריס ויראו את ישוע מתהלך על הים הלוך וקרב אל האניה וייראו׃"));
hebsuperobject.biblemap.put("b43_6_20", new hebsuperobject(43,6,20,"ויאמר אליהם אני הוא אל תיראו׃"));
hebsuperobject.biblemap.put("b43_6_21", new hebsuperobject(43,6,21,"ויואילו לקחת אתו אל האניה ורגע הגיעה האניה לארץ אשר הם הלכים שמה׃"));
hebsuperobject.biblemap.put("b43_6_22", new hebsuperobject(43,6,22,"ויהי ממחרת וירא המון העם העמד מעבר לים כי לא היתה אניה בזה בלתי אחת אשר ירדו בה תלמידיו וכי ישוע לא בא עם תלמידיו אל האניה אך תלמידיו לבדם נסעו מזה׃"));
hebsuperobject.biblemap.put("b43_6_23", new hebsuperobject(43,6,23,"ואניות אחרות באו מטיברים קרוב למקום אשר אכלו שם את הלחם בברכת האדון׃"));
hebsuperobject.biblemap.put("b43_6_24", new hebsuperobject(43,6,24,"ויהי כראות המון העם כי ישוע איננו שם אף לא תלמידיו וירדו גם הם באניות ויבאו אל כפר נחום לבקש את ישוע׃"));
hebsuperobject.biblemap.put("b43_6_25", new hebsuperobject(43,6,25,"וכאשר מצאו אתו מעבר הים ויאמרו אליו רבי מתי באת הלם׃"));
hebsuperobject.biblemap.put("b43_6_26", new hebsuperobject(43,6,26,"ויען אתם ישוע ויאמר אמן אמן אני אמר לכם לא על ראותכם את האתות תבקשוני כי אם על אשר אכלתם מן הלחם ותשבעו׃"));
hebsuperobject.biblemap.put("b43_6_27", new hebsuperobject(43,6,27,"אל תעמלו במאכל אשר יאבד כי אם במאכל הקים לחיי עולמים אשר בן האדם יתננו לכם כי בו חתם חותמו אביו האלהים׃"));
hebsuperobject.biblemap.put("b43_6_28", new hebsuperobject(43,6,28,"ויאמרו אליו מה נעשה לפעל פעלות אלהים׃"));
hebsuperobject.biblemap.put("b43_6_29", new hebsuperobject(43,6,29,"ויען ישוע ויאמר אליהם זאת פעלת אלהים כי תאמינו בזה אשר הוא שלחו׃"));
hebsuperobject.biblemap.put("b43_6_30", new hebsuperobject(43,6,30,"ויאמרו אליו מה אפוא האות אשר תעשה למעננראה ונאמין בך מה תפעל׃"));
hebsuperobject.biblemap.put("b43_6_31", new hebsuperobject(43,6,31,"אבותינו אכלו את המן במדבר כאשר כתוב לחם מן השמים נתן למו לאכל׃"));
hebsuperobject.biblemap.put("b43_6_32", new hebsuperobject(43,6,32,"ויאמר אליהם ישוע אמן אמן אני אמר לכם לא משה נתן לכם את הלחם מן השמים כי אם אבי נתן לכם את הלחם מן השמים האמתי׃"));
hebsuperobject.biblemap.put("b43_6_33", new hebsuperobject(43,6,33,"כי לחם אלהים הוא היורד מן השמים ונתן חיים לעולם׃"));
hebsuperobject.biblemap.put("b43_6_34", new hebsuperobject(43,6,34,"ויאמרו אליו אדני תנה לנו תמיד את הלחם הזה׃"));
hebsuperobject.biblemap.put("b43_6_35", new hebsuperobject(43,6,35,"ויאמר להם ישוע אנכי הוא לחם החיים כל הבא אלי לא ירעב ואשר יאמין בי לא יצמא עוד׃"));
hebsuperobject.biblemap.put("b43_6_36", new hebsuperobject(43,6,36,"ואני הנה אמרתי לכם כי גם חזיתם אתי ולא תאמינו׃"));
hebsuperobject.biblemap.put("b43_6_37", new hebsuperobject(43,6,37,"כל אשר יתננו לי אבי יבוא אלי והבא אלי לא אהדפנו החוצה׃"));
hebsuperobject.biblemap.put("b43_6_38", new hebsuperobject(43,6,38,"כי ירדתי מן השמים לא לעשות רצוני כי אם רצון שלחי׃"));
hebsuperobject.biblemap.put("b43_6_39", new hebsuperobject(43,6,39,"וזה רצון האב אשר שלחני כי כל אשר נתן לי לא יאבד לי כי אם אקימנו ביום האחרון׃"));
hebsuperobject.biblemap.put("b43_6_40", new hebsuperobject(43,6,40,"וזה רצון שלחי אשר כל הראה את הבן ומאמין בו יהיו לו חיי עולמים ואני אקימנו ביום האחרון׃"));
hebsuperobject.biblemap.put("b43_6_41", new hebsuperobject(43,6,41,"וילנו עליו היהודים על אמרו אנכי הוא הלחם הירד מן השמים׃"));
hebsuperobject.biblemap.put("b43_6_42", new hebsuperobject(43,6,42,"ויאמרו הלא זה הוא ישוע בן יוסף אשר אנחנו ידעים את אביו ואת אמו ואיך יאמר מן השמים באתי׃"));
hebsuperobject.biblemap.put("b43_6_43", new hebsuperobject(43,6,43,"ויען ישוע ויאמר אליהם אל תלינו איש אל רעהו׃"));
hebsuperobject.biblemap.put("b43_6_44", new hebsuperobject(43,6,44,"לא יוכל איש לבוא אלי בלתי אם ימשכהו אבי אשר שלחני ואני אקימנו ביום האחרון׃"));
hebsuperobject.biblemap.put("b43_6_45", new hebsuperobject(43,6,45,"הלא כתוב בנביאים וכל בניך למודי יהוה לכן כל אשר שמע מן האב ולמד יבא אלי׃"));
hebsuperobject.biblemap.put("b43_6_46", new hebsuperobject(43,6,46,"לא כאלו ראה איש את האב בלתי הבא מאת האלהים הוא ראה את האלהים׃"));
hebsuperobject.biblemap.put("b43_6_47", new hebsuperobject(43,6,47,"אמן אמן אני אמר לכם המאמין בי לו חיי עולמים׃"));
hebsuperobject.biblemap.put("b43_6_48", new hebsuperobject(43,6,48,"אנכי הוא לחם החיים׃"));
hebsuperobject.biblemap.put("b43_6_49", new hebsuperobject(43,6,49,"אבותיכם אכלו את המן במדבר וימתו׃"));
hebsuperobject.biblemap.put("b43_6_50", new hebsuperobject(43,6,50,"זה הוא הלחם הירד מן השמים למען יאכל איש ממנו ולא ימות׃"));
hebsuperobject.biblemap.put("b43_6_51", new hebsuperobject(43,6,51,"אנכי הלחם החי הירד מן השמים איש כי יאכל מן הלחם הזה יחיה לעולם והלחם אשר אתננו הוא בשרי אשר אני נתן בעד חיי העולם׃"));
hebsuperobject.biblemap.put("b43_6_52", new hebsuperobject(43,6,52,"ויתוכחו היהודים איש עם רעהו לאמר איכה יוכל זה לתת לנו את בשרו לאכל׃"));
hebsuperobject.biblemap.put("b43_6_53", new hebsuperobject(43,6,53,"ויאמר אליהם ישוע אמן אמן אני אמר לכם אם לא תאכלו את בשר בן האדם ושתיתם את דמו אין לכם חיים בקרבכם׃"));
hebsuperobject.biblemap.put("b43_6_54", new hebsuperobject(43,6,54,"האכל את בשרי והשתה את דמי יש לו חיי עולמים ואני אקימנו ביום האחרון׃"));
hebsuperobject.biblemap.put("b43_6_55", new hebsuperobject(43,6,55,"כי בשרי באמת הוא מאכל ודמי באמת הוא משקה׃"));
hebsuperobject.biblemap.put("b43_6_56", new hebsuperobject(43,6,56,"האכל את בשרי ושתה את דמי הוא ילין בי ואני בו׃"));
