hebsuperobject.biblemap.put("b43_12_1", new hebsuperobject(43,12,1,"וששת ימים לפני חג הפסח בא ישוע לבית היני מקום לעזר אשר העיר אתו מעם המתים׃"));
hebsuperobject.biblemap.put("b43_12_2", new hebsuperobject(43,12,2,"ויעשו לו שם משתה בערב ומרתא משרתת ולעזר היה אחד מן המסבים אתו׃"));
hebsuperobject.biblemap.put("b43_12_3", new hebsuperobject(43,12,3,"ותקח מרים מרקחת נרד זך ויקר מאד לטרא אחת משקלה ותמשח בה את רגלי ישוע ותנגב את רגליו בשערותיה והבית ימלא ריח המרקחת׃"));
hebsuperobject.biblemap.put("b43_12_4", new hebsuperobject(43,12,4,"ויאמר אחד מתלמידיו הוא יהודה בן שמעון איש קריות העתיד למסרו׃"));
hebsuperobject.biblemap.put("b43_12_5", new hebsuperobject(43,12,5,"מדוע לא נמכרה המרקחת בשלש מאות דינר ונתן לעניים׃"));
hebsuperobject.biblemap.put("b43_12_6", new hebsuperobject(43,12,6,"והוא לא דבר את זאת מחמלתו על העניים כי אם גנב היה וכיס הכסף אתו וישא כל אשר ישימו בו׃"));
hebsuperobject.biblemap.put("b43_12_7", new hebsuperobject(43,12,7,"ויאמר ישוע הניחה לה ליום קבורתי צפנה זאת׃"));
hebsuperobject.biblemap.put("b43_12_8", new hebsuperobject(43,12,8,"כי העניים תמיד המה עמכם ואני אינני תמיד עמכם׃"));
hebsuperobject.biblemap.put("b43_12_9", new hebsuperobject(43,12,9,"וישמעו עם רב ביהודה כי שם הוא ויבאו לא בעבור ישוע לבדו כי אם לראות גם את לעזר אשר העירו מעם המתים׃"));
hebsuperobject.biblemap.put("b43_12_10", new hebsuperobject(43,12,10,"וראשי הכהנים התיעצו להרג גם את לעזר׃"));
hebsuperobject.biblemap.put("b43_12_11", new hebsuperobject(43,12,11,"כי בגללו באו שמה רבים מן היהודים ויאמינו בישוע׃"));
hebsuperobject.biblemap.put("b43_12_12", new hebsuperobject(43,12,12,"ויהי ממחרת כשמוע המון רב אשר באו לחג החג כי יבא ישוע ירושלים׃"));
hebsuperobject.biblemap.put("b43_12_13", new hebsuperobject(43,12,13,"ויקחו בידם כפות תמרים ויצאו לקראתו ויריעו לאמר הושע נא ברוך הבא בשם יהוה מלך ישראל׃"));
hebsuperobject.biblemap.put("b43_12_14", new hebsuperobject(43,12,14,"וימצא ישוע עיר אחד וירכב עליו כאשר כתוב׃"));
hebsuperobject.biblemap.put("b43_12_15", new hebsuperobject(43,12,15,"אל תיראי בת ציון הנה מלכך יבוא לך רכב על עיר בן אתנות׃"));
hebsuperobject.biblemap.put("b43_12_16", new hebsuperobject(43,12,16,"וכל זאת לא הבינו תלמידיו בראשונה אך אחרי אשר נתפאר ישוע זכרו כי כן כתוב עליו וכי זאת עשו לו׃"));
hebsuperobject.biblemap.put("b43_12_17", new hebsuperobject(43,12,17,"ויעידו העם אשר היו אצלו בקראו אל לעזר לצאת מן הקבר ויער אתו מעם המתים׃"));
hebsuperobject.biblemap.put("b43_12_18", new hebsuperobject(43,12,18,"על זאת גם המון העם יצא לקראתו על שמעם כי עשה האות הזה׃"));
hebsuperobject.biblemap.put("b43_12_19", new hebsuperobject(43,12,19,"והפרושים דברו איש את אחיו לאמר הראיתם כי הועיל לא תועילו מאומה הנה כל העולם נמשך אחריו׃"));
hebsuperobject.biblemap.put("b43_12_20", new hebsuperobject(43,12,20,"ובתוך העלים להשתחות בחג היו אנשים יונים׃"));
hebsuperobject.biblemap.put("b43_12_21", new hebsuperobject(43,12,21,"ויקרבו אל פילפוס איש בית צידה אשר בארץ הגליל וישאלו ממנו לאמר אדני לראות את ישוע חפצנו׃"));
hebsuperobject.biblemap.put("b43_12_22", new hebsuperobject(43,12,22,"ויבא פילפוס ויגד זאת אל אנדרי ואנדרי ופילפוס הגידו אל ישוע׃"));
hebsuperobject.biblemap.put("b43_12_23", new hebsuperobject(43,12,23,"ויען אותם ישוע ויאמר באה השעה שיפאר בן האדם׃"));
hebsuperobject.biblemap.put("b43_12_24", new hebsuperobject(43,12,24,"אמן אמן אני אמר לכם אם לא יפל גרגר החטא אל תוך האדמה ומת ישאר לבדו וכאשר מת יעשה פרי הרבה׃"));
hebsuperobject.biblemap.put("b43_12_25", new hebsuperobject(43,12,25,"האהב את נפשו יאבדנה והשנא את נפשו בעולם הזה ינצרה לחיי נצח׃"));
hebsuperobject.biblemap.put("b43_12_26", new hebsuperobject(43,12,26,"החפץ לשרתני ילך אחרי ובאשר אהיה שם יהיה גם משרתי ואיש אשר ישרתני אתו יכבד אבי׃"));
hebsuperobject.biblemap.put("b43_12_27", new hebsuperobject(43,12,27,"עתה נבהלה נפשי ומה אמר הושיעני אבי מן השעה הזאת אך על כן באתי אל השעה הזאת׃"));
hebsuperobject.biblemap.put("b43_12_28", new hebsuperobject(43,12,28,"אבי פאר את שמך ותצא בת קול מן השמים גם פארתי וגם אוסיף לפאר׃"));
hebsuperobject.biblemap.put("b43_12_29", new hebsuperobject(43,12,29,"והעם העמדים שמה כשמעם אמרו רעם נשמע ואחרים אמרו מלאך דבר אתו׃"));
hebsuperobject.biblemap.put("b43_12_30", new hebsuperobject(43,12,30,"ויען ישוע ויאמר לא למעני היה הקול הזה כי אם למענכם׃"));
hebsuperobject.biblemap.put("b43_12_31", new hebsuperobject(43,12,31,"עתה משפט בא על העולם הזה עתה ישלך שר העולם הזה חוצה׃"));
hebsuperobject.biblemap.put("b43_12_32", new hebsuperobject(43,12,32,"ואני בהנשאי מעל הארץ אמשך כלם אלי׃"));
hebsuperobject.biblemap.put("b43_12_33", new hebsuperobject(43,12,33,"וזאת דבר לרמוז אי זה מות הוא עתיד למות׃"));
hebsuperobject.biblemap.put("b43_12_34", new hebsuperobject(43,12,34,"ויענו אתו העם ויאמרו הנה שמענו בתורה כי המשיח יכון לעולם ואיך אמרת כי בן האדם צריך להנשא ומי בן האדם הלזה׃"));
hebsuperobject.biblemap.put("b43_12_35", new hebsuperobject(43,12,35,"ויאמר אליהם ישוע אך למצער יהיה האור עמכם התהלכו בעוד לכם האור פן ישופכם חשך וההלך בחשך לא ידע אנה הוא הלך׃"));
hebsuperobject.biblemap.put("b43_12_36", new hebsuperobject(43,12,36,"בעוד לכם האור האמינו באור למען תהיו בני האור את הדברים האלה דבר ישוע וילך לו ויסתר מפניהם׃"));
hebsuperobject.biblemap.put("b43_12_37", new hebsuperobject(43,12,37,"רבים האתות אשר עשה לעיניהם ובכל זאת לא האמינו בו׃"));
hebsuperobject.biblemap.put("b43_12_38", new hebsuperobject(43,12,38,"למלאת דבר ישעיהו הנביא אשר אמר יהוה מי האמין לשמעתנו וזרוע יהוה על מי נגלתה׃"));
hebsuperobject.biblemap.put("b43_12_39", new hebsuperobject(43,12,39,"על כן לא יכלו להאמין כי עוד אמר ישעיהו׃"));
hebsuperobject.biblemap.put("b43_12_40", new hebsuperobject(43,12,40,"השע עיניהם והשמין לבבם פן יראו בעיניהם ולבבם יבין ושבו ורפאתי להם׃"));
hebsuperobject.biblemap.put("b43_12_41", new hebsuperobject(43,12,41,"כזאת דבר ישעיהו בראותו את תפארתו וינבא עליו׃"));
hebsuperobject.biblemap.put("b43_12_42", new hebsuperobject(43,12,42,"אולם רבים אף מן השרים האמינו בו אך בגלל הפרושים לא הודו למען אשר לא ינדו׃"));
hebsuperobject.biblemap.put("b43_12_43", new hebsuperobject(43,12,43,"כי אהבו כבוד אנשים יותר מכבוד האלהים׃"));
hebsuperobject.biblemap.put("b43_12_44", new hebsuperobject(43,12,44,"ויקרא ישוע ויאמר המאמין בי לא בי הוא מאמין כי אם בשלח אתי׃"));
hebsuperobject.biblemap.put("b43_12_45", new hebsuperobject(43,12,45,"והראה אתי את אשר שלחני הוא ראה׃"));
hebsuperobject.biblemap.put("b43_12_46", new hebsuperobject(43,12,46,"אני באתי לאור אל העולם למען כל אשר יאמין בי לא ישב בחשך׃"));
hebsuperobject.biblemap.put("b43_12_47", new hebsuperobject(43,12,47,"והשמע את דברי ולא ישמרם אני לא אשפט אתו כי לא באתי לשפט את העולם כי אם להושיע את העולם׃"));
hebsuperobject.biblemap.put("b43_12_48", new hebsuperobject(43,12,48,"ואיש אשר יבזני ולא יקח אמרי יש אחד אשר ידין אתו הדבר אשר דברתי הוא ידין אתו ביום האחרון׃"));
hebsuperobject.biblemap.put("b43_12_49", new hebsuperobject(43,12,49,"כי אני לא מלבי דברתי כי אם אבי השלח אתי הוא צוני את אשר אמר ואת אשר אדבר׃"));
hebsuperobject.biblemap.put("b43_12_50", new hebsuperobject(43,12,50,"ואני ידעתי כי מצותו חיי עולם לכן כל אשר אדבר כאשר אמר אלי אבי כן אני מדבר׃"));
hebsuperobject.biblemap.put("b43_13_1", new hebsuperobject(43,13,1,"ולפני חג הפסח כשידע ישוע כי באה שעתו לעבר מן העולם הזה אל אביו כאשר אהב את בחיריו אשר בעולם כן אהבם עד הקץ׃"));
hebsuperobject.biblemap.put("b43_13_2", new hebsuperobject(43,13,2,"ויהי אחרי החל הסעודה והשטן נתן בלב יהודה בן שמעון איש קריות למסרו׃"));
hebsuperobject.biblemap.put("b43_13_3", new hebsuperobject(43,13,3,"וידע ישוע כי נתן אביו את הכל בידו וכי מאלהים בא ואל אלהים ישוב׃"));
hebsuperobject.biblemap.put("b43_13_4", new hebsuperobject(43,13,4,"ויקם מעל השלחן ויפשט את בגדיו ויקח מטפחת ויחגרה׃"));
hebsuperobject.biblemap.put("b43_13_5", new hebsuperobject(43,13,5,"ואחר יצק מים בכיור ויחל לרחץ את רגלי תלמידיו ולנגב במטפחת אשר הוא חגור בה׃"));
hebsuperobject.biblemap.put("b43_13_6", new hebsuperobject(43,13,6,"ויקרב אל שמעון פטרוס והוא אמר אליו אדני האתה תרחץ את רגלי׃"));
hebsuperobject.biblemap.put("b43_13_7", new hebsuperobject(43,13,7,"ויען ישוע ויאמר אליו את אשר אני עשה אינך ידע כעת ואחרי כן תדע׃"));
hebsuperobject.biblemap.put("b43_13_8", new hebsuperobject(43,13,8,"ויאמר אליו פטרוס לעולם לא תרחץ את רגלי ויען ישוע אם לא ארחץ אתך אין לך חלק עמי׃"));
hebsuperobject.biblemap.put("b43_13_9", new hebsuperobject(43,13,9,"ויאמר אליו שמעון פטרוס אדני לא לבד את רגלי כי אם גם את ידי ואת ראשי׃"));
hebsuperobject.biblemap.put("b43_13_10", new hebsuperobject(43,13,10,"ויאמר אליו ישוע המרחץ אין לו לרחץ עוד כי אם את הרגלים כי כלו טהור הוא ואתם טהורים אך לא כלכם׃"));
hebsuperobject.biblemap.put("b43_13_11", new hebsuperobject(43,13,11,"כי ידע מי ימסרהו על כן אמר לא כלכם טהורים׃"));
hebsuperobject.biblemap.put("b43_13_12", new hebsuperobject(43,13,12,"ויהי אחרי אשר רחץ את רגלים וילבש את בגדיו וישב להסב ויאמר אליהם הידעתם מה הדבר אשר עשיתי לכם׃"));
hebsuperobject.biblemap.put("b43_13_13", new hebsuperobject(43,13,13,"אתם קראים לי רב ואדון והיטבתם אשר דברתם כי אני הוא׃"));
hebsuperobject.biblemap.put("b43_13_14", new hebsuperobject(43,13,14,"לכן אם אני המורה והאדון רחצתי את רגליכם גם אתם חיבים לרחץ איש את רגלי אחיו׃"));
hebsuperobject.biblemap.put("b43_13_15", new hebsuperobject(43,13,15,"כי מופת נתתי לכם למען תעשו גם אתם כאשר עשיתי לכם׃"));
hebsuperobject.biblemap.put("b43_13_16", new hebsuperobject(43,13,16,"אמן אמן אני אמר לכם כי העבד איננו גדול מאדניו והשלוח איננו גדול משלחו׃"));
hebsuperobject.biblemap.put("b43_13_17", new hebsuperobject(43,13,17,"אם ידעתם זאת אשריכם אם כן תעשו׃"));
hebsuperobject.biblemap.put("b43_13_18", new hebsuperobject(43,13,18,"לא על כלכם דברתי יודע אני את אשר בחרתי בהם אך למען ימלא הכתוב אוכל לחמי הגדיל עלי עקב׃"));
hebsuperobject.biblemap.put("b43_13_19", new hebsuperobject(43,13,19,"מעתה אני אמר לכם בטרם היותה למען בבואה תאמינו כי אני הוא׃"));
hebsuperobject.biblemap.put("b43_13_20", new hebsuperobject(43,13,20,"אמן אמן אני אמר לכם כי כל המקבל את אשר אשלחהו אתי הוא מקבל והמקבל אתי הוא מקבל את שלחי׃"));
hebsuperobject.biblemap.put("b43_13_21", new hebsuperobject(43,13,21,"ויהי ככלות ישוע לדבר הדברים האלה ויבהל ברוחו ויעד ויאמר אמן אמן אני אמר לכם כי אחד מכם ימסרני׃"));
hebsuperobject.biblemap.put("b43_13_22", new hebsuperobject(43,13,22,"ויתראו תלמידיו ויתמהו איש אל רעהו לדעת על מי דבר׃"));
hebsuperobject.biblemap.put("b43_13_23", new hebsuperobject(43,13,23,"ואחד מתלמידיו מסב על חיק ישוע אשר ישוע אהבו׃"));
hebsuperobject.biblemap.put("b43_13_24", new hebsuperobject(43,13,24,"וירמז לו שמעון פטרוס לדרש מי הוא זה אשר דבר עליו׃"));
hebsuperobject.biblemap.put("b43_13_25", new hebsuperobject(43,13,25,"ויפל על לב ישוע ויאמר אליו אדני מי הוא׃"));
hebsuperobject.biblemap.put("b43_13_26", new hebsuperobject(43,13,26,"ויען ישוע הנה זה הוא אשר אטבל פתי לתתו לו ויטבל את פתו ויתן אל יהודה בן שמעון איש קריות׃"));
hebsuperobject.biblemap.put("b43_13_27", new hebsuperobject(43,13,27,"ואחרי בלעו בא השטן אל קרבו ויאמר אליו ישוע את אשר תעשה עשה מהרה׃"));
hebsuperobject.biblemap.put("b43_13_28", new hebsuperobject(43,13,28,"ומן המסבים לא ידע איש על מה דבר אליו כזאת׃"));
hebsuperobject.biblemap.put("b43_13_29", new hebsuperobject(43,13,29,"כי יש אשר חשבו כי אמר אליו ישוע קנה לנו צרכי החג או לתת לאביונים יען אשר כיס הכסף תחת יד יהודה׃"));
hebsuperobject.biblemap.put("b43_13_30", new hebsuperobject(43,13,30,"והוא בקחתו את פת הלחם מהר לצאת החוצה ויהי לילה׃"));
hebsuperobject.biblemap.put("b43_13_31", new hebsuperobject(43,13,31,"הוא יצא וישוע אמר עתה נתפאר בן האדם והאלהים נתפאר בו׃"));
hebsuperobject.biblemap.put("b43_13_32", new hebsuperobject(43,13,32,"אם האלהים נתפאר בו גם האלהים הוא יפארהו בעצמו ובמהרה יפארהו׃"));
hebsuperobject.biblemap.put("b43_13_33", new hebsuperobject(43,13,33,"בני עוד מעט מזער אהיה עמכם אתם תבקשוני וכאשר אמרתי אל היהודים כי אל אשר אני הולך לא תוכלו לבוא שמה כן אליכם אמר אני עתה׃"));
hebsuperobject.biblemap.put("b43_13_34", new hebsuperobject(43,13,34,"מצוה חדשה אני נתן לכם כי תאהבו איש את אחיו כאשר אהבתי אתכם כן גם אתם איש את אחיו תאהבון׃"));
hebsuperobject.biblemap.put("b43_13_35", new hebsuperobject(43,13,35,"בזאת ידעו כלם כי תלמידי אתם בהיות אהבה ביניכם׃"));
hebsuperobject.biblemap.put("b43_13_36", new hebsuperobject(43,13,36,"ויאמר אליו פטרוס אדני אנה תלך ויען אתו ישוע אל אשר אני הולך שמה לא תוכל עתה ללכת אחרי אך אחרי כן תלך אחרי׃"));
hebsuperobject.biblemap.put("b43_13_37", new hebsuperobject(43,13,37,"ויאמר אליו פטרוס מדוע לא אוכל עתה ללכת אחריך הן נפשי בעד נפשך אתן׃"));
hebsuperobject.biblemap.put("b43_13_38", new hebsuperobject(43,13,38,"ויען אתו ישוע הכי תתן נפשך בעד נפשי אמן אמן אני אמר לך בטרם יקרא התרנגל תכחש בי שלש פעמים׃"));
