hebsuperobject.biblemap.put("b43_19_14", new hebsuperobject(43,19,14,"ואז היתה הכנת הפסח והשעה כשעה הששית ויאמר אל היהודים הנה מלככם והם צעקו טול טול צלב אתו׃"));
hebsuperobject.biblemap.put("b43_19_15", new hebsuperobject(43,19,15,"ויאמר אליהם פילטוס הצלב אצלב את מלככם ויענו ראשי הכהנים אין לנו מלך כי אם הקיסר׃"));
hebsuperobject.biblemap.put("b43_19_16", new hebsuperobject(43,19,16,"אז מסרו אליהם להצליבו ויקחו את ישוע ויוליכהו׃"));
