hebsuperobject.biblemap.put("b43_19_21", new hebsuperobject(43,19,21,"ויאמרו ראשי כהני היהודים אל פילטוס אל נא תכתב מלך היהודים כי אם אשר אמר אני מלך היהודים׃"));
hebsuperobject.biblemap.put("b43_19_22", new hebsuperobject(43,19,22,"ויען פילטוס ויאמר את אשר כתבתי כתבתי׃"));
