hebsuperobject.biblemap.put("b43_19_29", new hebsuperobject(43,19,29,"ושם כלי מלא חמץ ויטבלו ספוג בחמץ וישימהו על אזוב ויקריבהו אל פיו׃"));
hebsuperobject.biblemap.put("b43_19_30", new hebsuperobject(43,19,30,"ויקח ישוע את החמץ ויאמר כלה ויט את ראשו ויפקד את רוחו׃"));
