hebsuperobject.biblemap.put("b43_19_39", new hebsuperobject(43,19,39,"ויבא גם נקדימון אשר בא לפנים בלילה אל ישוע ויבא ערוב מר ואהלות כמאה ליטרין׃"));
hebsuperobject.biblemap.put("b43_19_40", new hebsuperobject(43,19,40,"ויקחו את גופת ישוע ויחתלוה בתכריכין עם הבשמים כאשר נהגים היהודים לקבר את מתיהם׃"));
hebsuperobject.biblemap.put("b43_19_41", new hebsuperobject(43,19,41,"ובמקום אשר נצלב שם היה גן ובגן קבר חדש אשר לא הושם בו איש עד עתה׃"));
