hebsuperobject.biblemap.put("b43_20_13", new hebsuperobject(43,20,13,"ויאמרו אליה אשה למה תבכי ותאמר אליהם כי נשאו מזה את אדני ולא ידעתי איפה הניחהו׃"));
hebsuperobject.biblemap.put("b43_20_14", new hebsuperobject(43,20,14,"ויהי בדברה זאת ותפן אחריה ותרא את ישוע עמד ולא ידעה כי הוא ישוע׃"));
