hebsuperobject.biblemap.put("b43_21_1", new hebsuperobject(43,21,1,"ויהי אחרי כן ויסף ישוע הגלות אל תלמידיו על ים טיבריה וכה נגלה אליהם׃"));
hebsuperobject.biblemap.put("b43_21_2", new hebsuperobject(43,21,2,"שמעון פטרוס ותומא הנקרא דידומוס ונתנאל מקנה אשר בארץ הגליל ובני זבדי ועוד שנים אחרים מתלמידיו ישבו יחדו׃"));
hebsuperobject.biblemap.put("b43_21_3", new hebsuperobject(43,21,3,"ויאמר אליהם שמעון פטרוס הנני הלך לדיג ויאמרו אליו גם אנחנו נלך עמך ויצאו וימהרו לרדת אל האניה ובלילה ההוא לא אחזו מאומה׃"));
