hebsuperobject.biblemap.put("b43_21_7", new hebsuperobject(43,21,7,"ויאמר התלמיד ההוא אשר ישוע אהבו אל פטרוס זה הוא האדון ויהי כשמע שמעון פטרוס כי הוא האדון ויחגר את מעילו כי עירום היה ויתנפל אל הים׃"));
hebsuperobject.biblemap.put("b43_21_8", new hebsuperobject(43,21,8,"והתלמידים הנשארים באו בספינה כי לא הרחיקו מן היבשה כי אם כמאתים אמה וימשכו את המכמרת עם הדגים׃"));
hebsuperobject.biblemap.put("b43_21_9", new hebsuperobject(43,21,9,"ויהי כצאתם אל היבשה ויראו שם גחלי אש ערוכים ודגים עליהם ולחם לאכל׃"));
