hebsuperobject.biblemap.put("b44_3_11", new hebsuperobject(44,3,11,"ויהי הוא מחזיק בפטרוס וביוחנן וירץ אליהם כל העם אל האולם הנקרא אולם של שלמה וישתוממו׃"));
hebsuperobject.biblemap.put("b44_3_12", new hebsuperobject(44,3,12,"וירא פטרוס ויען ויאמר אל העם אנשי ישראל מה אתם תמהים על זאת ומה תסתכלו בנו כאלו אנחנו בכחנו ובחסידותנו שמנו את זה מתהלך׃"));
hebsuperobject.biblemap.put("b44_3_13", new hebsuperobject(44,3,13,"אלהי אברהם אלהי יצחק ואלהי יעקב אלהי אבותינו הוא פאר את עבדו ישוע אשר אתם מסרתם אותו וכחשתם בו בפני פילטוס כשהיה דן לפטרו׃"));
hebsuperobject.biblemap.put("b44_3_14", new hebsuperobject(44,3,14,"אבל אתם כחשתם בקדוש ובצדיק ובקשתם כי יחן לכם איש רצח׃"));
hebsuperobject.biblemap.put("b44_3_15", new hebsuperobject(44,3,15,"ואת שר החיים הרגתם אשר האלהים הקימו מן המתים ואנחנו עדיו׃"));
hebsuperobject.biblemap.put("b44_3_16", new hebsuperobject(44,3,16,"ולמען אמונת שמו חזק שמו את האיש הזה אשר אתם ראים ומכירים אתו והאמונה אשר לנו על ידו היא העלתה לו את הארכה הזאת לעיני כלכם׃"));
hebsuperobject.biblemap.put("b44_3_17", new hebsuperobject(44,3,17,"ועתה אחי ידע אני כי בבלי דעת עשיתם גם אתם גם ראשיכם׃"));
hebsuperobject.biblemap.put("b44_3_18", new hebsuperobject(44,3,18,"והאלהים ככה מלא את אשר הגיד מקדם בפי כל נביאיו כי יענה המשיח׃"));
hebsuperobject.biblemap.put("b44_3_19", new hebsuperobject(44,3,19,"לכן הנחמו ושובו וימחו חטאיכם׃"));
hebsuperobject.biblemap.put("b44_3_20", new hebsuperobject(44,3,20,"למען אשר יבאו ימי רוחה מלפני יהוה וישלח את אשר בשרו לכם מקדם את ישוע המשיח׃"));
hebsuperobject.biblemap.put("b44_3_21", new hebsuperobject(44,3,21,"אשר צריך כי יקבלהו השמים עד ימי שוב כל הדברים לתקונם אשר דבר עליהם האלהים בפי נביאיו הקדושים מימי עולם׃"));
hebsuperobject.biblemap.put("b44_3_22", new hebsuperobject(44,3,22,"הן משה אמר אל אבותינו נביא יקים לכם יהוה אלהיכם מקרב אחיכם כמני אליו תשמעון ככל אשר ידבר אליכם׃"));
hebsuperobject.biblemap.put("b44_3_23", new hebsuperobject(44,3,23,"והיה כל הנפש אשר לא תשמע אל הנביא ההוא ונכרתה מעמיה׃"));
hebsuperobject.biblemap.put("b44_3_24", new hebsuperobject(44,3,24,"וגם כל הנביאים משמואל ואשר נבאו אחריו כלם הגידו מראש את הימים האלה׃"));
hebsuperobject.biblemap.put("b44_3_25", new hebsuperobject(44,3,25,"אתם בני הנביאים ובני הברית אשר כרת האלהים עם אבותינו באמרו אל אברהם ונברכו בזרעך כל משפחות האדמה׃"));
hebsuperobject.biblemap.put("b44_3_26", new hebsuperobject(44,3,26,"לכם בראשונה העמיד האלהים את עבדו ישוע וישלחהו לברך אתכם בשובכם כל איש מרע מעלליכם׃"));
hebsuperobject.biblemap.put("b44_4_1", new hebsuperobject(44,4,1,"ויהי בדברם אל העם ויקומו עליהם הכהנים ונגיד בית המקדש והצדוקים׃"));
hebsuperobject.biblemap.put("b44_4_2", new hebsuperobject(44,4,2,"כי חרה להם על אשר למדו את העם והגידו בישוע את תחית המתים׃"));
hebsuperobject.biblemap.put("b44_4_3", new hebsuperobject(44,4,3,"וישלחו בהם את ידיהם וישימום במשמר עד למחר כי כבר בא הערב׃"));
hebsuperobject.biblemap.put("b44_4_4", new hebsuperobject(44,4,4,"ורבים מהשמעים את הדבר האמינו ויהי מספר האנשים כחמשת אלפים׃"));
hebsuperobject.biblemap.put("b44_4_5", new hebsuperobject(44,4,5,"ויהי ממחרת ויקהלו שריהם ראשיהם וזקניהם וסופריהם ירושלים׃"));
hebsuperobject.biblemap.put("b44_4_6", new hebsuperobject(44,4,6,"וחנן הכהן הגדול וקיפא ויוחנן ואלכסנדרוס וכל אשר ממשפחת הכהן הגדול׃"));
hebsuperobject.biblemap.put("b44_4_7", new hebsuperobject(44,4,7,"ויעמידו אתם בתוך וישאלום באי זה כח ובאי זה שם עשיתם זאת׃"));
hebsuperobject.biblemap.put("b44_4_8", new hebsuperobject(44,4,8,"וימלא פטרוס רוח הקדש ויאמר אליהם אתם ראשי העם וזקני ישראל׃"));
hebsuperobject.biblemap.put("b44_4_9", new hebsuperobject(44,4,9,"אם אנחנו נחקרים היום על הטובה אשר עשינו לאיש חולה ושאלתם במה זה נושע׃"));
hebsuperobject.biblemap.put("b44_4_10", new hebsuperobject(44,4,10,"יודע לכלכם ולכל עם ישראל כי בשם ישוע המשיח הנצרי אשר צלבתם אותו ואשר האלהים הקימו מן המתים כי בשמו עמד זה לפניכם בריא׃"));
hebsuperobject.biblemap.put("b44_4_11", new hebsuperobject(44,4,11,"והוא האבן אשר מאסתם אתם הבונים ותהי לראש פנה׃"));
hebsuperobject.biblemap.put("b44_4_12", new hebsuperobject(44,4,12,"ואין הישועה באחר ואין תחת השמים שם אחר הנתן לבני אדם אשר בו נושע׃"));
hebsuperobject.biblemap.put("b44_4_13", new hebsuperobject(44,4,13,"ויהי כאשר ראו את בטחון לב של פטרוס ויוחנן והבינו כי הדיוטים ולא בעלי חכמה המה ויתמהו עליהם ויכירום כי התהלכו עם ישוע׃"));
hebsuperobject.biblemap.put("b44_4_14", new hebsuperobject(44,4,14,"ובראותם את האיש הנרפא עמד אצלם לא מצאו לדבר נגדם דבר׃"));
hebsuperobject.biblemap.put("b44_4_15", new hebsuperobject(44,4,15,"ויצוו אתם לצאת חוצה מן הסנהדרין ויתיעצו יחדו׃"));
hebsuperobject.biblemap.put("b44_4_16", new hebsuperobject(44,4,16,"ויאמרו מה נעשה לאנשים האלה הנה אות גלוי נעשה על ידיהם וגם מפרסם לכל ישבי ירושלים ולא נוכל לכחש בו׃"));
hebsuperobject.biblemap.put("b44_4_17", new hebsuperobject(44,4,17,"אך למען לא ירבה הדבר בעם ויפרץ נזהירה אתם בגערה לבלתי דבר עוד לכל אדם בשם הזה׃"));
hebsuperobject.biblemap.put("b44_4_18", new hebsuperobject(44,4,18,"ויקראו אתם ויצוום אשר לא ידברו דבר ולא ילמדו בשם ישוע׃"));
hebsuperobject.biblemap.put("b44_4_19", new hebsuperobject(44,4,19,"ויענו פטרוס ויוחנן ויאמרו אליהם הנכון הוא לפני האלהים שמע לכם יתר משמע לאלהים שפטו אתם׃"));
hebsuperobject.biblemap.put("b44_4_20", new hebsuperobject(44,4,20,"כי לא נוכל אנחנו לחדל מדבר את אשר ראינו ושמענו׃"));
hebsuperobject.biblemap.put("b44_4_21", new hebsuperobject(44,4,21,"ויוסיפו לגער בם ויפטרו אתם באשר לא מצאו דבר לענוש אתם מפני העם כי כלם מהללים את האלהים על הנעשה׃"));
hebsuperobject.biblemap.put("b44_4_22", new hebsuperobject(44,4,22,"כי בן ארבעים שנה ומעלה היה האיש ההוא אשר נעשה עמו אות הרפואה הזאת׃"));
hebsuperobject.biblemap.put("b44_4_23", new hebsuperobject(44,4,23,"ואחרי הפטרם באו אל אחיהם ויספרו להם את אשר אמרו אליהם הכהנים הגדולים והזקנים׃"));
hebsuperobject.biblemap.put("b44_4_24", new hebsuperobject(44,4,24,"ויהי כאשר שמעו את זאת וישאו את קולם בלב אחד לאלהים ויאמרו אדני אתה האל העשה את השמים ואת הארץ ואת הים ואת כל אשר בם׃"));
hebsuperobject.biblemap.put("b44_4_25", new hebsuperobject(44,4,25,"אשר אמרת בפי עבדך דוד למה רגשו גוים ולאמים יהגו ריק׃"));
hebsuperobject.biblemap.put("b44_4_26", new hebsuperobject(44,4,26,"יתיצבו מלכי ארץ ורוזנים נוסדו יחד על יהוה ועל משיחו׃"));
hebsuperobject.biblemap.put("b44_4_27", new hebsuperobject(44,4,27,"כי אמנם נוסדו הורדוס ופנטיוס פילטוס עם הגוים ולאמי ישראל על ישוע עבדך הקדוש אשר משחת׃"));
hebsuperobject.biblemap.put("b44_4_28", new hebsuperobject(44,4,28,"לעשות את אשר ידך ועצתך מקדם גזרה להיות׃"));
hebsuperobject.biblemap.put("b44_4_29", new hebsuperobject(44,4,29,"ועתה אדני ראה את גערתם ותן לעבדיך לדבר את דברך בכל בטחון לבם׃"));
hebsuperobject.biblemap.put("b44_4_30", new hebsuperobject(44,4,30,"בנטתך את ידך למרפא ולתת אתות ומופתים בשם ישוע עבדך הקדוש׃"));
hebsuperobject.biblemap.put("b44_4_31", new hebsuperobject(44,4,31,"ויהי כאשר התפללו וינע המקום אשר היו נקהלים שם וימלאו כלם רוח הקדש וידברו את דבר האלהים בבטחון לבב׃"));
hebsuperobject.biblemap.put("b44_4_32", new hebsuperobject(44,4,32,"וקהל המאמינים היה להם לב אחד ונפש אחת ואין איש מהם אומר על אשר בידו לי הוא כי הכל היה להם בשתפות׃"));
hebsuperobject.biblemap.put("b44_4_33", new hebsuperobject(44,4,33,"ובגבורה גדולה יעידו השליחים על תקומת האדון ישוע וחסד גדול נמשך לכלם׃"));
hebsuperobject.biblemap.put("b44_4_34", new hebsuperobject(44,4,34,"כי לא היה בהם חסר דבר כי כל בעלי שדות ובתים מכרו אתם ויביאו את כסף מחירם׃"));
hebsuperobject.biblemap.put("b44_4_35", new hebsuperobject(44,4,35,"וישימהו לרגלי השליחים ויתן לכל איש ואיש די מחסרו׃"));
hebsuperobject.biblemap.put("b44_4_36", new hebsuperobject(44,4,36,"ויוסף אשר כנוהו השליחים בשם בר נבא פרושו בן הנחמה איש לוי אשר נולד בארץ כתים׃"));
hebsuperobject.biblemap.put("b44_4_37", new hebsuperobject(44,4,37,"גם לו היה שדה וימכרהו ויבא את הכסף וישימהו לרגלי השליחים׃"));
hebsuperobject.biblemap.put("b44_5_1", new hebsuperobject(44,5,1,"ואיש אחד ושמו חנניה עם אשתו שפירה מכר את אחזתו׃"));
hebsuperobject.biblemap.put("b44_5_2", new hebsuperobject(44,5,2,"ויקח מן המחיר ויסתירהו ואשתו ידעת גם היא וחלק אחד הביא וישם לרגלי השליחים׃"));
hebsuperobject.biblemap.put("b44_5_3", new hebsuperobject(44,5,3,"ויאמר פטרוס חנניה למה זה מלא השטן את לבבך לשקר ברוח הקדש ולהסתיר ממחיר השדה׃"));
hebsuperobject.biblemap.put("b44_5_4", new hebsuperobject(44,5,4,"הלא שלך היה טרם המכרו וגם אחרי נמכר היה בידך ולמה שמת על לבבך הדבר הזה לא שקרת בבני אדם כי אם באלהים׃"));
hebsuperobject.biblemap.put("b44_5_5", new hebsuperobject(44,5,5,"וכשמע חנניה את הדברים האלה נפל ארצה ויגוע ותהי יראה גדולה על כל השמעים׃"));
hebsuperobject.biblemap.put("b44_5_6", new hebsuperobject(44,5,6,"ויקומו הצעירים ויאספו אתו וישאהו החוצה ויקברהו׃"));
hebsuperobject.biblemap.put("b44_5_7", new hebsuperobject(44,5,7,"ויהי כמשלש שעות ותבא אשתו והיא לא ידעה את הנעשה׃"));
hebsuperobject.biblemap.put("b44_5_8", new hebsuperobject(44,5,8,"ויען פטרוס ויאמר אליה אמרי לי הבמחיר הזה מכרתם את השדה ותאמר כן במחיר הזה׃"));
hebsuperobject.biblemap.put("b44_5_9", new hebsuperobject(44,5,9,"ויאמר פטרוס אליה למה זה נועדתם לנסות את רוח יהוה הנה בפתח רגלי המקברים את אישך ונשאו אתך החוצה׃"));
hebsuperobject.biblemap.put("b44_5_10", new hebsuperobject(44,5,10,"ותפל פתאם לרגליו ותגוע והבחורים באו והנה מתה וישאוה החוצה ויקברוה אצל אישה׃"));
hebsuperobject.biblemap.put("b44_5_11", new hebsuperobject(44,5,11,"ותהי יראה גדולה על כל הקהל ועל כל השמעים את אלה׃"));
hebsuperobject.biblemap.put("b44_5_12", new hebsuperobject(44,5,12,"ויעשו אתות ומופתים רבים בקרב העם על ידי השליחים וכלם נאספו לב אחד באולם של שלמה׃"));
hebsuperobject.biblemap.put("b44_5_13", new hebsuperobject(44,5,13,"ומן האחרים אין איש אשר מלאו לבו לחלות עליהם אך הוקיר אתם העם׃"));
hebsuperobject.biblemap.put("b44_5_14", new hebsuperobject(44,5,14,"אבל נספחו עוד יתר מאמינים אל האדון אנשים ונשים הרבה מאד׃"));
hebsuperobject.biblemap.put("b44_5_15", new hebsuperobject(44,5,15,"עד כי נשאו את החולים אל הרחבות וישימום על מטות ומשכבות למען אשר ילך פטרוס ונפל אך צלו על אחד מהם׃"));
hebsuperobject.biblemap.put("b44_5_16", new hebsuperobject(44,5,16,"וגם המון עם הערים אשר מסביב נקבצו ירושלים מביאים את החולים ואת הנלחצים מרוחות טמאות וירפאו כלם׃"));
hebsuperobject.biblemap.put("b44_5_17", new hebsuperobject(44,5,17,"ויקם הכהן הגדול וכל אשר אתו והם אנשי כת הצדוקים וימלאו קנאה׃"));
hebsuperobject.biblemap.put("b44_5_18", new hebsuperobject(44,5,18,"וישלחו יד בשליחים ויתנום במשמר העיר׃"));
hebsuperobject.biblemap.put("b44_5_19", new hebsuperobject(44,5,19,"ויהי בלילה ויפתח מלאך יהוה את דלתי בית הכלא ויוציאם לאמר׃"));
hebsuperobject.biblemap.put("b44_5_20", new hebsuperobject(44,5,20,"לכו והתיצבו במקדש ודברו אל העם את כל דברי החיים האלה׃"));
hebsuperobject.biblemap.put("b44_5_21", new hebsuperobject(44,5,21,"והם כשמעם את זאת באו אל המקדש בהית הבקר וילמדו שם ויבא הכהן הגדול ואשר אתו ויקראו אל הסנהדרין ואל כל זקני בני ישראל וישלחו אל בית האסורים להביא אתם׃"));
hebsuperobject.biblemap.put("b44_5_22", new hebsuperobject(44,5,22,"וילכו המשרתים ולא מצאום בבית הכלא וישובו ויגידו לאמר׃"));
hebsuperobject.biblemap.put("b44_5_23", new hebsuperobject(44,5,23,"את בית האסורים מצאנו סגור ומסגר והשמרים עמדים על הדלתות וכאשר פתחנו לא נמצא בו אדם׃"));
hebsuperobject.biblemap.put("b44_5_24", new hebsuperobject(44,5,24,"ויהי כשמע הכהן ונגיד המקדש וראשי הכהנים את הדברים האלה ויבהלו עליהם ויאמרו איך יפל הדבר הזה׃"));
hebsuperobject.biblemap.put("b44_5_25", new hebsuperobject(44,5,25,"ואיש אחד בא ויגד להם לאמר הנה האנשים אשר שמתם במשמר עמדים הם במקדש ומלמדים את העם׃"));
hebsuperobject.biblemap.put("b44_5_26", new hebsuperobject(44,5,26,"וילך שמה הנגיד עם משרתיו ויקחם אך לא בחזקה כי יראו מפני העם פן יסקלו׃"));
hebsuperobject.biblemap.put("b44_5_27", new hebsuperobject(44,5,27,"ויביאו אתם ויעמידום לפני הסנהדרין וישאלם הכהן הגדול לאמר׃"));
hebsuperobject.biblemap.put("b44_5_28", new hebsuperobject(44,5,28,"הלא צוה צוינו עליכם לבלתי למד בשם הזה והנה מלאתם את ירושלים תורתכם ותחפצו להביא עלינו את דמי האיש הזה׃"));
hebsuperobject.biblemap.put("b44_5_29", new hebsuperobject(44,5,29,"ויען פטרוס והשליחים ויאמרו הלא עלינו להקשיב בקול אלהים מהקשיב בקול בני אדם׃"));
hebsuperobject.biblemap.put("b44_5_30", new hebsuperobject(44,5,30,"אלהי אבותינו הקים את ישוע אשר שלחתם ידכם בו ותתלו אותו על העץ׃"));
hebsuperobject.biblemap.put("b44_5_31", new hebsuperobject(44,5,31,"את זה נשא האלהים בימינו לשר ולמושיע לתת תשובה לישראל וסליחת החטאים׃"));
hebsuperobject.biblemap.put("b44_5_32", new hebsuperobject(44,5,32,"ואנחנו עדיו על אלה וגם רוח הקדש אשר נתן אותו האלהים לשמעים בקולו׃"));
hebsuperobject.biblemap.put("b44_5_33", new hebsuperobject(44,5,33,"ויהי כשמעם ויתרגזו ויתיעצו להרוג אתם׃"));
hebsuperobject.biblemap.put("b44_5_34", new hebsuperobject(44,5,34,"ויקם בתוך הסנהדרין אחד מן הפרושים גמליאל שמו והוא מורה התורה מכבד בעיני כל העם ויצו להוציא את השליחים החוצה לזמן מעט׃"));
hebsuperobject.biblemap.put("b44_5_35", new hebsuperobject(44,5,35,"ויאמר אליהם אנשי ישראל השמרו לכם מפני האנשים האלה במה שאתם עשים להם׃"));
hebsuperobject.biblemap.put("b44_5_36", new hebsuperobject(44,5,36,"כי לפני הימים האלה קם תודס ויתן את נפשו כאחד הגדולים וידבקו בו כארבע מאות איש ויהרג וכל אשר שמעו אליו התפרדו ויהיו לאין׃"));
hebsuperobject.biblemap.put("b44_5_37", new hebsuperobject(44,5,37,"ואחר כן קם יהודה הגלילי בימי הספר ויסת אחריו עם רב וגם הוא נהרג וכל אשר שמעו אליו נפצו׃"));
hebsuperobject.biblemap.put("b44_5_38", new hebsuperobject(44,5,38,"ועתה אני אמר אליכם חדלו לכם מן האנשים האלה והניחו להם כי העצה והפעלה הזאת אם מאת אדם היא תפר׃"));
hebsuperobject.biblemap.put("b44_5_39", new hebsuperobject(44,5,39,"ואם מאת האלהים היא לא תוכלו להפר אתה פן תמצאו נלחמים את האלהים׃"));
hebsuperobject.biblemap.put("b44_5_40", new hebsuperobject(44,5,40,"וישמעו אליו ויקראו את השליחים וילקו אתם ויצוו אשר לא ידברו בשם ישוע ויפטרו אותם׃"));
hebsuperobject.biblemap.put("b44_5_41", new hebsuperobject(44,5,41,"ויצאו שמחים מלפני הסנהדרין על כי זכו לשאת על שמו כלמה׃"));
hebsuperobject.biblemap.put("b44_5_42", new hebsuperobject(44,5,42,"וכל היום לא חדלו ללמד במקדש ובבתים ולבשר את בשורת ישוע המשיח׃"));
hebsuperobject.biblemap.put("b44_6_1", new hebsuperobject(44,6,1,"בימים ההם כאשר רבו התלמידים היתה תלונת היונים על העבריים על אשר העלימו עיניהם מאלמנותיהם לבלתי תת להן יום יום את ארוחתן׃"));
hebsuperobject.biblemap.put("b44_6_2", new hebsuperobject(44,6,2,"ויקראו שנים העשר את המון התלמידים ויאמרו לא נאוה לנו כי נעזב את דבר האלהים ונשמש את השלחנות׃"));
hebsuperobject.biblemap.put("b44_6_3", new hebsuperobject(44,6,3,"לכן אחי ברו לכם שבעה אנשים מקרבכם אשר שם טוב להם ומלאים רוח הקדש וחכמה ונפקידם על הענין הזה׃"));
hebsuperobject.biblemap.put("b44_6_4", new hebsuperobject(44,6,4,"ואנחנו נשקד על התפלה ועל שמוש הדבר׃"));
hebsuperobject.biblemap.put("b44_6_5", new hebsuperobject(44,6,5,"וייטב הדבר לפני כל ההמון ויבחרו את אסטפנוס איש מלא אמונה ורוח הקדש ואת פילפוס ואת פרוכורוס ואת ניקנור ואת טימון ואת פרמנס ואת ניקלס גר אנטיוכיא׃"));
hebsuperobject.biblemap.put("b44_6_6", new hebsuperobject(44,6,6,"את אלה העמידו לפני השליחים ויתפללו ויסמכו את ידיהם עליהם׃"));
hebsuperobject.biblemap.put("b44_6_7", new hebsuperobject(44,6,7,"ויהי דבר האלהים הולך וגדל וירב מספר התלמידים בירושלים עד מאד וגם המון רב מן הכהנים נכנעו אל האמונה׃"));
hebsuperobject.biblemap.put("b44_6_8", new hebsuperobject(44,6,8,"ואסטפנוס מלא אמונה וגבורה ויעש אתות ומופתים גדולים בקרב העם׃"));
hebsuperobject.biblemap.put("b44_6_9", new hebsuperobject(44,6,9,"ויקומו אנשים מבית הכנסת הנקרא על שם הליברטינים ושל קורינים ואלכסנדריים ומן בני קיליקיא ואסיא ויתוכחו עם אסטפנוס׃"));
hebsuperobject.biblemap.put("b44_6_10", new hebsuperobject(44,6,10,"ולא יכלו עמד לנגד החכמה והרוח אשר דבר בו׃"));
hebsuperobject.biblemap.put("b44_6_11", new hebsuperobject(44,6,11,"ויסיתו בו אנשים אמרים שמענו אתו מדבר גדופים במשה ובאלהים׃"));
hebsuperobject.biblemap.put("b44_6_12", new hebsuperobject(44,6,12,"ויעוררו את העם ואת הזקנים והסופרים ויקומו עליו ויחטפהו ויביאהו לפני הסנהדרין׃"));
hebsuperobject.biblemap.put("b44_6_13", new hebsuperobject(44,6,13,"ויעמידו עדי שקר אשר אמרו האיש הזה איננו חדל מהטיח דברים נגד מקום הקדש הזה ונגד התורה׃"));
hebsuperobject.biblemap.put("b44_6_14", new hebsuperobject(44,6,14,"כי שמענהו אמר זה ישוע הנצרי יתץ את המקום הזה וישנה את החקים אשר מסר לנו משה׃"));
hebsuperobject.biblemap.put("b44_6_15", new hebsuperobject(44,6,15,"ויסתכלו בו כל הישבים בסנהדרין ויראו את פניו כפני מלאך אלהים׃"));
hebsuperobject.biblemap.put("b44_7_1", new hebsuperobject(44,7,1,"ויאמר הכהן הגדול האמת הוא אשר דברו׃"));
hebsuperobject.biblemap.put("b44_7_2", new hebsuperobject(44,7,2,"ויען ויאמר אנשים אחים ואבות שמעו אלהי הכבוד נראה אל אברהם אבינו בהיותו בארם נהרים לפני שבתו בחרן׃"));
hebsuperobject.biblemap.put("b44_7_3", new hebsuperobject(44,7,3,"ויאמר אליו לך לך מארצך וממולדתך ובא אל הארץ אשר אראך׃"));
hebsuperobject.biblemap.put("b44_7_4", new hebsuperobject(44,7,4,"ויצא מארץ כשדים וישב בחרן ואחרי מות אביו העביר אתו משם אל הארץ הזאת אשר אתם ישבים בה עתה׃"));
hebsuperobject.biblemap.put("b44_7_5", new hebsuperobject(44,7,5,"ולא נתן לו נחלה בה אף לא מדרך כף רגל ויבטח לתתה לאחזה לו ולזרעו אחריו ועוד לא היה לו בן׃"));
hebsuperobject.biblemap.put("b44_7_6", new hebsuperobject(44,7,6,"ויאמר לו אלהים כי גר יהיה זרעו בארץ לא להם ועבדום וענו אתם ארבע מאות שנה׃"));
hebsuperobject.biblemap.put("b44_7_7", new hebsuperobject(44,7,7,"וגם את הגוי אשר יעבדו דן אנכי אמר אלהים ואחרי כן יצאו ויעבדוני במקום הזה׃"));
hebsuperobject.biblemap.put("b44_7_8", new hebsuperobject(44,7,8,"ויתן לו את ברית המילה ובכן הוליד את יצחק וימל אתו ביום השמיני ויצחק הוליד את יעקב ויעקב הוליד את שנים עשר האבות׃"));
hebsuperobject.biblemap.put("b44_7_9", new hebsuperobject(44,7,9,"ויקנאו האבות ביוסף וימכרו אותו מצרימה ואלהים היה עמו׃"));
hebsuperobject.biblemap.put("b44_7_10", new hebsuperobject(44,7,10,"ויצילהו מכל צרותיו ויתן לו חן וחכמה לפני פרעה מלך מצרים וישימהו שליט על מצרים ועל כל ביתו׃"));
hebsuperobject.biblemap.put("b44_7_11", new hebsuperobject(44,7,11,"ויהי רעב על כל ארץ מצרים וכנען וצרה גדולה ואבותינו לא מצאו אכל׃"));
hebsuperobject.biblemap.put("b44_7_12", new hebsuperobject(44,7,12,"וישמע יעקב כי יש שבר במצרים וישלח שמה את אבותינו בפעם הראשונה׃"));
hebsuperobject.biblemap.put("b44_7_13", new hebsuperobject(44,7,13,"ובפעם השנית התודע יוסף אל אחיו ומולדת יוסף נגלתה לפרעה׃"));
hebsuperobject.biblemap.put("b44_7_14", new hebsuperobject(44,7,14,"וישלח יוסף ויקרא ליעקב אביו ולכל משפחתו בשבעים וחמש נפש׃"));
hebsuperobject.biblemap.put("b44_7_15", new hebsuperobject(44,7,15,"וירד יעקב מצרימה וימת הוא ואבותינו׃"));
hebsuperobject.biblemap.put("b44_7_16", new hebsuperobject(44,7,16,"ויובאו שכמה ויושמו בקבר אשר קנה אברהם בכספו מיד בני חמור אבי שכם׃"));
hebsuperobject.biblemap.put("b44_7_17", new hebsuperobject(44,7,17,"ויהי כאשר קרבה עת ההבטחה אשר נשבע אלהים לאברהם ויגדל העם וירב במצרים׃"));
hebsuperobject.biblemap.put("b44_7_18", new hebsuperobject(44,7,18,"עד כי קם מלך אחר אשר לא ידע את יוסף׃"));
hebsuperobject.biblemap.put("b44_7_19", new hebsuperobject(44,7,19,"וזה התחכם למשפחתנו וירע לאבותינו ויצו אתם להשליך את עלליהם אל פני השדה לבלתי החיותם׃"));
hebsuperobject.biblemap.put("b44_7_20", new hebsuperobject(44,7,20,"בעת ההיא נולד משה ויהי טוב לאלהים ויהי אמון בבית אביו שלשה ירחים׃"));
hebsuperobject.biblemap.put("b44_7_21", new hebsuperobject(44,7,21,"וכאשר השלך אספה אתו בת פרעה ותגדלהו לה לבן׃"));
hebsuperobject.biblemap.put("b44_7_22", new hebsuperobject(44,7,22,"וילמד משה בכל חכמת מצרים ויהי גבור בדברים ובמעשים׃"));
hebsuperobject.biblemap.put("b44_7_23", new hebsuperobject(44,7,23,"ובמלאת לו ארבעים שנה עלה על לבו לפקד את אחיו בני ישראל׃"));
hebsuperobject.biblemap.put("b44_7_24", new hebsuperobject(44,7,24,"וירא איש אחד מעשק חנם ויושע לו ויקם נקמת המכה בהכותו את המצרי׃"));
hebsuperobject.biblemap.put("b44_7_25", new hebsuperobject(44,7,25,"ויחשב בלבבו כי יבינו אחיו אשר על ידו יתן האלהים להם תשועה והם לא הבינו׃"));
hebsuperobject.biblemap.put("b44_7_26", new hebsuperobject(44,7,26,"ויהי ביום השני ויבא בתוכם והם נצים ויבקש לעשות שלום ביניהם לאמר אנשים אחים אתם ולמה תעשקו איש את אחיו׃"));
hebsuperobject.biblemap.put("b44_7_27", new hebsuperobject(44,7,27,"והעשק את רעהו הדפו לאמר מי שמך לאיש שר ושפט עלינו׃"));
hebsuperobject.biblemap.put("b44_7_28", new hebsuperobject(44,7,28,"הלהרגני אתה אמר כאשר הרגת אתמל את המצרי׃"));
hebsuperobject.biblemap.put("b44_7_29", new hebsuperobject(44,7,29,"ויברח משה על הדבר הזה ויהי גר בארץ מדין ויולד שם שני בנים׃"));
hebsuperobject.biblemap.put("b44_7_30", new hebsuperobject(44,7,30,"ובמלאת ארבעים שנה נראה אליו מלאך יהוה במדבר הר סיני בלבת אש מתוך הסנה׃"));
hebsuperobject.biblemap.put("b44_7_31", new hebsuperobject(44,7,31,"וירא משה ויתמה על המראה ובסורו לראות ויהי קול יהוה אליו׃"));
hebsuperobject.biblemap.put("b44_7_32", new hebsuperobject(44,7,32,"אנכי אלהי אבותיך אלהי אברהם אלהי יצחק ואלהי יעקב ויחרד משה ויירא מהביט׃"));
hebsuperobject.biblemap.put("b44_7_33", new hebsuperobject(44,7,33,"ויאמר אליו יהוה של נעליך מעל רגליך כי המקום אשר אתה עומד עליו אדמת קדש הוא׃"));
hebsuperobject.biblemap.put("b44_7_34", new hebsuperobject(44,7,34,"ראה ראיתי את עני עמי אשר במצרים ואת נאקתם שמעתי וארד להצילם ועתה לכה ואשלחך מצרימה׃"));
hebsuperobject.biblemap.put("b44_7_35", new hebsuperobject(44,7,35,"הוא משה אשר כחשו בו לאמר מי שמך לשר ושפט אתו שלח האלהים לשר וגאל ביד המלאך הנראה אליו בסנה׃"));
hebsuperobject.biblemap.put("b44_7_36", new hebsuperobject(44,7,36,"והוא הוציאם ויעש אתות ומפתים בארץ מצרים ובים סוף ובמדבר ארבעים שנה׃"));
hebsuperobject.biblemap.put("b44_7_37", new hebsuperobject(44,7,37,"הוא משה אשר אמר אל בני ישראל נביא מקרב אחיכם כמני יקים לכם יהוה אלהיכם אליו תשמעון׃"));
hebsuperobject.biblemap.put("b44_7_38", new hebsuperobject(44,7,38,"הוא אשר היה בקהל במדבר עם המלאך הדבר אליו בהר סיני ועם אבותינו ואשר קבל דברים חיים לתת לנו׃"));
hebsuperobject.biblemap.put("b44_7_39", new hebsuperobject(44,7,39,"הוא אשר אבותינו לא אבו לשמע לו כי אם מאסו אתו ולבבם פנה מצרימה׃"));
hebsuperobject.biblemap.put("b44_7_40", new hebsuperobject(44,7,40,"ויאמרו אל אהרן עשה לנו אלהים אשר ילכו לפנינו כי זה משה אשר העלנו מארץ מצרים לא ידענו מה היה לו׃"));
hebsuperobject.biblemap.put("b44_7_41", new hebsuperobject(44,7,41,"ויעשו עגל בימים ההם ויזבחו זבחים לאליל וישמחו במעשה ידיהם׃"));
hebsuperobject.biblemap.put("b44_7_42", new hebsuperobject(44,7,42,"ויפן מהם האלהים ויתנם לעבד את צבא השמים כאשר כתוב בספר הנביאים הזבחים ומנחה הגשתם לי במדבר ארבעים שנה בית ישראל׃"));
hebsuperobject.biblemap.put("b44_7_43", new hebsuperobject(44,7,43,"ונשאתם את סכת מלכם ואת כוכב אלהיכם רמפן הצלמים אשר עשיתם להשתחות להם והגליתי אתכם מהלאה לבבל׃"));
hebsuperobject.biblemap.put("b44_7_44", new hebsuperobject(44,7,44,"משכן העדות היה לאבותינו במדבר כאשר צוה המדבר אל משה לעשות אתו כתבנית אשר ראה׃"));
hebsuperobject.biblemap.put("b44_7_45", new hebsuperobject(44,7,45,"ואבותינו לקחהו וגם הביאהו אתם הם ויהושע ברשתם את ארצות הגוים אשר גרש אתם אלהים מפני אבותינו עד ימי דוד׃"));
hebsuperobject.biblemap.put("b44_7_46", new hebsuperobject(44,7,46,"והוא מצא חן בעיני אלהים וישאל למצא משכנות לאלהי יעקב׃"));
hebsuperobject.biblemap.put("b44_7_47", new hebsuperobject(44,7,47,"ושלמה בנה לו בית׃"));
hebsuperobject.biblemap.put("b44_7_48", new hebsuperobject(44,7,48,"אבל העליון לא ישכן בהיכלות מעשי ידים כאשר אמר הנביא׃"));
hebsuperobject.biblemap.put("b44_7_49", new hebsuperobject(44,7,49,"השמים כסאי והארץ הדם רגלי אי זה בית אשר תבנו לי אמר יהוה ואי זה מקום מנוחתי׃"));
hebsuperobject.biblemap.put("b44_7_50", new hebsuperobject(44,7,50,"הלא את כל אלה ידי עשתה׃"));
hebsuperobject.biblemap.put("b44_7_51", new hebsuperobject(44,7,51,"קשי ערף וערלי לב ואזנים ממרים הייתם תמיד ברוח הקדש כאבותיכם גם אתם׃"));
hebsuperobject.biblemap.put("b44_7_52", new hebsuperobject(44,7,52,"מי מהנביאים אשר לא רדפוהו אבותיכם אף המיתו את המבשרים מקדם ביאת הצדיק אשר עתה הייתם אתם מוסריו ומרצחיו׃"));
hebsuperobject.biblemap.put("b44_7_53", new hebsuperobject(44,7,53,"אתם אשר קבלתם את התורה על ידי מלאכות המלאכים ולא שמרתם אותה׃"));
hebsuperobject.biblemap.put("b44_7_54", new hebsuperobject(44,7,54,"ויהי כשמעם את אלה התרגזו בלבבם ויחרקו עליו שניהם׃"));
hebsuperobject.biblemap.put("b44_7_55", new hebsuperobject(44,7,55,"והוא מלא רוח הקדש ויבט השמימה וירא את כבוד אלהים ואת ישוע נצב לימין האלהים׃"));
hebsuperobject.biblemap.put("b44_7_56", new hebsuperobject(44,7,56,"ויאמר הנני ראה את השמים נפתחים ואת בן האדם נצב לימין האלהים׃"));
hebsuperobject.biblemap.put("b44_7_57", new hebsuperobject(44,7,57,"ויצעקו בקול גדול ויאטמו את אזניהם ויתנפלו עליו כלם יחד׃"));
hebsuperobject.biblemap.put("b44_7_58", new hebsuperobject(44,7,58,"וידחפהו חוצה לעיר ויסקלו אתו באבנים והעדים פשטו את בגדיהם לרגלי בחור אחד ושמו שאול׃"));
hebsuperobject.biblemap.put("b44_7_59", new hebsuperobject(44,7,59,"ויסקלו את אסטפנוס והוא משוע ואמר אדני ישוע קבל את רוחי׃"));
hebsuperobject.biblemap.put("b44_7_60", new hebsuperobject(44,7,60,"ויכרע על ברכיו ויצעק בקול גדול יהוה אל תשמר להם את החטא הזה ויהי אחרי דברו כדבר הזה ויישן׃"));
hebsuperobject.biblemap.put("b44_8_1", new hebsuperobject(44,8,1,"ושאול גם הוא היה רצה בהרגתו ותהי ביום ההוא רדיפה גדולה על הקהלה אשר בירושלים ויפצו כלם בערי יהודה ושמרון לבד מן השליחים׃"));
hebsuperobject.biblemap.put("b44_8_2", new hebsuperobject(44,8,2,"וישאו אנשים חסידים את אסטפנוס ויקברהו ויספדו עליו מספד גדול׃"));
hebsuperobject.biblemap.put("b44_8_3", new hebsuperobject(44,8,3,"ושאול החריב את הקהלה וישוטט בבתים ויסחב משם אנשים ונשים ויסגירם לכלא׃"));
hebsuperobject.biblemap.put("b44_8_4", new hebsuperobject(44,8,4,"והנפוצים עברו בארץ ויבשרו את הדבר׃"));
hebsuperobject.biblemap.put("b44_8_5", new hebsuperobject(44,8,5,"ופילפוס ירד אל עיר שמרון ויכרז להם את המשיח׃"));
hebsuperobject.biblemap.put("b44_8_6", new hebsuperobject(44,8,6,"ויקשב המון העם בלב אחד אל אמרי פילפוס בשמעם ובראותם את האתות אשר עשה׃"));
hebsuperobject.biblemap.put("b44_8_7", new hebsuperobject(44,8,7,"כי רבים היו אחוזי רוחות הטמאה והרוחות יצאו מהם צעקות בקול גדול ורבים נכי אברים ופסחים וירפאו׃"));
hebsuperobject.biblemap.put("b44_8_8", new hebsuperobject(44,8,8,"ותהי שמחה גדולה בעיר ההיא׃"));
hebsuperobject.biblemap.put("b44_8_9", new hebsuperobject(44,8,9,"ואיש אחד ושמו שמעון היה מלפנים בעיר מכשף ומשמים את עם שמרון באמרו על נפשו כי גדול הוא׃"));
hebsuperobject.biblemap.put("b44_8_10", new hebsuperobject(44,8,10,"ויקשיבו אליו מקטנם ועד גדולם לאמר זה הוא גבורת האלהים הגדולה׃"));
hebsuperobject.biblemap.put("b44_8_11", new hebsuperobject(44,8,11,"ויקשיבו אליו על היותו משמים אותם בכשפיו ימים רבים׃"));
hebsuperobject.biblemap.put("b44_8_12", new hebsuperobject(44,8,12,"ויהי כאשר האמינו לפילפוס בבשרו את מלכות האלהים ואת שם ישוע המשיח ויטבלו אנשים ונשים׃"));
hebsuperobject.biblemap.put("b44_8_13", new hebsuperobject(44,8,13,"ויאמן שמעון גם הוא ויטבל וידבק בפילפוס וירא את האתות והמפתים הגדלים אשר נעשו וישתומם׃"));
hebsuperobject.biblemap.put("b44_8_14", new hebsuperobject(44,8,14,"וישמעו השליחים אשר בירושלים כי קבלה שמרון את דבר האלהים וישלחו אליהם את פטרוס ואת יוחנן׃"));
hebsuperobject.biblemap.put("b44_8_15", new hebsuperobject(44,8,15,"וירדו שמה ויתפללו בעדם אשר יקבלו את רוח הקדש׃"));
hebsuperobject.biblemap.put("b44_8_16", new hebsuperobject(44,8,16,"כי הרוח לא צלחה עד עתה על אחד מהם והם רק נטבלים בשם האדון ישוע׃"));
hebsuperobject.biblemap.put("b44_8_17", new hebsuperobject(44,8,17,"ויסמכו את ידיהם עליהם ויקבלו את רוח הקדש׃"));
hebsuperobject.biblemap.put("b44_8_18", new hebsuperobject(44,8,18,"ויהי בראות שמעון כי בסמיכות ידי השליחים נתן רוח הקדש ויבא לפניהם כסף׃"));
hebsuperobject.biblemap.put("b44_8_19", new hebsuperobject(44,8,19,"ויאמר תנו נא גם לי את היכלת הזאת אשר יקבל את רוח הקדש כל אשר אשים עליו את ידי׃"));
hebsuperobject.biblemap.put("b44_8_20", new hebsuperobject(44,8,20,"ויאמר אליו פטרוס כספך יהי אתך לאבדון יען חשבת לקנות במחיר את מתת האלהים׃"));
hebsuperobject.biblemap.put("b44_8_21", new hebsuperobject(44,8,21,"אין לך חלק וגורל בדבר הזה כי לבבך איננו ישר לפני האלהים׃"));
hebsuperobject.biblemap.put("b44_8_22", new hebsuperobject(44,8,22,"ועתה שוב מרעתך זאת והתחנן אל האלהים אולי תסלח לך מזמת לבבך׃"));
hebsuperobject.biblemap.put("b44_8_23", new hebsuperobject(44,8,23,"כי ראה אנכי כי באת לידי מרורת רוש וחרצבות רשע׃"));
hebsuperobject.biblemap.put("b44_8_24", new hebsuperobject(44,8,24,"ויען שמעון ויאמר העתירו אתם בעדי אל יהוה לבלתי בוא עלי דבר מכל אשר אמריתם׃"));
hebsuperobject.biblemap.put("b44_8_25", new hebsuperobject(44,8,25,"והמה אחרי אשר העידה ודברו את דבר יהוה שבו ירושלים ויבשרו את הבשורה בכפרים רבים אשר לשמרונים׃"));
hebsuperobject.biblemap.put("b44_8_26", new hebsuperobject(44,8,26,"וידבר מלאך יהוה אל פילפוס לאמר קום לך הנגבה על הדרך הירדת מירושלים עזתה והיא חרבה׃"));
hebsuperobject.biblemap.put("b44_8_27", new hebsuperobject(44,8,27,"ויקם וילך והנה איש כושי והוא סריס ושליט לקנדק מלכת כוש וממנה על כל גנזיה אשר עלה אל ירושלים להשתחות׃"));
hebsuperobject.biblemap.put("b44_8_28", new hebsuperobject(44,8,28,"ויהי בשובו והוא ישב על מרכבתו וקרא בספר ישעיה הנביא׃"));
hebsuperobject.biblemap.put("b44_8_29", new hebsuperobject(44,8,29,"ויאמר הרוח אל פילפוס גשה והלוה על המרכבה הזאת׃"));
hebsuperobject.biblemap.put("b44_8_30", new hebsuperobject(44,8,30,"וירץ פילפוס אליה וישמע אתו קרא בספר ישעיה הנביא ויאמר הגם תבין את אשר אתה קורא׃"));
hebsuperobject.biblemap.put("b44_8_31", new hebsuperobject(44,8,31,"ויאמר ואיככה אוכל אם אין איש אשר יורני ויבקש מאת פילפוס לעלות ולשבת אצלו׃"));
hebsuperobject.biblemap.put("b44_8_32", new hebsuperobject(44,8,32,"וענין הכתוב אשר קרא זה הוא כשה לטבח יובל וכרחל לפני גוזזיה נאלמה ולא יפתח פיו׃"));
hebsuperobject.biblemap.put("b44_8_33", new hebsuperobject(44,8,33,"בעצר משפטו לקח ואת דורו מי ישוחח כי נגזרו מארץ חייו׃"));
hebsuperobject.biblemap.put("b44_8_34", new hebsuperobject(44,8,34,"ויען הסריס ויאמר אל פילפוס אשאלה ממך על מי הנביא מדבר את זאת על נפשו או על איש אחר׃"));
hebsuperobject.biblemap.put("b44_8_35", new hebsuperobject(44,8,35,"ויפתח פילפוס את פיו ויחל מן הכתוב הזה ויבשר אותו את ישוע׃"));
hebsuperobject.biblemap.put("b44_8_36", new hebsuperobject(44,8,36,"ויהי בעברם בדרך ויבאו אל מקום מים ויאמר הסריס הנה מים מה ימנעני מהטבל׃"));
hebsuperobject.biblemap.put("b44_8_37", new hebsuperobject(44,8,37,"ויאמר פילפוס אם מאמין אתה בכל לבבך מתר לך ויען ויאמר אני מאמין כי ישוע המשיח בן האלהים הוא׃"));
hebsuperobject.biblemap.put("b44_8_38", new hebsuperobject(44,8,38,"ויצו להעמיד את המרכבה וירדו שניהם אל תוך המים פילפוס והסריס ויטבל אותו׃"));
hebsuperobject.biblemap.put("b44_8_39", new hebsuperobject(44,8,39,"ויהי כי עלו מן המים וישא רוח יהוה את פילפוס ולא יסף הסריס לראותו כי הלך לדרכו שמח׃"));
hebsuperobject.biblemap.put("b44_8_40", new hebsuperobject(44,8,40,"ופילפוס נמצא באשדוד ויעבר ויבשר בכל הערים עד באו לקסרין׃"));
hebsuperobject.biblemap.put("b44_9_1", new hebsuperobject(44,9,1,"ושאול עודנו יפח זעם ורצח על תלמידי האדון ויבא אל הכהן הגדול׃"));
hebsuperobject.biblemap.put("b44_9_2", new hebsuperobject(44,9,2,"וישאל מאתו מכתבים לדמשק אל בתי הכנסיות למען אשר יאסר את אשר ימצא בדרך ההיא אנשים או נשים ויביאם ירושלים׃"));
hebsuperobject.biblemap.put("b44_9_3", new hebsuperobject(44,9,3,"ויהי הוא הלך וקרב לדמשק והנה פתאם נגה עליו מסביב אור מן השמים׃"));
hebsuperobject.biblemap.put("b44_9_4", new hebsuperobject(44,9,4,"ויפל ארצה וישמע קול מדבר אליו שאול שאול למה תרדפני׃"));
hebsuperobject.biblemap.put("b44_9_5", new hebsuperobject(44,9,5,"ויאמר מי אתה אדני ויאמר האדון אנכי ישוע אשר אתה רודף קשה לך לבעט בדרבנות׃"));
hebsuperobject.biblemap.put("b44_9_6", new hebsuperobject(44,9,6,"והוא חרד ונבעת ויאמר אדני מה תחפץ ואעשה ויען האדון קום לך העירה ויאמר לך את אשר עליך לעשות׃"));
hebsuperobject.biblemap.put("b44_9_7", new hebsuperobject(44,9,7,"והאנשים אשר הלכו אתו עמדו נאלמים כי שמעו את הקול ואיש לא הביטו׃"));
hebsuperobject.biblemap.put("b44_9_8", new hebsuperobject(44,9,8,"ויקם שאול מן הארץ ובפתחו את עיניו לא ראה איש ויחזיקו בידו ויוליכהו לדמשק׃"));
hebsuperobject.biblemap.put("b44_9_9", new hebsuperobject(44,9,9,"ויחשכו עיניו מראות שלשת ימים ולא אכל ולא שתה׃"));
hebsuperobject.biblemap.put("b44_9_10", new hebsuperobject(44,9,10,"ותלמיד אחד היה בדמשק חנניה שמו ויאמר אליו האדון במחזה חנניה ויאמר הנני אדני׃"));
hebsuperobject.biblemap.put("b44_9_11", new hebsuperobject(44,9,11,"ויאמר אליו האדון קום לך אל הרחוב הנקרא הישר ושאל בבית יהודה לאיש טרסי ושמו שאול כי הנה הוא מתפלל׃"));
hebsuperobject.biblemap.put("b44_9_12", new hebsuperobject(44,9,12,"וירא במחזה והנה איש ושמו חנניה בא החדרה ושם עליו את ידו למען ישוב ויראה׃"));
hebsuperobject.biblemap.put("b44_9_13", new hebsuperobject(44,9,13,"ויען חנניה ויאמר אדני שמעתי רבים מספרים על האיש הזה כמה רעות עשה לקדושיך בירושלים׃"));
hebsuperobject.biblemap.put("b44_9_14", new hebsuperobject(44,9,14,"וגם פה רשיון יש לו מאת ראשי הכהנים לאסר את כל הקראים בשמך׃"));
hebsuperobject.biblemap.put("b44_9_15", new hebsuperobject(44,9,15,"ויאמר אליו האדון לך כי כלי חפץ הוא לי לשאת את שמי לפני גוים ומלכים ולפני בני ישראל׃"));
hebsuperobject.biblemap.put("b44_9_16", new hebsuperobject(44,9,16,"כי אני אראהו כמה יש לו לסבל למען שמי׃"));
hebsuperobject.biblemap.put("b44_9_17", new hebsuperobject(44,9,17,"וילך חנניה ויבא הביתה וישם את ידיו עליו ויאמר שאול אחי האדון ישוע הנראה אליך בדרך אשר באת בה שלחני למען תשוב ותראה ותמלא רוח הקדש׃"));
hebsuperobject.biblemap.put("b44_9_18", new hebsuperobject(44,9,18,"וכרגע נפלו מעל עיניו כמו קשקשים וישב פתאם לראות ויקם ויטבל׃"));
hebsuperobject.biblemap.put("b44_9_19", new hebsuperobject(44,9,19,"ויאכל לחם ויחזק וישב שאול ימים אחדים עם התלמידים אשר בדמשק׃"));
hebsuperobject.biblemap.put("b44_9_20", new hebsuperobject(44,9,20,"וימהר ויקרא בבתי הכנסיות את המשיח לאמר כי הוא הוא בן האלהים׃"));
hebsuperobject.biblemap.put("b44_9_21", new hebsuperobject(44,9,21,"וישתוממו כל השמעים ויאמרו הלא זה הוא אשר האביד בירושלים את קראי השם הזה ולמען זאת בא הנה להביאם אסורים לפני ראשי הכהנים׃"));
hebsuperobject.biblemap.put("b44_9_22", new hebsuperobject(44,9,22,"ושאול היה הולך וחזק ויהם את היהודים ישבי דמשק בהוכיחו כי זה הוא המשיח׃"));
hebsuperobject.biblemap.put("b44_9_23", new hebsuperobject(44,9,23,"ויהי כי ארכו לו שם הימים ויועצו היהודים יחדו להמיתו׃"));
hebsuperobject.biblemap.put("b44_9_24", new hebsuperobject(44,9,24,"ויודע לשאול ארבם והמה שמרו את השערים יומם ולילה למען ימיתהו׃"));
hebsuperobject.biblemap.put("b44_9_25", new hebsuperobject(44,9,25,"ויקחו אתו התלמידים לילה ויורידהו בסל בעד החומה׃"));
hebsuperobject.biblemap.put("b44_9_26", new hebsuperobject(44,9,26,"ויהי כבוא שאול ירושלימה ויבקש להלות אל התלמידים וייראו כלם מפניו ולא האמינו כי תלמיד הוא׃"));
hebsuperobject.biblemap.put("b44_9_27", new hebsuperobject(44,9,27,"ויקח אתו בר נבא ויביאהו אל השליחים ויספר להם את אשר ראה בדרך את האדון וכי הוא דבר אליו ואיך בדמשק השמיע בבטחון את שם ישוע׃"));
hebsuperobject.biblemap.put("b44_9_28", new hebsuperobject(44,9,28,"ויהי אתם יוצא ובא בירושלים׃"));
hebsuperobject.biblemap.put("b44_9_29", new hebsuperobject(44,9,29,"ויקרא בבטחון בשם האדון ישוע וידבר ויתוכח גם עם היהודים היונים והם זממו להמיתו׃"));
hebsuperobject.biblemap.put("b44_9_30", new hebsuperobject(44,9,30,"וישמעו האחים ויורידו אתו לקסרין וישלחהו אל טרסוס׃"));
hebsuperobject.biblemap.put("b44_9_31", new hebsuperobject(44,9,31,"ויהי שלום לקהלות בכל מקמות יהודה והגליל ושמרון ותבנינה ותתהלכנה ביראת האדון ותרבינה בנחמת רוח הקדש׃"));
hebsuperobject.biblemap.put("b44_9_32", new hebsuperobject(44,9,32,"ויהי בסבב פטרוס בכל המקמות וירד גם אל הקדושים אשר ישבו בלד׃"));
hebsuperobject.biblemap.put("b44_9_33", new hebsuperobject(44,9,33,"וימצא שם איש שמו אניס והוא שכב על משכבו זה שמנה שנים והוא נכה אברים׃"));
hebsuperobject.biblemap.put("b44_9_34", new hebsuperobject(44,9,34,"ויאמר אליו פטרוס אניס רפאך ישוע המשיח קום הצע לך אתה ויקם פתאם׃"));
hebsuperobject.biblemap.put("b44_9_35", new hebsuperobject(44,9,35,"ויראו אותו כל ישבי לד והשרון ויפנו אל האדון׃"));
hebsuperobject.biblemap.put("b44_9_36", new hebsuperobject(44,9,36,"ותלמידה ביפו ושמה טביתא תרגומו צביה והיא מלאה מעשים טובים וצדקות אשר עשתה׃"));
hebsuperobject.biblemap.put("b44_9_37", new hebsuperobject(44,9,37,"ויהי בימים ההם ותחלה ותמת וירחצו אתה וישימוה בעליה׃"));
hebsuperobject.biblemap.put("b44_9_38", new hebsuperobject(44,9,38,"ולד קרובה היא ליפו וישמעו התלמידים כי פטרוס שם וישלחו אליו שני אנשים ויפצרו בו לבלתי העצל לעבר אליהם׃"));
hebsuperobject.biblemap.put("b44_9_39", new hebsuperobject(44,9,39,"ויקם פטרוס וילך אתם ובבאו העלהו אל העליה ותנשנה אליו כל האלמנות בוכיות ומראות לו את הכתנת ואת הבגדים אשר עשתה צביה בעודה עמהן׃"));
hebsuperobject.biblemap.put("b44_9_40", new hebsuperobject(44,9,40,"ויוצא פטרוס את כלם החוצה ויכרע על ברכיו ויתפלל ויפן אל גויתה ויאמר טביתא קומי ותפתח את עיניה ותרא את פטרוס ותתעודד׃"));
hebsuperobject.biblemap.put("b44_9_41", new hebsuperobject(44,9,41,"וישלח ידו ויקם אתה ויקרא את הקדושים ואת האלמנות ויעמד אתה חיה לפניהם׃"));
hebsuperobject.biblemap.put("b44_9_42", new hebsuperobject(44,9,42,"ויודע הדבר בכל יפו ויאמינו רבים באדון׃"));
hebsuperobject.biblemap.put("b44_9_43", new hebsuperobject(44,9,43,"ויואל לשבת ביפו ימים רבים עם בורסי אחד ושמו שמעון׃"));
hebsuperobject.biblemap.put("b44_10_1", new hebsuperobject(44,10,1,"ואיש היה בקסרין ושמו קרניליוס שר מאה מן הגדוד הנקרא האיטלקי׃"));
hebsuperobject.biblemap.put("b44_10_2", new hebsuperobject(44,10,2,"והוא חסיד וירא אלהים עם כל בני ביתו ועשה צדקות הרבה לעם ומתפלל תמיד לאלהים׃"));
hebsuperobject.biblemap.put("b44_10_3", new hebsuperobject(44,10,3,"ומחזה נראה אליו כשעה התשיעית ליום וירא מלאך אלהים בא אליו פנימה ואמר קרניליוס׃"));
hebsuperobject.biblemap.put("b44_10_4", new hebsuperobject(44,10,4,"ויבט אליו ויירא ויאמר מה זה אדני ויאמר אליו תפלותיך וצדקותיך עלו לזכרון לפני האלהים׃"));
hebsuperobject.biblemap.put("b44_10_5", new hebsuperobject(44,10,5,"ועתה שלח לך אנשים אל יפו והבא אליך את שמעון המכנה פטרוס׃"));
hebsuperobject.biblemap.put("b44_10_6", new hebsuperobject(44,10,6,"הוא מתגורר עם בורסי אחד שמעון שמו אשר ביתו על יד הים הוא יאמר לך את אשר עליך לעשות׃"));
hebsuperobject.biblemap.put("b44_10_7", new hebsuperobject(44,10,7,"וילך לו המלאך הדבר אל קרניליוס ויקרא אל שנים מעבדי ביתו ואל איש מלחמה אחד ירא אלהים מן העמדים תמיד לפניו לשרתו׃"));
hebsuperobject.biblemap.put("b44_10_8", new hebsuperobject(44,10,8,"ויספר להם את כל הדברים וישלחם אל יפו׃"));
hebsuperobject.biblemap.put("b44_10_9", new hebsuperobject(44,10,9,"ויהי ממחרת והמה הלכים בדרך וקרבים לעיר ויעל פטרוס על הגג להתפלל כשעה הששית׃"));
hebsuperobject.biblemap.put("b44_10_10", new hebsuperobject(44,10,10,"והוא רעב ויתאו לטעם לחם ובהכינם לו נפלה תרדמה עליו׃"));
hebsuperobject.biblemap.put("b44_10_11", new hebsuperobject(44,10,11,"וירא את השמים נפתחים והנה כלי ירד אליו כדמות מטפחת בד גדולה ויורד בארבע כנפותיו על הארץ׃"));
hebsuperobject.biblemap.put("b44_10_12", new hebsuperobject(44,10,12,"ובתוכו מכל בהמת הארץ וחיה ורמש ועוף השמים׃"));
hebsuperobject.biblemap.put("b44_10_13", new hebsuperobject(44,10,13,"ויהי קול אליו לאמר קום פטרוס זבח ואכל׃"));
hebsuperobject.biblemap.put("b44_10_14", new hebsuperobject(44,10,14,"ויאמר פטרוס חלילה לי אדני כי מעולם לא אכלתי כל פגול וטמא׃"));
hebsuperobject.biblemap.put("b44_10_15", new hebsuperobject(44,10,15,"ויהי עוד קול אליו פעם שנית לאמר את אשר טהר האלהים אתה אל תטמאנו׃"));
hebsuperobject.biblemap.put("b44_10_16", new hebsuperobject(44,10,16,"וכן היה שלש פעמים והכלי שב והעלה השמימה׃"));
hebsuperobject.biblemap.put("b44_10_17", new hebsuperobject(44,10,17,"ויהי בהתפעם רוח פטרוס על המראה אשר ראה והנה האנשים השלוחים מאת קרניליוס שאלו לבית שמעון ויעמדו על הפתח׃"));
hebsuperobject.biblemap.put("b44_10_18", new hebsuperobject(44,10,18,"ויקראו וידרשו היש מתגורר שם שמעון המכנה פטרוס׃"));
hebsuperobject.biblemap.put("b44_10_19", new hebsuperobject(44,10,19,"ופטרוס עודנו חשב עם לבבו על המראה והרוח אמר אליו הנה שלשה אנשים מבקשים אותך׃"));
hebsuperobject.biblemap.put("b44_10_20", new hebsuperobject(44,10,20,"לכן קום רד ולכה נא אתם ואל תתמהמה כי אנכי שלחתים׃"));
hebsuperobject.biblemap.put("b44_10_21", new hebsuperobject(44,10,21,"וירד פטרוס אל האנשים הנשלחים אליו מאת קרניליוס ויאמר אנכי האיש אשר אתם מבקשים למה זה באתם הנה׃"));
hebsuperobject.biblemap.put("b44_10_22", new hebsuperobject(44,10,22,"ויאמרו קרניליוס שר מאה איש צדיק וירא אלהים ולו שם טוב בכל עם היהודים צוה על פי מלאך קדוש לקרא לך אל ביתו ולשמע דברים מפיך׃"));
hebsuperobject.biblemap.put("b44_10_23", new hebsuperobject(44,10,23,"ויקרא אתם אליו ויאספם הביתה ויהי ממחרת ויצא פטרוס אתם ומקצת האחים אשר ביפו הלכו עמו׃"));
hebsuperobject.biblemap.put("b44_10_24", new hebsuperobject(44,10,24,"ולמחרתו באו אל קסרין וקרניליוס מחכה להם ועמו בני משפחתו וקרוביו ומידעיו הנקהלים אליו׃"));
hebsuperobject.biblemap.put("b44_10_25", new hebsuperobject(44,10,25,"ויהי כבוא פטרוס ויצא קרניליוס לקראתו ויפל לרגליו וישתחו׃"));
hebsuperobject.biblemap.put("b44_10_26", new hebsuperobject(44,10,26,"ויקם אותו פטרוס ויאמר קום כי גם אני אנוש אנכי׃"));
hebsuperobject.biblemap.put("b44_10_27", new hebsuperobject(44,10,27,"וידבר אתו ויבא הביתה וימצא רבים נאספים שמה׃"));
hebsuperobject.biblemap.put("b44_10_28", new hebsuperobject(44,10,28,"ויאמר אליהם אתם ידעתם כי אסור הוא לאיש יהודי להלות ולקרב אל נכרי ואתי הורה אלהים לבלתי אמר חל או טמא על כל אדם׃"));
hebsuperobject.biblemap.put("b44_10_29", new hebsuperobject(44,10,29,"ובעבור זאת כאשר נקראתי לא נמנעתי מהלך ועתה אשאלכם מדוע קראתם לי׃"));
hebsuperobject.biblemap.put("b44_10_30", new hebsuperobject(44,10,30,"ויאמר קרניליוס זה ארבעה ימים הייתי צם עד השעה הזאת ובשעה התשיעית התפללתי בתוך ביתי והנה איש נצב לפני בלבוש זהר׃"));
hebsuperobject.biblemap.put("b44_10_31", new hebsuperobject(44,10,31,"ויאמר קרניליוס נשמעה תפלתך וצדקותיך היו לזכרון לפני האלהים׃"));
hebsuperobject.biblemap.put("b44_10_32", new hebsuperobject(44,10,32,"ועתה שלח אל יפו וקרא אליך את שמעון המכנה פטרוס מתגורר הוא בבית שמעון הבורסי על הים אשר בבואו ידבר לך׃"));
hebsuperobject.biblemap.put("b44_10_33", new hebsuperobject(44,10,33,"ואמהר ואשלח אליך ואתה היטבת לעשות אשר באת אלי והננו כלנו פה לפני האלהים לשמע את כל אשר צוית מאת יהוה׃"));
hebsuperobject.biblemap.put("b44_10_34", new hebsuperobject(44,10,34,"ויפתח פטרוס את פיו ויאמר עתה ידעתי באמת כי האלהים איננו נשא פנים׃"));
hebsuperobject.biblemap.put("b44_10_35", new hebsuperobject(44,10,35,"כי אם בכל עם ועם הירא אותו ועשה צדק רצוי הוא לפניו׃"));
hebsuperobject.biblemap.put("b44_10_36", new hebsuperobject(44,10,36,"וישלח את דברו לבני ישראל ויבשר את השלום על ידי ישוע המשיח והוא אדון הכל׃"));
hebsuperobject.biblemap.put("b44_10_37", new hebsuperobject(44,10,37,"אתם ידעתם את הדבר הנעשה בכל יהודה החל מן הגליל אחרי הטבילה אשר קרא אותה יוחנן׃"));
hebsuperobject.biblemap.put("b44_10_38", new hebsuperobject(44,10,38,"את אשר משח האלהים את ישוע הנצרי ברוח הקדש ובגבורה ויעבר בארץ עשה חסד ורפא את כל הנכבשים תחת יד השטן כי האלהים היה עמו׃"));
hebsuperobject.biblemap.put("b44_10_39", new hebsuperobject(44,10,39,"ואנחנו עדים על כל אשר עשה בארץ היהודים ובירושלים ואשר הרגהו בהוקיעם אתו על העץ׃"));
hebsuperobject.biblemap.put("b44_10_40", new hebsuperobject(44,10,40,"אותו הקים האלהים ביום השלישי ויתנהו להראות בגלוי׃"));
hebsuperobject.biblemap.put("b44_10_41", new hebsuperobject(44,10,41,"לא לכל העם כי אם לנו העדים אשר האלהים בחר בהם מראש אשר אכלנו ושתינו אתו אחרי קומו מן המתים׃"));
hebsuperobject.biblemap.put("b44_10_42", new hebsuperobject(44,10,42,"ויצו אתנו להשמיע לעם ולהעיד כי אתו שם האלהים לשופט החיים והמתים׃"));
hebsuperobject.biblemap.put("b44_10_43", new hebsuperobject(44,10,43,"ועליו כל הנביאים מעידים כי יקבלו סליחת החטאים בשמו כל המאמינים בו׃"));
hebsuperobject.biblemap.put("b44_10_44", new hebsuperobject(44,10,44,"עוד פטרוס מדבר הדברים האלה ורוח הקדש צלחה על כל השמעים את הדבר׃"));
hebsuperobject.biblemap.put("b44_10_45", new hebsuperobject(44,10,45,"והמאמינים בני המילה אשר באו את פטרוס השתוממו כי מתנת רוח הקדש נשפכה גם על הגוים׃"));
hebsuperobject.biblemap.put("b44_10_46", new hebsuperobject(44,10,46,"כי שמעו אתם ממללים בלשנות ומגדלים את האלהים׃"));
hebsuperobject.biblemap.put("b44_10_47", new hebsuperobject(44,10,47,"ויען פטרוס ויאמר היוכל איש למנע את המים מטבל את אלה אשר קבלו את רוח הקדש גם הם כמנו׃"));
hebsuperobject.biblemap.put("b44_10_48", new hebsuperobject(44,10,48,"ויצו לטבל אתם בשם האדון ויבקשו ממנו לשבת אתם ימים אחדים׃"));
hebsuperobject.biblemap.put("b44_11_1", new hebsuperobject(44,11,1,"וישמעו השליחים והאחים אשר ביהודה כי קבלו גם הגוים את דבר האלהים׃"));
hebsuperobject.biblemap.put("b44_11_2", new hebsuperobject(44,11,2,"ויהי כאשר עלה פטרוס ירושלים ויריבו עמו בני המילה לאמר׃"));
hebsuperobject.biblemap.put("b44_11_3", new hebsuperobject(44,11,3,"אל אנשים אשר להם ערלה באת ותאכל אתם לחם׃"));
hebsuperobject.biblemap.put("b44_11_4", new hebsuperobject(44,11,4,"ויחל פטרוס ויספר להם את כל אשר קרהו לאמר׃"));
hebsuperobject.biblemap.put("b44_11_5", new hebsuperobject(44,11,5,"מתפלל הייתי בעיר יפו וארדם וארא מראה והנה כלי כדמות מטפחת בד גדולה יורד מן השמים ותורד בארבע כנפותיה ותבא עדי׃"));
hebsuperobject.biblemap.put("b44_11_6", new hebsuperobject(44,11,6,"והסתכלתי בה ואבין וארא את בהמת הארץ ואת החיה ורמש האדמה ועוף השמים׃"));
hebsuperobject.biblemap.put("b44_11_7", new hebsuperobject(44,11,7,"ואשמע גם קול האמר אלי קום פטרוס זבח ואכל׃"));
hebsuperobject.biblemap.put("b44_11_8", new hebsuperobject(44,11,8,"ואמר חלילה לי אדני כי כל פגול וטמא לא בא בפי מעולם׃"));
hebsuperobject.biblemap.put("b44_11_9", new hebsuperobject(44,11,9,"ויענני הקול שנית מן השמים ויאמר את אשר טהר האלהים אתה אל תטמאנו׃"));
hebsuperobject.biblemap.put("b44_11_10", new hebsuperobject(44,11,10,"וכן היה שלש פעמים והכל שב והעלה השמימה׃"));
hebsuperobject.biblemap.put("b44_11_11", new hebsuperobject(44,11,11,"והנה כרגע שלשה אנשים עמדים על פתח הבית אשר אנכי שם והמה שלוחים אלי מקסרין׃"));
hebsuperobject.biblemap.put("b44_11_12", new hebsuperobject(44,11,12,"ויאמר אלי הרוח ללכת אתם ולבלתי התמהמה וילכו אתי גם ששת האחים האלה ונבוא אל בית האיש׃"));
hebsuperobject.biblemap.put("b44_11_13", new hebsuperobject(44,11,13,"ויגד לנו את אשר ראה המלאך נצב בביתו ואמר אליו שלח אל יפו אנשים וקרא אליך את שמעון המכנה פטרוס׃"));
hebsuperobject.biblemap.put("b44_11_14", new hebsuperobject(44,11,14,"והוא ידבר אליך דברים אשר תושע בהם אתה וכל ביתך׃"));
hebsuperobject.biblemap.put("b44_11_15", new hebsuperobject(44,11,15,"וכאשר החלותי לדבר צלחה עליהם רוח הקדש כאשר צלחה עלינו בתחלה׃"));
hebsuperobject.biblemap.put("b44_11_16", new hebsuperobject(44,11,16,"ואזכר את דבר האדון אשר אמר יוחנן הטביל במים ואתם תטבלו ברוח הקדש׃"));
hebsuperobject.biblemap.put("b44_11_17", new hebsuperobject(44,11,17,"ועתה אם מתנה אחת נתן האלהים להם ולנו המאמינים באדון ישוע המשיח מי אנכי כי אעצר לאלהים׃"));
hebsuperobject.biblemap.put("b44_11_18", new hebsuperobject(44,11,18,"ויהי כשמעם זאת ויחרישו ויהללו את האלהים ויאמרו אכן גם לגוים נתן האלהים התשובה לחיים׃"));
hebsuperobject.biblemap.put("b44_11_19", new hebsuperobject(44,11,19,"והנפוצים מפני הצרה אשר היתה על אודת אסטפנוס הלכו ובאו עד פינוקיא וקפרוס ואנטיוכיא ולא דברו את הדבר כי אם אל היהודים לבדם׃"));
hebsuperobject.biblemap.put("b44_11_20", new hebsuperobject(44,11,20,"ויהי בתוכם אנשי קפרוס וקוריני אשר באו אל אנטיוכיא וידברו גם אל היונים ויבשרו אתם את האדון ישוע׃"));
hebsuperobject.biblemap.put("b44_11_21", new hebsuperobject(44,11,21,"ותהי עמהם יד יהוה ומספר רב האמינו וישובו אל האדון׃"));
hebsuperobject.biblemap.put("b44_11_22", new hebsuperobject(44,11,22,"וישמע הדבר באזני הקהלה אשר בירושלים וישלחו את בר נבא אל אנטיוכיא׃"));
hebsuperobject.biblemap.put("b44_11_23", new hebsuperobject(44,11,23,"ויבא שמה וישמח כראתו את חסד האלהים ויזהר את כלם לדבקה באדון בלב נכון׃"));
hebsuperobject.biblemap.put("b44_11_24", new hebsuperobject(44,11,24,"כי איש טוב היה ומלא רוח הקדש ואמונה ויאספו לאדון עם רב׃"));
hebsuperobject.biblemap.put("b44_11_25", new hebsuperobject(44,11,25,"וילך בר נבא משם אל טרסוס לבקש את שאול וימצא אתו ויביאהו אל אנטיוכיא׃"));
hebsuperobject.biblemap.put("b44_11_26", new hebsuperobject(44,11,26,"ויהיו ישבים יחד בקהלה שנה תמימה ומלמדים עם רב אז הוחל באנטיוכיא לקרא את התלמידים בשם משיחיים׃"));
hebsuperobject.biblemap.put("b44_11_27", new hebsuperobject(44,11,27,"ויהי בימים ההם ויבאו נביאים מירושלים אל אנטיוכיא׃"));
hebsuperobject.biblemap.put("b44_11_28", new hebsuperobject(44,11,28,"ויקם אחד מהם ושמו אגבוס ויגד על פי הרוח כי רעב גדול יבוא על כל ישבי תבל ויהי כן בימי קלודיוס׃"));
hebsuperobject.biblemap.put("b44_11_29", new hebsuperobject(44,11,29,"ויועצו התלמידים לשלח איש איש מאשר תשיג ידו לעזרת האחים הישבים ביהודה׃"));
hebsuperobject.biblemap.put("b44_11_30", new hebsuperobject(44,11,30,"וכן גם עשו וישלחו אל הזקנים על ידי בר נבא ושאול׃"));
hebsuperobject.biblemap.put("b44_12_1", new hebsuperobject(44,12,1,"בעת ההיא שלח המלך הורדוס את ידו להרע לאנשים מן הקהל׃"));
hebsuperobject.biblemap.put("b44_12_2", new hebsuperobject(44,12,2,"וימת את יעקב אחי יוחנן בחרב׃"));
hebsuperobject.biblemap.put("b44_12_3", new hebsuperobject(44,12,3,"וירא כי טוב הדבר בעיני היהודים ויוסף לתפש גם את פטרוס והימים ימי חג המצות׃"));
hebsuperobject.biblemap.put("b44_12_4", new hebsuperobject(44,12,4,"ויאחז אתו ויתנהו במשמר וימסרהו לארבע מחלקות של ארבעה אנשי צבא לשמרו כי אמר להעלותו אחרי הפסח לפני העם׃"));
hebsuperobject.biblemap.put("b44_12_5", new hebsuperobject(44,12,5,"פטרוס היה עצור במשמר והקהלה העתירה בעדו בחזקה אל האלהים׃"));
hebsuperobject.biblemap.put("b44_12_6", new hebsuperobject(44,12,6,"ויהי בלילה ההוא אשר אמר הורדוס להביאו מחר לדין ויישן פטרוס בין שני אנשי צבא והוא אסור בשנים זקים ושמרי הפתח שמרים את המשמר׃"));
hebsuperobject.biblemap.put("b44_12_7", new hebsuperobject(44,12,7,"והנה מלאך יהוה נצב עליו ואור נגה בחדר ויספק על ירך פטרוס ויעירהו לאמר קום מהרה ויפלו מוסרותיו מעל ידיו׃"));
hebsuperobject.biblemap.put("b44_12_8", new hebsuperobject(44,12,8,"ויאמר אליו המלאך חגר מתניך והנעל את רגליך ויעש כן ויאמר אליו עטה מעילך ולך אחרי׃"));
hebsuperobject.biblemap.put("b44_12_9", new hebsuperobject(44,12,9,"ויצא וילך אחריו ולא ידע אם אמת הוא הנעשה לו על ידי המלאך כי כמראה היה בעיניו׃"));
hebsuperobject.biblemap.put("b44_12_10", new hebsuperobject(44,12,10,"ויעברו דרך המשמרת הראשונה והשניה ויבאו עד שער הברזל אשר יצאו בו העירה ויפתח השער לפניהם מאליו ויצאו החוצה וילכו מהלך רחוב אחד והמלאך סר מעליו פתאם׃"));
hebsuperobject.biblemap.put("b44_12_11", new hebsuperobject(44,12,11,"ויהי בשובו אל דעתו ויאמר פטרוס עתה זה ידעתי באמת כי האלהים שלח את מלאכו ויפלטני מיד הורדוס ומכל מזמת עם היהודים׃"));
hebsuperobject.biblemap.put("b44_12_12", new hebsuperobject(44,12,12,"הוא חשב כזאת והוא בא עד בית מרים אם יוחנן המכנה מרקוס אשר נקהלו שם רבים והם מתפללים יחד׃"));
hebsuperobject.biblemap.put("b44_12_13", new hebsuperobject(44,12,13,"וידפק על דלת השער ותגש נערה להקשיב ושמה רודי׃"));
hebsuperobject.biblemap.put("b44_12_14", new hebsuperobject(44,12,14,"ותכר את קול פטרוס ומשמחתה לא פתחה לו אם דלת השער ותרץ הביתה ותגד להם כי פטרוס עמד על השער׃"));
hebsuperobject.biblemap.put("b44_12_15", new hebsuperobject(44,12,15,"ויאמרו אליה משגעת את והיא מתאמצת כי כן הוא ויאמרו מלאכו הוא׃"));
hebsuperobject.biblemap.put("b44_12_16", new hebsuperobject(44,12,16,"ופטרוס הרבה לדפק ויפתחו ויראהו וישתוממו׃"));
hebsuperobject.biblemap.put("b44_12_17", new hebsuperobject(44,12,17,"והוא רמז להם בידו לחשות ויספר להם את אשר הוציאו האדון מן המשמר ויאמר הגידו את זאת ליעקב ולאחים ויצא וילך לו למקום אחר׃"));
hebsuperobject.biblemap.put("b44_12_18", new hebsuperobject(44,12,18,"הבקר אור ומבוכה לא קטנה היתה בין אנשי הצבא על פטרוס מה היה לו׃"));
hebsuperobject.biblemap.put("b44_12_19", new hebsuperobject(44,12,19,"ויהי כאשר בקש אותו הורדוס ולא מצאו חקר את השמרים ויצו להוציאם למות וירד מיהודה אל קסרין וישב שם׃"));
hebsuperobject.biblemap.put("b44_12_20", new hebsuperobject(44,12,20,"ואיבה היתה בין הורדוס ובין בני צור וצידון ויבאו אליו בלב אחד ויפתו את בלסטוס אשר על חדר המלך ויבקשו שלום יען לקחו מחית ארצותם מארץ המלך׃"));
hebsuperobject.biblemap.put("b44_12_21", new hebsuperobject(44,12,21,"ויהי ביום המועד וילבש הורדוס לבוש מלכות וישב על כסא המשפט ויטף אליהם אמרתו׃"));
hebsuperobject.biblemap.put("b44_12_22", new hebsuperobject(44,12,22,"ויריעו לו העם לאמר קול אלהים הוא ולא קול אדם׃"));
hebsuperobject.biblemap.put("b44_12_23", new hebsuperobject(44,12,23,"ויכהו מלאך יהוה פתאם עקב אשר לא נתן הכבוד לאלהים ויאכלהו תולעים ויגוע׃"));
hebsuperobject.biblemap.put("b44_12_24", new hebsuperobject(44,12,24,"ודבר אלהים הולך ורב׃"));
hebsuperobject.biblemap.put("b44_12_25", new hebsuperobject(44,12,25,"וישובו בר נבא ושאול מירושלים אחרי כלותם את השמוש ויקחו אתם את יוחנן המכנה מרקוס׃"));
hebsuperobject.biblemap.put("b44_13_1", new hebsuperobject(44,13,1,"ואנשים נביאים ומלמדים היו באנטיוכיא בקהלה אשר בה בר נבא ושמעון הנקרא ניגר ולוקיוס הקוריני ומנחם אשר גדל עם הורדוס שר הרבע ושאול׃"));
hebsuperobject.biblemap.put("b44_13_2", new hebsuperobject(44,13,2,"ויהי בשרתם את יהוה ובצומם ויאמר רוח הקדש הבדילו לי את בר נבא ואת שאול למלאכה אשר קראתים לה׃"));
hebsuperobject.biblemap.put("b44_13_3", new hebsuperobject(44,13,3,"ויצומו ויתפללו ויסמכו את ידיהם עליהם וישלחום׃"));
hebsuperobject.biblemap.put("b44_13_4", new hebsuperobject(44,13,4,"והמה המשלחים על ידי רוח הקדש ירדו אל סלוקיא ומשם באו באניה אל קפרוס׃"));
hebsuperobject.biblemap.put("b44_13_5", new hebsuperobject(44,13,5,"ויבאו אל עיר סלמיס ויגידו את דבר האלהים בבתי הכנסיות אשר ליהודים ויהי להם גם יוחנן למשרת׃"));
hebsuperobject.biblemap.put("b44_13_6", new hebsuperobject(44,13,6,"ויעברו בכל האי עד פפוס וימצאו איש מגוש אחד נביא שקר איש יהודי ושמו בר ישוע׃"));
hebsuperobject.biblemap.put("b44_13_7", new hebsuperobject(44,13,7,"אשר היה עם סרגיוס פולוס שר המדינה איש נבון והוא קרא אליו את בר נבא ואת שאול ויתאו לשמע את דבר האלהים׃"));
hebsuperobject.biblemap.put("b44_13_8", new hebsuperobject(44,13,8,"ויעמד לנגדם אלימא המגוש כי זה תרגום שמו ויבקש להטות את השר מן האמונה׃"));
hebsuperobject.biblemap.put("b44_13_9", new hebsuperobject(44,13,9,"ושאול הנקרא גם פולוס מלא רוח הקדש ויסתכל בו׃"));
hebsuperobject.biblemap.put("b44_13_10", new hebsuperobject(44,13,10,"ויאמר אתה המלא כל מרמה וכל עולה בן בליעל ושונא כל צדק הלא תחדל לסלף את דרכי יהוה הישרים׃"));
hebsuperobject.biblemap.put("b44_13_11", new hebsuperobject(44,13,11,"ועתה הנה יד יהוה בך והיית עור ולא תראה את השמש עד עת מועד ותפל עליו פתאם אפלה וחשכה ויפן הנה והנה ויבקש איש להוליכו בידו׃"));
hebsuperobject.biblemap.put("b44_13_12", new hebsuperobject(44,13,12,"והשר כאשר ראה את המעשה האמין וישתומם על תורת האדון׃"));
hebsuperobject.biblemap.put("b44_13_13", new hebsuperobject(44,13,13,"ויצאו פולוס והאנשים אשר אתו מפפוס וירדו באניה ויבאו אל פרגי אשר בפמפוליא ויעזב אתם יוחנן וישב ירושלים׃"));
hebsuperobject.biblemap.put("b44_13_14", new hebsuperobject(44,13,14,"והמה נסעו מפרגי ויבאו אל אנטיוכיא אשר בפיסדיא ויבאו אל בית הכנסת ביום השבת וישבו׃"));
hebsuperobject.biblemap.put("b44_13_15", new hebsuperobject(44,13,15,"ויהי אחר קריאת התורה והנביאים וישלחו אליהם ראשי הכנסת לאמר אנשים אחים אם יש לכם דבר מוסר לעם דברו׃"));
hebsuperobject.biblemap.put("b44_13_16", new hebsuperobject(44,13,16,"ויקם פולוס וינף ידו ויאמר אנשי ישראל ויראי אלהים שמעו׃"));
hebsuperobject.biblemap.put("b44_13_17", new hebsuperobject(44,13,17,"אלהי העם הזה אלהי ישראל בחר באבותינו וירומם את העם בהיותם גרים בארץ מצרים ובזרוע רמה הוציאם משם׃"));
hebsuperobject.biblemap.put("b44_13_18", new hebsuperobject(44,13,18,"וישא אתם ויכלכלם במדבר כארבעים שנה׃"));
hebsuperobject.biblemap.put("b44_13_19", new hebsuperobject(44,13,19,"וישמד שבעה גוים בארץ כנען ויחלק את ארצם להם לנחלה׃"));
hebsuperobject.biblemap.put("b44_13_20", new hebsuperobject(44,13,20,"ואחרי כן נתן להם שפטים כארבע מאות וחמשים שנה עד ימי שמואל הנביא׃"));
hebsuperobject.biblemap.put("b44_13_21", new hebsuperobject(44,13,21,"וישאלו מלך ויתן להם אלהים את שאול בן קיש איש משבט בנימן ארבעים שנה׃"));
hebsuperobject.biblemap.put("b44_13_22", new hebsuperobject(44,13,22,"וכהסירו אותו ויקם את דוד למלך עליהם אשר גם העיד עליו לאמר מצאתי דוד את בן ישי איש כלבבי והוא יעשה את כל חפצי׃"));
hebsuperobject.biblemap.put("b44_13_23", new hebsuperobject(44,13,23,"וזה הוא אשר מזרעו הקים האלהים כפי ההבטחה גואל לישראל את ישוע׃"));
hebsuperobject.biblemap.put("b44_13_24", new hebsuperobject(44,13,24,"אשר לפני בואו קדם יוחנן לקרא את טבילת התשובה אל כל עם ישראל׃"));
hebsuperobject.biblemap.put("b44_13_25", new hebsuperobject(44,13,25,"ויהי ככלות יוחנן את מרוצתו ויאמר למי תחשבוני לא אני הוא כי הנה הוא בא אחרי ואני נקלתי מהתיר את נעלי רגליו׃"));
hebsuperobject.biblemap.put("b44_13_26", new hebsuperobject(44,13,26,"אנשים אחים בני משפחת אברהם ויראי אלהים אשר בקרבכם לכם שלוח דבר הישועה הזאת׃"));
hebsuperobject.biblemap.put("b44_13_27", new hebsuperobject(44,13,27,"כי ישבי ירושלים וראשיהם יען אשר לא הכירהו מלאו במשפטם אשר שפטהו את דברי הנביאים הנקראים בכל שבת׃"));
hebsuperobject.biblemap.put("b44_13_28", new hebsuperobject(44,13,28,"ואף כי לא מצאו בו משפט מות שאלו מאת פילטוס להמיתו׃"));
hebsuperobject.biblemap.put("b44_13_29", new hebsuperobject(44,13,29,"וכאשר השלימו את כל הדברים הכתובים עליו הורידו אותו מן העץ ויניחהו בקבר׃"));
hebsuperobject.biblemap.put("b44_13_30", new hebsuperobject(44,13,30,"אבל האלהים הקימו מן המתים׃"));
hebsuperobject.biblemap.put("b44_13_31", new hebsuperobject(44,13,31,"וירא ימים רבים אל העלים אתו מן הגליל ירושלימה והמה עתה עדיו אל העם׃"));
hebsuperobject.biblemap.put("b44_13_32", new hebsuperobject(44,13,32,"ואנחנו מבשרים אתכם את בשורת ההבטחה אשר היתה לאבותינו כי אתה מלא האלהים לבנינו בהקימו את ישוע׃"));
hebsuperobject.biblemap.put("b44_13_33", new hebsuperobject(44,13,33,"ככתוב במזמור השני בני אתה היום ילדתיך׃"));
hebsuperobject.biblemap.put("b44_13_34", new hebsuperobject(44,13,34,"ועל אשר הקים אתו מן המתים לבלתי שוב עוד לשחת כה אמר אתן לכם חסדי דוד הנאמנים׃"));
hebsuperobject.biblemap.put("b44_13_35", new hebsuperobject(44,13,35,"על כן הוא אמר גם במקום אחר לא תתן חסידך לראות שחת׃"));
hebsuperobject.biblemap.put("b44_13_36", new hebsuperobject(44,13,36,"כי דוד גוע אחרי שרתו בדורו לעצת האלהים ויאסף אל אבותיו וירא את השחת׃"));
hebsuperobject.biblemap.put("b44_13_37", new hebsuperobject(44,13,37,"ואשר האלהים הקים אותו הוא לא ראה השחת׃"));
hebsuperobject.biblemap.put("b44_13_38", new hebsuperobject(44,13,38,"לכן אנשים אחים יודע לכם כי על ידי זה הגד לכם סליחת החטאים׃"));
hebsuperobject.biblemap.put("b44_13_39", new hebsuperobject(44,13,39,"ובכל אשר לא יכלתם להצטדק בתורת משה כל המאמין יצדק על ידו׃"));
hebsuperobject.biblemap.put("b44_13_40", new hebsuperobject(44,13,40,"לכן השמרו לכם פן יבוא עליכם הנאמר בנביאים׃"));
hebsuperobject.biblemap.put("b44_13_41", new hebsuperobject(44,13,41,"ראו בגדים והתמהו ושמו כי פעל פעל אני בימיכם פעל אשר לא תאמינו כי יספר לכם׃"));
hebsuperobject.biblemap.put("b44_13_42", new hebsuperobject(44,13,42,"ובצאת היהודים מבית הכנסת בקשו מהם הגוים לדבר אליהם את הדברים האלה בשבת הבאה׃"));
hebsuperobject.biblemap.put("b44_13_43", new hebsuperobject(44,13,43,"ורבים מן היהודים ומגרי הצדק הלכו אחרי פולוס ובר נבא בהפרד הקהל והמה דברו על לבם ויזהירום לעמד בחסד האלהים׃"));
hebsuperobject.biblemap.put("b44_13_44", new hebsuperobject(44,13,44,"ובשבת השנית נקהלה כמעט כל העיר לשמע את דבר יהוה׃"));
hebsuperobject.biblemap.put("b44_13_45", new hebsuperobject(44,13,45,"ויהי כראות היהודים את המון העם וימלאו קנאה ויכחישו את דברי פולוס הכחש וגדף׃"));
hebsuperobject.biblemap.put("b44_13_46", new hebsuperobject(44,13,46,"אז הגידו פולוס ובר נבא על פניהם לאמר בדין היה להשמיע אתכם בראשונה את דבר האלהים ועתה אחרי אשר מאסתם אותו ואינכם זכים בעיניכם לחיי העולם לכן הננו פנים אל הגוים׃"));
hebsuperobject.biblemap.put("b44_13_47", new hebsuperobject(44,13,47,"כי כן צוה עלינו האדון נתתיך לאור גוים להיות ישועתי עד קצה הארץ׃"));
hebsuperobject.biblemap.put("b44_13_48", new hebsuperobject(44,13,48,"וישמחו הגוים כשמעם ויהללו את דבר יהוה ויאמינו כל אשר היו מוכנים לחיי עולם׃"));
hebsuperobject.biblemap.put("b44_13_49", new hebsuperobject(44,13,49,"ויפרץ דבר יהוה בכל המקום׃"));
hebsuperobject.biblemap.put("b44_13_50", new hebsuperobject(44,13,50,"אך היהודים הסיתו את הנשים החסידות והחשובות ואת אצילי העיר ויעוררו רדיפה על פולוס ובר נבא ויגרשום מגבולם׃"));
hebsuperobject.biblemap.put("b44_13_51", new hebsuperobject(44,13,51,"והמה נערו עליהם את העפר מעל רגליהם וילכו לאיקניון׃"));
hebsuperobject.biblemap.put("b44_13_52", new hebsuperobject(44,13,52,"והתלמידים מלאו שמחה ורוח הקדש׃"));
hebsuperobject.biblemap.put("b44_14_1", new hebsuperobject(44,14,1,"ויהי באיקניון ויבאו יחדו אל בית כנסת היהודים וידברו שם עד כי האמין המון רב מן היהודים ומן היונים׃"));
hebsuperobject.biblemap.put("b44_14_2", new hebsuperobject(44,14,2,"אך היהודים אשר לא האמינו עוררו והכעיסו את נפשות הגוים על האחים׃"));
hebsuperobject.biblemap.put("b44_14_3", new hebsuperobject(44,14,3,"וישבו שם ימים רבים וילמדו בבטחונם ביהוה העמיד על דבר חסדו בעשותו על ידם אתות ומופתים׃"));
hebsuperobject.biblemap.put("b44_14_4", new hebsuperobject(44,14,4,"ויחלק המון העיר לחצי אלה נטו אחרי היהודים ואלה אחרי השליחים׃"));
hebsuperobject.biblemap.put("b44_14_5", new hebsuperobject(44,14,5,"ויהי רגשת הגוים והיהודים עם ראשיהם להתעלל בהם ולסקלם׃"));
hebsuperobject.biblemap.put("b44_14_6", new hebsuperobject(44,14,6,"ויודע להם ויברחו לערי לוקוניא אל לוסטרא ודרבי וסביבותן׃"));
hebsuperobject.biblemap.put("b44_14_7", new hebsuperobject(44,14,7,"ויבשרו שם הבשורה׃"));
