hebsuperobject.biblemap.put("b44_16_23", new hebsuperobject(44,16,23,"ויהי אחרי הכות אתם מכה רבה וישליכום במשמר ויצוו את שומר האסורים לשמרם היטב׃"));
hebsuperobject.biblemap.put("b44_16_24", new hebsuperobject(44,16,24,"והוא כאשר צוה כן השליכם בחדר המשמר הפנימי וישם את רגליהם בסד׃"));
hebsuperobject.biblemap.put("b44_16_25", new hebsuperobject(44,16,25,"ויהי כחצות הלילה ויתפללו פולוס וסילא ויזמרו לאלהים והאסורים מקשיבים אליהם׃"));
hebsuperobject.biblemap.put("b44_16_26", new hebsuperobject(44,16,26,"ופתאם היה רעש גדול עד אשר זעו מוסדות בית הכלא וכרגע נפתחו כל הדלתות ומוסרות כלם נתקו׃"));
hebsuperobject.biblemap.put("b44_16_27", new hebsuperobject(44,16,27,"ושומר האסורים נעור משנתו ויהי כראתו את דלתות המשמר נפתחות וישלף חרבו ויבקש לאבד את עצמו בחשבו כי ברחו האסורים׃"));
hebsuperobject.biblemap.put("b44_16_28", new hebsuperobject(44,16,28,"ויקרא פולוס בקול גדול לאמר אל תעש לך מאומה רע כי פה אנחנו כלנו׃"));
