hebsuperobject.biblemap.put("b44_16_30", new hebsuperobject(44,16,30,"ויוציאם החוצה ויאמר אדני מה עלי לעשות למען אושע׃"));
hebsuperobject.biblemap.put("b44_16_31", new hebsuperobject(44,16,31,"ויאמרו האמן באדון ישוע המשיח ותושע אתה וביתך׃"));
