hebsuperobject.biblemap.put("b44_16_34", new hebsuperobject(44,16,34,"ויעלם אל ביתו וישם שלחן לפניהם ויגל עם כל ביתו על היותו מאמין באלהים׃"));
hebsuperobject.biblemap.put("b44_16_35", new hebsuperobject(44,16,35,"ובחית הבקר שלחו השרים את השוטרים לאמר שלח את האנשים ההם׃"));
hebsuperobject.biblemap.put("b44_16_36", new hebsuperobject(44,16,36,"ויגד שמר האסורים לפולוס את הדברים האלה לאמר שלחו השרים לפטר אתכם ועתה צאו ולכו בשלום׃"));
hebsuperobject.biblemap.put("b44_16_37", new hebsuperobject(44,16,37,"ויאמר פולוס אליהם הכה הכונו נגד כל העם בלא דין ומשפט ואנחנו אנשים רומיים וישליכו אתנו במשמר ועתה נסתר יגרשונו אל נא כי אם יבאו המה ויוציאונו׃"));
hebsuperobject.biblemap.put("b44_16_38", new hebsuperobject(44,16,38,"ויגידו השטרים לשרים את הדברים האלה ויהי כשמעם כי רומיים הם וייראו׃"));
hebsuperobject.biblemap.put("b44_16_39", new hebsuperobject(44,16,39,"ויבאו ויחלו פניהם ויוציאום וישאלו מהם לצאת מן העיר׃"));
hebsuperobject.biblemap.put("b44_16_40", new hebsuperobject(44,16,40,"ויצאו מן המשמר ויבאו אל בית לודיא ויראו את האחים ויזהירום וילכו לדרכם׃"));
hebsuperobject.biblemap.put("b44_17_1", new hebsuperobject(44,17,1,"ויהי אחרי עברם באמפפוליס ובאפלוינא ויבאו אל תסלוניקי אשר שם בית כנסת היהודים׃"));
hebsuperobject.biblemap.put("b44_17_2", new hebsuperobject(44,17,2,"ופולוס נכנס אליהם כמשפטו ושלש שבתות דבר עמהם בדברי הכתובים׃"));
hebsuperobject.biblemap.put("b44_17_3", new hebsuperobject(44,17,3,"פתוח והוכח להם כי צריך היה אשר יענה המשיח ויקום מן המתים וכי זה הוא המשיח ישוע אשר אני מגיד לכם׃"));
hebsuperobject.biblemap.put("b44_17_4", new hebsuperobject(44,17,4,"ויאמינו מקצתם ויספחו על פולוס וסילא וגם יראי אלהים מן היונים לרב וגם נשים חשובות מספר לא מעט׃"));
hebsuperobject.biblemap.put("b44_17_5", new hebsuperobject(44,17,5,"ויקנאו הסוררים שביהודים ויקחו להם אנשי בליעל מבטלני השוק ויקבצו המון ויהמו את העיר ויסבו על בית יסון ויבקשו להביאם חוצה אל העם׃"));
hebsuperobject.biblemap.put("b44_17_6", new hebsuperobject(44,17,6,"וכאשר לא מצאו אותם סחבו את יסון ואנשים מן האחים לפני ראשי העיר ויצעקו לאמר הנה המדיחים את כל ישבי תבל באו גם הלום׃"));
hebsuperobject.biblemap.put("b44_17_7", new hebsuperobject(44,17,7,"ויסון אסף אתם אל ביתו והם כלם עשים נגד דתי הקיסר באמרם כי יש מלך אחר והוא ישוע׃"));
hebsuperobject.biblemap.put("b44_17_8", new hebsuperobject(44,17,8,"ויחרידו את העם ואת ראשי העיר אשר שמעו את זאת׃"));
hebsuperobject.biblemap.put("b44_17_9", new hebsuperobject(44,17,9,"ויקחו ערבון מידי יסון ומידי האחרים ואחרי כן פטרום׃"));
hebsuperobject.biblemap.put("b44_17_10", new hebsuperobject(44,17,10,"והאחים מהרו לשלח את פולוס ואת סילא לילה לברואה והם בבאם שמה הלכו לבית כנסת היהודים׃"));
hebsuperobject.biblemap.put("b44_17_11", new hebsuperobject(44,17,11,"ואלה היו נדיבים מאנשי תסלוניקי ויקבלו את הדבר בכל לב וידרשו בכתובים יום יום לדעת אם כן הוא הדבר׃"));
hebsuperobject.biblemap.put("b44_17_12", new hebsuperobject(44,17,12,"ויאמינו הרבה מהם וגם מן הנשים החשובות היוניות ומן האנשים לא מעט׃"));
hebsuperobject.biblemap.put("b44_17_13", new hebsuperobject(44,17,13,"ויהי כאשר שמעו היהודים אשר מתסלוניקי כי מגיד פולוס את דבר אלהים גם בברואה ויבאו ויעררו את העם גם שמה׃"));
hebsuperobject.biblemap.put("b44_17_14", new hebsuperobject(44,17,14,"וימהרו האחים וישלחו את פולוס ללכת עד הים וסילא וטימותיוס נשארו שם׃"));
hebsuperobject.biblemap.put("b44_17_15", new hebsuperobject(44,17,15,"והמלוים את פולוס הוליכהו עד אתינס ויקבלו מצותו אל סילא וטימותיוס כי בוא יבאו אליו במהרה וילכו׃"));
hebsuperobject.biblemap.put("b44_17_16", new hebsuperobject(44,17,16,"ויהי באתינס ופולוס מהכה להם ותתחמץ רוחו בקרבו בראתו את העיר מלאה אלילים׃"));
hebsuperobject.biblemap.put("b44_17_17", new hebsuperobject(44,17,17,"על כן בבית הכנסת דבר עם היהודים ועם יראי אלהים ובשוק יום יום עם הנקרים אליו׃"));
hebsuperobject.biblemap.put("b44_17_18", new hebsuperobject(44,17,18,"וגם מקצת הפילוסופים מתלמידי אפיקורוס ומחברת האסטוא התגרו בו ויש אשר אמרו מה יאמר המפטפט הלז ואחרים אמרים כפי הנראה הוא מגיד אלהי נכר יען כי בשר אתם את ישוע ואת התחיה׃"));
hebsuperobject.biblemap.put("b44_17_19", new hebsuperobject(44,17,19,"ויאחזוהו ויביאהו אל גבעת המשפט הנקראה בשם אריופגוס ויאמרו הנוכל לדעת מה היא זו התורה החדשה אשר אתה משמיע׃"));
hebsuperobject.biblemap.put("b44_17_20", new hebsuperobject(44,17,20,"כי דברים זרים אתה מביא באזנינו והננו חפצים לדעת מה ענינם׃"));
