hebsuperobject.biblemap.put("b44_17_22", new hebsuperobject(44,17,22,"ויעמד פולוס בתוך ועד השפטים ויאמר אנשי אתינס הנני ראה בכל כי יראי אלים אתם מאד׃"));
hebsuperobject.biblemap.put("b44_17_23", new hebsuperobject(44,17,23,"כי אני עבר ומתבונן אל עבדות אלהיכם ואמצא מזבח אחד כתוב עליו לאל הנעלם ועתה את אשר עבדתם ואינכם ידעים אתו אני מגיד לכם׃"));
hebsuperobject.biblemap.put("b44_17_24", new hebsuperobject(44,17,24,"האל אשר עשה את העולם וכל אשר בו לא ישכן בהיכלות מעשה ידים כי הוא אדון השמים והארץ׃"));
hebsuperobject.biblemap.put("b44_17_25", new hebsuperobject(44,17,25,"גם לא ישרתוהו ידי בני אדם כאלו יצטרך לדבר כי הוא הנתן לכל חיים ונשמה וכל דבר׃"));
hebsuperobject.biblemap.put("b44_17_26", new hebsuperobject(44,17,26,"ויושב כל עממי בני אדם על כל פני האדמה מדם אחד ויצב מועדים קבועים וגבולת מושבם׃"));
hebsuperobject.biblemap.put("b44_17_27", new hebsuperobject(44,17,27,"למען יבקשו את האלהים אולי ימששהו וימצאהו אף כי איננו רחוק מכל אחד ממנו׃"));
hebsuperobject.biblemap.put("b44_17_28", new hebsuperobject(44,17,28,"כי בו אנחנו חיים ומתנועעים וקימים כאשר גם מקצת משורריכם אמרו אף ילידיו אנחנו׃"));
hebsuperobject.biblemap.put("b44_17_29", new hebsuperobject(44,17,29,"והנה בהיותנו ילידי האלהים לא נכון לנו לחשב כי האלהות תדמה לזהב או לכסף או לאבן מעשה חרש ומחשבת בני אדם׃"));
hebsuperobject.biblemap.put("b44_17_30", new hebsuperobject(44,17,30,"לכן עתה אחרי עבר אלהים על ימות הסכלות מצוה הוא את כל בני האדם בכל אפסי ארץ כי שוב ישובו׃"));
hebsuperobject.biblemap.put("b44_17_31", new hebsuperobject(44,17,31,"יען אשר שת יום לשפט תבל בצדק על ידי איש אשר הפקידו ויתן עדות נאמנה לכלם בהקימו אתו מן המתים׃"));
hebsuperobject.biblemap.put("b44_17_32", new hebsuperobject(44,17,32,"וכאשר שמעו תחית מתים אלה הלעיגו לו ואלה אמרו על זאת נשמעך בעת אחרת׃"));
hebsuperobject.biblemap.put("b44_17_33", new hebsuperobject(44,17,33,"ובכן יצא פולוס מתוכם׃"));
hebsuperobject.biblemap.put("b44_17_34", new hebsuperobject(44,17,34,"ומקצתם דבקו בו ויאמינו ובהם דיונוסיוס מן השפטים של אריופגוס ואשה אחת שמה דמריס ועוד אחרים עמהם׃"));
hebsuperobject.biblemap.put("b44_18_1", new hebsuperobject(44,18,1,"אחר כן סר פולוס מאתינס ויבא אל קורנתוס׃"));
hebsuperobject.biblemap.put("b44_18_2", new hebsuperobject(44,18,2,"וימצא יהודי נולד בפונטוס ומשו עקילס אשר בא מקרוב מן איטליא הוא ואשתו פריסקלה מפני אשר צוה קלודיוס את כל היהודים לסור מעיר רומי׃"));
hebsuperobject.biblemap.put("b44_18_3", new hebsuperobject(44,18,3,"ויבא אליהם ויהי בהיתם בני אמנות אחת וישב אתם ויעש מלאכתו ואמנותם עשות יריעות אהלים׃"));
hebsuperobject.biblemap.put("b44_18_4", new hebsuperobject(44,18,4,"וידבר בבית הכנסת בכל שבת ושבת ויוכח את היהודים ואת היונים׃"));
hebsuperobject.biblemap.put("b44_18_5", new hebsuperobject(44,18,5,"וכבוא סילא וטימותיוס ממקדוניא היה פולוס נגש ברוח להעיד אל היהודים כי ישוע הוא המשיח׃"));
hebsuperobject.biblemap.put("b44_18_6", new hebsuperobject(44,18,6,"ויהי כהמרותם וכגדפם וינער את בגדיו ויאמר אליהם דמכם בראשיכם ואנכי נקי מעתה אלכה לי אל הגוים׃"));
hebsuperobject.biblemap.put("b44_18_7", new hebsuperobject(44,18,7,"וילך משם ויבא אל בית איש ושמו יוסטוס איש ירא אלהים וביתו סמוך לבית הכנסת׃"));
hebsuperobject.biblemap.put("b44_18_8", new hebsuperobject(44,18,8,"וקריספוס ראש הכנסת האמין באדון הוא וכל ביתו וגם קורנתים רבים בשמעם האמינו ויטבלו׃"));
hebsuperobject.biblemap.put("b44_18_9", new hebsuperobject(44,18,9,"ודבר האדון היה אל פולוס במחזה בלילה לאמר אל תירא כי אם דבר ואל תחשה׃"));
hebsuperobject.biblemap.put("b44_18_10", new hebsuperobject(44,18,10,"כי עמך אנכי ואיש אל יגע בך להרע לך כי גוי גדול לי בעיר הזאת׃"));
hebsuperobject.biblemap.put("b44_18_11", new hebsuperobject(44,18,11,"וישב שם שנה וששה חדשים וילמד בהם את דבר האלהים׃"));
hebsuperobject.biblemap.put("b44_18_12", new hebsuperobject(44,18,12,"ויהי בהיות גליון שר מדינת אכיא ויקומו היהודים כלם וחדו על פולוס ויביאהו אל כסא המשפט׃"));
hebsuperobject.biblemap.put("b44_18_13", new hebsuperobject(44,18,13,"ויאמרו האיש הזה מפתה את בני האדם לעבד את אלהים בלא כתורה׃"));
hebsuperobject.biblemap.put("b44_18_14", new hebsuperobject(44,18,14,"ויהי אך בקש פולוס לפתח את פיו וגליון אמר אל היהודים לאמר אם היה דבר פשע או מעשה נבלה כי עתה נשאתי ושמעתי אתכם היהודים כמשפט׃"));
hebsuperobject.biblemap.put("b44_18_15", new hebsuperobject(44,18,15,"אבל אם היא שאלה על מלין ושמות והדת שלכם ראו אתם ואני אין רצוני להיות שפט על דברים כאלה׃"));
hebsuperobject.biblemap.put("b44_18_16", new hebsuperobject(44,18,16,"ויגרש אתם מלפני כסא המשפט׃"));
hebsuperobject.biblemap.put("b44_18_17", new hebsuperobject(44,18,17,"ויאחזו כל היונים את סוסתנים ראש הכנסת ויכוהו לפני כסא המשפט וגליון לא שת לבו גם לזאת׃"));
hebsuperobject.biblemap.put("b44_18_18", new hebsuperobject(44,18,18,"ופולוס ישב שם עוד ימים רבים ויפטר מן האחים וירד באניה ללכת אל סוריא ואתו פריסקלה ועקילס ויגלח את ראשו בקנכרי כי נדר עליו׃"));
hebsuperobject.biblemap.put("b44_18_19", new hebsuperobject(44,18,19,"ויבאו אל אפסוס ויעזב אתם שם והוא הלך לבית הכנסת וידבר עם היהודים׃"));
hebsuperobject.biblemap.put("b44_18_20", new hebsuperobject(44,18,20,"ויבקשו ממנו להאריך ימי שבתו אתם ולא אבה׃"));
hebsuperobject.biblemap.put("b44_18_21", new hebsuperobject(44,18,21,"כי אם נפטר מהם באמרו מחיב אני לחג את החג הבא בירושלים ואחרי כן אשובה אליכם אם ירצה יהוה וילך לו באניה מן אפסוס׃"));
hebsuperobject.biblemap.put("b44_18_22", new hebsuperobject(44,18,22,"ויבא אל קסרין ויעל וישאל לשלום הקהלה וירד אל אנטיוכיא׃"));
hebsuperobject.biblemap.put("b44_18_23", new hebsuperobject(44,18,23,"וישב שם ימים אחדים וילך למסעיו ויעבר בארץ גלטיא ופרוגיא ויחזק את כל התלמידים׃"));
hebsuperobject.biblemap.put("b44_18_24", new hebsuperobject(44,18,24,"ואיש יהודי בא אל אפסוס ועיר מולדתו אלכסנדריא ושמו אפולוס איש דברים ותקיף בכתובים׃"));
hebsuperobject.biblemap.put("b44_18_25", new hebsuperobject(44,18,25,"הוא היה מלמד דרך האדון והוא מדבר כחם רוחו ומלמד היטב על אדות ישוע ולא ידע כי אם טבילת יוחנן לבדה׃"));
hebsuperobject.biblemap.put("b44_18_26", new hebsuperobject(44,18,26,"והוא החל לקרא בבית הכנסת בבטחון לבב וישמעו אתו עקילס ופריסקלה ויקחהו אליהם ויוסיפו להגיד לו את דרך האלהים באר היטב׃"));
hebsuperobject.biblemap.put("b44_18_27", new hebsuperobject(44,18,27,"ויחפץ ללכת לאכיא ויכתבו האחים אל התלמידים ויעררו אתם לקבלו ויבא שמה ויעזר הרבה את המאמינים על ידי החסד׃"));
hebsuperobject.biblemap.put("b44_18_28", new hebsuperobject(44,18,28,"כי בחזקה התוכח עם היהודים לפני כל העם ויראם מן המקראות כי ישוע הוא המשיח׃"));
hebsuperobject.biblemap.put("b44_19_1", new hebsuperobject(44,19,1,"ויהי בהיות אפולוס בקורנתוס ויעבר פולוס במדינות העליונת וירד אל אפסוס וימצא שם תלמידים׃"));
hebsuperobject.biblemap.put("b44_19_2", new hebsuperobject(44,19,2,"ויאמר אליהם הקבלתם את רוח הקדש אחרי אשר האמנתם ויאמרו אליו אף לא שמענו כי ישנו רוח הקדש׃"));
hebsuperobject.biblemap.put("b44_19_3", new hebsuperobject(44,19,3,"ויאמר אליהם על מה אפוא הטבלתם ויאמרו על טבילת יוחנן׃"));
hebsuperobject.biblemap.put("b44_19_4", new hebsuperobject(44,19,4,"ויאמר פולוס יוחנן הטביל בטבילת התשובה ואמר אל העם כי האמן יאמינו באשר יבוא אחריו והוא המשיח ישוע׃"));
hebsuperobject.biblemap.put("b44_19_5", new hebsuperobject(44,19,5,"ויהי כשמעם זאת ויבאו להטבל בשם ישוע האדון׃"));
hebsuperobject.biblemap.put("b44_19_6", new hebsuperobject(44,19,6,"ויסמך פולוס את ידיו עליהם ויבא עליהם רוח הקדש וימללו בלשנות ויתנבאו׃"));
hebsuperobject.biblemap.put("b44_19_7", new hebsuperobject(44,19,7,"ויהיו כלם כשנים עשר איש׃"));
hebsuperobject.biblemap.put("b44_19_8", new hebsuperobject(44,19,8,"ויבא אל בית הכנסת ויקרא שם בבטחון לבב וידבר עמם כשלשה חדשים ויט את לבם אל דברי מלכות האלהים׃"));
hebsuperobject.biblemap.put("b44_19_9", new hebsuperobject(44,19,9,"אך מקצתם הקשו את לבבם ולא האמינו וידברו סרה על הדרך הזה לפני הקהל ויסר מהם ויבדל מתוכם את התלמידים וידבר אתם יום יום בבית המדרש של טורנוס׃"));
hebsuperobject.biblemap.put("b44_19_10", new hebsuperobject(44,19,10,"וכן היתה כשנתים ימים עד כי שמעו כל ישבי אסיא גם יהודים גם יונים את דבר ישוע האדון׃"));
hebsuperobject.biblemap.put("b44_19_11", new hebsuperobject(44,19,11,"וגבורות גדלות עשה האלהים על ידי פולוס׃"));
hebsuperobject.biblemap.put("b44_19_12", new hebsuperobject(44,19,12,"עד כי גם הניחו סודרים וחגרת מעל עור בשרו על החולים ויסורו מהם תחלאיהם וגם הרוחות הרעות יצאו׃"));
hebsuperobject.biblemap.put("b44_19_13", new hebsuperobject(44,19,13,"ואנשים מן היהודים הסבבים ומלחשים הואילו להזכיר את שם ישוע האדון על אחוזי הרוחות הרעות לאמר משביע אני אתכם בישוע אשר פולוס קרא אותו׃"));
hebsuperobject.biblemap.put("b44_19_14", new hebsuperobject(44,19,14,"והעשים ככה היו שבעת בני סקוה אחד מגדולי כהני היהודים׃"));
hebsuperobject.biblemap.put("b44_19_15", new hebsuperobject(44,19,15,"ויען הרוח ויאמר את ישוע אני מכיר ואת פולוס אני ידע ומי אתם׃"));
hebsuperobject.biblemap.put("b44_19_16", new hebsuperobject(44,19,16,"ויקפץ עליהם בעל הרוח הרעה ויגבר עליהם ויכבשם עד כי נסו מן הבית ההוא עירמים ופצועים׃"));
hebsuperobject.biblemap.put("b44_19_17", new hebsuperobject(44,19,17,"ויודע הדבר לכל היהודים והיונים ישבי אפסוס ויפל פחד על כלם ויגדל שם האדון ישוע׃"));
hebsuperobject.biblemap.put("b44_19_18", new hebsuperobject(44,19,18,"ורבים מן המאמינים באו ויתודו ויודיעו את אשר עשו׃"));
hebsuperobject.biblemap.put("b44_19_19", new hebsuperobject(44,19,19,"ורבים אשר עשקו במעשי כשפים הביאו את ספריהם וישרפום לעיני כל ויחשבו את ערכם וימצאהו חמשת רבבות דינרים׃"));
hebsuperobject.biblemap.put("b44_19_20", new hebsuperobject(44,19,20,"ודבר יהוה גבר מאד וילך הלך וחזק׃"));
hebsuperobject.biblemap.put("b44_19_21", new hebsuperobject(44,19,21,"ויהי ככלות הדברים האלה יעץ פולוס ברוחו לעבר בתוך מקדוניא ואכיא וללכת ירושלים ויאמר אחרי היותי שמה ראה אראה גם את רומי׃"));
hebsuperobject.biblemap.put("b44_19_22", new hebsuperobject(44,19,22,"וישלח שנים מן המשרתים אתו את טימותיוס ואת ארסטוס אל מקדוניא והוא התמהמה עוד ימים באסיא׃"));
hebsuperobject.biblemap.put("b44_19_23", new hebsuperobject(44,19,23,"ותהי בעת ההיא מהומה לא קטנה על אדות דרך יהוה׃"));
hebsuperobject.biblemap.put("b44_19_24", new hebsuperobject(44,19,24,"כי צורף כסף אחד דמטריוס שמו עשה היכלות כסף של ארטמיס המציא לחרשים משכרת לא מעט׃"));
hebsuperobject.biblemap.put("b44_19_25", new hebsuperobject(44,19,25,"ויקהל אתם ואת שאר העסקים במלאכה ההיא ויאמר אנשים אתם ידעים כי מן המלאכה הזאת עשינו כבוד׃"));
hebsuperobject.biblemap.put("b44_19_26", new hebsuperobject(44,19,26,"ואתם ראים ושמעים אשר לא באפסוס לבדה כי גם כמעט באסיא כלה זה פולוס פתה והדיח המון עם רב לאמר לא אלהים אלה הנעשים בידים׃"));
hebsuperobject.biblemap.put("b44_19_27", new hebsuperobject(44,19,27,"ועתה עוד מעט ולא לבד חלקנו זה יהיה לבוז כי אם גם היכל ארטמיס המלכת הגדולה יחשב לאין ותכלה תפארתה אשר כל אסיא וכל ישבי תבל מכבדים אותה׃"));
hebsuperobject.biblemap.put("b44_19_28", new hebsuperobject(44,19,28,"ויהי כשמעם את דבריו וימלאו חמה ויצעקו לאמר גדולה ארטמיס של האפסיים׃"));
hebsuperobject.biblemap.put("b44_19_29", new hebsuperobject(44,19,29,"ותמלא כל העיר מבוכה ויסערו כלם יחדו אל התאטרון ויחטפו אתם את גיוס ואת ארסטרכוס אנשים מקדונים וחברי פולוס במסעיו׃"));
hebsuperobject.biblemap.put("b44_19_30", new hebsuperobject(44,19,30,"ויאל פולוס לבוא אל תוך העם ולא הניחו לו התלמידים׃"));
hebsuperobject.biblemap.put("b44_19_31", new hebsuperobject(44,19,31,"וגם מקצת ראשי אסיא אשר היו אהביו שלחו אליו ויזהירו אתו אשר לא ימלאהו לבו לבוא אל התאטרון׃"));
hebsuperobject.biblemap.put("b44_19_32", new hebsuperobject(44,19,32,"ויצעקו שם אלה בכה ואלה בכה כי מבוכה גדולה היתה בקהל ורבם לא ידעו על מה זה נאספו׃"));
hebsuperobject.biblemap.put("b44_19_33", new hebsuperobject(44,19,33,"וימשכו מתוך ההמון את אלכסנדר והיהודים דחו אתו עד צאתו וינף אלכסנדר את ידו ויבקש להצטדק לפני העם׃"));
hebsuperobject.biblemap.put("b44_19_34", new hebsuperobject(44,19,34,"והמה כהכירם כי הוא יהודי נשאו כלם קול ויצעקו כשתי שעות לאמר גדולה ארטמיס של האפסיים׃"));
hebsuperobject.biblemap.put("b44_19_35", new hebsuperobject(44,19,35,"ויהס סופר העיר את העם ויאמר אנשי אפסוס מי הוא האיש אשר לא ידע כי העיר אפסוס סכנת היא להיכל של ארטמיס המלכת הגדולה ולצלמה היורד מן השמים׃"));
hebsuperobject.biblemap.put("b44_19_36", new hebsuperobject(44,19,36,"ויען אשר אין לכחש בדברים האלה ראוי לכם להיות שקטים ולא לעשות דבר נמהר׃"));
hebsuperobject.biblemap.put("b44_19_37", new hebsuperobject(44,19,37,"כי הבאתם את האנשים האלה אשר אינם גזלי מקדש גם אינם מגדפי מלכתכם׃"));
hebsuperobject.biblemap.put("b44_19_38", new hebsuperobject(44,19,38,"לכן אם לדמטריוס ולחרשים אשר אתו דבר ריב עם איש הנה לנו ימי בית דין ושרי המדינה ויביאו אליהם את ריבם׃"));
hebsuperobject.biblemap.put("b44_19_39", new hebsuperobject(44,19,39,"ואם תבקשו דבר אחר שפוט ישפט בקהל כפי החק׃"));
hebsuperobject.biblemap.put("b44_19_40", new hebsuperobject(44,19,40,"כי הלא בסכנה אנחנו להתחיב במרד בעבור היום הזה ודבר אין לנו לתת דין וחשבון על הרגשה הזאת׃"));
hebsuperobject.biblemap.put("b44_19_41", new hebsuperobject(44,19,41,"ויהי כאשר כלה לדבר כדברים האלה וישלח את הקהל׃"));
hebsuperobject.biblemap.put("b44_20_1", new hebsuperobject(44,20,1,"ואחרי אשר שקטה המהומה קרא פולוס לתלמידים ויברכם ויצא ללכת אל מקדוניא׃"));
hebsuperobject.biblemap.put("b44_20_2", new hebsuperobject(44,20,2,"ויעבר במדינות ההן ויזהר אתם בדברים רבים ויבא אל ארץ יון׃"));
hebsuperobject.biblemap.put("b44_20_3", new hebsuperobject(44,20,3,"וישב שם שלשה חדשים ויהי באמרו ללכת אל סוריא ויארבו לו היהודים ויגמר בלבו לשוב דרך מקדוניא׃"));
hebsuperobject.biblemap.put("b44_20_4", new hebsuperobject(44,20,4,"וילכו אתו עד לאסיא סופטרוס הברואי ומן התסלוניקים ארסטרכוס וסקונדוס וגיוס הדרבי וטימותיוס ומן אסיא טוכיקוס וטרופימוס׃"));
hebsuperobject.biblemap.put("b44_20_5", new hebsuperobject(44,20,5,"ואלה הלכו לפנינו ויוחילו לנו בטרואס׃"));
hebsuperobject.biblemap.put("b44_20_6", new hebsuperobject(44,20,6,"ואנחנו יצאנו מן פילפי אחרי ימי חג המצות ומקץ חמשה ימים באנו באניה אליהם אל טרואס ונשב שם שבעת ימים׃"));
hebsuperobject.biblemap.put("b44_20_7", new hebsuperobject(44,20,7,"ויהי באחד בשבת כאשר נאספו התלמידים לבצע הלחם וידבר אתם פולוס כי אמר ללכת משם למחרת היום ויארך הדבר עד חצות הלילה׃"));
hebsuperobject.biblemap.put("b44_20_8", new hebsuperobject(44,20,8,"ונרות רבים היו בעליה אשר נאספנו שם׃"));
hebsuperobject.biblemap.put("b44_20_9", new hebsuperobject(44,20,9,"ובחור אחד שמו אבטוכוס ישב בחלון וירדם בהאריך פולוס את אמרתו ותגבר עליו שנתו ויפל מהמדור השלישי למטה וישאהו מת׃"));
hebsuperobject.biblemap.put("b44_20_10", new hebsuperobject(44,20,10,"וירד פולוס ויגהר עליו ויחבקהו ויאמר אל תבהלו כי נשמתו בו׃"));
hebsuperobject.biblemap.put("b44_20_11", new hebsuperobject(44,20,11,"ואחר עלה ויבצע הלחם ויטעם וירב לשיח אתם עד אור הבקר ויצא ללכת לדרכו׃"));
hebsuperobject.biblemap.put("b44_20_12", new hebsuperobject(44,20,12,"והם הביאו את הנער חי וינחמו עד מאד׃"));
hebsuperobject.biblemap.put("b44_20_13", new hebsuperobject(44,20,13,"ואנחנו קדמנו לרדת באניה ונעבר אסוסה למען נקח שם אתנו את פולוס כי כן צוה והוא חשב בלבו ללכת שמה ברגליו׃"));
hebsuperobject.biblemap.put("b44_20_14", new hebsuperobject(44,20,14,"ויפגש אתנו באסוס ונקח אתו ונבא אל מיטוליני׃"));
hebsuperobject.biblemap.put("b44_20_15", new hebsuperobject(44,20,15,"ומשם יצאנו באניה ונפגע ממחרת אל מול כיוס וביום השלישי עברנו אל סמוס ונלן בטרוגוליון ולמחרתו באנו אל מיליטוס׃"));
hebsuperobject.biblemap.put("b44_20_16", new hebsuperobject(44,20,16,"כי פולוס אמר לעבר מעל פני אפסוס פן יצטרך להתמהמה באסיא כי אץ לבוא אם יוכל עד חג השבועות ירושלים׃"));
hebsuperobject.biblemap.put("b44_20_17", new hebsuperobject(44,20,17,"וממיליטוס שלח אל אפסוס ויקרא את זקני הקהלה׃"));
hebsuperobject.biblemap.put("b44_20_18", new hebsuperobject(44,20,18,"ויבאו אליו ויאמר להם אתם ידעתם איך הייתי עמכם בכל עת מן היום הראשון אשר דרכה רגלי באסיא׃"));
hebsuperobject.biblemap.put("b44_20_19", new hebsuperobject(44,20,19,"אשר עבדתי את האדון בכל ענוה ובדמעות הרבה ובמסות המצאות אתי בנכלי היהודים׃"));
hebsuperobject.biblemap.put("b44_20_20", new hebsuperobject(44,20,20,"איך לא כחדתי מכם כל דבר תועלת והגדתיו לכם ולמדתי אתכם ברבים ובכל בית ובית׃"));
hebsuperobject.biblemap.put("b44_20_21", new hebsuperobject(44,20,21,"ואעיד ליהודים וליונים את התשובה לאלהים ואת האמונה באדנינו ישוע המשיח׃"));
hebsuperobject.biblemap.put("b44_20_22", new hebsuperobject(44,20,22,"ועתה הנני אסיר הרוח ללכת ירושלים ואינני יודע מה יקרני שם׃"));
hebsuperobject.biblemap.put("b44_20_23", new hebsuperobject(44,20,23,"אפס כי רוח הקדש מעיד בכל עיר ועיר לאמר כי מוסרות וצרות נכונו לי׃"));
hebsuperobject.biblemap.put("b44_20_24", new hebsuperobject(44,20,24,"אבל לא אחוש לאחת מהנה וגם נפשי לא יקרה בעיני למען אשלים בשמחה את מרוצתי ואת השרות אשר קבלתי מאת האדון ישוע להעיד על בשורת חסד אליהם׃"));
hebsuperobject.biblemap.put("b44_20_25", new hebsuperobject(44,20,25,"ועתה הנה ידע אנכי כי אתם כלכם אשר התהלכתי בקרבכם הלוך והשמע את מלכות יהוה כי לא תוסיפו עוד ראות פני׃"));
hebsuperobject.biblemap.put("b44_20_26", new hebsuperobject(44,20,26,"על כן מעיד אני בכם היום הזה כי נקי אנכי מדמי כלכם׃"));
hebsuperobject.biblemap.put("b44_20_27", new hebsuperobject(44,20,27,"כי לא כחדתי מהגיד לכם את עצת האלהים כלה׃"));
hebsuperobject.biblemap.put("b44_20_28", new hebsuperobject(44,20,28,"לכן שמרו את נפשותיכם ואת כל העדר אשר הקים אתכם רוח הקדש לפקידים בו לרעות את עדת האלהים אשר קנה לו בדם נפשו׃"));
hebsuperobject.biblemap.put("b44_20_29", new hebsuperobject(44,20,29,"כי ידע אני אשר אחרי צאתי יבואו בתוככם זאבים עזים אשר לא יחוסו על העדר׃"));
hebsuperobject.biblemap.put("b44_20_30", new hebsuperobject(44,20,30,"וגם מקרבכם יקומו אנשים דברי תהפכות להטות אחריהם את התלמידים׃"));
hebsuperobject.biblemap.put("b44_20_31", new hebsuperobject(44,20,31,"על כן שקדו וזכרו כי שלש שנים יומם ולילה לא חדלתי לדבר על לב כל אחד מכם בדמעות׃"));
hebsuperobject.biblemap.put("b44_20_32", new hebsuperobject(44,20,32,"ועתה אחי אני מסר אתכם לאלהים ולדבר חסדו אשר לו היכלת לבנות אתכם ולתת לכם נחלה בקרב כל המקדשים׃"));
hebsuperobject.biblemap.put("b44_20_33", new hebsuperobject(44,20,33,"לא חמדתי כסף איש או זהבו או לבושו׃"));
hebsuperobject.biblemap.put("b44_20_34", new hebsuperobject(44,20,34,"ואתם ידעתם כי ידי אלה עבדו בעד צרכי ובעד צרכי ההלכים אתי׃"));
hebsuperobject.biblemap.put("b44_20_35", new hebsuperobject(44,20,35,"ובכל הראיתי אתכם כי כן עלינו לעמל ולתמך את החלשים ולזכר את דברי האדון ישוע כי הוא אמר טוב אשר תתן משתקח׃"));
hebsuperobject.biblemap.put("b44_20_36", new hebsuperobject(44,20,36,"ואחרי דברו את הדברים האלה כרע על ברכיו ויתפלל עם כלם׃"));
hebsuperobject.biblemap.put("b44_20_37", new hebsuperobject(44,20,37,"ויבכו כלם בכי גדול ויפלו על צוארי פולוס וינשקו לו׃"));
hebsuperobject.biblemap.put("b44_20_38", new hebsuperobject(44,20,38,"וביותר התעצבו על הדבר אשר דבר כי לא יוסיפו עוד לראות פניו וילוהו אל האניה׃"));
hebsuperobject.biblemap.put("b44_21_1", new hebsuperobject(44,21,1,"ויהי כאשר נפרדנו מהם ונרד באניה ונבוא דרך ישרה אל קוס וממחרת אל רודוס ומשם אל פטרה׃"));
hebsuperobject.biblemap.put("b44_21_2", new hebsuperobject(44,21,2,"שם מצאנו אניה עברת אל פינוקיא ונרד בה ונעבר׃"));
hebsuperobject.biblemap.put("b44_21_3", new hebsuperobject(44,21,3,"ונרא את פני קפרוס ונעזבנה לשמאל ונעבר אל סוריא ונגיע אל צור כי שמה יוציאו את מעמסת האניה׃"));
hebsuperobject.biblemap.put("b44_21_4", new hebsuperobject(44,21,4,"ונמצא את התלמידים ונשב שם שבעת ימים והם אמרו אל פולוס על פי הרוח לבלתי עלות ירושלים׃"));
hebsuperobject.biblemap.put("b44_21_5", new hebsuperobject(44,21,5,"ויהי אחרי מלאת לנו הימים האלה ונצא ללכת לדרכנו וילוו אתנו כלם עם נשיהם ועם טפם עד מחוץ לעיר ונכרע על ברכינו על חוף הים ונתפלל׃"));
hebsuperobject.biblemap.put("b44_21_6", new hebsuperobject(44,21,6,"ונברך איש את רעהו ונעל אל האניה והמה שבו איש לביתו׃"));
hebsuperobject.biblemap.put("b44_21_7", new hebsuperobject(44,21,7,"ואנחנו כלינו את דרך הים ונרד מצור אל עכו ונשאל לשלום האחים ונשב אתם יום אחד׃"));
hebsuperobject.biblemap.put("b44_21_8", new hebsuperobject(44,21,8,"וממחרת נסענו פולוס ואשר אתו ונבא אל קסרין ונלך אל בית פילפוס המבשר אשר הוא אחד מן השבעה ונשב עמו׃"));
hebsuperobject.biblemap.put("b44_21_9", new hebsuperobject(44,21,9,"ולו היו ארבע בנות בתולות מתנבאות׃"));
hebsuperobject.biblemap.put("b44_21_10", new hebsuperobject(44,21,10,"ויהי בהיותנו שם ימים רבים וירד נביא אחד מיהודה ושמו אגבוס׃"));
hebsuperobject.biblemap.put("b44_21_11", new hebsuperobject(44,21,11,"ויבא אצלנו ויקח את אזור פולוס ויאסר בו את ידיו ואת רגליו ויאמר כה אמר רוח הקדש ככה יאסרו היהודים בירושלים את האיש אשר לו האזור הזה ויסגירהו בידי הגוים׃"));
hebsuperobject.biblemap.put("b44_21_12", new hebsuperobject(44,21,12,"ויהי כאשר שמענו את זאת ונבקש ממנו אנחנו ואנשי המקום אשר לא יעלה ירושלים׃"));
hebsuperobject.biblemap.put("b44_21_13", new hebsuperobject(44,21,13,"ויען פולוס ויאמר מה לכם אשר תבכו ותשברו לבבי כי נכון אנכי לא לבד להאסר כי גם למות בירושלים על שם ישוע האדון׃"));
hebsuperobject.biblemap.put("b44_21_14", new hebsuperobject(44,21,14,"ולא אבה שמע ונרף ממנו לאמר רצון יהוה יעשה׃"));
hebsuperobject.biblemap.put("b44_21_15", new hebsuperobject(44,21,15,"ואחרי הימים האלה נשאנו את כלינו ונעל ירושלים׃"));
hebsuperobject.biblemap.put("b44_21_16", new hebsuperobject(44,21,16,"וילכו אתנו גם תלמידים מקסרין ויביאו אתנו ללון בבית איש כתי ושמו מנסון והוא תלמיד ישן׃"));
hebsuperobject.biblemap.put("b44_21_17", new hebsuperobject(44,21,17,"ויהי כבאנו ירושלים ויקבלו אתנו האחים בשמחה׃"));
hebsuperobject.biblemap.put("b44_21_18", new hebsuperobject(44,21,18,"וממחרת היום נכנס פולוס עמנו אל יעקב ויבאו שמה כל הזקנים׃"));
hebsuperobject.biblemap.put("b44_21_19", new hebsuperobject(44,21,19,"וישאלם לשלום ויספר אחת לאחת את אשר עשה האלהים לגוים בשרותו׃"));
hebsuperobject.biblemap.put("b44_21_20", new hebsuperobject(44,21,20,"וישמעו ויהללו את האלהים ויאמרו אליו הנך ראה אחינו כמה רבבות יהודים באו להאמין וכלם מקנאים לתורה׃"));
hebsuperobject.biblemap.put("b44_21_21", new hebsuperobject(44,21,21,"והם שמעו עליך שמועה כי תלמד את כל היהודים אשר בקרב הגוים לסור ממשה באמרך כי אין עליהם למול את בניהם וללכת בחקות התורה׃"));
hebsuperobject.biblemap.put("b44_21_22", new hebsuperobject(44,21,22,"ועתה מה לעשות הנה האסף יאסף המון העם כי ישמעו כי באת׃"));
hebsuperobject.biblemap.put("b44_21_23", new hebsuperobject(44,21,23,"לכן עשה זאת אפוא אשר נאמר אליך׃"));
hebsuperobject.biblemap.put("b44_21_24", new hebsuperobject(44,21,24,"הנה יש אתנו ארבעה אנשים אשר נדר עליהם אתם קח לך והטהר אתם ושלם ההוצאות בעדם למען אשר יגלחו את ראשם וידעו כלם כי שמועת שוא שמעו עליך וכי אתה בעצמך מתהלך בחקות התורה׃"));
hebsuperobject.biblemap.put("b44_21_25", new hebsuperobject(44,21,25,"ועל דבר המאמינים בגוים כתבנו וגזרנו אשר לא ישמרו דבר מאלה רק להשמר מזבחי אלילים ומן הדם ומבשר הנחנק ומן הזנות׃"));
hebsuperobject.biblemap.put("b44_21_26", new hebsuperobject(44,21,26,"ויקח פולוס את האנשים ויטהר אתם וממחרת בא אל המקדש ויגד כי מלאו ימי טהרתם עד כי הקרב קרבן כל אחד מהם׃"));
hebsuperobject.biblemap.put("b44_21_27", new hebsuperobject(44,21,27,"ויהי כאשר קרבו שבעת הימים לכלות והיהודים אשר מאסיא ראו אתו במקדש ויעוררו את כל ההמון וישלחו בו את ידיהם׃"));
hebsuperobject.biblemap.put("b44_21_28", new hebsuperobject(44,21,28,"ויצעקו לאמר אנשי ישראל עזרו זה הוא האיש המלמד את כל אדם אשר בכל הארץ סרה על העם הזה ועל התורה ועל המקום הזה וגם הביא אל המקדש אנשים יונים ויחלל את המקום הקדוש הזה׃"));
hebsuperobject.biblemap.put("b44_21_29", new hebsuperobject(44,21,29,"כי היו ראים את טרופימוס האפסי בעיר אתו ויחשבו כי פולוס הביא אתו אל המקדש׃"));
hebsuperobject.biblemap.put("b44_21_30", new hebsuperobject(44,21,30,"ותהם כל העיר וירץ העם ויקבץ ויאחזו את פולוס וימשכהו אל מחוץ למקדש ופתאם סגרו הדלתות׃"));
hebsuperobject.biblemap.put("b44_21_31", new hebsuperobject(44,21,31,"והמה מבקשים להמיתו והשמועה באה אל שר האלף של הגדוד כי כל ירושלים נבוכה׃"));
hebsuperobject.biblemap.put("b44_21_32", new hebsuperobject(44,21,32,"וימהר ויקח אתו אנשי צבא ושרי מאות וירץ אליהם ויהי כראותם את שר האלף ואת אנשי הצבא ויחדלו מהכות את פולוס׃"));
hebsuperobject.biblemap.put("b44_21_33", new hebsuperobject(44,21,33,"ויגש שר האלף ויחזק בו ויצו לאסרו בנחשתים וישאל מי הוא זה ומה עשה׃"));
hebsuperobject.biblemap.put("b44_21_34", new hebsuperobject(44,21,34,"ויענו מן העם אלה בכה ואלה בכה ולא יכל לדעת מאומה אל נכון מרב השאון ויצו להוליכו אל המצד׃"));
hebsuperobject.biblemap.put("b44_21_35", new hebsuperobject(44,21,35,"ויהי כבואו עד המעלות וישאהו אנשי הצבא מפני חמת העם׃"));
hebsuperobject.biblemap.put("b44_21_36", new hebsuperobject(44,21,36,"כי עם רב הלך אחריו והם צעקים ואמרים הסר אתו׃"));
hebsuperobject.biblemap.put("b44_21_37", new hebsuperobject(44,21,37,"וכאשר הקריב פולוס להאסף אל המצד אמר אל שר האלף התניח לי לדבר אליך דבר ויאמר הידעת יונית׃"));
hebsuperobject.biblemap.put("b44_21_38", new hebsuperobject(44,21,38,"האם אינך המצרי אשר לפני הימים האלה העיר מרד והוציא המדברה ארבעת אלפים אנשי דמים׃"));
hebsuperobject.biblemap.put("b44_21_39", new hebsuperobject(44,21,39,"ויאמר פולוס איש יהודי אנכי מטרסוס בן עיר קיליקיא אשר איננה בלי שם ועתה אשאלה מאתך הניחה לי לדבר אל העם׃"));
hebsuperobject.biblemap.put("b44_21_40", new hebsuperobject(44,21,40,"וינח לו ויעמד פולוס על המעלות הניף ידו אל העם ותהי דממה רבה וידבר בלשון עברית ויאמר׃"));
hebsuperobject.biblemap.put("b44_22_1", new hebsuperobject(44,22,1,"אנשים אחים ואבות שמעו נא את דברי התנצלותי אליכם׃"));
hebsuperobject.biblemap.put("b44_22_2", new hebsuperobject(44,22,2,"ויהי כשמעם כי הוא מדבר אליהם בלשון עברית ויחשו עוד יותר׃"));
hebsuperobject.biblemap.put("b44_22_3", new hebsuperobject(44,22,3,"ויאמר איש יהודי אנכי נולד בטרסוס אשר בקיליקיא ומגדל בעיר הזאת לרגלי גמליאל ומלמד לפי דקדוקי תורת אבותינו ואהי מקנא לאלהים כמוכם כלכם היום׃"));
hebsuperobject.biblemap.put("b44_22_4", new hebsuperobject(44,22,4,"וארדף את הדרך הזאת עד מות ואהי אסר ומסגיר לכלא אנשים ונשים׃"));
hebsuperobject.biblemap.put("b44_22_5", new hebsuperobject(44,22,5,"כאשר גם יעיד עלי הכהן הגדול וכל בית הזקנים אשר מהם לקחתי מכתבים ואלך אל האחים לדמשק לאסר אף את הנמצאים שמה להביאם ירושלים למען יוסרו׃"));
hebsuperobject.biblemap.put("b44_22_6", new hebsuperobject(44,22,6,"ויהי אנכי הלך וקרב לדמשק כעת צהרים ופתאם נגה עלי מסביב אור גדול מן השמים׃"));
hebsuperobject.biblemap.put("b44_22_7", new hebsuperobject(44,22,7,"ואפל ארצה ואשמע קול מדבר אלי שאול שאול למה תרדפני׃"));
hebsuperobject.biblemap.put("b44_22_8", new hebsuperobject(44,22,8,"ואען ואמר מי אתה אדני ויאמר אלי אני ישוע הנצרי אשר אתה רדפו׃"));
hebsuperobject.biblemap.put("b44_22_9", new hebsuperobject(44,22,9,"והאנשים אשר אתי ראו את האור וייראו ואת קול המדבר אלי לא שמעו׃"));
hebsuperobject.biblemap.put("b44_22_10", new hebsuperobject(44,22,10,"ואמר מה אעשה אדני ויאמר אלי האדון קום לך אל דמשק ושם יאמר לך את כל אשר צוית לעשות׃"));
hebsuperobject.biblemap.put("b44_22_11", new hebsuperobject(44,22,11,"ואני לא יכלתי לראות מפני זהר האור ההוא והאנשים אשר אתי הוליכוני ביד ואבא לדמשק׃"));
hebsuperobject.biblemap.put("b44_22_12", new hebsuperobject(44,22,12,"ואיש חסיד כפי התורה ושמו חנניה אשר קנה לו שם טוב בקרב כל היהודים הישבים שם׃"));
hebsuperobject.biblemap.put("b44_22_13", new hebsuperobject(44,22,13,"ויבא אלי ויעמד ויאמר אלי שאול אחי שוב ראה ובשעה ההיא נפקחו עיני וראיתיהו׃"));
hebsuperobject.biblemap.put("b44_22_14", new hebsuperobject(44,22,14,"ויאמר אלהי אבותינו בחר בך לדעת את רצונו ולראות את הצדיק ולשמע קול מפיהו׃"));
hebsuperobject.biblemap.put("b44_22_15", new hebsuperobject(44,22,15,"כי היו תהיה לו לעד אל כל בני האדם על כל אשר ראית ושמעת׃"));
hebsuperobject.biblemap.put("b44_22_16", new hebsuperobject(44,22,16,"ועתה למה תתמהמה קום והטבל והתרחץ מחטאתיך בקראך בשמו׃"));
hebsuperobject.biblemap.put("b44_22_17", new hebsuperobject(44,22,17,"ויהי בשובי אל ירושלים ואתפלל במקדש ותהי עלי יד יהוה׃"));
hebsuperobject.biblemap.put("b44_22_18", new hebsuperobject(44,22,18,"וארא אותו מדבר אלי חושה צא במהרה מירושלים כי לא יקבלו את עדותך עלי׃"));
hebsuperobject.biblemap.put("b44_22_19", new hebsuperobject(44,22,19,"ואמר הלא הם יודעים את אשר השלכתי אל הכלא והלקיתי בבתי הכנסיות את המאמינים בך׃"));
hebsuperobject.biblemap.put("b44_22_20", new hebsuperobject(44,22,20,"ובהשפך דם אסטפנוס עדך אף אני עמדתי שם חפץ בהרגתו ושומר את בגדי הרגיו׃"));
hebsuperobject.biblemap.put("b44_22_21", new hebsuperobject(44,22,21,"ויאמר אלי לך כי אני אל הגיום עד למרחוק אשלחך׃"));
hebsuperobject.biblemap.put("b44_22_22", new hebsuperobject(44,22,22,"וישמעו אליו עד הדבר הזה וישאו את קולם ויאמרו הסירו איש כזה מעל האדמה כי איננו ראוי כי יחיה׃"));
hebsuperobject.biblemap.put("b44_22_23", new hebsuperobject(44,22,23,"ויהי בהיותם צעקים ומשליכים את בגדיהם מעליהם וזרקים עפר השמימה׃"));
hebsuperobject.biblemap.put("b44_22_24", new hebsuperobject(44,22,24,"ויצו שר האלף להוליכו אל המצד ויאמר לבדקו במלקות למען ידע מדוע המה ככה צעקים עליו׃"));
hebsuperobject.biblemap.put("b44_22_25", new hebsuperobject(44,22,25,"ויהי כאשר אסר להכותו ברצועות ויאמר אל שר המאה העמד עליו האף רשיון לכם להכות איש רומי בלא דין ומשפט׃"));
hebsuperobject.biblemap.put("b44_22_26", new hebsuperobject(44,22,26,"וילך שר המאה כשמעו את הדבר הזה ויגד לשר האלף לאמר ראה מה תעשה כי האיש הזה רומי הוא׃"));
hebsuperobject.biblemap.put("b44_22_27", new hebsuperobject(44,22,27,"ויבא שר האלף ויאמר אמר לי הרומי אתה ויאמר הן׃"));
hebsuperobject.biblemap.put("b44_22_28", new hebsuperobject(44,22,28,"ויען שר האלף אני בכסף רב קניתי לי משפט האזרחות הזאת ויאמר פולוס ואני אף נולדתי בה׃"));
hebsuperobject.biblemap.put("b44_22_29", new hebsuperobject(44,22,29,"אז הרפו ממנו האנשים אשר באו לבדקו וגם שר האלף ירא בדעתו כי רומי הוא והוא אסר אותו׃"));
hebsuperobject.biblemap.put("b44_22_30", new hebsuperobject(44,22,30,"וממחרת בקש לדעת אל נכון על מה שטנים אותו היהודים ויתר מוסרותיו ויצו להביא את ראשי הכהנים ואת כל הסנהדרין ויורד את פולוס ויעמידהו בתוכם׃"));
hebsuperobject.biblemap.put("b44_23_1", new hebsuperobject(44,23,1,"ויבט פולוס אל הסנהדרין ויאמר אנשים אחים בכל תמת לבבי התהלכתי לפני אלהים עד היום הזה׃"));
hebsuperobject.biblemap.put("b44_23_2", new hebsuperobject(44,23,2,"ויצו חנניה הכהן הגדול את העמדים עליו להכותו על פיו׃"));
hebsuperobject.biblemap.put("b44_23_3", new hebsuperobject(44,23,3,"ויאמר אליו פולוס יככה אלהים הקיר המסיד האמנם ישב אתה לדין אתי כתורה ואתה מצוה להכותי שלא כתורה׃"));
hebsuperobject.biblemap.put("b44_23_4", new hebsuperobject(44,23,4,"ויאמרו העמדים שם את הכהן הגדול לאלהים תחרף׃"));
hebsuperobject.biblemap.put("b44_23_5", new hebsuperobject(44,23,5,"ויאמר פולוס אחי לא ידעתי אשר הוא הכהן הגדול כי כתוב נשיא בעמך לא תאר׃"));
hebsuperobject.biblemap.put("b44_23_6", new hebsuperobject(44,23,6,"ופולוס ידע כי מקצתם צדוקים ומקצתם פרושים ויצעק בתוך הסנהדרין אנשים אחים פרוש בן פרוש אנכי ועל תקות המתים ותחיתם אני נדין׃"));
hebsuperobject.biblemap.put("b44_23_7", new hebsuperobject(44,23,7,"ובדברו הדבר הזה היה ריב בין הצדוקים ובין הפרושים ויחלק ההמון׃"));
hebsuperobject.biblemap.put("b44_23_8", new hebsuperobject(44,23,8,"כי הצדוקים אמרים אין תחיה ואין מלאך ורוח והפרושים מודים בשניהם׃"));
hebsuperobject.biblemap.put("b44_23_9", new hebsuperobject(44,23,9,"ותהי המלה גדולה ויקומו הסופרים אשר מכת הפרושים ויתוכחו לאמר לא מצאנו דבר רע באיש הזה ואם רוח דבר אליו או מלאך אל נלחמה באלהים׃"));
hebsuperobject.biblemap.put("b44_23_10", new hebsuperobject(44,23,10,"והריב הולך וחזק ושר האלף דאג פן ישסעו את פולוס ויצו את אנשי הצבא לרדת ולחטף אתו מתוכם ולהכיאו אל המצד׃"));
hebsuperobject.biblemap.put("b44_23_11", new hebsuperobject(44,23,11,"ובלילה ההוא נצב עליו האדון ויאמר חזק פולוס כי כאשר העידות לי בירושלים כן העד תעיד ברומי׃"));
hebsuperobject.biblemap.put("b44_23_12", new hebsuperobject(44,23,12,"ויהי לפנות הבקר ויתחברו היהודים ויאסרו אסר על נפשם לאמר כי לא יאכלו ולא ישתו עד אם יהרגו את פולוס׃"));
hebsuperobject.biblemap.put("b44_23_13", new hebsuperobject(44,23,13,"ומספר הבאים באלה הזאת היה יותר מארבעים׃"));
hebsuperobject.biblemap.put("b44_23_14", new hebsuperobject(44,23,14,"ויגשו לא ראשי הכהנים ואל הזקנים לאמר אסר אסרנו על נפשנו לבלתי טעם מאומה עד אם הרגנו את פולוס׃"));
hebsuperobject.biblemap.put("b44_23_15", new hebsuperobject(44,23,15,"ועתה הודיעו אתם והסנהדרין את שר האלף ויורידהו מחר אליכם כאלו תחפצו לדרש היטב את ענינו ואנחנו נכונים להמיתו בטרם יקרב אליכם׃"));
hebsuperobject.biblemap.put("b44_23_16", new hebsuperobject(44,23,16,"וישמע בן אחות פולוס את ארבם וילך ויבא אל המצד ויגד לפולוס׃"));
hebsuperobject.biblemap.put("b44_23_17", new hebsuperobject(44,23,17,"ויקרא פולוס לאחד משרי המאות ויאמר אליו הולך את הבחור הזה אל שר האלף כי דבר לו להודיעו׃"));
hebsuperobject.biblemap.put("b44_23_18", new hebsuperobject(44,23,18,"ויקחהו ויוליכהו אל שר האלף ויאמר פולוס האסיר קראני אליו ויבקש ממני להוליך אליך את הבחור הזה כי דבר לו להגיד לך׃"));
hebsuperobject.biblemap.put("b44_23_19", new hebsuperobject(44,23,19,"ויאחז שר האלף בידו ויסר עמו לבדו וישאלהו מה הוא זה אשר לך להודיעני׃"));
hebsuperobject.biblemap.put("b44_23_20", new hebsuperobject(44,23,20,"ויאמר כי נועצו היהודים יחדו לבקש ממך כי מחר תוריד את פולוס לפני הסנהדרין והמה כאלו חפצים לדעת היטב את ענינו׃"));
hebsuperobject.biblemap.put("b44_23_21", new hebsuperobject(44,23,21,"ואתה אל תשמע להם כי ארבים לו מהם יותר מארבעים איש אשר אסרו אסר על נפשם לבלתי אכל ושתות בטרם יהרגוהו והמה עתה עמדים ומחכים להבטחתך׃"));
hebsuperobject.biblemap.put("b44_23_22", new hebsuperobject(44,23,22,"וישלח שר האלף את הבחור ויצוהו לבלתי אמר לאיש כי גלה לו את הדבר הזה׃"));
hebsuperobject.biblemap.put("b44_23_23", new hebsuperobject(44,23,23,"ויקרא לשני שרי מאות ויאמר החלצו מאתכם אנשי צבא מאתים ללכת לקסרין ופרשים שבעים ומשכי קשת מאתים מן השעה השלישית בלילה׃"));
hebsuperobject.biblemap.put("b44_23_24", new hebsuperobject(44,23,24,"ובהמות יכינו להרכיב את פולוס למען הביאו שלם אל פיליכס ההגמון׃"));
hebsuperobject.biblemap.put("b44_23_25", new hebsuperobject(44,23,25,"ויכתב אגרת וזה תכן דבריה׃"));
hebsuperobject.biblemap.put("b44_23_26", new hebsuperobject(44,23,26,"קלודיוס לוסיאס אל פיליכס ההגמון האדיר שלום׃"));
hebsuperobject.biblemap.put("b44_23_27", new hebsuperobject(44,23,27,"את האיש הזה תפשו היהודים ויבקשו המיתו ואבא עם אנשי הצבא ואפלטהו מתוכם בשמעי כי רומי הוא׃"));
hebsuperobject.biblemap.put("b44_23_28", new hebsuperobject(44,23,28,"ובאשר חפצתי לדעת על מה שמנו אתו הורדתיו אל הסנהדרין שלהם׃"));
hebsuperobject.biblemap.put("b44_23_29", new hebsuperobject(44,23,29,"ואמצא כי שטנו אתו על דברי שאלות דתם ולא על דבר אשר יהיה עליו חיב מיתה או מוסרות׃"));
hebsuperobject.biblemap.put("b44_23_30", new hebsuperobject(44,23,30,"ועתה הנה הגד לי שהיהודים מתנכלים באיש הזה ואשלחהו אליך מיד וגם את שטניו צויתי לבוא ולהגיש שטנתם לפניך ואתה שלום׃"));
hebsuperobject.biblemap.put("b44_23_31", new hebsuperobject(44,23,31,"ויקחו אנשי הצבא את פולוס כאשר צוו ויביאהו לילה אל אנטפטריס׃"));
hebsuperobject.biblemap.put("b44_23_32", new hebsuperobject(44,23,32,"וממחרת הניחו את הפרשים ללכת אתו וישובו למצד׃"));
hebsuperobject.biblemap.put("b44_23_33", new hebsuperobject(44,23,33,"והמה באו אל קסרין ויתנו את האגרת בידי ההגמון ויעמידו לפניו גם את פולוס׃"));
hebsuperobject.biblemap.put("b44_23_34", new hebsuperobject(44,23,34,"ויהי כקרא ההגמון את האגרת וישאל מאי זה מדינה הוא ויהי כשמעו כי הוא מקיליקיא ויאמר׃"));
hebsuperobject.biblemap.put("b44_23_35", new hebsuperobject(44,23,35,"אשמע את דבריך בבוא גם שטניך הנה ויצו לשמרו בבית המשפט אשר להורדוס׃"));
hebsuperobject.biblemap.put("b44_24_1", new hebsuperobject(44,24,1,"וירד חנניה הכהן הגדול אחרי חמשת ימים ואתו הזקנים וטרטלוס איש דברים ויודיעו את ההגמון את דבר ריבם עם פולוס׃"));
hebsuperobject.biblemap.put("b44_24_2", new hebsuperobject(44,24,2,"ויהי כאשר קראו לו ויחל טרטלוס לשטנו ויאמר׃"));
hebsuperobject.biblemap.put("b44_24_3", new hebsuperobject(44,24,3,"את אשר ישבנו על ידך בשלום רב ואשר נעשו תקנות רבות לעם הזה בהשגחתך נקבל על כל פנים ובכל מקום בכל תודה פיליכס האדיר׃"));
hebsuperobject.biblemap.put("b44_24_4", new hebsuperobject(44,24,4,"אך לבלתי הלאותך הרבה אתחנן אליך אשר תשמענו בקצר דברינו כחמלתך׃"));
hebsuperobject.biblemap.put("b44_24_5", new hebsuperobject(44,24,5,"כי מצאנו את האיש הזה כקטב ומגרה מדינים בין כל היהודים על פני תבל והוא ראש כת הנצרים׃"));
hebsuperobject.biblemap.put("b44_24_6", new hebsuperobject(44,24,6,"והוא גם נסה לחלל את המקדש ונתפש אותו ונחפץ לשפטו על פי תורתנו׃"));
hebsuperobject.biblemap.put("b44_24_7", new hebsuperobject(44,24,7,"ויבא עלינו לוסיאס שר האלף ברב כח ויחטפהו מידינו׃"));
hebsuperobject.biblemap.put("b44_24_8", new hebsuperobject(44,24,8,"ויצו את שטניו כי בוא יבאו לפניך ואתה כי תחקר אתו תוכל לדעת מפיהו את כל הדברים האלה אשר אנחנו טענים עליו׃"));
hebsuperobject.biblemap.put("b44_24_9", new hebsuperobject(44,24,9,"וימלאו היהודים את דבריו לאמר כי כן הוא׃"));
hebsuperobject.biblemap.put("b44_24_10", new hebsuperobject(44,24,10,"וירמז ההגמון אל פולוס לדבר ויען ויאמר יען אשר ידעתי כי זה כמה שנים שופט אתה לעם הזה הנני מצטדק בלב בטוח על עניני׃"));
hebsuperobject.biblemap.put("b44_24_11", new hebsuperobject(44,24,11,"אתה תוכל לדעת אשר לא עברו יותר משנים עשר יום מעת עלותי ירושלים להשתחות׃"));
hebsuperobject.biblemap.put("b44_24_12", new hebsuperobject(44,24,12,"ולא במקדש מצאוני מתוכח עם איש או מעורר מהומה בעם ולא בבתי הכנסיות ולא בעיר׃"));
hebsuperobject.biblemap.put("b44_24_13", new hebsuperobject(44,24,13,"גם אין ביכלתם להוכיח עלי את אשר עתה הם שטנים אותי׃"));
hebsuperobject.biblemap.put("b44_24_14", new hebsuperobject(44,24,14,"אבל את זאת מודה אני לפניך כי בדרך ההיא אשר יקראוה בשם כת בה אני עבד את אלהי אבותינו בהאמיני בכל הכתוב בתורה ובנביאים׃"));
hebsuperobject.biblemap.put("b44_24_15", new hebsuperobject(44,24,15,"ותקותי לאלהים אשר גם הם יחכו לה זאת היא כי עתידה להיות תחית המתים לצדיקים ולרשעים׃"));
hebsuperobject.biblemap.put("b44_24_16", new hebsuperobject(44,24,16,"ובזאת גם עמלתי להיות תמים לב לאלהים ולאדם תמיד׃"));
hebsuperobject.biblemap.put("b44_24_17", new hebsuperobject(44,24,17,"ומקץ שנים רבות באתי להביא נדבות לעמי ולהקריב קרבן׃"));
hebsuperobject.biblemap.put("b44_24_18", new hebsuperobject(44,24,18,"ואהי מטהר במקדש לא בהמון עם ולא במהומה וכן מצאוני אנשים יהודים מאסיא׃"));
hebsuperobject.biblemap.put("b44_24_19", new hebsuperobject(44,24,19,"אשר עליהם היה לעמד הנה לפניך ולענות בי אם היה להם דבר נגדי׃"));
hebsuperobject.biblemap.put("b44_24_20", new hebsuperobject(44,24,20,"או אלה ידברו נא המה מה מצאו בי עול בעמדי לפני הסנהדרין׃"));
hebsuperobject.biblemap.put("b44_24_21", new hebsuperobject(44,24,21,"בלתי אם הדבר האחד ההוא אשר קראתי בעמדי בתוכם כי על תחית המתים אני נדון היום לפניכם׃"));
hebsuperobject.biblemap.put("b44_24_22", new hebsuperobject(44,24,22,"ויהי כשמע פיליכס את הדרים האלה ויאחר את דינם לעת אחרת כי עניני הדרך הזאת נודעו לו היטב ויאמר ברדת אלי לוסיאס שר האלף אשפט על דברכם׃"));
hebsuperobject.biblemap.put("b44_24_23", new hebsuperobject(44,24,23,"ויצו את שר המאה לשום אתו במשמר ולתת לו רוחה ולבלתי מנע איש ממידעיו משרת אתו ומבוא אליו׃"));
hebsuperobject.biblemap.put("b44_24_24", new hebsuperobject(44,24,24,"ואחרי ימים אחדים בא פיליכס עם אשתו דרוסלה והיא יהודית וישלח לקרא לפולוס וישמע אתו על דבר האמונה במשיח׃"));
hebsuperobject.biblemap.put("b44_24_25", new hebsuperobject(44,24,25,"ויהי כדברו על הצדק והפרישות ועל הדין העתיד לבוא ויחרד פיליכס ויען עתה זה לך ולכשאפנה אשוב לקרא לך׃"));
hebsuperobject.biblemap.put("b44_24_26", new hebsuperobject(44,24,26,"והוא מקוה כי פולוס יתן לו שחד למען יתירהו ובעבור זה קרא לו פעמים הרבה וידבר עמו׃"));
hebsuperobject.biblemap.put("b44_24_27", new hebsuperobject(44,24,27,"ויהי מקץ שנתים ימים ויקם פרקיוס פסטוס תחת פיליכס ופיליכס חפץ להתרצות אל היהודים ויעזב את פולוס חבוש בבית האסורים׃"));
hebsuperobject.biblemap.put("b44_25_1", new hebsuperobject(44,25,1,"ויבא פסטוס אל המדינה ויעל אחרי שלשת ימים מקסרין לירושלים׃"));
hebsuperobject.biblemap.put("b44_25_2", new hebsuperobject(44,25,2,"והכהן הגדול וראשי היהודים הודיעהו את ריבם עם פולוס׃"));
hebsuperobject.biblemap.put("b44_25_3", new hebsuperobject(44,25,3,"ויפצרו בו וישאלו ממנו לעשות חסד עמהם להביאו ירושלים והמה מתנכלים אתו להמיתו בדרך׃"));
hebsuperobject.biblemap.put("b44_25_4", new hebsuperobject(44,25,4,"ויען אותם פסטוס כי עצור פולוס בקסרין וכי גם הוא ישוב שמה בקרוב׃"));
hebsuperobject.biblemap.put("b44_25_5", new hebsuperobject(44,25,5,"ויאמר לכן ירדו אתי העצומים שבכם ואם יש מאום באיש הזה יענו בו׃"));
hebsuperobject.biblemap.put("b44_25_6", new hebsuperobject(44,25,6,"וישב בתוכם יותר מימים עשרה וירד אל קסרין וממחרת ישב על כסא המשפט ויצו להביא את פולוס׃"));
hebsuperobject.biblemap.put("b44_25_7", new hebsuperobject(44,25,7,"הוא בא והיהודים אשר ירדו מירושלים סבבהו ויאשימו את פולוס באשמות רבות וקשות אשר לא יכלו להוכיח׃"));
hebsuperobject.biblemap.put("b44_25_8", new hebsuperobject(44,25,8,"והוא הצטדק לאמר לא חטאתי במאומה לא לדת היהודים ולא למקדש ולא לקיסר׃"));
hebsuperobject.biblemap.put("b44_25_9", new hebsuperobject(44,25,9,"ופסטוס חפץ להתרצות אל היהודים ויען את פולוס ויאמר התחפץ לעלות ירושלים להשפט על אלה לפני שמה׃"));
hebsuperobject.biblemap.put("b44_25_10", new hebsuperobject(44,25,10,"ויאמר פולוס לפני כסא משפט הקיסר אני עמד ושם נכון לי להשפט לא הרעתי ליהודים כאשר ידעת היטב גם אתה׃"));
hebsuperobject.biblemap.put("b44_25_11", new hebsuperobject(44,25,11,"אם הרעתי ויש בי דבר משפט מות בל אחשך נפשי ממות ואם אין בי מאומה מכל אשר הם ענים בי איש לא יוכל להסגירני אל ידם את הקיסר אני קורא לדיני׃"));
hebsuperobject.biblemap.put("b44_25_12", new hebsuperobject(44,25,12,"ויועץ פסטוס עם יועציו ויען אתו לאמר את הקיסר קראת אל הקיסר תלך׃"));
hebsuperobject.biblemap.put("b44_25_13", new hebsuperobject(44,25,13,"ויהי אחרי ימים וירדו המלך אגרפס וברניקה אל קסרין לשאל לשלום פסטוס׃"));
hebsuperobject.biblemap.put("b44_25_14", new hebsuperobject(44,25,14,"ויהי כי ארכו להם הימים שם ויספר פסטוס למלך את ריב פולוס לאמר יש פה איש אשר פיליכס הניחו אסור׃"));
hebsuperobject.biblemap.put("b44_25_15", new hebsuperobject(44,25,15,"ובהיותי בירושלים דברו אלי ראשי הכהנים וזקני היהודים על אדותיו ויבקשו ממני לחרץ משפטו׃"));
hebsuperobject.biblemap.put("b44_25_16", new hebsuperobject(44,25,16,"ואען אותם כי אין מנהג הרומיים למגן איש לאבד בטרם יעמד הנשטן לנכח שטניו ונתן לו מקום להצטדק מן השטנה׃"));
hebsuperobject.biblemap.put("b44_25_17", new hebsuperobject(44,25,17,"ולכן כאשר באו הנה יחד חשתי ולא התמהמהתי כי ממחרת היום ישבתי על כסא המשפט ואצוה להביא את האיש׃"));
hebsuperobject.biblemap.put("b44_25_18", new hebsuperobject(44,25,18,"ויעמדו עליו שטניו ולא הביאו עליו דבר רע מאשר הייתי חשד אתו׃"));
hebsuperobject.biblemap.put("b44_25_19", new hebsuperobject(44,25,19,"רק היו להם דברי ריבת לנגדו על עבודת אלהיהם ועל מת אחד ישוע שמו אשר אמר עליו פולוס כי הוא חי׃"));
hebsuperobject.biblemap.put("b44_25_20", new hebsuperobject(44,25,20,"ותכבד בעיני השאלה הזאת ואמר החפץ אתה ללכת ירושלים ולהשפט שם על אלה׃"));
hebsuperobject.biblemap.put("b44_25_21", new hebsuperobject(44,25,21,"אבל פולוס שאל להניחו במשמר עד צאת משפטו מלפני אגוסטוס ואצוה לשמרו עד אשר אשלח אתו אל הקיסר׃"));
hebsuperobject.biblemap.put("b44_25_22", new hebsuperobject(44,25,22,"ויאמר אגרפס אל פסטוס גם אני חפץ לשמע את האיש ויאמר מחר תשמענו׃"));
hebsuperobject.biblemap.put("b44_25_23", new hebsuperobject(44,25,23,"ויהי ממחרת כבוא אגרפס וברניקה בהוד גדול ויבאו אל אלם המשמעת המה ושרי האלף ונגידי העיר ויצו פסטוס ויביאו את פולוס׃"));
hebsuperobject.biblemap.put("b44_25_24", new hebsuperobject(44,25,24,"ויאמר פסטוס אגרפס המלך וכל האישים אשר אתם פה אתנו הנכם ראים את האיש אשר בעבורו פגעו בי כל המון היהודים גם בירושלים וגם פה ויצעקו כי אינו בדין שיחיה עוד׃"));
hebsuperobject.biblemap.put("b44_25_25", new hebsuperobject(44,25,25,"ואני כאשר הכרתי כי לא עשה דבר לחיבו מיתה והוא גם הוא קרא את אגוסטוס לדינו יעצתי לשלח אתו שמה׃"));
hebsuperobject.biblemap.put("b44_25_26", new hebsuperobject(44,25,26,"אך אין לי לכתב עליו דבר נכון לאדנינו ובעבור זאת הביאתיו לפניכם וביותר לפניך המלך אגרפס למען יחקר וידעתי מה אכתב׃"));
hebsuperobject.biblemap.put("b44_25_27", new hebsuperobject(44,25,27,"כי לא יתכן בעיני לשלוח אסיר מבלי הודיע גם את עלילת הדברים אשר שמו לו׃"));
hebsuperobject.biblemap.put("b44_26_1", new hebsuperobject(44,26,1,"ויאמר אגרפס אל פולוס נתן לך לדבר בעד נפשך אז יצטדק פולוס ויושט את ידו ויאמר׃"));
hebsuperobject.biblemap.put("b44_26_2", new hebsuperobject(44,26,2,"מאשר אני את נפשי המלך אגרפס כי לפניך אצטדק היום על כל אשר שטנים אתי היהודים׃"));
hebsuperobject.biblemap.put("b44_26_3", new hebsuperobject(44,26,3,"כי אתה ידע היטב כל המנהגים והשאלות אשר בין היהודים ועל כן אשאלה מאתך לשמע אתי כארך רוחך׃"));
hebsuperobject.biblemap.put("b44_26_4", new hebsuperobject(44,26,4,"הנה את דרכי מנעורי אשר התהלכתי בה מאז בתוך עמי ובירושלים ידעים אותה כל היהודים׃"));
hebsuperobject.biblemap.put("b44_26_5", new hebsuperobject(44,26,5,"כי מימים ראשונים ידעוני אם ירצו להעיד כי כפרוש התנהגתי על פי הכת המקפדת ביותר בעבודתנו׃"));
hebsuperobject.biblemap.put("b44_26_6", new hebsuperobject(44,26,6,"ועתה אני עמד להשפט על תקות ההבטחה אשר הבטיחה האלהים את אבותינו׃"));
hebsuperobject.biblemap.put("b44_26_7", new hebsuperobject(44,26,7,"ואשר שנים עשר שבטינו מיחלים להגיע לה בעבדם את יהוה תמיד יומם ולילה על דבר התקוה הזאת אגרפס המלך שטנים אותי היהודים׃"));
hebsuperobject.biblemap.put("b44_26_8", new hebsuperobject(44,26,8,"מדוע יפלא בעיניכם כי האלהים יחיה מתים׃"));
hebsuperobject.biblemap.put("b44_26_9", new hebsuperobject(44,26,9,"הן לפנים אני חשבתי צדקה לצרר את שם ישוע הנצרי עד מאד׃"));
hebsuperobject.biblemap.put("b44_26_10", new hebsuperobject(44,26,10,"כאשר עשיתי בירושלים וגם קדושים רבים אני הסגרתי לבתי כלאים ברשיון אשר קבלתי מאת הכהנים הגדולים וכשנהרגו הסכמתי׃"));
hebsuperobject.biblemap.put("b44_26_11", new hebsuperobject(44,26,11,"ובכל בתי הכנסיות יסרתי אתם פעמים רבות ואנסתים לגדף ואתהוללה בם עד מאד וארדפם עד לערים אשר חוצה לארץ׃"));
hebsuperobject.biblemap.put("b44_26_12", new hebsuperobject(44,26,12,"ויהי בלכתי על זאת לדמשק ברשיון הכהנים הגדולים ובמצותם׃"));
hebsuperobject.biblemap.put("b44_26_13", new hebsuperobject(44,26,13,"והנה אדני המלך בצהרים בדרך ראיתי אור צח מזהר השמש אשר משמים נגה עלי מסביב ועל ההלכים אתי׃"));
hebsuperobject.biblemap.put("b44_26_14", new hebsuperobject(44,26,14,"ונפל כלנו ארצה ואשמע קול מדבר אלי בלשון עברית לאמר שאול שאול למה תרדפני קשה לך לבעט בדרבנות׃"));
hebsuperobject.biblemap.put("b44_26_15", new hebsuperobject(44,26,15,"ואמר מי אתה אדני ויאמר אנכי ישוע אשר אתה רדף׃"));
hebsuperobject.biblemap.put("b44_26_16", new hebsuperobject(44,26,16,"אבל קום ועמד על רגליך כי לבעבור זאת נראיתי אליך לבחר בך למשרת ולעד על אשר ראית ועל אשר אראך׃"));
hebsuperobject.biblemap.put("b44_26_17", new hebsuperobject(44,26,17,"בהצילי אותך מן העם ומן הגוים אשר אשלחך עתה אליהם׃"));
hebsuperobject.biblemap.put("b44_26_18", new hebsuperobject(44,26,18,"לפקח את עיניהם למען ישובו מחשך לאור ומיד השטן אל האלהים וימצאו באמונתם בי את סליחת החטאים ואת הנחלה בתוך המקדשים׃"));
hebsuperobject.biblemap.put("b44_26_19", new hebsuperobject(44,26,19,"על כן המלך אגרפס לא המריתי את המראה אשר ראיתי מן השמים׃"));
hebsuperobject.biblemap.put("b44_26_20", new hebsuperobject(44,26,20,"כי אם קראתי ראשונה לישבי דמשק וירושלים ובכל ארץ יהודה וגם לגוים כי ינחמו וישובו אל האלהים ויעשו מעשים ראוים לתשובה׃"));
hebsuperobject.biblemap.put("b44_26_21", new hebsuperobject(44,26,21,"ובגלל הדבר הזה תפשו אתי היהודים במקדש ויבקשו להמיתני׃"));
hebsuperobject.biblemap.put("b44_26_22", new hebsuperobject(44,26,22,"והאלהים היה בעזרי ועד היום הזה עמד אני ומעיד לפני קטן וגדול ואינני מדבר דבר זולתי אשר דברו הנביאים ומשה כי עתידות הנה להיות׃"));
hebsuperobject.biblemap.put("b44_26_23", new hebsuperobject(44,26,23,"כי המשיח מענה הוא וכי ראשון הוא לקמים מן המתים להפיץ אור בעם ובגוים׃"));
hebsuperobject.biblemap.put("b44_26_24", new hebsuperobject(44,26,24,"ויהי הוא מצטדק כזאת ויען פסטוס בקול גדול לאמר הנך משתגע פולוס רב הלמוד הביאך לידי שגעון׃"));
hebsuperobject.biblemap.put("b44_26_25", new hebsuperobject(44,26,25,"ויאמר פולוס אינני משגע פסטוס האדיר כי אם דברי אמת וטעם אביע׃"));
hebsuperobject.biblemap.put("b44_26_26", new hebsuperobject(44,26,26,"כי המלך יודע את אלה ועל זאת גם בבטחון אני מדבר אליו יען אשר לא אאמין כי נעלם ממנו דבר מן הדברים האלה כי לא בקרן זוית נעשתה זאת׃"));
hebsuperobject.biblemap.put("b44_26_27", new hebsuperobject(44,26,27,"המלך אגרפס המאמין אתה בנביאים ידעתי כי מאמין אתה׃"));
hebsuperobject.biblemap.put("b44_26_28", new hebsuperobject(44,26,28,"ויאמר אגרפס אל פולוס עוד מעט ופתיתני להיות נצרי׃"));
hebsuperobject.biblemap.put("b44_26_29", new hebsuperobject(44,26,29,"ויאמר פולוס אבקשה מאלהים אשר אם במעט ואם בהרבה לא אתה לבדך כי גם כל השמעים אותי יהיו כמוני זולתי המוסרות האלה׃"));
hebsuperobject.biblemap.put("b44_26_30", new hebsuperobject(44,26,30,"ויהי בדברו הדבר הזה ויקם המלך וההגמון וברניקה והישבים אתם׃"));
hebsuperobject.biblemap.put("b44_26_31", new hebsuperobject(44,26,31,"ויסורו החדרה וידברו איש אל רעהו לאמר האיש הזה לא עשה דבר אשר יהיה עליו חיב מיתה או מוסרות׃"));
hebsuperobject.biblemap.put("b44_26_32", new hebsuperobject(44,26,32,"ויאמר אגרפס אל פסטוס האיש הזה יוכל להפטר לולא קרא את הקיסר לדינו׃"));
hebsuperobject.biblemap.put("b44_27_1", new hebsuperobject(44,27,1,"וכאשר נגמר הדין כי נעבר באניה אל איטליא מסרו את פולוס ומקצת אסירים אחרים אל שר המאה לגדוד אגוסטוס ושמו יוליוס׃"));
hebsuperobject.biblemap.put("b44_27_2", new hebsuperobject(44,27,2,"ונרד אל אניה אדרמטית נכנים לבוא על פני חוף אסיא ונעבר הימה ויהי אתנו ארסטרכוס מוקדון מן תסלוניקי׃"));
hebsuperobject.biblemap.put("b44_27_3", new hebsuperobject(44,27,3,"וממחרת הגענו אל צידון ויוליוס עשה עם פולוס חסד וינח לו ללכת אל מידעיו להעזר בידם׃"));
hebsuperobject.biblemap.put("b44_27_4", new hebsuperobject(44,27,4,"ונלך משם ונעבר בסביבות קפרוס כי הרוחות היו לנגדנו׃"));
hebsuperobject.biblemap.put("b44_27_5", new hebsuperobject(44,27,5,"ונעבר את הים אשר לפני קיליקיא ופמפוליא ונבא אל מורא אשר בלוקיא׃"));
hebsuperobject.biblemap.put("b44_27_6", new hebsuperobject(44,27,6,"וימצא שם שר המאה אניה אלכסנדרית באה לאיטליא ויעבירנו לתוכה׃"));
hebsuperobject.biblemap.put("b44_27_7", new hebsuperobject(44,27,7,"והאניה הלכה בכבדות ימים רבים ואחרי אשר יגענו ובאנו אל מול קנידוס לא הניחנו הרוח לחתר אל היבשה ונעבר מצד לקריטי על פני הר סלמוני׃"));
hebsuperobject.biblemap.put("b44_27_8", new hebsuperobject(44,27,8,"ואחרי אשר עברנו בתלאות באנו למקום אחד נקרא קלילמני אשר קרובה לו עיר ושמה לסיא׃"));
hebsuperobject.biblemap.put("b44_27_9", new hebsuperobject(44,27,9,"ויהי מקץ ימים רבים כאשר באה עת הסכנה לירדי הים כי גם הצום כבר עבר ויזהר אתם פולוס׃"));
hebsuperobject.biblemap.put("b44_27_10", new hebsuperobject(44,27,10,"ויאמר אליהם אנשים ראה אני כי הליכתנו תהיה אך מרי ונזק גדול לא לבד למעמסה ולאניה כי אם גם לנפשותינו׃"));
hebsuperobject.biblemap.put("b44_27_11", new hebsuperobject(44,27,11,"ושר המאה לא שמע אל דברי פולוס כי אם אל רב החבל ואל בעל האניה׃"));
hebsuperobject.biblemap.put("b44_27_12", new hebsuperobject(44,27,12,"ובאשר החוף איננו טוב לעמד בו בימי הסתיו יעצו הרבים לעבר משם לאמר אולי נוכל הגיע לפיניכס וישבנו שם בימי הסתו והוא חוף בקריטי פונה דרך הדרום מערבה ודרך הצפון מערבה׃"));
hebsuperobject.biblemap.put("b44_27_13", new hebsuperobject(44,27,13,"ורוח נשבת לאט מדרום ויחשבו כי תצלח עצתם בידם ויעלו העוגין ויעברו לעמת שפת קריטי׃"));
hebsuperobject.biblemap.put("b44_27_14", new hebsuperobject(44,27,14,"ויהי כמעט אחרי כן ותפגע בה רוח נחשל הנקראה אוריקלידון׃"));
hebsuperobject.biblemap.put("b44_27_15", new hebsuperobject(44,27,15,"ותטרף האניה ולא יכלה לעמד נגד הרוח ונרף ידינו ממנה וננדף׃"));
hebsuperobject.biblemap.put("b44_27_16", new hebsuperobject(44,27,16,"ותרץ האניה אל עבר אי קטן הנקרא קלודה וכמעט לא יכלנו לאחז העברה׃"));
hebsuperobject.biblemap.put("b44_27_17", new hebsuperobject(44,27,17,"ואחרי משכם אותה אליהם ויקחו לעזר את כל אשר השיגה ידם ויחשבו את דפני האניה ומיראתם פן יפלו אל בין רכסי החול הורידו את כלי המפרש וכה נדפו׃"));
hebsuperobject.biblemap.put("b44_27_18", new hebsuperobject(44,27,18,"ויהי הסער הולך וסער עלינו וממחרת הטילו את המעמסה אל הים׃"));
hebsuperobject.biblemap.put("b44_27_19", new hebsuperobject(44,27,19,"וביום השלישי הטלנו בידינו את כלי האניה אל הים׃"));
hebsuperobject.biblemap.put("b44_27_20", new hebsuperobject(44,27,20,"ויהי ימים רבים גם השמש גם הכוכבים לא נראו והסערה חזקה עלינו עד מאד ותכרת ממנו כל תקות ישועה׃"));
hebsuperobject.biblemap.put("b44_27_21", new hebsuperobject(44,27,21,"ופולוס עמד בתוכם אחרי האריכם בצום ויאמר אנשים לו שמעתם אלי ולא יצאתם מקריטי כי אז לא קרנו הנזק הזה במריכם׃"));
hebsuperobject.biblemap.put("b44_27_22", new hebsuperobject(44,27,22,"ועתה אני אמר אליכם חזקו ואמצו כי לא תאבד נפש מכם בלתי הספינה לבדה׃"));
hebsuperobject.biblemap.put("b44_27_23", new hebsuperobject(44,27,23,"כי בלילה הזה נצב עלי מלאך האלהים אשר לו אנכי ואשר אני עבד אתו׃"));
hebsuperobject.biblemap.put("b44_27_24", new hebsuperobject(44,27,24,"ויאמר אל תירא פולוס עליך עוד לעמד לפני הקיסר ועתה הנה נתן לך האלהים את כל ההלכים אתך באניה׃"));
hebsuperobject.biblemap.put("b44_27_25", new hebsuperobject(44,27,25,"על כן אנשים חזקו ואמצו כי מאמין אני לאלהים כי כן יהיה כאשר נאמר אלי׃"));
hebsuperobject.biblemap.put("b44_27_26", new hebsuperobject(44,27,26,"אך השלך נשלך אל אחד האיים׃"));
hebsuperobject.biblemap.put("b44_27_27", new hebsuperobject(44,27,27,"ויהי כבוא ליל ארבעה עשר לטלטולנו בים אדריה כחצות הלילה ויחשבו המלחים כי קרבה להם יבשת׃"));
hebsuperobject.biblemap.put("b44_27_28", new hebsuperobject(44,27,28,"ויורידו את האנך וימצאו עמק הים עשרים קומה ויעברו מעט ויוסיפו להוריד את האנך וימצאו קומות חמש עשרה׃"));
hebsuperobject.biblemap.put("b44_27_29", new hebsuperobject(44,27,29,"וייראו פן יפגעו בשני הסלעים וישליכו מעל אחורי האניה ארבעה עוגינים אל הים ויכספו לאור היום׃"));
hebsuperobject.biblemap.put("b44_27_30", new hebsuperobject(44,27,30,"והמלחים בקשו לברח מן האניה ויורידו את העברה אל הים באמרם כי יש את נפשם לשלח עוגינים גם מראש האניה׃"));
hebsuperobject.biblemap.put("b44_27_31", new hebsuperobject(44,27,31,"ויאמר פולוס אל שר המאה ואל אנשי הצבא לאמר אם אלה לא ישבו אתנו באניה לא תוכלו אתם להושע׃"));
hebsuperobject.biblemap.put("b44_27_32", new hebsuperobject(44,27,32,"ויקצצו אנשי הצבא את חבלי העברה ויתנוה לנפול׃"));
hebsuperobject.biblemap.put("b44_27_33", new hebsuperobject(44,27,33,"והבקר טרם יאור ופולוס מבקש מכלם לטעם לחם לאמר היום יום ארבעה עשר אשר חכיתם צמים ולא טעמתם מאומה׃"));
hebsuperobject.biblemap.put("b44_27_34", new hebsuperobject(44,27,34,"על כן קרא אני אתכם לטעם לחם כי הוא למחיתכם כי איש איש מכם לא יפל משערת ראשו ארצה׃"));
hebsuperobject.biblemap.put("b44_27_35", new hebsuperobject(44,27,35,"הוא דבר את הדברים האלה והוא לקח את הלחס ויודה לאלהים לפני כלם ויבצע ויחל לאכל׃"));
hebsuperobject.biblemap.put("b44_27_36", new hebsuperobject(44,27,36,"ויאמץ לב כלם ויטעמו אכל גם המה׃"));
hebsuperobject.biblemap.put("b44_27_37", new hebsuperobject(44,27,37,"ואנחנו כל נפש אשר באניה מאתים ושבעים ושש׃"));
hebsuperobject.biblemap.put("b44_27_38", new hebsuperobject(44,27,38,"ויאכלו לשבעה ויקלו מעל האניה ויטילו את הצדה אל הים׃"));
hebsuperobject.biblemap.put("b44_27_39", new hebsuperobject(44,27,39,"הבקר אור ולא הכירו את הארץ אבל ראו כמפרץ וחוף לו ויועצו לנהג אליו את האניה אם יוכלו׃"));
hebsuperobject.biblemap.put("b44_27_40", new hebsuperobject(44,27,40,"ויגדעו את העוגינים ויעזבום לים ויתירו גם את מיתרי המנהיג ויפרשו מפרש התרן אל פני הרוח ויבקשו לבא אל החוף׃"));
hebsuperobject.biblemap.put("b44_27_41", new hebsuperobject(44,27,41,"ויפגעו במקום אשר הים משני עבריו ותדבק בו האניה ותעמד ראשה לא ינוע ואחוריה נשברו משאון הגלים׃"));
hebsuperobject.biblemap.put("b44_27_42", new hebsuperobject(44,27,42,"ותהי עצת אנשי הצבא להמית את האסירים פן ישחה איש מהם וימלט׃"));
hebsuperobject.biblemap.put("b44_27_43", new hebsuperobject(44,27,43,"ושר המאה חפץ להציל את פולוס וינא את עצתם ויאמר מי ידע לשחות ירד ויעבר ליבשה בראשנה והנשארים אלה על קרשים ואלה על שברי האניה׃"));
hebsuperobject.biblemap.put("b44_27_44", new hebsuperobject(44,27,44,"ויהי כן וימלטו כלם אל היבשה׃"));
hebsuperobject.biblemap.put("b44_28_1", new hebsuperobject(44,28,1,"וכאשר נמלטנו נודע לנו כי שם האי מליטי׃"));
hebsuperobject.biblemap.put("b44_28_2", new hebsuperobject(44,28,2,"והנכרים לא המעיטו חסדם עמנו כי בערו אש ויאספו את כלנו אליהם מפני הגשם היורד ומפני הקר׃"));
hebsuperobject.biblemap.put("b44_28_3", new hebsuperobject(44,28,3,"ופולוס אסף לו ערמת קצים וישם על המוקד ומן החם יצא אפעה ויאחז בידו׃"));
hebsuperobject.biblemap.put("b44_28_4", new hebsuperobject(44,28,4,"ויראו הנכרים את החיה תלויה על ידו ויאמרו איש אל רעהו אכן רצח האיש הזה אשר הנקמה לא הניחה לו לחיות אף כי נמלט מן הים׃"));
hebsuperobject.biblemap.put("b44_28_5", new hebsuperobject(44,28,5,"והוא נער את החיה מעל ידו אל תוך האש ולא הרעה לו מאומה׃"));
hebsuperobject.biblemap.put("b44_28_6", new hebsuperobject(44,28,6,"והם הוחילו אשר יצבה גופו או אשר יפל מת פתאם ויחילו עד בוש והנה לא קרהו כל אסון ויהפך לבם ויאמרו כי אלהים הוא׃"));
hebsuperobject.biblemap.put("b44_28_7", new hebsuperobject(44,28,7,"ובסביבו המקום ההוא היו שדות אשר לראש אנשי האי ושמו פובליוס הוא הביא אתנו לביתו ויכלכל אתנו בטובו שלשת ימים׃"));
hebsuperobject.biblemap.put("b44_28_8", new hebsuperobject(44,28,8,"ואבי פובליוס מוטל למשכב בקדחת ובמחלה המעים ויבא אליו פולוס ויתפלל וישם עליו את ידיו וירפאהו׃"));
hebsuperobject.biblemap.put("b44_28_9", new hebsuperobject(44,28,9,"ואחרי המעשה הזה באו גם החלים האחרים אשר באי וירפאו׃"));
hebsuperobject.biblemap.put("b44_28_10", new hebsuperobject(44,28,10,"ויכבדנו כבוד גדול ובלכתנו משם ספקו לנו די צרכנו׃"));
hebsuperobject.biblemap.put("b44_28_11", new hebsuperobject(44,28,11,"ואחרי שלשת חדשים עברנו משם באניה אלכסנדרית אשר ישבה באי בימי הסתו ואות דגלה התאומים׃"));
hebsuperobject.biblemap.put("b44_28_12", new hebsuperobject(44,28,12,"ונבא אל סרקוסא ונשב שם שלשת ימים׃"));
hebsuperobject.biblemap.put("b44_28_13", new hebsuperobject(44,28,13,"משם סבנו ונבא אל רגיון ובנשב רוח דרומית ביום המחרת באנו ביום השני אל פוטיולי׃"));
hebsuperobject.biblemap.put("b44_28_14", new hebsuperobject(44,28,14,"ושם מצאנו אחים ויבקשו ממנו לשבת אתם שבעת ימים ובכן הלכנו אל רומי׃"));
hebsuperobject.biblemap.put("b44_28_15", new hebsuperobject(44,28,15,"וישמעו האחים את בואנו ויצאו משם לקראתנו עד לשוק אפיוס ועד לשלשת החניות וירא אתם פולוס ויודה לאלהים ויתחזק׃"));
hebsuperobject.biblemap.put("b44_28_16", new hebsuperobject(44,28,16,"ואחר באנו אל רומי העביר שר המאה את האסירים אל שר הצבא ולפולוס הניחו לשבת לבדו עם איש הצבא השמר אותו׃"));
hebsuperobject.biblemap.put("b44_28_17", new hebsuperobject(44,28,17,"ויהי אחרי שלשת ימים ויקרא פולוס אליו את ראשי היהודים ויקהלו אליו ויאמר אליהם אנשים אחים אף כי לא מעלתי מעל בעמנו ובחקות אבותינו אסרוני בירושלים וימסרוני לידי הרומיים׃"));
hebsuperobject.biblemap.put("b44_28_18", new hebsuperobject(44,28,18,"והם אחרי חקרם אתי אמרו לפטרני כי לא נמצא בי משפט מות׃"));
hebsuperobject.biblemap.put("b44_28_19", new hebsuperobject(44,28,19,"והיהודים קמו בי ואאנס לקרא את הקיסר לדיני אך אין נפשי לשטון את עמי בדבר׃"));
hebsuperobject.biblemap.put("b44_28_20", new hebsuperobject(44,28,20,"ובעבור זאת קראתי לכם לראותכם ולדבר עמכם כי בגלל תקות ישראל אסור אני בכבל הזה׃"));
hebsuperobject.biblemap.put("b44_28_21", new hebsuperobject(44,28,21,"ויאמרו אליו לא קבלנו על אדותיך אגרות מארץ יהודה ולא בא הנה אחד מן האחים אשר הגיד או דבר עליך דבר רע׃"));
hebsuperobject.biblemap.put("b44_28_22", new hebsuperobject(44,28,22,"אמנם חפצים אנחנו לשמע את אשר בלבבך כי נודע לנו אשר בכל מקום יריבו אל הכת הזאת׃"));
hebsuperobject.biblemap.put("b44_28_23", new hebsuperobject(44,28,23,"וישימו לו יום מועד ויבאו אליו רבים אל מלונו ויעד בהם ויבאר את מלכות אלהים ויוכח להם עניני ישוע מתורת משה ומן הנביאים מבקר עד ערב׃"));
hebsuperobject.biblemap.put("b44_28_24", new hebsuperobject(44,28,24,"ויש אשר שמעו אל דבריו ויש אשר לא האמינו׃"));
hebsuperobject.biblemap.put("b44_28_25", new hebsuperobject(44,28,25,"ויחלקו בדעתם ויסורו ללכת לדרכם בדבר פולוס הדבר האחד הזה היטב דבר רוח הקדש לאבותינו בפי ישעיהו הנביא לאמר׃"));
hebsuperobject.biblemap.put("b44_28_26", new hebsuperobject(44,28,26,"לך ואמרת לעם הזה שמעו שמוע ואל תבינו וראו ראו ואל תדעו׃"));
hebsuperobject.biblemap.put("b44_28_27", new hebsuperobject(44,28,27,"כי שמן לב העם הזה ואזניו כבדו ואת עיניו השע פן יראה בעיניו ובאזניו ישמע ולבבו יבין ושב ורפאתיו׃"));
hebsuperobject.biblemap.put("b44_28_28", new hebsuperobject(44,28,28,"לכן דעו כי לגוים נשלחה תשועת אלהים והמה ישמעו׃"));
hebsuperobject.biblemap.put("b44_28_29", new hebsuperobject(44,28,29,"ויהי בדברו זאת הלכו מאתו היהודים ויתוכחו הרבה איש עם רעהו׃"));
hebsuperobject.biblemap.put("b44_28_30", new hebsuperobject(44,28,30,"ופולוס ישב שנתים ימים בביתו אשר שכר לו ויקבל את כל הבאים אליו׃"));
hebsuperobject.biblemap.put("b44_28_31", new hebsuperobject(44,28,31,"ויקרא את מלכות האלהים וילמד את דרכי ישוע המשיח אדנינו בכל בטחון לבו ואין מנע׃"));

