hebsuperobject.biblemap.put("b45_14_20", new hebsuperobject(45,14,20,"אל תהרוס את מעשה אלהים על דבר מאכל הן הכל טהור הוא אבל רע לאדם אשר יאכלנו במכשל לבו׃"));
hebsuperobject.biblemap.put("b45_14_21", new hebsuperobject(45,14,21,"טוב שלא תאכל בשר ולא תשתה יין ולא תעשה דבר אשר יתנגף בו אחיך או יכשל או יחלש׃"));
hebsuperobject.biblemap.put("b45_14_22", new hebsuperobject(45,14,22,"יש לך אמונה תהי לך לבדך לפני האלהים אשרי מי שלא ידין את נפשו בדבר אשר כשר בעיניו׃"));
hebsuperobject.biblemap.put("b45_14_23", new hebsuperobject(45,14,23,"ואשר לו ספק באכלו נאשם כי לא עשה מאמונה וכל אשר נעשה מבלי אמונה חטא הוא,׃"));
hebsuperobject.biblemap.put("b45_15_1", new hebsuperobject(45,15,1,"ועלינו החזקים לשאת חלשות הכשלים ואל נבקש הנאת עצמנו׃"));
hebsuperobject.biblemap.put("b45_15_2", new hebsuperobject(45,15,2,"כי כל אחד ממנו יבקש הנאת חברו לטוב לו למען יבנה׃"));
hebsuperobject.biblemap.put("b45_15_3", new hebsuperobject(45,15,3,"כי גם המשיח לא בקש הנאת עצמו אלא ככתוב חרפות חורפיך נפלו עלי׃"));
hebsuperobject.biblemap.put("b45_15_4", new hebsuperobject(45,15,4,"כי כל אשר נכתב מלפנים ללמדנו נכתב למען תהיה לנו תקוה בסבלנות ובתנחומות הכתובים׃"));
hebsuperobject.biblemap.put("b45_15_5", new hebsuperobject(45,15,5,"ואלהי הסבלנות והנחמה הוא יתן לכם להיות כלכם לב אחד על פי המשיח ישוע׃"));
hebsuperobject.biblemap.put("b45_15_6", new hebsuperobject(45,15,6,"אשר תכבדו בנפש אחת ובפה אחד את האלהים אבי אדנינו ישוע המשיח׃"));
hebsuperobject.biblemap.put("b45_15_7", new hebsuperobject(45,15,7,"על כן תקבלו איש את אחיו כאשר גם המשיח קבל אתנו לכבוד האלהים׃"));
hebsuperobject.biblemap.put("b45_15_8", new hebsuperobject(45,15,8,"ואני אמר כי ישוע המשיח היה למשרת בני המילה למען אמת האלהים לקים את ההבטחות אשר לאבות׃"));
hebsuperobject.biblemap.put("b45_15_9", new hebsuperobject(45,15,9,"והגוים המה יכבדו את האלהים למען רחמיו ככתוב על כן אודך בגוים ולשמך אזמרה׃"));
hebsuperobject.biblemap.put("b45_15_10", new hebsuperobject(45,15,10,"ואומר הרנינו גוים עמו׃"));
