hebsuperobject.biblemap.put("b45_15_12", new hebsuperobject(45,15,12,"וישעיהו אמר והיה שרש ישי אשר עמד לנס עמים אליו גוים יקוו׃"));
hebsuperobject.biblemap.put("b45_15_13", new hebsuperobject(45,15,13,"ואלהי התקוה הוא ימלא אתכם כל שמחה ושלום באמונה למען תעדף תקותכם בגבורת רוח הקדש׃"));
hebsuperobject.biblemap.put("b45_15_14", new hebsuperobject(45,15,14,"והנה אחי מבטח אני בכם כי גם אתם בעצמכם מלאי אהבת חסד ממלאים כל דעת ותוכלו להוכיח איש את רעהו׃"));
hebsuperobject.biblemap.put("b45_15_15", new hebsuperobject(45,15,15,"אך העזתי מעט בכתבי אליכם הנה והנה כמזכיר אתכם על פי החסד הנתון לי מאת האלהים׃"));
hebsuperobject.biblemap.put("b45_15_16", new hebsuperobject(45,15,16,"להיותי משרת ישוע המשיח לגוים ולכהן בבשורת האלהים למען יהיה קרבן הגוים רצוי ומקדש ברוח הקדש׃"));
