hebsuperobject.biblemap.put("b45_15_19", new hebsuperobject(45,15,19,"בגבורת אתות ומופתים ובגבורת רוח אלהים עד כי מלאתי מירושלים וסביבותיה ועד לאלוריקון את בשורת המשיח׃"));
hebsuperobject.biblemap.put("b45_15_20", new hebsuperobject(45,15,20,"בהשתדלי להגיד את הבשורה לא במקמות אשר שם כבר נקרא שם המשיח לבלתי בנות על יסוד אחרים׃"));
hebsuperobject.biblemap.put("b45_15_21", new hebsuperobject(45,15,21,"כי אם ככתוב אשר לא ספר להם ראו ואשר לא שמעו התבוננו׃"));
