hebsuperobject.biblemap.put("b45_15_23", new hebsuperobject(45,15,23,"אבל כעת אשר אין עוד מקום לי בגלילות האלה ונכסף נכספתי לבא אליכם זה שנים רבות׃"));
hebsuperobject.biblemap.put("b45_15_24", new hebsuperobject(45,15,24,"אבוא אליכם בלכתי לאספמיא כי מקוה אנכי לראותכם בעברי ואתם תשלחוני שמה ואשבעה מעט מכם בראשונה׃"));
hebsuperobject.biblemap.put("b45_15_25", new hebsuperobject(45,15,25,"אמנם עתה אלכה ירושלימה לעזור את הקדושים׃"));
