hebsuperobject.biblemap.put("b45_15_27", new hebsuperobject(45,15,27,"כי הואילו ואף מחיבים הם להם כי אם היה לגוים חלק בדברי הרוח אשר להם הלא עליהם לעזרם גם בדברי הגוף׃"));
hebsuperobject.biblemap.put("b45_15_28", new hebsuperobject(45,15,28,"לכן כשגמרתי את זאת וחתמתי להם הפרי הזה אז אעברה דרך ארצכם לאספמיא׃"));
hebsuperobject.biblemap.put("b45_15_29", new hebsuperobject(45,15,29,"ויודע אנכי כי בבאי אליכם אבוא במלא ברכת בשורת המשיח׃"));
hebsuperobject.biblemap.put("b45_15_30", new hebsuperobject(45,15,30,"ואני מעורר אתכם אחי באדנינו ישוע המשיח ובאהבת הרוח להתאמץ אתי בהעתיר בעדי אל האלהים׃"));
hebsuperobject.biblemap.put("b45_15_31", new hebsuperobject(45,15,31,"למען אשר אנצל מהסוררים בארץ יהודה ויערב על הקדושים שמושי לשם ירושלים׃"));
hebsuperobject.biblemap.put("b45_15_32", new hebsuperobject(45,15,32,"ואשר אבוא אליכם במשחה ברצון אלהים ואנפש עמכם׃"));
hebsuperobject.biblemap.put("b45_15_33", new hebsuperobject(45,15,33,"ואלהי השלום עם כלכם אמן׃"));
