hebsuperobject.biblemap.put("b45_16_3", new hebsuperobject(45,16,3,"שאלו לשלום פריסקלא ועקילס שהם חברי בעבודת המשיח ישוע׃"));
hebsuperobject.biblemap.put("b45_16_4", new hebsuperobject(45,16,4,"ואשר מסרו את צוארם בעד נפשי ולא אני לבדי אודה להם כי גם כל קהלות הגוים וגם לשלום הקהלה בביתם׃"));
hebsuperobject.biblemap.put("b45_16_5", new hebsuperobject(45,16,5,"שאלו לשלום אפינטוס חביבי שהוא ראשית אכיא למשיח׃"));
