hebsuperobject.biblemap.put("b45_16_7", new hebsuperobject(45,16,7,"שאלו לשלום אנדרוניקוס ויוניס שהם קרובי ואסורים אתי ולהם שם בשליחים ולפני היו במשיח׃"));
hebsuperobject.biblemap.put("b45_16_8", new hebsuperobject(45,16,8,"שאלו לשלום אמפליאס חביבי באדנינו׃"));
hebsuperobject.biblemap.put("b45_16_9", new hebsuperobject(45,16,9,"שאלו לשלום אורבנוס חברנו בעבודת המשיח ולשלום אסטכים חביבי׃"));
