hebsuperobject.biblemap.put("b45_16_11", new hebsuperobject(45,16,11,"שאלו לשלום הורודיון קרובי שאלו לשלום בני ביתו של נרקיסוס אשר הם באדנינו׃"));
hebsuperobject.biblemap.put("b45_16_12", new hebsuperobject(45,16,12,"שאלו לשלום טרופינה וטרופסה העמלות באדנינו שאלו לשלום פרסיס החביבה שעמלה עמל רב באדנינו׃"));
hebsuperobject.biblemap.put("b45_16_13", new hebsuperobject(45,16,13,"שאלו לשלום רופוס הנבחר באדנינו ולשלום אמו שהיא כאם לי׃"));
