hebsuperobject.biblemap.put("b45_16_18", new hebsuperobject(45,16,18,"כי אנשים אשר כאלה אינם עבדים את אדנינו ישוע המשיח כי אם את כרשם ובאמרי נעם ושפת חלקות יתעו את לב הפתאים׃"));
hebsuperobject.biblemap.put("b45_16_19", new hebsuperobject(45,16,19,"כי משמעתכם נודעת לכל לכן אני שמח עליכם אך חפצתי היתכם חכמים להטיב ותמימים לבלתי הרע׃"));
hebsuperobject.biblemap.put("b45_16_20", new hebsuperobject(45,16,20,"ואלהי השלום הוא ידכא את השטן במהרה תחת רגליכם חסד אדנינו ישוע המשיח עמכם׃"));
hebsuperobject.biblemap.put("b45_16_21", new hebsuperobject(45,16,21,"טימותיוס חברי ולוקיוס ויסון וסוספטרוס קרובי שאלים לשלומכם׃"));
hebsuperobject.biblemap.put("b45_16_22", new hebsuperobject(45,16,22,"אני טרטיוס כותב האגרת הזאת שאל לשלומכם באדנינו׃"));
