hebsuperobject.biblemap.put("b47_5_8", new hebsuperobject(47,5,8,"אמנם בטוחים אנחנו ונבחרה לנו להתרחק מן הגוף ולהיות קרובים אל האדון׃"));
hebsuperobject.biblemap.put("b47_5_9", new hebsuperobject(47,5,9,"על כן גם נשתדל אם קרובים אם רחוקים להיות לו לרצון׃"));
hebsuperobject.biblemap.put("b47_5_10", new hebsuperobject(47,5,10,"כי כלנו עתידים להראות לפני כסא דין המשיח למען יקבל איש ואיש כפי פעלו בחיי גופו אם טוב ואם רע׃"));
hebsuperobject.biblemap.put("b47_5_11", new hebsuperobject(47,5,11,"ועתה יען אשר ידענו יראת האדון נדבר על לב בני אדם ולאלהים אנחנו גלוים ואקוה כי גלוים אנחנו גם במדעכם׃"));
hebsuperobject.biblemap.put("b47_5_12", new hebsuperobject(47,5,12,"כי אין אנחנו משתבחין שנית אליכם אך נתנים לכם סבה להתפאר בנו לנגד המתפארים בפנים ולא בלב׃"));
hebsuperobject.biblemap.put("b47_5_13", new hebsuperobject(47,5,13,"כי אם השתגענו לאלהים היתה זאת ואם השכלנו לכם היתה׃"));
hebsuperobject.biblemap.put("b47_5_14", new hebsuperobject(47,5,14,"כי אהבת המשיח דחקת אתנו בהיותנו דנים אשר אם מת אחד בעד כלם כלם מתו׃"));
hebsuperobject.biblemap.put("b47_5_15", new hebsuperobject(47,5,15,"ובעד כלם מת למען לא יחיו החיים עוד לנפשם כי אם לאשר מת ויקם בעדם׃"));
hebsuperobject.biblemap.put("b47_5_16", new hebsuperobject(47,5,16,"לכן מעתה אנחנו לא נדע איש לפי הבשר וגם אם ידענו את המשיח לפי הבשר מעתה לא נדעהו עוד׃"));
hebsuperobject.biblemap.put("b47_5_17", new hebsuperobject(47,5,17,"ובכן מי שהוא במשיח בריאה חדשה הוא הישנות עברו והנה הכל נהיה לחדש׃"));
hebsuperobject.biblemap.put("b47_5_18", new hebsuperobject(47,5,18,"והכל מאת האלהים המרצה אתנו לעצמו על ידי ישוע המשיח ויתן לנו שרות הרצוי׃"));
hebsuperobject.biblemap.put("b47_5_19", new hebsuperobject(47,5,19,"יען אשר אלהים היה במשיח מרצה את העולם לעצמו ולא חשב להם את פשעיהם וישם בנו את דבר הרצוי׃"));
hebsuperobject.biblemap.put("b47_5_20", new hebsuperobject(47,5,20,"לכן מליצי המשיח אנחנו וכאלו האלהים מזהיר אתכם על ידנו נבקש מכם בעד המשיח התרצו נא אל האלהים׃"));
hebsuperobject.biblemap.put("b47_5_21", new hebsuperobject(47,5,21,"כי את אשר לא ידע חטאת אתו עשה לחטאת בעדנו למען נהיה בו אנחנו לצדקת אלהים׃"));
hebsuperobject.biblemap.put("b47_6_1", new hebsuperobject(47,6,1,"ואנחנו כעזרים נזהירה אתכם שלא תקבלו לריק את חסד אלהים׃"));
hebsuperobject.biblemap.put("b47_6_2", new hebsuperobject(47,6,2,"כי הוא אומר בעת רצון עניתיך וביום ישועה עזרתיך הנה עתה עת רצון הנה עתה יום ישועה׃"));
hebsuperobject.biblemap.put("b47_6_3", new hebsuperobject(47,6,3,"ואין אנחנו נתנים מכשול במאומה פן יהיה שרותנו לנאצה׃"));
hebsuperobject.biblemap.put("b47_6_4", new hebsuperobject(47,6,4,"כי אם נראה בכל דבר כמשרתי אלהים בסבלנות רבה בלחץ ובצרות ובמצוקות׃"));
hebsuperobject.biblemap.put("b47_6_5", new hebsuperobject(47,6,5,"במכות ובמוסרות ובמהומות בתלאות בשקידות ובצומות׃"));
hebsuperobject.biblemap.put("b47_6_6", new hebsuperobject(47,6,6,"בטהרה ובדעת ובארך רוח ובחסד וברוח הקדש ובאהבה לא צבועה׃"));
hebsuperobject.biblemap.put("b47_6_7", new hebsuperobject(47,6,7,"בדבר האמת ובגבורת אלהים בכלי נשק הצדקה מימין ומשמאל׃"));
hebsuperobject.biblemap.put("b47_6_8", new hebsuperobject(47,6,8,"בכבוד ובקלון בשם רע ובשם טוב כמתעים ובכל זאת נאמנים׃"));
hebsuperobject.biblemap.put("b47_6_9", new hebsuperobject(47,6,9,"כאינם ידועים וגם ידועים כמתים והננו חיים כמיסרים ולא מומתים׃"));
hebsuperobject.biblemap.put("b47_6_10", new hebsuperobject(47,6,10,"כנעצבים ובכל עת שמחים כרשים ומעשירי רבים כאשר אין להם מאומה ויש להם כל׃"));
hebsuperobject.biblemap.put("b47_6_11", new hebsuperobject(47,6,11,"פינו פתוח לכם אנשי קורנתוס ורחב לבנו׃"));
hebsuperobject.biblemap.put("b47_6_12", new hebsuperobject(47,6,12,"לא צר מקומכם בנו אך צר המקום במעיכם׃"));
hebsuperobject.biblemap.put("b47_6_13", new hebsuperobject(47,6,13,"והיה זה גמולי כמו אל בנים אנכי מדבר אם תרחיבו לבבכם גם אתם׃"));
hebsuperobject.biblemap.put("b47_6_14", new hebsuperobject(47,6,14,"אל תהיו משכי על זר עם חסרי אמונה כי אי זה שתפות יש לצדקה עם העול ואי זה התחברות לאור עם החשך׃"));
hebsuperobject.biblemap.put("b47_6_15", new hebsuperobject(47,6,15,"ואי זה הסכמה למשיח עם בליאל או מה חלק המאמין עם שאיננו מאמין׃"));
hebsuperobject.biblemap.put("b47_6_16", new hebsuperobject(47,6,16,"ואי זה דבק יש להיכל אלהים עם האלילים כי אתם היכל אלהים חיים כמו שאמר האלהים ושכנתי והתהלכתי בתוכם והייתי להם לאלהים והם יהיו לי לעם׃"));
hebsuperobject.biblemap.put("b47_6_17", new hebsuperobject(47,6,17,"על כן צאו מתוכם והברו נאם יהוה וטמא אל תגעו ואני אקבץ אתכם׃"));
hebsuperobject.biblemap.put("b47_6_18", new hebsuperobject(47,6,18,"והייתי לכם לאב ואתם תהיו לי לבנים ולבנות נאם יהוה צבאות׃"));
hebsuperobject.biblemap.put("b47_7_1", new hebsuperobject(47,7,1,"לכן חביבי בהיות לנו ההבטחות האלה נטהרה את עצמנו מכל טמאת בשר ורוח להשלים קדשתנו ביראת אלהים׃"));
hebsuperobject.biblemap.put("b47_7_2", new hebsuperobject(47,7,2,"תנו לנו מקום בלבבכם לא חמסנו איש לא חבלנו איש לא עשקנו איש׃"));
hebsuperobject.biblemap.put("b47_7_3", new hebsuperobject(47,7,3,"לא לחיב אתכם אני מדבר הלא הקדמתי לאמר כי אתם בלבנו יחד למות ויחד לחיות׃"));
hebsuperobject.biblemap.put("b47_7_4", new hebsuperobject(47,7,4,"רב בטחוני עליכם רבה תהלתי בכם מלאתי נחמה שבעתי שמחות בכל לחצנו׃"));
hebsuperobject.biblemap.put("b47_7_5", new hebsuperobject(47,7,5,"כי גם בבאנו אל מקדוניא לא היתה מרגעה לבשרנו רק נלחצנו בכל מחוץ מלחמות ומחדרים אימה׃"));
hebsuperobject.biblemap.put("b47_7_6", new hebsuperobject(47,7,6,"אבל האלהים המנחם את השפלים נחם אתנו בבוא טיטוס׃"));
hebsuperobject.biblemap.put("b47_7_7", new hebsuperobject(47,7,7,"ולא בבאו לבד כי אם גם בנחמה אשר נחם בכם בהגידו לנו את תשוקתכם ואת אבלכם ואת קנאתכם לי ובכן הוספתי לשמוח׃"));
hebsuperobject.biblemap.put("b47_7_8", new hebsuperobject(47,7,8,"כי גם אם העצבתי אתכם באגרת אינני מתחרט גם כי התחרטתי לפני מזה בראותי כי האגרת ההיא העציבה אתכם ואף אם לשעה׃"));
hebsuperobject.biblemap.put("b47_7_9", new hebsuperobject(47,7,9,"עתה אני שמח לא על אשר נעצבתם כי אם על אשר נעצבתם לתשובה כי נעצבתם כרצון אלהים למען לא תשאו נזק במאומה על ידנו׃"));
hebsuperobject.biblemap.put("b47_7_10", new hebsuperobject(47,7,10,"כי העצבת שהיא כרצון אלהים תפעל תשובה לישועה אשר איש לא יתחרט עליה אבל עצבת העולם פעלת את המות׃"));
hebsuperobject.biblemap.put("b47_7_11", new hebsuperobject(47,7,11,"כי ראו נא את אשר נעצבתם כרצון אלהים כמה הביא אתכם זה לידי זריזות גם להתנצלות גם לרגז גם ליראה גם לתשוקה גם לקנאה גם לנקמה ובכל הוכחתם כי נקיים אתם בדבר ההוא׃"));
hebsuperobject.biblemap.put("b47_7_12", new hebsuperobject(47,7,12,"לכן גם אם כתבתי לכם לא כתבתי בעבור העלב ולא בעבור הנעלב רק למען תגלה בכם זריזותנו בעדכם לפני האלהים׃"));
hebsuperobject.biblemap.put("b47_7_13", new hebsuperobject(47,7,13,"ובעבור זאת נחמנו בנחמתכם ועוד שמחה יתרה היתה לנו בשמחת טיטוס כי הונח רוחו על ידי כלכם׃"));
hebsuperobject.biblemap.put("b47_7_14", new hebsuperobject(47,7,14,"כי במה שהתהללתי לפניו בכם לא נכלמתי כי כמו שדברנו הכל אליכם באמת כן גם תהלתנו אל טיטוס היתה אמת׃"));
hebsuperobject.biblemap.put("b47_7_15", new hebsuperobject(47,7,15,"וביותר מעיו המו לכם בזכרו את משמעת כלכם ואת אשר קבלתם אתו ביראה ובחרדה׃"));
hebsuperobject.biblemap.put("b47_7_16", new hebsuperobject(47,7,16,"לכן אני שמח כי בכל דבר יאמץ לבי בכם׃"));
hebsuperobject.biblemap.put("b47_8_1", new hebsuperobject(47,8,1,"והננו מודיעים אתכם אחי את חסד אלהים הנתן בקהלות מקדוניא׃"));
hebsuperobject.biblemap.put("b47_8_2", new hebsuperobject(47,8,2,"כי ברב נסיון הלחץ רבתה שמחתם ושפלות רישם העדיפה להראות עשר תמתם׃"));
hebsuperobject.biblemap.put("b47_8_3", new hebsuperobject(47,8,3,"כי מעיד אנכי אשר לפי כחם ויותר מכחם התנדבו׃"));
hebsuperobject.biblemap.put("b47_8_4", new hebsuperobject(47,8,4,"ויבקשו ממנו ברב תחנונים להיות חברינו בגמילות חסדם לעזרת הקדשים׃"));
hebsuperobject.biblemap.put("b47_8_5", new hebsuperobject(47,8,5,"ולא כאשר הוחלנו כי אם את עצמם נתנו בראשונה לאדון וגם לנו ברצון האלהים׃"));
hebsuperobject.biblemap.put("b47_8_6", new hebsuperobject(47,8,6,"עד כי בקשנו מן טיטוס כאשר החל כן גם לגמור בכם את גמילות החסד הזאת׃"));
hebsuperobject.biblemap.put("b47_8_7", new hebsuperobject(47,8,7,"אבל כאשר הותרתם בכל באמונה ובדבור ובדעת ובכל זריזות ובאהבתכם אתנו כן גם תותירו בחסד הזה׃"));
hebsuperobject.biblemap.put("b47_8_8", new hebsuperobject(47,8,8,"ואינני אמר זאת בדרך צווי כי אם לבחן על ידי זריזות אחרים גם את אמתת אהבתכם׃"));
hebsuperobject.biblemap.put("b47_8_9", new hebsuperobject(47,8,9,"כי ידעים אתם את חסד אדנינו ישוע המשיח כי בהיותו עשיר נעשה רש בעבורכם למען תעשירו על ידי רישו׃"));
hebsuperobject.biblemap.put("b47_8_10", new hebsuperobject(47,8,10,"ואחוה את דעתי בדבר הזה כי זאת להועיל לכם אשר הקדמתם כבר בשנה שעברה לא לעשות בלבד כי גם לחפץ׃"));
hebsuperobject.biblemap.put("b47_8_11", new hebsuperobject(47,8,11,"ועתה גם השלימו את המעשה למען כאשר התנדבתם לעשות כן גם תגמרו כפי יכלתכם׃"));
hebsuperobject.biblemap.put("b47_8_12", new hebsuperobject(47,8,12,"כי בהמצא לאיש רוח נדיבה רצויה היא לפי מה שיש לו ולא לפי מה שאין לו׃"));
hebsuperobject.biblemap.put("b47_8_13", new hebsuperobject(47,8,13,"כי לא למען תהיה רוחה לאחרים ולכם צוקה כי אם בשווי ימלא יתרכם בעת הזאת את מחסורם׃"));
hebsuperobject.biblemap.put("b47_8_14", new hebsuperobject(47,8,14,"למען גם יתרם יהיה למלא מחסרכם כדי להשות׃"));
hebsuperobject.biblemap.put("b47_8_15", new hebsuperobject(47,8,15,"ככתוב לא העדיף המרבה והממעיט לא החסיר׃"));
hebsuperobject.biblemap.put("b47_8_16", new hebsuperobject(47,8,16,"ותודות לאלהים הנותן גם בלב טיטוס לשקד עליכם בשקידה כזאת׃"));
hebsuperobject.biblemap.put("b47_8_17", new hebsuperobject(47,8,17,"כי שמע לבקשתנו ובשקידתו היתרה הלך אליכם מרצון נפשו׃"));
hebsuperobject.biblemap.put("b47_8_18", new hebsuperobject(47,8,18,"ועמו יחדו שלחנו האח אשר יצא שבחו בבשורה בכל הקהלות׃"));
hebsuperobject.biblemap.put("b47_8_19", new hebsuperobject(47,8,19,"ומלבד זאת גם נבחר הוא מאת הקהלות ללכת אתנו להביא החסד הזה הגבוי על ידינו לכבוד האדון ולאמץ לבבכם׃"));
hebsuperobject.biblemap.put("b47_8_20", new hebsuperobject(47,8,20,"ונשמר בזאת שלא יוציא איש עלינו דבה רעה בשפעת המתנה הזאת הגבויה על ידינו׃"));
hebsuperobject.biblemap.put("b47_8_21", new hebsuperobject(47,8,21,"כי משגיחים אנחנו על הטוב לא לפני האדון לבד כי גם לפני האדם׃"));
hebsuperobject.biblemap.put("b47_8_22", new hebsuperobject(47,8,22,"ונשלח עמהם את אחינו אשר בחנו את שקידתו פעמים רבות בדברים הרבה ועתה הוא שקוד עוד יותר בגדל בטחונו עליכם׃"));
hebsuperobject.biblemap.put("b47_8_23", new hebsuperobject(47,8,23,"אם לטיטוס הנה חברי הוא ועזרי בכם ואם לאחינו הנה שלוחי הקהלות הם ותפארת המשיח׃"));
hebsuperobject.biblemap.put("b47_8_24", new hebsuperobject(47,8,24,"על כן הראו והוכיחו להם לפני הקהלות את אהבתכם ואת תהלתנו עליכם׃"));
hebsuperobject.biblemap.put("b47_9_1", new hebsuperobject(47,9,1,"אמנם אין צרך לכתב אליכם על דבר השרות אשר לעזרת הקדשים׃"));
hebsuperobject.biblemap.put("b47_9_2", new hebsuperobject(47,9,2,"כי ידעתי את נדיבתכם אשר עליה אני מתהלל בכם לפני אנשי מקדוניא לאמר אכיא מזמנת היא משנה שעברה ותער הקנאה היוצאת מכם את רוח רבם׃"));
hebsuperobject.biblemap.put("b47_9_3", new hebsuperobject(47,9,3,"אבל שלחתי את האחים פן תהיה לריק תהלתנו עליכם בדבר הזה ולמען תהיו מזמנים כאשר אמרתי׃"));
hebsuperobject.biblemap.put("b47_9_4", new hebsuperobject(47,9,4,"פן בבוא אתי אנשים ממקדוניא ימצאו אתכם לא מזמנים ונבוש אנחנו ואינני אמר אתם בבטחון הזה אשר התהללנו בו׃"));
hebsuperobject.biblemap.put("b47_9_5", new hebsuperobject(47,9,5,"על כן היה נכון בעיני לבקש מן האחים כי יקדמו ללכת אליכם ויכינו את ברכתכם המיועדה מלפנים למען תהיה מוכנת כעין ברכה ולא כעין כילות׃"));
hebsuperobject.biblemap.put("b47_9_6", new hebsuperobject(47,9,6,"כי הנה הזרע בצמצום גם יקצר בצמצום והזרע בברכות גם יקצר בברכות׃"));
hebsuperobject.biblemap.put("b47_9_7", new hebsuperobject(47,9,7,"וכל איש כאשר ידבנו לבו לא מצער ולא מאנס כי הנתן בלב שמח יאהבנו אלהים׃"));
hebsuperobject.biblemap.put("b47_9_8", new hebsuperobject(47,9,8,"ואלהים יכל להשפיע עליכם כל חסד עד כי יהיה לכם בכל עת די ספוקכם בכל ותותירו בכל מעשה טוב׃"));
hebsuperobject.biblemap.put("b47_9_9", new hebsuperobject(47,9,9,"ככתוב פזר נתן לאביונים צדקתו עמדת לעד׃"));
hebsuperobject.biblemap.put("b47_9_10", new hebsuperobject(47,9,10,"והנתן זרע לזרע ולחם לאכל יתן וירבה את זרעכם ויפריא תנובות צדקתכם׃"));
hebsuperobject.biblemap.put("b47_9_11", new hebsuperobject(47,9,11,"למען תעשירו בכל לכל התמימות הפעלת תודה לאלהים על ידינו׃"));
hebsuperobject.biblemap.put("b47_9_12", new hebsuperobject(47,9,12,"כי שרות העבודה הזאת לא לבד ימלא את מחסרי הקדושים כי גם יודו רבים לאלהים בשרותכם הנאמן הזה׃"));
hebsuperobject.biblemap.put("b47_9_13", new hebsuperobject(47,9,13,"ויכבדו את האלהים על משמעת הודאתכם לבשורת המשיח ועל תמת התחברותכם אליהם ואל כלם׃"));
hebsuperobject.biblemap.put("b47_9_14", new hebsuperobject(47,9,14,"והם בהתחננם בעדכם נכספים לכם בעבור חסד האלהים אשר גבר עליכם׃"));
hebsuperobject.biblemap.put("b47_9_15", new hebsuperobject(47,9,15,"ותודה לאלהים על מתנתו העצומה מספר׃"));
hebsuperobject.biblemap.put("b47_10_1", new hebsuperobject(47,10,1,"ואני פולוס הנני מזהיר אתכם בענות המשיח וחמלתו אשר פנים בפנים שפל אני בתוככם וברחקי מתאמץ עליכון׃"));
hebsuperobject.biblemap.put("b47_10_2", new hebsuperobject(47,10,2,"ואתחננה שלא אצטרך בבאי להתאמץ בבטחון ההוא אשר אחשב להתגבר בו נגד האנשים החשבים אתנו כמתהלכים לפי הבשר׃"));
hebsuperobject.biblemap.put("b47_10_3", new hebsuperobject(47,10,3,"כי בלכתנו בבשר לא נלחם לפי הבשר׃"));
hebsuperobject.biblemap.put("b47_10_4", new hebsuperobject(47,10,4,"כי כלי מלחמתנו אינם של הבשר כי אם חזקים הם לאלהים להרס מבצרים׃"));
hebsuperobject.biblemap.put("b47_10_5", new hebsuperobject(47,10,5,"באשר הרסים אנחנו תחבלות וכל מרום המתנשא נגד דעת האלהים ושובים כל מזמה למשמעת המשיח׃"));
hebsuperobject.biblemap.put("b47_10_6", new hebsuperobject(47,10,6,"ונכונים לנקם נקמה מאת כל מרי אם תשלם משמעתכם׃"));
hebsuperobject.biblemap.put("b47_10_7", new hebsuperobject(47,10,7,"התשפטו למראה פנים איש כי יבטח להיות למשיח ישוב וידין בלבו כי כאשר הוא למשיח כן למשיח גם אנחנו׃"));
hebsuperobject.biblemap.put("b47_10_8", new hebsuperobject(47,10,8,"וגם כי אתהלל יותר מעט על דבר הרשות אשר נתן לנו האדון לבנותכם ולא לשחתכם לא אבוש׃"));
hebsuperobject.biblemap.put("b47_10_9", new hebsuperobject(47,10,9,"למען לא אראה כמאים אתכם על ידי האגרות׃"));
hebsuperobject.biblemap.put("b47_10_10", new hebsuperobject(47,10,10,"כאמרם הן האגרות קשות הנה וחזקות אבל הגוף בהיותו לנגדנו חלוש הוא וניבו נבזה׃"));
hebsuperobject.biblemap.put("b47_10_11", new hebsuperobject(47,10,11,"ידע נא האמר כזאת כי כמו שאנחנו בדבור על ידי אגרות בהיותנו רחוקים כן גם בפעל אנחנו בהיותנו קרובים׃"));
hebsuperobject.biblemap.put("b47_10_12", new hebsuperobject(47,10,12,"כי אל נעז פנינו לכלל אתנו עם האנשים המשבחים נפשם או לערך אתנו לאלה אכן נבערו מדעת המדדים את נפשם בנפשם וערכים את נפשם לנפשם׃"));
hebsuperobject.biblemap.put("b47_10_13", new hebsuperobject(47,10,13,"ואנחנו לא נתגדל לבלי מדה כי אם כפי מדת הגבול אשר חלק לנו האלהים לחק להגיע גם עדיכם׃"));
hebsuperobject.biblemap.put("b47_10_14", new hebsuperobject(47,10,14,"כי לולא הגענו עדיכם לא נשתרע למעלה מערכנו הלא כבר קדמנו גם אתכם בבשורת המשיח׃"));
hebsuperobject.biblemap.put("b47_10_15", new hebsuperobject(47,10,15,"לא נתגדל לבלי מדה ביגיעת אחרים אבליש לנו התקוה כי ברבות אמונתכם נכבד בכם כפי גבולנו עד למעלה׃"));
hebsuperobject.biblemap.put("b47_10_16", new hebsuperobject(47,10,16,"כדי לבשר הבשורה גם להלאה מכם ולא להתהלל במה שמוכן כבר בגבול אחרים׃"));
hebsuperobject.biblemap.put("b47_10_17", new hebsuperobject(47,10,17,"והמתהלל יתהלל ביהוה׃"));
hebsuperobject.biblemap.put("b47_10_18", new hebsuperobject(47,10,18,"כי לא המשבח נפשו משבח הוא כי אם אשר ישבחנו יהוה׃"));
hebsuperobject.biblemap.put("b47_11_1", new hebsuperobject(47,11,1,"אחלי תשאו מעט אולתי ואף אמנם תשאוני׃"));
hebsuperobject.biblemap.put("b47_11_2", new hebsuperobject(47,11,2,"כי מקנא אני לכם קנאת אלהים כי ארשתי אתכם לאיש אחד להעמיד אתכם בתולה טהורה לפני המשיח׃"));
hebsuperobject.biblemap.put("b47_11_3", new hebsuperobject(47,11,3,"אך ירא אנכי פן כאשר השיא הנחש בערמתו את חוה כן תשטינה גם מחשבותיכם מן התמימות אשר עם המשיח׃"));
hebsuperobject.biblemap.put("b47_11_4", new hebsuperobject(47,11,4,"כי אם יבא הבא והגיד לכם ישוע אחר אשר לא הגדנהו או אם תקחו רוח אחר אשר לא לקחתם אתו או בשורה אחרת אשר לא קבלתם אתה כי עתה היטב תשאהו׃"));
hebsuperobject.biblemap.put("b47_11_5", new hebsuperobject(47,11,5,"אולם אחשב אשר אינני נפל במאומה מהשליחים הגדולים כל כך׃"));
hebsuperobject.biblemap.put("b47_11_6", new hebsuperobject(47,11,6,"ואף אם בער אנכי בדבור אינני בער בדעת כי אם בכל נגלינו אליכם בפני כל אדם׃"));
hebsuperobject.biblemap.put("b47_11_7", new hebsuperobject(47,11,7,"או החטא חטאתי בהשפילי את עצמי למען הגביהכם כי בשרתי אתכם בלא מחיר את בשורת האלהים׃"));
hebsuperobject.biblemap.put("b47_11_8", new hebsuperobject(47,11,8,"קהלות אחרות שלותי בקחתי מהן שכר למען אשרתכם ובהיותי עמכם ואחסר לא הלאיתי אדם׃"));
hebsuperobject.biblemap.put("b47_11_9", new hebsuperobject(47,11,9,"כי את מחסורי מלאו האחים בבאם ממקדוניא ובכל דבר נשמרתי מהיות לכם למשא וגם אשמר׃"));
hebsuperobject.biblemap.put("b47_11_10", new hebsuperobject(47,11,10,"באמתו של המשיח אשר בי מעיד אני בכם כי תהלתי זאת לא תכלא ממני בגלילות אכיא׃"));
hebsuperobject.biblemap.put("b47_11_11", new hebsuperobject(47,11,11,"על מה זה העל אשר אינני אהב אתכם האלהים יודע׃"));
hebsuperobject.biblemap.put("b47_11_12", new hebsuperobject(47,11,12,"אבל את אשר אני עשה אוסיף לעשות לבלתי תת מקום למבקשים עלילה למען במה שיתהללו בו ימצאו דומים לנו׃"));
hebsuperobject.biblemap.put("b47_11_13", new hebsuperobject(47,11,13,"כי אנשים כאלה שליחי שקר הם פעלי רמיה מתחפשים לשליחי המשיח׃"));
hebsuperobject.biblemap.put("b47_11_14", new hebsuperobject(47,11,14,"ואיננו פלא הלא השטן גם הוא מתחפש למלאך האור׃"));
hebsuperobject.biblemap.put("b47_11_15", new hebsuperobject(47,11,15,"לכן אין זה דבר גדול אם גם משרתיו יתחפשו למשרתי הצדק אשר אחריתם תהיה לפי מעלליהם׃"));
hebsuperobject.biblemap.put("b47_11_16", new hebsuperobject(47,11,16,"ואשוב ואמר אל נא יחשבני איש לסכל ואם אין אף כסכל קבלוני למען אתהלל מעט גם אני׃"));
hebsuperobject.biblemap.put("b47_11_17", new hebsuperobject(47,11,17,"את אשר אדבר לא לפי דרך האדון אנכי מדבר כי אם כמו בסכלות אעז ואתהלל׃"));
hebsuperobject.biblemap.put("b47_11_18", new hebsuperobject(47,11,18,"רבים מתהללים לפי הבשר לכן אתהלל אף אני׃"));
hebsuperobject.biblemap.put("b47_11_19", new hebsuperobject(47,11,19,"כי נשאים אתם ברצון את הסכלים בהיותכם חכמים׃"));
hebsuperobject.biblemap.put("b47_11_20", new hebsuperobject(47,11,20,"הלא תשאו אם יעביד אתכם איש אם יבלע אם ילכד אתכם אם יתנשא אם יכה אתכם על פניכם׃"));
hebsuperobject.biblemap.put("b47_11_21", new hebsuperobject(47,11,21,"לחרפתנו אנכי אמר זאת כאלו היינו רפים אבל בכל אשר ירהב איש להתהלל בסכלות אמר זאת ארהב גם אנכי׃"));
hebsuperobject.biblemap.put("b47_11_22", new hebsuperobject(47,11,22,"עברים הם כן גם אנכי ישראלים הם כן גם אנכי זרע אברהם הם כן גם אנכי׃"));
hebsuperobject.biblemap.put("b47_11_23", new hebsuperobject(47,11,23,"משרתי המשיח הם כמתהולל אמר אנכי יותר מהם יותר ביגיעות יותר למאד במכות יותר במוסרות ורב פעמים במצוקות מות׃"));
hebsuperobject.biblemap.put("b47_11_24", new hebsuperobject(47,11,24,"חמש פעמים ספגתי בידי היהודים ארבעים חסר אחת׃"));
hebsuperobject.biblemap.put("b47_11_25", new hebsuperobject(47,11,25,"שלש פעמים יסרתי בשוטים פעם אחת סקלתי ושלש פעמים נשברה לי הספינה ואהי במצולות ים לילה ויום׃"));
hebsuperobject.biblemap.put("b47_11_26", new hebsuperobject(47,11,26,"במסעות רבות בסכנות נהרות בסכנות שדדים בסכנות מצד בני עמי בסכנות מצד הגוים סכנות בעיר סכנות במדבר סכנות בים סכנות בתוך אחי שקר׃"));
hebsuperobject.biblemap.put("b47_11_27", new hebsuperobject(47,11,27,"בעמל ובתלאה בשקידות הרבה ברעב ובצמא בצומות הרבה בקר ובעירום׃"));
hebsuperobject.biblemap.put("b47_11_28", new hebsuperobject(47,11,28,"מלבד שאר הדברים הבאים עלי יום יום והדאגה לכל הקהלות׃"));
hebsuperobject.biblemap.put("b47_11_29", new hebsuperobject(47,11,29,"מי יחלש ואני לא אחלש מי יכשל ולא יבער לבי׃"));
hebsuperobject.biblemap.put("b47_11_30", new hebsuperobject(47,11,30,"אם יש להתהלל אתהלל בחלשתי׃"));
hebsuperobject.biblemap.put("b47_11_31", new hebsuperobject(47,11,31,"האלהים אבי ישוע המשיח אדנינו המברך לעולמי עולמים הוא ידע כי לא אשקר׃"));
hebsuperobject.biblemap.put("b47_11_32", new hebsuperobject(47,11,32,"בדמשק שמר הנצב של המלך ארטס את עיר הדמשקיין ויבקש לתפשני׃"));
hebsuperobject.biblemap.put("b47_11_33", new hebsuperobject(47,11,33,"ובעד החלון הורידו אתי בסל מעל החומה ואמלט מידיו׃"));
hebsuperobject.biblemap.put("b47_12_1", new hebsuperobject(47,12,1,"אמנם להתהלל לא יועיל לי כי אבוא למראות האדון וחזינותיו׃"));
hebsuperobject.biblemap.put("b47_12_2", new hebsuperobject(47,12,2,"ידעתי איש במשיח זה ארבע עשרה שנה אם היה בגוף לא ידעתי או מחוץ לגוף לא ידעתי האלהים יודע והאיש ההוא לקח עד לרקיע השלישי׃"));
hebsuperobject.biblemap.put("b47_12_3", new hebsuperobject(47,12,3,"וידעתי את האיש ההוא אם בגוף או מחוץ לגוף לא ידעתי האלהים יודע׃"));
hebsuperobject.biblemap.put("b47_12_4", new hebsuperobject(47,12,4,"אשר העלה אל הפרדס וישמע דברים נסתרים אשר נמנע מאיש למללם׃"));
hebsuperobject.biblemap.put("b47_12_5", new hebsuperobject(47,12,5,"על איש כמוהו אתהלל אך על עצמי לא אתהלל זולתי בחלשותי׃"));
hebsuperobject.biblemap.put("b47_12_6", new hebsuperobject(47,12,6,"כי לו חפצתי להתהלל לא אהיה סכל כי אמת אדבר אבל אתאפק פן יחשבני איש יותר ממה שיראה בי או שישמע ממני׃"));
hebsuperobject.biblemap.put("b47_12_7", new hebsuperobject(47,12,7,"ולמען אשר לא אתרומם ברב גדל החזינות נתן לי סלון בבשרי מלאך השטן להכתני באגרוף למען לא אתרומם׃"));
hebsuperobject.biblemap.put("b47_12_8", new hebsuperobject(47,12,8,"על זאת התחננתי שלש פעמים אל האדון להסירו ממני׃"));
hebsuperobject.biblemap.put("b47_12_9", new hebsuperobject(47,12,9,"ויאמר אלי די לך חסדי כי בחלשה תשלם גבורתי על כן שמח לבי להתהלל בחלשותי למען תשרה עלי גבורת המשיח׃"));
hebsuperobject.biblemap.put("b47_12_10", new hebsuperobject(47,12,10,"לכן רצתה נפשי בחלשות ובחרפות ובצרות וברדיפות ובמצוקות בעד המשיח כי כאשר חלשתי אז גבור אני׃"));
hebsuperobject.biblemap.put("b47_12_11", new hebsuperobject(47,12,11,"סכל הייתי בהתהללי אתם הכרחתם אתי תחת אשר היה עליכם להזכירני לשבח כי לא נפל אנכי במאומה מן השליחים הגדולים כל כך אף כי כאין אנכי׃"));
hebsuperobject.biblemap.put("b47_12_12", new hebsuperobject(47,12,12,"הן אתות השליח נעשו בקרבכם בכל סבלנות באתות ובמופתים ובגבורות׃"));
hebsuperobject.biblemap.put("b47_12_13", new hebsuperobject(47,12,13,"כי במה נגרעתם מן הקהלות האחרות אם לא הלאיתי אתכם סלחו נא לי את העולה הזאת׃"));
hebsuperobject.biblemap.put("b47_12_14", new hebsuperobject(47,12,14,"הנני מוכן לבוא אליכם פעם שלישית ולא אלאה אתכם כי לא אבקש את אשר לכם כי אם אתכם כי הבנים אינם חיבים לאצר אוצרות לאבות כי אם האבות לבנים׃"));
hebsuperobject.biblemap.put("b47_12_15", new hebsuperobject(47,12,15,"אבל אני בכל חפץ לבבי אפזר וגם אפזר בעד נפשותיכם אף אם תחת אהבתי לכם היתרה אתם תמעיטו את אהבתכם אלי׃"));
hebsuperobject.biblemap.put("b47_12_16", new hebsuperobject(47,12,16,"אך אם כן הוא ואני לא הכבדתי עליכם אולי כאיש ערום במרמה לכדתי אתכם׃"));
hebsuperobject.biblemap.put("b47_12_17", new hebsuperobject(47,12,17,"האף הוניתי אתכם ביד אחד מאלה אשר שלחתי אליכם׃"));
hebsuperobject.biblemap.put("b47_12_18", new hebsuperobject(47,12,18,"בקשתי מן טיטוס ושלחתי אתו את האח הכי הונה אתכם טיטוס הלא ברוח אחד התהלכנו הלא במעגל אחד׃"));
hebsuperobject.biblemap.put("b47_12_19", new hebsuperobject(47,12,19,"התחשבו עוד כי מתנצלים אנחנו אליכם לא כי לפני האלהים נדבר במשיח וכל זאת חביבי למען תבנו׃"));
hebsuperobject.biblemap.put("b47_12_20", new hebsuperobject(47,12,20,"כי ירא אנכי פן בבאי לא אמצא אתכם כאשר חפצתי ואתם גם אתם לא תמצאו אתי כאשר חפצתם פן יהיה בכם מצה וקנאה ורגז ומריבות ודבה רעה ורכילות וגאות ומבוכה׃"));
hebsuperobject.biblemap.put("b47_12_21", new hebsuperobject(47,12,21,"פן אשוב לבוא וישפילני אלהי אצלכם ואתאבל על רבים אשר חטאו כבר ולא שבו מן הטמאה ומן הזנות ומן הזמה אשר עשו׃"));
hebsuperobject.biblemap.put("b47_13_1", new hebsuperobject(47,13,1,"זאת הפעם השלישית אשר אבוא אליכם על פי שנים עדים או שלשה עדים יקום כל דבר׃"));
hebsuperobject.biblemap.put("b47_13_2", new hebsuperobject(47,13,2,"מקדם אמרתי בהיותי אצלכם בפעם השנית ומקדם אני אמר וכתב עתה ברחקי מכם לאשר חטאו לפני מזה ולכל הנשארים כי בשובי לבוא לא אחוס׃"));
hebsuperobject.biblemap.put("b47_13_3", new hebsuperobject(47,13,3,"יען בקשכם לבחן את המשיח המדבר בי אשר איננו חלש לכם כי אם גבור הוא בתוככם׃"));
